INSERT INTO `prescript` VALUES (57511, '26029', '肥气主方', '半夏3钱，楂肉2钱，橘红1钱5分，白术1钱，柴胡1钱，人参7分，川芎5分，生姜1片。', '水煎，早空心、午前服。病初起，可加白芥子5分攻隐伏痰，青皮5分破固结之气，久则忌用；白术渐加至3钱，人参渐加至1钱5分；楂肉渐减至不用；虚寒加肉桂5分。', '《活人方》卷四', '肥气，形枯神萎，其脉两关沉弦而急，或弦滑而数。', '-', '-', '柴胡', '[\'半夏\', \'橘红\', \'钱分\', \'白术\', \'柴胡\', \'人参\', \'川芎\', \'生姜\']');
INSERT INTO `prescript` VALUES (57512, '26026', '地黄汤', '生干地黄（切，焙）1两，赤茯苓（去黑皮）1两，柴胡（去苗）1两，射干半两，甘草（炙，锉）半两，麦门冬（去心，焙）半两，半夏（汤洗7遍）3分，麻黄（去根节）3分，紫菀（去苗土）3分，五味子3分，黄芩（去黑心）3分，桑根白皮（锉）3分。', '每服3钱匕，水1盏，加生姜1枣大（拍碎），大枣2枚（擘破），煎至7分，去滓温服，不拘时候。', '《圣济总录》卷四十九', '肺痿咽燥，咳嗽吐脓血，胸胁胀满短气，羸瘦不思饮食。', '-', '-', '柴胡', '[\'生干\', \'地黄\', \'切焙\', \'茯苓\', \'黑皮\', \'柴胡\', \'射干\', \'半两\', \'甘草\', \'半两\', \'麦门冬\', \'半两\', \'半夏\', \'汤洗\', \'遍分\', \'麻黄\', \'根节\', \'紫菀\', \'苗土\', \'五味子\', \'黄芩\', \'黑心\', \'分桑根\', \'白皮\', \'锉分\']');
