INSERT INTO `prescript` VALUES (57517, '25774', '诃黎勒散', '诃黎勒2两（用皮），鳖甲1两（涂醋炙令黄，去裙襕），枳壳半两（麸炒微黄，去瓤），白茯苓1两，紫菀半两（去苗土），柴胡1两（去苗），黄耆1两（锉），杏仁半两（汤浸，去皮尖双仁，麸炒微黄），百合1两，甘草半两（炙微赤，锉），酸枣仁1两。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷二十七', '虚劳咳嗽，或时寒热，不得眠卧。', '忌苋菜。', '-', '柴胡', '[\'黎勒\', \'两用\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'枳壳\', \'半两\', \'微黄\', \'白茯苓\', \'紫菀\', \'半两\', \'苗土\', \'柴胡\', \'两去\', \'黄耆\', \'杏仁\', \'半两\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\', \'百合\', \'甘草\', \'半两\', \'炙微\', \'酸枣仁\']');
INSERT INTO `prescript` VALUES (57518, '25761', '赤茯苓散', '赤茯苓1两，鳖甲2两（涂醋炙令黄，去裙襕），柴胡1两（去苗），麦门冬1两（去心），人参3分（去芦头），桃仁3分（汤浸，去皮尖双仁，麸炒微黄），木香3分，白术3分，桂心半两，川大黄1两（锉碎，微炒），瞿麦3分，赤芍药3分，当归3分，半夏3分（汤洗7遍去滑），甘草半两（炙微赤，锉）。', '每服4钱，以水一中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十', '妇人骨蒸及劳血等疾，面色黄瘦，四肢无力，烦疼，痰壅涕唾稠粘，不思饮食。', '-', '-', '柴胡', '[\'茯苓\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'柴胡\', \'两去\', \'麦门冬\', \'两去\', \'人参\', \'分去\', \'芦头\', \'桃仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'木香\', \'白术\', \'分桂心\', \'半两\', \'大黄\', \'瞿麦分\', \'芍药\', \'当归\', \'半夏\', \'分汤\', \'洗遍\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57519, '25756', '阿胶散', '阿胶1两半（捣碎，炒令黄燥），赤芍药1两，当归1两（锉，微炒），柴胡1两（去苗），麦门冬1两半（去心），黄芩1两，白茯苓1两，白术1两，甘草半两（炙微赤，锉）。', '每服1钱，以水1中盏，加薤白2茎，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十四', '妊娠疟疾，憎寒，头痛壮热，腹痛及胎不安稳，腰脐下重。', '-', '-', '柴胡', '[\'阿胶\', \'两半\', \'捣碎\', \'炒令\', \'黄燥\', \'芍药\', \'当归\', \'柴胡\', \'两去\', \'麦门冬\', \'两半\', \'去心\', \'黄芩\', \'白茯苓\', \'白术\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57520, '25723', '和中解托汤', '柴胡1钱，干葛1钱，山楂1钱，泽泻1钱，陈皮8分，甘草3分，生姜、大枣。', '方中生姜、大枣用量原缺。如头痛者，加川芎8分；呕恶者，加半夏5分；兼寒滞不散者，加桂枝、防风；胸腹有微滞者，加厚朴8分。', '《不居集》上集卷十', '外感之症，手足厥冷，恶寒淅沥，肢节酸疼，有似阳微者；口渴欲饮，舌上微苔，有似阴弱者。', '-', '-', '柴胡', '[\'柴胡\', \'干葛钱\', \'山楂\', \'泽泻\', \'陈皮分\', \'甘草\', \'生姜\', \'大枣\']');
INSERT INTO `prescript` VALUES (57521, '25722', '快膈汤', '白芍1两，当归1两，熟地1两，柴胡1钱，甘草1钱，生地2钱，麦冬2钱，枳壳3钱，半夏3钱。', '水煎服。', '《辨证录》卷六', '舒肝解火，补肾济水。主肝火郁结不伸，闷烦躁急，吐痰黄块。', '-', '-', '柴胡', '[\'白芍\', \'当归\', \'熟地\', \'柴胡\', \'甘草\', \'生地\', \'麦冬\', \'枳壳\', \'半夏\']');
INSERT INTO `prescript` VALUES (57522, '25700', '固气防风汤', '柴胡1钱，升麻1钱，黄耆1钱，防风5分，羌活5分，陈皮5分，人参5分，甘草5分，藁本3分，青皮3分，黄柏5分，白蔻2分。', '-', '《嵩崖尊生》卷七', '风热肩背痛，小便数少。', '气虚人匆服。', '-', '柴胡', '[\'柴胡\', \'升麻\', \'黄耆\', \'防风\', \'羌活\', \'陈皮分\', \'人参\', \'甘草\', \'藁本\', \'青皮\', \'黄柏\', \'分白蔻分\']');
INSERT INTO `prescript` VALUES (57523, '25698', '补劳饮', '黄耆（锉）2两，当归（切，焙）2两，生干地黄（焙）2两，人参1两，白茯苓（去黑皮）1两，芍药1两，五味子（去粗皮）1两，牛膝（酒浸，切，焙）1两，陈橘皮（去白，炒）1两，麦门冬（去心，焙）1两，枳壳（去瓤，麸炒）1两，甘草（炙，锉）1两，柴胡（去苗）1两半。', '每服5钱匕，水1盏半，加生姜5片，大枣3枚（擘），同煎至8分，去滓温服，不拘时候。', '《圣济总录》卷九十三', '通经脉。主男子、妇人虚劳骨蒸，传尸染着，不能断绝，服阿魏丸经脉犹未通，或四肢虚羸，饮食全少。', '-', '-', '柴胡', '[\'黄耆\', \'当归\', \'切焙\', \'两生\', \'地黄\', \'人参\', \'白茯苓\', \'黑皮\', \'芍药\', \'五味子\', \'粗皮\', \'牛膝\', \'浸切\', \'橘皮\', \'麦门冬\', \'枳壳\', \'甘草\', \'柴胡\', \'去苗\', \'两半\']');
INSERT INTO `prescript` VALUES (57524, '25662', '地黄丸', '生地黄1斤，生姜2两，蜜3两（以上2味同捣取汁，和蜜，银器内煎成膏），柴胡（去苗）半两，前胡（去芦头）半两，山栀子仁半两，百合半两，天门冬（去心，焙）半两，百部半两，桔梗（炒）半两，木通（锉）半两，甘草（炙，锉）半两，恶实（炒）半两，紫苏子半两，人参1分，桂（去粗皮）1分，木香1分，芎穷1分，当归（切，焙）1分，射干1分。', '每服30丸，临卧生姜汤送下。', '《圣济总录》卷四十九', '肺热，上热下冷，背髆疼痛，痰涕多。', '-', '-', '柴胡', '[\'生地黄\', \'生姜\', \'两蜜\', \'以上\', \'味同\', \'取汁\', \'银器\', \'煎成\', \'柴胡\', \'去苗\', \'半两\', \'胡去\', \'芦头\', \'半两\', \'栀子\', \'半两\', \'百合\', \'半两\', \'天门冬\', \'半两\', \'百部\', \'半两\', \'桔梗\', \'半两\', \'木通\', \'半两\', \'甘草\', \'半两\', \'恶实\', \'半两\', \'紫苏子\', \'半两\', \'人参\', \'分桂去\', \'粗皮分\', \'木香\', \'分芎\', \'穷分\', \'当归\', \'切焙分\', \'射干\']');
INSERT INTO `prescript` VALUES (57525, '25649', '克效汤', '地骨皮1两，柴胡7钱半，防风7钱半，黄芩7钱半，甘草（炙）7钱半，葛根7钱半。', '每服2钱，用水6分，煎至3分，不拘时服。', '《奇效良方》卷六十四', '潮热往来，久而不解，烦渴昏倦，肌瘦减食。', '-', '-', '柴胡', '[\'地骨皮\', \'柴胡\', \'钱半\', \'防风\', \'钱半\', \'黄芩\', \'钱半\', \'甘草\', \'葛根\', \'钱半\']');
INSERT INTO `prescript` VALUES (57526, '25609', '当归饮子', '当归1两，大黄1两，柴胡1两，人参1两，黄芩1两，甘草1两，芍药1两，滑石半两。', '每服3-5钱，水1盏，加生姜3片，同煎7分，去滓温服。', '《准绳·类方》卷七', '目泪不止。', '-', '-', '柴胡', '[\'当归\', \'大黄\', \'柴胡\', \'人参\', \'黄芩\', \'甘草\', \'芍药\', \'滑石\', \'半两\']');
INSERT INTO `prescript` VALUES (57527, '25516', '固真丸', '黄柏（酒洗）5分，白芍药5分，柴胡1钱，白石脂（火烧赤，水飞，细研，晒干）1钱，白龙骨（酒煮，晒干，水飞，为末）2钱，当归（酒洗）2钱，干姜4钱（炮）。', '空心白沸汤送下。无令胃中停滞，待少时以早饭压之，是不令热药犯胃也。', '《兰室秘藏》卷中', '白带久下不止，脐腹冷痛，阴中亦然，目中溜火，视物（目巟）(目巟）然无所见，齿皆恶热饮，痛须得黄连细末擦之乃止，唯喜干食，大恶汤饮。', '忌生冷、硬物、酒、湿面。', '-', '柴胡', '[\'黄柏\', \'酒洗\', \'分白\', \'芍药\', \'柴胡\', \'白石\', \'火烧\', \'赤水\', \'飞细研\', \'晒干\', \'钱白\', \'龙骨\', \'晒干\', \'水飞\', \'当归\', \'洗钱\', \'干姜\', \'钱炮\']');
INSERT INTO `prescript` VALUES (57528, '25508', '防风当归饮子', '防风1两，当归1两，大黄1两，柴胡1两，人参1两，黄芩1两，甘草1两（炙），芍药1两，滑石6两。', '防风当归饮（《医学入门》卷七）。方中“防风”原脱，据《袖珍方》补。《杂病源流犀烛》有赤苓，无黄芩。', '《宣明论》卷十二', '宣通气血，调顺饮食，泻心肝之阳，补脾肾之阴。主脾肾真阴损虚，肝心风热郁甚，阳胜阴衰，邪气上逆，上实下虚，怯弱不耐；或表热而身热恶寒；或里热而燥热烦渴；或邪热半在表，半在里，进退出入不已，而为寒热往来；或表多则恶寒，里多则发热；或表之阳分正气与邪相助，并甚于里，蓄热极深而外无阳气，里热极甚，阳极似阴而寒战，腹满，烦渴者；或里之阴分正气反助邪气并甚于表，则燥热烦渴而汗出也；或邪热壅塞者；或烦热痛者；或热结极甚，阳气不通而反觉冷痛；或中外热郁烦躁甚，喜凉畏热者；或热极闭寒不得宣通，阳极似阴，中外喜热而反畏寒者；', '-', '-', '柴胡', '[\'防风\', \'当归\', \'大黄\', \'柴胡\', \'人参\', \'黄芩\', \'甘草\', \'芍药\', \'滑石\']');
INSERT INTO `prescript` VALUES (57529, '25479', '赤芍药散', '赤芍药半两，柴胡半两（去苗），桔梗半两（去芦头），木通3分（锉），赤茯苓半两，鳖甲半两（涂醋炙令黄，去裙襕），郁李仁半两（汤浸，去皮尖，微炒）。', '每服4钱，以水一中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十七', '热病，心腹胀满，或时疼痛，饮食全微。', '-', '-', '柴胡', '[\'芍药\', \'半两\', \'柴胡\', \'半两\', \'去苗\', \'桔梗\', \'半两\', \'芦头\', \'木通\', \'茯苓\', \'半两\', \'鳖甲\', \'半两\', \'涂醋\', \'炙令\', \'黄去\', \'郁李仁\', \'半两\', \'汤浸\', \'皮尖\']');
INSERT INTO `prescript` VALUES (57530, '25452', '和膈散', '柴胡1钱，白芍1两，生地5钱，玄参3钱，麦冬2钱，茯苓2钱，竹茹1团，芥子1钱。', '水煎服。', '《辨证录》卷一', '补水济木。主冬月伤寒，至5-6日，往来寒热，胸胁苦满，或呕或吐，或渴或不渴，或烦或不烦，已用小柴胡汤和解后。', '-', '-', '柴胡', '[\'柴胡\', \'白芍\', \'生地\', \'玄参\', \'麦冬\', \'茯苓\', \'竹茹\', \'芥子\']');
INSERT INTO `prescript` VALUES (57531, '25438', '赤茯苓散', '赤茯苓3分，芎半两，桔梗半两（去芦头），五味子半两，木香半两，当归3分，柴胡1两（去苗），鳖甲半两（涂醋炙令黄，去裙襕），桂心3分，枳壳半两（锉炒微黄，去瓤），白术1两，赤芍药1两。', '每服3钱，以水一中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷二十九', '虚劳，身体背膊疼痛，心膈妨闷，不欲饮食，食则腹胀，坐卧不安，口苦头痛，手足无力。', '忌苋菜。', '-', '柴胡', '[\'茯苓\', \'分芎\', \'半两\', \'桔梗\', \'半两\', \'芦头\', \'五味子\', \'半两\', \'木香\', \'半两\', \'当归\', \'柴胡\', \'两去\', \'鳖甲\', \'半两\', \'涂醋\', \'炙令\', \'黄去\', \'桂心分\', \'枳壳\', \'半两\', \'微黄\', \'白术\', \'两赤\', \'芍药\']');
INSERT INTO `prescript` VALUES (57532, '25313', '百合汤', '柴胡（去芦）1钱，人参（去芦）5分，黄芩1钱，甘草5分，知母（去粗）8分，百合1钱2分，陈皮（去白）1钱，生地黄7分。', '水1钟，加生姜3片，捶碎，醋煮鳖甲，煎之温服。渴，加天花粉；胸中烦热，加山栀；有微头疼，加羌活、川芎；呕吐，入姜汁炒半夏；胸闷，加枳壳、桔梗；食复者，加枳实、黄连，甚重大便实，加大黄；胸中虚烦，加竹茹、竹叶；愈后干呕、错语失神、呻吟、睡不安，加黄连、犀角；咳喘，加杏仁；心中惊惕为血少，加当归、茯神、远志；虚汗，加黄耆；脾虚，加白术；腹如雷鸣，加煨生姜；劳复时热不除，加葶苈、乌梅、生姜、生姜汁。', '《伤暑全书》卷下', '暑病已愈而触犯者。', '-', '-', '柴胡', '[\'柴胡\', \'芦钱\', \'人参\', \'芦分\', \'黄芩\', \'甘草\', \'知母\', \'粗分\', \'百合\', \'钱分\', \'陈皮\', \'白钱\', \'生地黄\']');
INSERT INTO `prescript` VALUES (57533, '25310', '陈橘皮丸', '陈橘皮2两（汤浸，去白瓤，焙），槟榔1两，柴胡1两半（去苗），诃黎勒皮1两，白芍药1两，紫菀1两（去苗土），川大黄2两（锉碎，微炒），木香3分，杏仁1两（汤浸，去皮尖双仁，麸炒微黄）。', '每服30丸，食前粥饮送下。', '《圣惠》卷二十七', '虚劳咳嗽，腹胁妨闷，大腹气滞，肢节烦疼。', '-', '-', '柴胡', '[\'橘皮\', \'两汤\', \'浸去\', \'槟榔\', \'柴胡\', \'两半\', \'苗诃黎勒皮\', \'两白\', \'芍药\', \'紫菀\', \'两去\', \'苗土\', \'大黄\', \'木香\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (57534, '25288', '补肝汤', '柴胡5分，升麻5分，藁本5分，白茯苓7分，炒神曲1钱，苍术1钱，半夏2钱，生姜10片。', '柴胡半夏汤（原书同卷）、半夏苍术汤（《张氏医通》卷十四）。', '《兰室秘藏》卷中', '素有风证，不敢见风，眼涩，头痛眼黑，胸中有痰，恶心，兀兀欲吐，遇风但觉皮肉紧，手足难举重物；如居暖室，少出微汗，其证乃减，再或遇风，病即复。', '-', '-', '柴胡', '[\'柴胡\', \'升麻\', \'藁本\', \'白茯苓\', \'神曲\', \'苍术\', \'半夏\', \'生姜\']');
INSERT INTO `prescript` VALUES (57535, '25284', '来苏散', '柴胡（去芦）2两，甘草（炙）2两，干姜2两，肉桂（去粗皮，不见火）1两，桔梗1两，防风1两，荆芥穗1两，五加皮1两，芍药半两，麻黄（去节）1两半，陈皮（去白）1两半，黄耆（蜜水浸一宿，炙）1分。', '每服2钱，水1盏，加生姜3片，同煎8分，不拘时候热服。', '《局方》卷二（续添诸局经验秘方）', '和顺三焦，辟瘴气，进饮食。主四时瘟疫、伤寒，身体壮热，头痛憎寒，项脊拘急，浑身疼痛，烦渴闷乱，大小便涩，嗜卧少力，全不思饮食；及诸气疾，五劳七伤，山岚瘴疟，寒热往来。', '-', '-', '柴胡', '[\'柴胡\', \'去芦\', \'甘草\', \'干姜\', \'肉桂\', \'粗皮\', \'不见\', \'火两\', \'桔梗\', \'防风\', \'荆芥\', \'五加皮\', \'芍药\', \'半两\', \'麻黄\', \'去节\', \'两半\', \'陈皮\', \'两半\', \'黄耆\', \'水浸\', \'一宿炙分\']');
INSERT INTO `prescript` VALUES (57536, '25263', '和胃汤', '升麻半钱，柴胡半钱，当归身2钱，草豆蔻半钱，半夏3分，干姜7分，甘草7分，红消7分，黄耆半钱。', '上都作1服。水2盏，煎至1盏，去滓稍热服，两饭间饮之。', '《普济方》卷二○八', '泄痢。', '-', '-', '柴胡', '[\'升麻\', \'半钱\', \'柴胡\', \'半钱\', \'当归\', \'身钱\', \'草豆蔻\', \'半钱\', \'半夏\', \'干姜\', \'甘草\', \'分红\', \'消分\', \'黄耆\', \'半钱\']');
INSERT INTO `prescript` VALUES (57537, '25248', '金宅龙脑丸', '龙脑薄荷5两，真蒲黄1两，麦门冬2两，阿胶1两，甘草1两半，人参1两，川当归1两，黄耆1两半，木通1两，生干地黄半两，柴胡半两。', '每服20丸，病上焦，饭后用熟水吞下，微嚼破更好；病下焦，空心服。', '《续本事》卷二', '胸中郁热，肺热喘嗽，口臭喉腥，脾疸口甘，丈夫吐血，妇人血崩。', '-', '-', '柴胡', '[\'龙脑\', \'薄荷\', \'两真\', \'蒲黄\', \'麦门冬\', \'阿胶\', \'甘草\', \'两半\', \'人参\', \'两川\', \'当归\', \'两黄耆\', \'两半\', \'木通\', \'两生\', \'地黄\', \'半两\', \'柴胡\', \'半两\']');
INSERT INTO `prescript` VALUES (57538, '25241', '当归龙荟丸', '当归（酒拌）1两，龙胆草（酒拌炒）1两，栀子仁（炒）1两，黄连1两，青皮1两，黄芩1两，大黄（酒拌炒）5钱，芦荟5钱，青黛5钱，柴胡5钱，木香2钱5分，麝香5分（另研）。', '每服20-30丸，生姜汤送下。', '《外科发挥》卷五', '瘰疬肿痛，或胁下作痛，似有积块．及下疳便痈，小便涩，大便秘，或瘀血凝滞．小腹作痛。', '-', '-', '柴胡', '[\'当归\', \'酒拌\', \'龙胆草\', \'酒拌\', \'栀子\', \'黄连\', \'青皮\', \'黄芩\', \'两大\', \'黄酒\', \'芦荟\', \'青黛钱\', \'柴胡\', \'木香\', \'钱分\', \'麝香\', \'另研\']');
INSERT INTO `prescript` VALUES (57539, '25240', '赤芍药丸', '赤芍药3分，川大黄3分（锉碎，微炒），桂心半两，赤茯苓半两，柴胡半两（去苗），鳖甲3分（涂醋炙令黄，去裙襕）。', '芍药丸（《圣济总录》卷一七五）。', '《圣惠》卷八十八', '小儿丁奚，虽食不生肌肉，腹大，食不消化。小儿寒热久不解，仍不能食饮，苦食不消，哺露坚痞腹大，下痢不止。', '-', '-', '柴胡', '[\'芍药\', \'分川\', \'大黄\', \'碎微\', \'炒桂心\', \'半两\', \'茯苓\', \'半两\', \'柴胡\', \'半两\', \'去苗\', \'鳖甲\', \'分涂\', \'炙令\', \'黄去\']');
INSERT INTO `prescript` VALUES (57540, '25191', '净液汤', '桂技2分，连翘5分，生地黄5分，桔梗5分，升麻5分，甘草5分，当归梢7分，麻黄1钱，草豆蔻仁1钱，羌活1钱，防风1钱，柴胡1钱，苍术1钱，酒黄芩2钱，红花少许。', '水2盏，煎至1盏，去滓，食后热服。', '《兰室秘藏》卷下', '皮肤痒，腋下疮，背上疮，耳聋耳鸣。', '-', '-', '柴胡', '[\'桂技分\', \'连翘\', \'生地黄\', \'桔梗\', \'升麻\', \'甘草\', \'当归\', \'麻黄\', \'草豆蔻\', \'仁钱\', \'羌活\', \'防风\', \'柴胡\', \'苍术\', \'钱酒\', \'黄芩\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (57541, '25185', '阿胶散', '阿胶1两（捣碎，炒令黄燥），白茯苓3分，麦门冬3分（去心），柴胡3分（去苗），甘草半两（炙微赤，锉），黄芩半两，当归半两（锉，微炒），芎1两。', '每服4钱，以水1中盏，加生姜半分，大枣3枚，煎至6分，去滓稍热服，不拘时候。', '《圣惠》卷七十五', '妊娠胎动不安，心神虚烦，腹内疼痛。', '-', '-', '柴胡', '[\'阿胶\', \'捣碎\', \'炒令\', \'黄燥\', \'白茯苓\', \'麦门冬\', \'分去\', \'柴胡\', \'分去\', \'甘草\', \'半两\', \'炙微\', \'黄芩\', \'半两\', \'当归\', \'半两\']');
INSERT INTO `prescript` VALUES (57542, '25144', '参宝定惊丹', '人参3两，白术2两1钱，茯苓7钱5分，白芍7钱5分，半夏2钱4分，麦冬2钱4分，枳壳2钱4分，甘草2钱4分，山楂肉2钱4分，干姜2钱4分，柴胡2钱4分，神曲2钱4分，炒黑荆芥1钱3分半，薄荷叶1钱3分半，石菖蒲1钱3分半，槟榔1钱3分半，麦芽1钱3分半，广木香7分半。', '每服1丸，白汤送下；有痰，姜汤送下；重症，参汤送下。危急者用2-3丸。', '《慈幼新书》卷七', '小儿惊疳吐泻。', '-', '-', '柴胡', '[\'人参\', \'白术\', \'茯苓\', \'钱分\', \'白芍\', \'钱分\', \'半夏\', \'钱分\', \'麦冬\', \'钱分\', \'枳壳\', \'钱分\', \'甘草\', \'钱分\', \'山楂\', \'肉钱分\', \'干姜\', \'钱分\', \'柴胡\', \'钱分\', \'神曲\', \'钱分\', \'荆芥\', \'钱分\', \'薄荷叶\', \'钱分\', \'石菖蒲\', \'钱分\', \'槟榔\', \'钱分\', \'麦芽\', \'钱分\', \'半广\', \'木香\', \'分半\']');
INSERT INTO `prescript` VALUES (57543, '25126', '补肝散', '熟地黄半两，白茯苓（去皮）半两，家菊半两，细辛半两，芍药3分，柏子仁1分，甘草半钱（炙），防风1分，北柴胡1两（去芦）。', '每服3钱，水1盏半煎，食后服。', '《得效》卷十六', '肝肾俱虚，圆翳内障，黑珠上一点圆翳，日中见之差小，阴处见之则大白，或明或暗，视物不明，以冷药治之，转见黑花。', '-', '-', '柴胡', '[\'熟地黄\', \'半两\', \'白茯苓\', \'去皮\', \'两家\', \'半两\', \'细辛\', \'半两\', \'芍药\', \'柏子仁\', \'甘草\', \'半钱\', \'防风\', \'分北\', \'柴胡\', \'两去\']');
