INSERT INTO `prescript` VALUES (57545, '25111', '补虚汤', '附子（炮裂，去皮脐）1两，熟干地黄（焙）1两，当归（切，焙）1两，肉苁蓉（酒浸，切，焙）1两，柴胡（去苗）1两，黄耆1两，芍药（炒）3分，人参3分，白茯苓（去黑皮）3分，芎3分。', '每服5钱匕，水1盏半，加生姜5片，大枣3枚（擘），同煎至8分，去滓温服，不拘时候。', '《圣济总录》卷一六四', '产后虚羸，寒热往来。', '-', '-', '柴胡', '[\'附子\', \'炮裂\', \'去皮\', \'两熟\', \'地黄\', \'当归\', \'切焙\', \'肉苁蓉\', \'浸切\', \'柴胡\', \'黄耆\', \'芍药\', \'炒分\', \'人参\', \'白茯苓\', \'黑皮\', \'分芎\']');
INSERT INTO `prescript` VALUES (57546, '25081', '伐邪汤', '石膏3钱，人参3钱，半夏2钱，柴胡2钱，麦冬5钱，茯苓1两，甘草1钱，厚朴1钱，枳壳1钱。', '水煎服。', '《辨证录》卷八', '疟疾发热，头痛鼻干、渴欲饮水，日眴眴不得眠，甚则烦躁，畏火光，厌听人声喧哗。', '-', '-', '柴胡', '[\'石膏\', \'人参\', \'半夏\', \'柴胡\', \'麦冬\', \'茯苓\', \'甘草\', \'厚朴\', \'枳壳\']');
INSERT INTO `prescript` VALUES (57547, '25046', '沉香鳖甲煮散', '沉香半两，木香半两，人参半两，黄耆半两，牛膝（去苗）半两，茯苓半两，紫巴戟（去心）半两，川当归半两，秦艽（去芦）半两，柴胡（去芦）半两，荆芥半两，半夏（生姜汁浸2宿，炒令黄色）半两，桂心1两（不得近火，去粗皮），附子1两（炮，去皮脐），羌活3分，干地黄3分，干蝎1分，鳖甲1两（醋炙令黄），肉豆蔻4枚（去壳）。', '沉香鳖甲散（《局方》卷五吴直阁增诸家名方）。', '《博济》卷一', '五劳七伤，气血虚损，腰背拘急，手足沉重，百节酸痛，面色黄黑，肢体倦怠，行动喘乏，胸膈不快，咳嗽痰涎，夜多异梦，盗汗失精，嗜卧少力，肌肤瘦瘁，不思饮食，日渐羸瘦。', '忌毒物。', '-', '柴胡', '[\'沉香\', \'半两\', \'木香\', \'半两\', \'人参\', \'半两\', \'黄耆\', \'半两\', \'牛膝\', \'去苗\', \'半两\', \'茯苓\', \'半两\', \'紫巴戟\', \'半两\', \'当归\', \'半两\', \'秦艽\', \'去芦\', \'半两\', \'柴胡\', \'去芦\', \'半两\', \'荆芥\', \'半两\', \'半夏\', \'姜汁\', \'浸宿\', \'炒令\', \'黄色\', \'半两\', \'桂心\', \'不得\', \'近火去\', \'粗皮\', \'附子\', \'两炮\', \'去皮\', \'羌活\', \'分干\', \'地黄\', \'分干\', \'蝎分\', \'鳖甲\', \'两醋\', \'炙令\', \'肉豆蔻\']');
INSERT INTO `prescript` VALUES (57548, '25027', '大连翘饮', '连翘1两，瞿麦穗1两，滑石1两，车前子1两，牛蒡子（炒）1两，红芍药1两，山栀半两，木通半两，川当归半两，防风半两，黄芩（去心）1两半，柴胡（去芦）2两，甘草（炙）2两，荆芥穗1两半，蝉蜕（去大脚）1分。', '上(口父)咀。每服1大钱，水1小盏，煎服。风热、痰热、变蒸热、肝热、大肠热、瘾疹热，加麦门冬去心煎；丹热、实热、血热、三焦热、小肠热、龙带热，加大黄及灯心煎；疮疹热、麻子热、温气热、已出证热、未出证热，加紫草茸、川当归同煎；余毒热、胎热、肺热、伤寒后余毒热、疮疹后余毒热，加薄荷煎；项上生核作热，痄腮热，痈疖毒热，加大黄、朴消煎。', '《活幼口议》卷十六', '解利心经邪热。主小儿上焦壅热，口舌生疮，小便赤涩。以及风热，丹热，疮疹热，余毒热。', '-', '-', '柴胡', '[\'连翘\', \'麦穗\', \'滑石\', \'车前子\', \'牛蒡子\', \'两红\', \'芍药\', \'两山\', \'半两\', \'木通\', \'半两\', \'当归\', \'半两\', \'防风\', \'半两\', \'黄芩\', \'去心\', \'两半\', \'柴胡\', \'去芦\', \'甘草\', \'荆芥\', \'两半\', \'蝉蜕\', \'大脚\']');
INSERT INTO `prescript` VALUES (57549, '24985', '柴胡饮子', '柴胡2两（去苗），川升麻1两半，赤芍药1两，黄芩1两半，甘草1两（炙微赤，锉），枳壳1两半（麸炒微黄，去瓤），麦门冬2两（去心），竹叶2两，栀子仁1两。', '每服半两，以水1大盏，加豉50粒，葱白1茎，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十七', '热病5日，已得汗，毒气不尽，犹乍寒乍热，昏昏如醉，胁下牢满，骨节疼痛，不能食下，舌本干燥，口内生疮。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'苗川\', \'升麻\', \'两半\', \'芍药\', \'黄芩\', \'两半\', \'甘草\', \'两炙微\', \'枳壳\', \'两半\', \'微黄\', \'麦门冬\', \'两去\', \'竹叶\', \'栀子\']');
INSERT INTO `prescript` VALUES (57550, '24963', '赤苓散', '桑白皮1钱，赤苓1钱，柴胡1钱，生地1钱半，炙草5分，射干7分，枳壳7分，贝母7分，前胡7分，赤芍7分，天冬7分，百合7分，槟榔7分。', '加生姜，水煎服。', '《嵩崖尊生》卷十三', '脚气，咽不利。', '-', '-', '柴胡', '[\'桑白皮\', \'赤苓\', \'柴胡\', \'生地\', \'钱半炙草分\', \'射干\', \'枳壳\', \'贝母\', \'分前\', \'胡分\', \'赤芍\', \'天冬\', \'百合\', \'槟榔\']');
INSERT INTO `prescript` VALUES (57551, '24891', '赤茯苓散', '赤茯苓1两，柴胡1两半（去苗），地骨皮3分，鳖甲1两半（涂醋炙微黄，去裙襕），桑根白皮1两（锉），枳壳1两（麸炒微黄，去瓤），川大黄1两（锉碎，微炒），芎半两，川朴消1两。', '每服4钱，以水一中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十一', '骨蒸烦热，四肢疼痛，背膊壅闷。', '-', '-', '柴胡', '[\'茯苓\', \'柴胡\', \'两半\', \'去苗\', \'地骨皮\', \'鳖甲\', \'两半\', \'涂醋\', \'微黄\', \'桑根\', \'白皮\', \'枳壳\', \'两麸\', \'微黄\', \'大黄\', \'半两\', \'川朴\', \'消两\']');
INSERT INTO `prescript` VALUES (57552, '24848', '参黄散', '参三七1两，大黄4两，厚朴1两，枳实1两，桃仁3两，归尾3两，赤芍1两5钱，红花5钱，川山甲5钱，郁金1两，胡索1两，肉桂5钱，柴胡6钱，甘草4钱，青皮1两。', '酒调下。', '《伤科补要》卷三', '逐瘀下降，疏通。主体实重伤。', '-', '-', '柴胡', '[\'三七\', \'大黄\', \'厚朴\', \'枳实\', \'桃仁\', \'两归尾\', \'赤芍\', \'花钱\', \'川山甲\', \'郁金\', \'两胡索\', \'肉桂\', \'柴胡\', \'甘草\', \'青皮\']');
INSERT INTO `prescript` VALUES (57553, '24825', '赤茯苓散', '赤茯苓1两，紫苏叶半两，黄耆2两（锉），人参半两（去芦头），陈橘皮半两（汤浸，去白瓤，焙），柴胡1两（去苗），大腹皮半两（锉），前胡3分（去芦头），甘草半两（炙微赤，锉）。', '每服4钱，以水一中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十四', '妊娠心膈气壅滞，烦躁，口干食少。', '-', '-', '柴胡', '[\'茯苓\', \'紫苏叶\', \'半两\', \'黄耆\', \'人参\', \'半两\', \'芦头\', \'橘皮\', \'半两\', \'汤浸\', \'柴胡\', \'两去\', \'大腹皮\', \'半两\', \'胡分\', \'芦头\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57554, '24754', '补中胜毒饼', '黄耆1钱，人参3分，甘草5分，当归3分，生地3分，熟地3分，白芍3分，陈皮3分，升麻5分，柴胡5分，连翘1钱，防风5分。', '本方改为汤剂，名“补中胜毒汤”（见《寿世保元》卷九）。如足阳明部疮多，倍升麻，加漏芦1钱，干葛5分；手、足太阳项脊背腰强者，加羌活1钱，独活5分；肿甚加鼠粘子3分；坚硬加昆布，硬甚加三棱、莪术各2分；寒月身凉，或有腹痛，加肉桂2分；暑日身热，或有烦闷，加酒黄连、黄柏各3分；肠胃有瘀血，加牡丹皮2分；少食，加麦芽、神曲各2分；便秘，加酒大黄或麻仁、桃仁、秦艽；阴寒秘结，去诸苦药，加附子1钱，姜煎冷服。', '《医学入门》卷八', '瘰疬马刀挟瘿，在足少阳、阳明部分，受心脾之邪而作。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'甘草\', \'当归\', \'生地\', \'熟地\', \'白芍\', \'陈皮分\', \'升麻\', \'柴胡\', \'连翘\', \'防风\']');
INSERT INTO `prescript` VALUES (57555, '24745', '赤芍药散', '赤芍药1钱（酒炒），白茯芩1钱，甘草5分（炙），柴胡5分。', '加生姜、大枣，水煎服。', '《校注妇人良方》卷六', '血风，烦闷不食，体倦头眩，身体疼痛。', '-', '-', '柴胡', '[\'芍药\', \'钱酒\', \'炒白\', \'茯芩\', \'甘草\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57556, '24658', '甘草石膏汤', '生地黄1分，细辛1分，熟地黄3分，黄连3分，甘草5分，石膏6分，柴胡7分，黄柏1钱，知母1钱，当归身1钱，桃仁（炒，去皮尖）1钱，荆芥穗1钱，防风1钱，升麻1钱5分，红花少许，杏仁6个，小椒2个。', '都作1服，水2盏，煎至1盏，食后温服。', '《兰室秘藏》卷上', '渴病久愈，又添舌白滑微肿，咽喉咽津觉痛，嗌肿时时有，渴喜冷饮，口中白沫如胶。', '-', '-', '柴胡', '[\'生地黄\', \'细辛\', \'熟地黄\', \'黄连\', \'甘草\', \'石膏\', \'柴胡\', \'黄柏\', \'知母\', \'当归\', \'身钱\', \'桃仁\', \'炒去\', \'皮尖\', \'荆芥\', \'防风\', \'升麻\', \'钱分\', \'红花\', \'少许\', \'杏仁\', \'小椒个\']');
INSERT INTO `prescript` VALUES (57557, '24644', '扶劳四物汤', '当归（酒洗）1钱，川芎1钱，白芍（酒炒）1钱，熟地黄1钱，黄耆（蜜炙）1钱，麦门冬（去心）1钱，柴胡7分，地骨皮7分，秦艽7分，丹参7分，天花粉7分，陈皮7分，香附7分，砂仁7分，枳壳（麸炒）7分，前胡7分。', '水煎服。', '《鲁府禁方》卷三', '妇人血虚成劳，遍身骨节酸痛，五心烦热，盗汗，不进饮食。', '-', '-', '柴胡', '[\'当归\', \'洗钱\', \'川芎\', \'白芍\', \'熟地黄\', \'黄耆\', \'蜜炙钱\', \'麦门冬\', \'心钱\', \'柴胡\', \'地骨皮\', \'秦艽\', \'丹参\', \'天花粉\', \'陈皮分\', \'香附\', \'砂仁\', \'枳壳\', \'分前\', \'胡分\']');
INSERT INTO `prescript` VALUES (57558, '24633', '沉香煎', '石斛5两，椒（去目，炒出汗）2两，附子（炮，去皮脐）2两，秦艽（去土）2两，鳖甲（煮，刮去筋膜，炙）2两，柴胡（去苗）2两，沉香2两，木香2两，槟榔2两，黄耆2两。', '每服20丸，食前以饮送下。', '《全生指迷方》卷二', '由暴怒或惊恐，气上而不下，动伤于肝，气结聚成形，始得之在肝，其脉牢大而结，腹胁有块，大小成形，按之不动，推之不移，久久令人寒热如疟，咳嗽，面目浮肿，动辄微喘，日就羸瘦，不传者。', '-', '-', '柴胡', '[\'石斛\', \'两椒\', \'出汗\', \'附子\', \'去皮\', \'秦艽\', \'鳖甲\', \'刮去\', \'筋膜\', \'柴胡\', \'沉香\', \'木香\', \'槟榔\', \'两黄耆\']');
INSERT INTO `prescript` VALUES (57559, '24631', '空青膏', '羌活1两半，防风1两半，黄连1两半，甘草1两半，柴胡7钱，川芎5钱，枯芩1两半（洒炒一半）。', '空青散（《玉案》卷四）。头痛甚，加细辛3钱；痰厥痛，加半夏；偏头痛倍羌、防、芎。', '《简明医彀》卷五', '偏正头痛久不愈，及风热目痛，脑疼。', '血虚者不宜服。', '-', '柴胡', '[\'羌活\', \'两半\', \'防风\', \'两半\', \'黄连\', \'两半\', \'甘草\', \'两半\', \'柴胡\', \'川芎\', \'枯芩\', \'两半\', \'一半\']');
INSERT INTO `prescript` VALUES (57560, '24559', '防葵散', '防葵1两，木香5钱，柴胡半两，黄芩半两。', '每次5钱，水煎服。', '《云歧子保命集》卷下', '伤寒汗下后，脐左有动气者。', '-', '-', '柴胡', '[\'防葵\', \'木香\', \'柴胡\', \'半两\', \'黄芩\', \'半两\']');
INSERT INTO `prescript` VALUES (57561, '24538', '拨云散', '当归尾3钱4分，防风3钱4分，胆草3钱4分，黄连3钱4分，连翘3钱4分，黄芩3钱4分，黄柏3钱4分，硼粉3钱4分，石决3钱4分，蒙花3钱4分，车前3钱4分，赤芍花粉3钱4分，谷精3钱4分，柴胡3钱4分，玄参3钱4分，川军3钱4分，菊花3钱4分，山栀3钱4分，木通3钱4分，蝉蜕3钱4分，荆芥3钱4分，木贼3钱4分，蒺藜3钱4分，生地3钱4分，羌活3钱4分，川芎3钱4分，甘草3钱4分，薄荷3钱4分，草决3钱4分，甘石3斤2两。', '用银簪蘸冷水点药，上于眼内或眼角。', '《全国中药成药处方集》（吉林方）', '蠲翳清蒙，收瞳明目，解痒止痛。主云蒙翳睛，暴发火眼，烂眼边，瞳仁散大，迎风流泪。', '-', '-', '柴胡', '[\'当归\', \'尾钱分\', \'防风\', \'钱分\', \'胆草\', \'钱分\', \'黄连\', \'钱分\', \'连翘\', \'钱分\', \'黄芩\', \'钱分\', \'黄柏\', \'钱分\', \'硼粉\', \'分石\', \'决钱\', \'分蒙\', \'花钱\', \'分车\', \'钱分\', \'赤芍\', \'花粉\', \'分谷\', \'精钱分\', \'柴胡\', \'钱分\', \'玄参\', \'钱分\', \'川军\', \'钱分\', \'菊花\', \'分山\', \'栀钱分\', \'木通\', \'钱分\', \'蝉蜕\', \'钱分\', \'荆芥\', \'钱分\', \'木贼\', \'钱分\', \'蒺藜\', \'钱分\', \'生地\', \'钱分\', \'羌活\', \'钱分\', \'川芎\', \'钱分\', \'甘草\', \'钱分\', \'薄荷\', \'钱分\', \'草决\', \'分甘石\', \'斤两\']');
INSERT INTO `prescript` VALUES (57562, '24513', '柴胡饮子', '柴胡半两，黄芩半两，人参半两，甘草半两，升麻半两，地骨皮半两，赤茯苓半两，赤芍药半两，草龙胆半两。', '上(口父)咀。每服4钱，水1盏，加薄荷、沙糖，同煎7分，温服，不拘时候，小儿分作3服。', '《普济方》卷一三二', '大人、小儿伤寒，余热不已。', '-', '-', '柴胡', '[\'柴胡\', \'半两\', \'黄芩\', \'半两\', \'人参\', \'半两\', \'甘草\', \'半两\', \'升麻\', \'半两\', \'地骨皮\', \'半两\', \'茯苓\', \'半两\', \'芍药\', \'半两\', \'龙胆\', \'半两\']');
INSERT INTO `prescript` VALUES (57563, '24497', '鸡肉丸', '黄连（姜炒）1两，柴胡1两，鹤虱1两，秦艽1两，知母（酒炒）1两，黄芩（酒炒）1两，使君子（炒）1两，芦荟1两，芜荑5钱。', '用黄雌鸡1只，以大麻子饲之，七日缢死，去毛净，尾上开一孔，取肠净拭干，入前药末于内，缝密，以小甑先用黑豆铺底，安鸡上，又以黑豆盖之，厚3寸，早晨蒸至晚，俟冷去骨，捣烂为丸，如干加酒少许，如麻子大。每服10丸，5岁20丸，白滚汤送下。', '《玉案》卷六', '小儿疳痨壮热，形体羸瘦，眼闭不开，四肢渐小，肚腹渐大。', '-', '-', '柴胡', '[\'黄连\', \'柴胡\', \'鹤虱\', \'秦艽\', \'知母\', \'黄芩\', \'使君子\', \'芦荟\', \'两芜\']');
INSERT INTO `prescript` VALUES (57564, '24480', '当归汤', '当归（切，焙）1两半，黄耆（锉）1两半，芍药1两半，桂（去粗皮）1两，芎1两，甘草（炙）1两，人参1两，柴胡（去苗）1两。', '每服2钱匕，羊肉汁1盏，同煎至7分，去滓温服，不拘时候。', '《圣济总录》卷一六三', '产后血虚，肢体壮热，烦闷，困瘁不食。', '-', '-', '柴胡', '[\'当归\', \'切焙\', \'两半\', \'黄耆\', \'两半\', \'芍药\', \'两半\', \'桂去\', \'粗皮\', \'两芎\', \'甘草\', \'人参\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57565, '24435', '补真丸', '当归5分，人参5分，橘红5分，白术5分，白茯苓3钱，麦门冬3钱，黄耆（蜜炙）7钱，粉草2钱（炙），木香2分，柴胡2分。', '上以生姜、大枣为引，水煎服。', '《医部全录》卷四三六引《幼科全书》', '小儿痰饮为患，呕吐恶心，头眩短气，中脘不快，发为寒热，或因生冷伤脾。', '-', '-', '柴胡', '[\'当归\', \'人参\', \'橘红\', \'白术\', \'白茯苓\', \'麦门冬\', \'黄耆\', \'蜜炙钱\', \'粉草\', \'木香\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57566, '24388', '济阴化痰饮', '小生地3钱，银花1钱5分，玄参1钱5分，广皮7分，远志8分，柴胡8分，桔梗1钱2分，川贝1钱，赤苓2钱，甘草6分。', '水煎服。投5-7剂，兼用吹散可愈。', '《喉科紫珍集》卷上', '济阴化痰。主喉证。', '-', '-', '柴胡', '[\'小生\', \'地钱\', \'花钱\', \'玄参\', \'钱分\', \'广皮分\', \'远志\', \'柴胡\', \'桔梗\', \'钱分\', \'川贝\', \'赤苓\', \'甘草\']');
INSERT INTO `prescript` VALUES (57567, '24368', '变阳汤', '人参、黄耆2两，金银花半斤（煎，汤代之），附子1钱，荆芥（炒黑）3钱，柴胡2钱，白芍1两，天花粉5钱，生甘草5钱。', '井花水煎汁2碗服，滓再煎。服后阴必变阳作痛。再1剂，而痛亦消；再1剂，全愈。', '《辨证录》卷十三', '背痈。背心发瘰，痒甚，已而背如山重，悠悠发红晕，如盘之大。此阴痈初起之形象。', '-', '-', '柴胡', '[\'人参\', \'黄耆\', \'金银花\', \'半斤\', \'汤代\', \'附子\', \'荆芥\', \'黑钱\', \'柴胡\', \'白芍\', \'两天\', \'花粉\', \'生甘草\']');
INSERT INTO `prescript` VALUES (57568, '24363', '肥儿丸', '三棱（煨）4钱，蓬术（煨）4钱，川楝子4钱，龙胆草4钱，黄连4钱，柴胡半两，地骨皮半两，枳壳（麸炒）3钱，麦糵3钱，当归3钱，白芜荑2钱，芦荟1钱，木香1钱。', '每服30丸，米饮送下。', '《婴童百问》卷八', '化虫，消疳，退疳热。主小儿因缺乳，食肉太早，或患脏腑胃虚所致疳，黄瘦，肚急。', '-', '-', '柴胡', '[\'三棱\', \'蓬术\', \'川楝子\', \'龙胆草\', \'黄连\', \'柴胡\', \'半两\', \'地骨皮\', \'半两\', \'枳壳\', \'钱麦\', \'当归\', \'钱白\', \'芦荟\', \'木香\']');
INSERT INTO `prescript` VALUES (57569, '24347', '柴胡饮子', '柴胡半两，人参半两，黄芩半两，甘草半两，大黄半两，当归半两，芍药半两。', '柴胡饮（《校注妇人良方》卷五）、人参柴胡饮子（《准绳·类方》卷一）。', '《宣明论》卷四', '伤寒发汗未解，气血已虚，寒热往来，口干烦渴，大便秘结，脉洪实弦数。一切肌骨蒸积热作，蓄热寒战，或下后热未愈，汗后劳复或骨蒸肺痿喘嗽，妇人余疾，产后经病。', '-', '-', '柴胡', '[\'柴胡\', \'半两\', \'人参\', \'半两\', \'黄芩\', \'半两\', \'甘草\', \'半两\', \'大黄\', \'半两\', \'当归\', \'半两\', \'芍药\', \'半两\']');
