INSERT INTO `prescript` VALUES (57571, '24266', '赤茯苓散', '赤茯苓1两，柴胡1两（去苗），枳壳1两（麸炒微黄，去瓤），白术1两，人参半两（去芦头），旋覆花半两，半夏3分（汤浸7遍去滑），杏仁3分（汤浸，去皮尖双仁，麸炒微黄），槟榔1两。', '每服5钱，以水1大盏，加生姜半分，煎至5分，去滓温服，不拘时候。', '《圣惠》卷五十一', '痰饮干呕，食不消化，及脾胃气膈。', '-', '-', '柴胡', '[\'茯苓\', \'柴胡\', \'两去\', \'枳壳\', \'两麸\', \'微黄\', \'白术\', \'人参\', \'半两\', \'芦头\', \'旋覆花\', \'半两\', \'半夏\', \'分汤\', \'浸遍\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'槟榔\']');
