INSERT INTO `prescript` VALUES (57576, '24083', '变蒸散', '柴胡（去芦，洗，锉）1两，甘草（炙）1两，人参（去芦，洗，锉）1两，元参（净洗，锉）1两，麦门冬子（去心）1两半，龙胆草半两（若变蒸或常服只1分，随时加减）。', '每服1钱，水1小盏，煎至3-5沸，温服，1日3-5服。', '《幼幼新书》卷八引王（公儿）方', '常服去疳。主小儿体性常热，及变蒸惊热不解，夹热烦躁，时叫泣无歇，乃骨热生疮，面色常黄瘦瘁，不进奶食。', '-', '-', '柴胡', '[\'柴胡\', \'芦洗\', \'甘草\', \'人参\', \'芦洗\', \'两元\', \'参净\', \'麦门冬\', \'子去\', \'两半\', \'龙胆草\', \'半两\', \'若变\', \'常服\', \'只分\', \'随时\', \'加减\']');
INSERT INTO `prescript` VALUES (57577, '24079', '阿胶散', '阿胶（碎，炒）1两，人参（去芦头）1两，茯苓（去皮）1两，玄参（去苗）1两，丹参（去芦头）1两，防风（去叉）1两，黄耆1两，生干地黄（焙）1两，地骨皮1两，山栀子仁1两，葛根1两，柴胡（去苗）1两，秦艽（去苗土）1两，黄连（去须）1两，龙胆（去土）1两，枳壳（去瓤，麸炒）1两，麦门冬（去心，焙）1两，百合1两，鳖甲（去裙，醋炙）1两，甜葶苈（隔纸炒）1两，防己1两，甘草（炙）1两，栝楼根1两，马兜铃1两，大黄（锉，炒）1两，桔梗（炒）1两，知母（焙）1两，贝母（去心）1两，款冬花1两，石膏（碎）1两，麻黄', '每服3钱匕，水1盏半，加青蒿7枝（切），同煎至7分，去滓，食后、临卧温服。', '《圣济总录》卷九十', '虚劳体热，消瘦骨蒸。', '-', '-', '柴胡', '[\'阿胶\', \'人参\', \'芦头\', \'茯苓\', \'去皮\', \'玄参\', \'丹参\', \'芦头\', \'防风\', \'黄耆\', \'两生\', \'地黄\', \'地骨皮\', \'两山\', \'栀子\', \'葛根\', \'柴胡\', \'秦艽\', \'苗土\', \'黄连\', \'龙胆\', \'枳壳\', \'麦门冬\', \'百合\', \'鳖甲\', \'两甜\', \'隔纸\', \'防己\', \'甘草\', \'栝楼\', \'根两\', \'马兜铃\', \'大黄\', \'桔梗\', \'知母\', \'贝母\', \'款冬花\', \'石膏\', \'麻黄\']');
INSERT INTO `prescript` VALUES (57578, '24044', '当归丸', '当归2两，葶苈2两，附子2两，吴茱萸2两，大黄2两，黄芩1两半，桂心1两半，干姜1两半，牡丹1两半，芎1两半，细辛1两6铢，秦椒1两6铢，柴胡1两6铢，厚朴1两6铢，牡蒙（1方无）1两，甘草1两，虻虫50个，水蛭50个。', '每服15丸，空心、以酒送下，日2次。', '《千金》卷四', '女人脐下症结刺痛，如虫所啮，及如锥刀所刺，或赤白带下十二疾，腰背疼痛，月水或在月前，或在月后。', '有胎勿服之。', '-', '柴胡', '[\'当归\', \'两葶\', \'附子\', \'吴茱萸\', \'大黄\', \'黄芩\', \'两半\', \'桂心\', \'两半\', \'干姜\', \'两半\', \'牡丹\', \'两半\', \'两半\', \'细辛\', \'两铢\', \'秦椒\', \'两铢\', \'柴胡\', \'两铢\', \'厚朴\', \'两铢\', \'蒙方\', \'甘草\', \'水蛭\']');
INSERT INTO `prescript` VALUES (57579, '24017', '补中益气汤', '黄耆1钱半，人参1钱，甘草（炙）1钱，当归身（酒洗，焙干）半钱，柴胡半钱，陈皮半钱，白术半钱，升麻3分，葛根半钱。', '上作1服。水煎，午前稍热服。嗽者，黄耆用半钱，并去人参，不渴者，去葛根；头痛，加蔓荆子3分，痛甚，加川芎5分；顶痛、脑痛者，加藁本5分，细辛3分；头痛有痰，沉重懒倦者，乃太阴、厥阴头痛，加半夏半钱或1钱，生姜3片；耳鸣目黄，颊颔肿，颈肩臑肘臂外后廉痛，面赤，脉洪大者，加羌活1钱，防风7分，甘草3分，藁本5分，通其经血，加黄芩、黄连各3分，消其肿；咽痛颔肿，脉洪大，面赤，加黄芩3分，桔梗7分，甘草3分；口干、咽干或渴者，加葛根5分，升胃气上行以润之；心下痞，瞀闷者，加芍药、黄连各1钱；如痞腹胀，加枳实3分，', '《丹溪心法》卷三', '补元气，泻火邪。主内伤，喜怒过度，饮食失节，寒温不适，劳役所伤，以致中气不足，阴火独旺，上乘阳分，荣卫失守，气高而喘，身热而烦，短气上逆，鼻息不调，怠情嗜卧，四肢困倦不收，无气以动，亦无气以言。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'甘草\', \'当归\', \'身酒\', \'焙干\', \'半钱\', \'柴胡\', \'半钱\', \'陈皮\', \'半钱\', \'白术\', \'半钱\', \'升麻\', \'葛根\', \'半钱\']');
