INSERT INTO `prescript` VALUES (57582, '23919', '保幼培元丹', '藿香1两6钱，赤苓6钱，清夏4钱，柴胡8钱，苏叶8钱，木瓜6钱，白芷4钱，广皮8钱，檀香6钱，扁豆6钱，三仙2两，木通4钱，泽泻6钱，竹茹6钱，山药9钱，砂仁4钱，白术8钱（土炒），甘草4钱，木香4钱，川连4钱。', '每次服1丸，生姜汤送下；白开水亦可。', '《全国中药成药处方集》（济南方）', '小儿中寒中暑，腹痛呕吐泄泻，头痛身烧。', '-', '-', '柴胡', '[\'藿香\', \'赤苓\', \'清夏钱\', \'柴胡\', \'苏叶\', \'木瓜\', \'白芷\', \'钱广皮\', \'檀香\', \'扁豆\', \'钱三仙\', \'木通\', \'泽泻\', \'竹茹\', \'山药\', \'砂仁\', \'白术\', \'钱土\', \'甘草\', \'木香\', \'钱川\']');
INSERT INTO `prescript` VALUES (57583, '23882', '百花煎', '白蜜5合，生地黄汁3合，生姜汁1合，黄牛乳5合，藕汁3合，秦艽1两（去苗），白茯苓1两，柴胡1两（去苗），干柿5枚（煮软，细研如糊），杏仁2两（汤浸，去皮尖双仁，麸炒微黄），黄明胶5两（捣碎，炒令黄燥）。', '本方方名，《普济方》引作“补肺白花煎”。', '《圣惠》卷六', '肺壅热，吐血后咳嗽、虚劳少力。', '-', '-', '柴胡', '[\'白蜜合\', \'生地黄\', \'合生\', \'姜汁\', \'合黄\', \'牛乳\', \'合藕汁合\', \'秦艽\', \'两去\', \'白茯苓\', \'柴胡\', \'两去\', \'苗干\', \'软细\', \'研如糊\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'黄明胶\', \'捣碎\', \'炒令\', \'黄燥\']');
INSERT INTO `prescript` VALUES (57584, '23881', '活络疏肝散', '柴胡1钱半，牛蒡子1钱半，淮牛膝1钱半，青皮1钱半，防风1钱半，花粉3钱，土茯苓3钱，山慈菇（去皮毛）2钱，葛根2钱，夏枯草2钱，生甘草1钱。', '若有实热者，酌加芩、连。', '《疬科全书》', '风火疬。初起或在两耳之下，或环颈皆是，或单在左耳之下，或单在右耳之中，无论核之多少，色带红光，即有欲破之势，或痛或不痛，或寒热交作。', '-', '-', '柴胡', '[\'柴胡\', \'钱半\', \'牛蒡子\', \'钱半淮\', \'牛膝\', \'钱半\', \'青皮\', \'钱半\', \'防风\', \'钱半\', \'花粉\', \'土茯苓\', \'山慈菇\', \'皮毛\', \'葛根\', \'夏枯草\', \'生甘草\']');
INSERT INTO `prescript` VALUES (57585, '23832', '百效丸', '贝母2钱，川厚朴2钱，血竭1钱5分，柴胡2钱，上肉桂1钱5分，巴豆（去油）2钱，玄参2钱，肉豆蔻1钱5分，知母2钱，真麝香1分，冰片1分，神金10张，辰砂2钱。', '未满周岁每服3丸，周岁以上每服5丸，用葱白1寸，灯心7根，煎汤将丸溶化，加白糖少许，温服。', '《经验奇效良方》', '消积理滞，镇惊化痰。主小儿急慢惊风，痰喘气促，寒火结胸，大小便闭塞，一切食积痰疟，腹疼腹胀，红白痢疾，及发斑出疹，热毒内陷等证。', '-', '-', '柴胡', '[\'贝母\', \'钱川\', \'厚朴\', \'血竭\', \'钱分\', \'柴胡\', \'肉桂\', \'钱分\', \'巴豆\', \'油钱\', \'玄参\', \'肉豆蔻\', \'钱分\', \'知母\', \'麝香\', \'冰片\', \'分神\', \'金张\', \'辰砂\']');
INSERT INTO `prescript` VALUES (57586, '23806', '补肝祛疟汤', '白芍1两，当归1两，何首乌（生用）1两，鳖甲3钱，茯苓5钱，青皮1钱，柴胡1钱，半夏2钱，甘草1钱。', '水煎服。', '《辨证录》卷八', '补肝以祛邪。主厥阴肝经之疟。', '-', '-', '柴胡', '[\'白芍\', \'当归\', \'何首乌\', \'生用\', \'鳖甲\', \'茯苓\', \'青皮\', \'柴胡\', \'半夏\', \'甘草\']');
INSERT INTO `prescript` VALUES (57587, '23787', '拨翳汤', '白蒺藜（炒）7分，花粉（酒蒸）7分，葛根7分，薄荷7分，防风7分，川芎7分，羌活7分，谷精草7分，密蒙花7分，甘菊7分，草决明7分，山栀5分，木贼草5分，生地1钱半，当归1钱，柴胡8分，川连（酒炒）3分，生姜1片。', '《外科证治全书》本方用法：水煎，食远服。便闭，加大黄。', '《外科集腋》卷二', '痘毒攻目生翳，如翳膜遮盖瞳神者。', '-', '-', '柴胡', '[\'蒺藜\', \'炒分\', \'花粉\', \'酒蒸分\', \'葛根\', \'薄荷\', \'防风\', \'川芎\', \'羌活\', \'谷精草\', \'密蒙花\', \'甘菊\', \'草决明\', \'分山\', \'栀分\', \'木贼\', \'草分\', \'生地\', \'钱半\', \'当归\', \'柴胡\', \'分川\', \'连酒\', \'炒分\', \'生姜\']');
INSERT INTO `prescript` VALUES (57588, '23768', '大效紫菀丸', '紫菀2两，人参2两，巴豆（醋煮，去心膜，研）1两，肉苁蓉1两，吴茱萸1两，菖蒲1两，干姜1两，白槟榔1两，当归1两，防风1两，茯神1两，桔梗1两，车前子1两，川椒1两，乌头（炮，去皮脐）1两，猪牙皂角（去皮子，涂酥炙）1两，白术1两1分，汉防己1两1分，柴胡1两1分，羌活1两1分，麦门冬1两1分，甘草1两1分，黄连1两半，厚朴1两半，干地黄1两半，茯苓1两半，大黄1两半，肉豆蔻3分。', '每服5丸，空心茶、酒或熟水送下。当宣3-5行，不定，以温粥止之。', '《鸡峰》卷二十五', '积聚癖块，大如拳掌，亦如杯碗；及黄疸病，朝起呕吐，上攻心膈，两肋分痛胀，彻连甲脊，痛无休息，时常绕脐；九种心痛，五淋、五痔；胃口闭塞，吐逆，饮食积年不消；妇人断续多年；诸风，身体顽麻，不知痒痛，半面浮疼，眼目冷泪，遍身如锥刀所刺，眉毛坠落，面上生疮，游如虫行，莫知所有，或手足烦热，或夜卧不安；小儿72种风，及25种惊痫；夜梦鬼交，四肢无力沉重，饮食无味，昏昏似醉，只欲求死，真如鬼魅，终日忧烦不乐，悲啼歌哭；月候不调，或多或少，时似有孕，连年羸瘦，在床渐困。', '-', '-', '柴胡', '[\'紫菀\', \'人参\', \'巴豆\', \'心膜\', \'研两\', \'肉苁蓉\', \'吴茱萸\', \'菖蒲\', \'干姜\', \'两白\', \'槟榔\', \'当归\', \'防风\', \'茯神\', \'桔梗\', \'车前子\', \'两川椒\', \'乌头\', \'去皮\', \'猪牙皂\', \'角去\', \'皮子\', \'白术\', \'两分\', \'防己\', \'两分\', \'柴胡\', \'两分\', \'羌活\', \'两分\', \'麦门冬\', \'两分\', \'甘草\', \'两分\', \'黄连\', \'两半\', \'厚朴\', \'两半\', \'地黄\', \'两半\', \'茯苓\', \'两半\', \'大黄\', \'两半\', \'肉豆蔻\']');
INSERT INTO `prescript` VALUES (57589, '23714', '沉香末子', '香附（盐水炒）4两，槟榔4两，厚朴（姜汁炒）4两，陈皮（炒）4两，枳实2两，青皮2两，山楂2两，麦芽2两，神曲（炒）2两，萝卜子（炒）2两，白酒药（炒）2两，柴胡1两2钱，川芎1两2钱，桔梗1两2钱，白术1两，干姜1两，沉香1两，木香1两。', '周岁，每服7分；2-3周者，每服1钱，糖拌，空心食前以姜汤送下。', '《幼科指掌》卷三', '因乳食伤脾胃，以致呕吐泄泻，痢疾、疟疾，心腹疼痛，大便下血，寒热不止，及胸腹胀满，嗳气作酸，恶心，恶闻食气，及不思食，下泄臭屁者。', '-', '-', '柴胡', '[\'香附\', \'盐水\', \'槟榔\', \'厚朴\', \'姜汁\', \'陈皮\', \'枳实\', \'青皮\', \'山楂\', \'麦芽\', \'神曲\', \'萝卜\', \'白酒\', \'柴胡\', \'川芎\', \'桔梗\', \'白术\', \'干姜\', \'沉香\', \'木香\']');
INSERT INTO `prescript` VALUES (57590, '23698', '恒山散', '恒山1两（锉），柴胡1两（去苗），栀子仁1两，石膏2两，乌梅肉3-7枚（微炒），甘草1两（炙微赤，锉），蜀漆2两，鳖甲2两（涂醋炙令黄，去裙襕）。', '每服5钱，以水1大盏，入竹叶2-7片，豉50粒，煎至5分，去滓温服，不拘时候。', '《圣惠》卷五十二', '心疟，令人心烦，渴欲得饮水，寒热不歇，乍来乍去，不思饮食。', '-', '-', '柴胡', '[\'恒山\', \'柴胡\', \'两去\', \'栀子\', \'石膏\', \'乌梅\', \'甘草\', \'两炙微\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\']');
INSERT INTO `prescript` VALUES (57591, '23596', '茯苓汤', '赤茯苓（去黑皮）半两，柴胡（去苗）半两，枳壳（去瓤，麸炒）半两，桑根白皮（锉）半两，麦门冬（去心，焙）半两，葛根（锉）3分，甘草（炙，锉）半两，桂（去粗皮）1分，人参1分。', '每服3钱匕，用水1盏，加生姜3片，大枣2个（擘），煎至7分，去滓温服。', '《圣济总录》卷三十一', '伤寒汗后，余热不除，及四肢拘急痛，胸膈不利，呕逆不思饮食。', '-', '-', '柴胡', '[\'茯苓\', \'黑皮\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'枳壳\', \'半两\', \'桑根\', \'白皮\', \'半两\', \'麦门冬\', \'半两\', \'葛根\', \'锉分\', \'甘草\', \'半两\', \'桂去\', \'粗皮分\', \'人参\']');
INSERT INTO `prescript` VALUES (57592, '23553', '肥气丸', '厚朴半两，黄连7钱，柴胡2两，椒（炒去汗）4钱，巴豆霜5分，川乌头（切，去皮）1钱2分，干姜（炮）半钱，皂角（去皮弦子，煨）1钱半，白茯苓（去皮）1钱半，广术（炮）2钱半，人参（去芦）2钱半，甘草（炙）3钱，昆布2钱半。', '初服2丸，1日加1丸，2日加2丸，渐渐加至大便微溏，再从2丸加服。周而复始，积减大半勿服。秋、冬，加厚朴半两（通前1两），减黄连1钱半；若治风痫，于1料中加人参、茯神、菖蒲各3钱，黄连只依春、夏用7钱，虽秋、冬不减，空心淡醋汤送下。', '《东垣试效方》卷二', '肝之积，在左胁下，如覆杯，有头足，久不愈，令人发咳逆、痎疟，连岁不已。', '-', '-', '柴胡', '[\'厚朴\', \'半两\', \'黄连\', \'柴胡\', \'两椒\', \'巴豆霜\', \'分川\', \'乌头\', \'去皮\', \'钱分\', \'干姜\', \'炮半钱\', \'皂角\', \'去皮\', \'弦子\', \'白茯苓\', \'去皮\', \'钱半广术\', \'人参\', \'芦钱\', \'甘草\', \'昆布\', \'钱半\']');
INSERT INTO `prescript` VALUES (57593, '23546', '连胡丸', '黄连（去须）1两，胡黄连（去芦）1两，神曲（炒）1两，麦蘖（炒）1两，柴胡（去梗）1两，芜荑仁（研）1两，白茯苓（去皮）1两，青皮半两（去瓤）。', '每服20丸，米饮送下。', '《魏氏家藏方》卷十', '小儿疳热。', '-', '-', '柴胡', '[\'黄连\', \'胡黄连\', \'去芦\', \'神曲\', \'两麦蘖\', \'柴胡\', \'两芜\', \'仁研\', \'白茯苓\', \'去皮\', \'青皮\', \'半两\']');
INSERT INTO `prescript` VALUES (57594, '23541', '补阴益气煎', '生地5钱，人参1钱半，当归3钱（醋炒），升麻3分（醋炒），山药3钱（炒），柴胡5分（醋炒），炙草8分，陈皮1钱半。', '炒黑荷叶1张，水煎，去滓温服。', '《医略六书》卷二十六', '崩漏，气血两亏，清阳下陷，脉软弦微数者。', '-', '-', '柴胡', '[\'生地\', \'人参\', \'钱半\', \'当归\', \'升麻\', \'分醋\', \'山药\', \'柴胡\', \'分醋\', \'草分\', \'陈皮\']');
INSERT INTO `prescript` VALUES (57595, '23537', '柴胡汤', '柴胡（去苗）半两，甘草（炙）1分。', '柴胡甘草汤（《直指》卷十六）。', '《圣济总录》卷六十', '黄疸。热疸。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'半两\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (57596, '23511', '和血解阳汤', '熟地1钱，生地1钱，当归1钱半，杜仲1钱半，柴胡1钱半，知母1钱半，前胡1钱半，黄耆1钱半，白术1钱，泽兰1钱，姜黄1钱，黄柏1钱，大黄1钱，芒消1钱。', '生姜、大枣为引。', '《点点经》卷四', '酒伤肝，病发周身麻胀，四肢筋痛，手足拘挛难伸，麻木不仁，捏不知痛，或头疼痛，或腹坚硬，二便胀塞不利，脉浮数，汗潮如雨者。', '-', '-', '柴胡', '[\'熟地\', \'生地\', \'当归\', \'钱半\', \'杜仲\', \'钱半\', \'柴胡\', \'钱半\', \'知母\', \'钱半前\', \'胡钱\', \'黄耆\', \'白术\', \'泽兰\', \'姜黄\', \'黄柏\', \'大黄\', \'钱芒\', \'消钱\']');
INSERT INTO `prescript` VALUES (57597, '23473', '和疟饮', '当归1两（酒洗），白芍1两（酒洗），白术5钱（土炒），云苓5钱，制半夏3钱，青皮1钱5分，柴胡1钱5分，焦楂3钱，草蔻仁1钱（研），炙甘草5分，煨姜3钱。', '河、井水各半煎服。如热重，加青蒿3钱；如恶心呕吐，加藿香3钱，乌梅2个；如口苦，加炒黄芩1钱2分；如口作渴，加花粉2钱；如胸口饱胀，加槟榔1钱5分；如腰背痛，加独活1钱5分；如舌苔黄，大便结，加瓜蒌仁3钱；如舌赤心热，加炒黑山栀仁2钱；如腹作泻，加煨广木香1钱5分，车前子3钱。', '《慈航集》卷下', '足少阳胆经之疟，初病令人身体解（亻亦），寒不甚，热不甚，恶见人，见人心惕惕然，热多汗出甚，口苦耳聋，胸胁胀闷作痛，或呕或不呕，半表半里之症。', '-', '-', '柴胡', '[\'当归\', \'两酒\', \'白芍\', \'两酒\', \'白术\', \'钱土\', \'炒云苓\', \'钱制\', \'半夏\', \'青皮\', \'钱分\', \'柴胡\', \'钱分\', \'焦楂\', \'草蔻仁\', \'研炙\', \'甘草\', \'姜钱\']');
INSERT INTO `prescript` VALUES (57598, '23456', '经效方', '当归1钱半，芍药（炒）8分，苦梗8分，槟榔8分，枳壳（麸炒）8分，桂心6分，青木香6分，柴胡6分。', '水煎服。', '《济阴纲目》卷十一', '产后肝经气滞不平，胁胀腹痛，或寒热往来，内热晡热。', '-', '-', '柴胡', '[\'当归\', \'钱半\', \'芍药\', \'炒分\', \'苦梗分\', \'槟榔\', \'枳壳\', \'分桂心分\', \'青木香\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57599, '23438', '柴胡汤', '柴胡（去苗）1两，黄耆（锉）1两，赤茯苓（去黑皮）1两，秦艽（去苗土）半两，地骨皮半两，黄芩（去黑心）半两，葛根（锉）半两，枳壳（去瓤，麸炒）半两，人参3分，甘草（炙）3分，鳖甲（去裙襕，醋浸，炙）1两。', '每服3钱匕，水1盏，加生姜3片，煎至7分，去滓温服，不拘时候。', '《圣济总录》卷三十一', '伤寒后夹劳，五心烦热，背膊疼痛，手足无力，不能饮食。', '-', '-', '柴胡', '[\'柴胡\', \'黄耆\', \'茯苓\', \'黑皮\', \'秦艽\', \'苗土\', \'半两\', \'地骨皮\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'葛根\', \'半两\', \'枳壳\', \'半两\', \'人参\', \'甘草\', \'炙分\', \'鳖甲\']');
INSERT INTO `prescript` VALUES (57600, '23422', '鸡肉煎丸', '宣连2两，银柴胡1两，芜荑半两，鹤虱（川）半两，秦艽（净）1两，知母1两，使君子1两，子芩1两。', '蒸鸡丸（《玉机微义》卷五十）。', '《幼幼新书》卷二十六引《家宝》', '小儿疳劳，骨蒸潮热，盗汗瘦弱，腹急面黄，食不生肌肉。', '忌猪肉。', '-', '柴胡', '[\'宣连\', \'银柴胡\', \'两芜\', \'半两\', \'鹤虱\', \'半两\', \'秦艽\', \'知母\', \'使君子\', \'两子\']');
INSERT INTO `prescript` VALUES (57601, '23399', '连翘败毒膏', '连翘40两，防风40两，白芷40两，黄连40两，苦参40两，薄荷40两，当归40两，荆芥穗40两，花粉40两，甘草40两，黄芩40两，赤芍40两，柴胡24两，麻黄80两，羌活80两，黄柏80两，地丁240两，大黄240两，银花160两。', '每服5钱，日服2次，温开水调服。', '《北京市中药成方选集》', '清热解毒，散风消肿。主疮疡初起，红肿疼痛，憎寒壮热。', '孕妇忌服。', '-', '柴胡', '[\'连翘\', \'防风\', \'白芷\', \'黄连\', \'苦参\', \'薄荷\', \'当归\', \'荆芥\', \'花粉\', \'甘草\', \'黄芩\', \'赤芍\', \'柴胡\', \'麻黄\', \'羌活\', \'黄柏\', \'两地\', \'大黄\', \'银花\']');
INSERT INTO `prescript` VALUES (57602, '23366', '茯苓散', '赤茯苓（去黑皮）1两半，郁李仁（去皮）1两半，赤芍药1两半，大腹2个（并子），百合柴胡（去苗）1两，桑根白皮（锉）1两，陈橘皮（汤浸，去白，焙）1两，枳壳（去瓤，麸炒）1两，知母（锉，焙）1两。', '本方方名，原书文瑞楼本作“茯苓饮”。', '《圣济总录》（人卫本）卷一三六', '风气攻头面浮肿，烦渴，心中躁闷，肚腹胀满，小便秘涩。', '-', '-', '柴胡', '[\'茯苓\', \'黑皮\', \'两半\', \'郁李仁\', \'去皮\', \'两半\', \'芍药\', \'两半\', \'大腹个\', \'并子\', \'百合\', \'柴胡\', \'去苗\', \'两桑根\', \'白皮\', \'橘皮\', \'汤浸\', \'枳壳\', \'知母\']');
INSERT INTO `prescript` VALUES (57603, '23361', '柴胡当归汤', '柴胡3两，白术2两，人参1两，甘草（炙）1两，当归1两，赤芍药1两，五味子半两，木通半两。', '当归汤（《妇人良方》卷六）。本方方名，《袖珍》引作“当归散”。', '《活人书》卷十九', '妇人伤寒，喘急烦躁，或战而作寒，阴阳俱虚，不可下者。', '-', '-', '柴胡', '[\'柴胡\', \'白术\', \'人参\', \'甘草\', \'当归\', \'两赤\', \'芍药\', \'五味子\', \'半两\', \'木通\', \'半两\']');
INSERT INTO `prescript` VALUES (57604, '23356', '谷芽枳实小柴胡汤', '谷芽1钱，枳实1钱，厚朴1钱，山栀6分，大黄6分，柴胡6分，黄芩6分，陈皮5分，半夏5分，人参5分，炙甘草5分。', '上加水2盏，生姜3片，大枣1个，煎8分，不拘时候服。', '《医统》卷十八', '谷疸，食已即肌，头痛，心中郁怫不安，饥饱所致蒸变而黄。', '-', '-', '柴胡', '[\'谷芽\', \'枳实\', \'厚朴\', \'钱山\', \'栀分\', \'大黄\', \'柴胡\', \'黄芩\', \'陈皮分\', \'半夏\', \'人参\', \'甘草\']');
INSERT INTO `prescript` VALUES (57605, '23349', '柴胡汤', '柴胡（去苗）1两，茵陈蒿1两，升麻3分，龙胆3分。', '每服5钱匕，水1盏半，煎至8分，去滓，加地黄汁1合搅，食后温服。', '《圣济总录》卷六十', '黄疸，通身并黄。', '-', '-', '柴胡', '[\'柴胡\', \'茵陈蒿\', \'升麻\', \'龙胆\']');
INSERT INTO `prescript` VALUES (57606, '23321', '防葵散', '防葵半两，柴胡半两（去苗），川大黄半两（锉碎，微炒），桑根白皮半两（锉），甘草1分（炙微赤，锉）。', '每服1钱，以水1小盏，煎至5分，去滓温服，日3次。', '《圣惠》卷八十四', '小儿疟发后，肚胀，兼头面浮肿。', '-', '-', '柴胡', '[\'防葵\', \'半两\', \'柴胡\', \'半两\', \'苗川\', \'大黄\', \'半两\', \'桑根\', \'白皮\', \'半两\', \'甘草\', \'分炙微\']');
