INSERT INTO `prescript` VALUES (57608, '23296', '昆布丸', '昆布1两半（洗去咸味），羚羊角屑半两，柴胡3分（去苗），麦门冬1两半（去心，焙），杏仁半两（汤浸，去皮尖双仁，麸炒微黄），天门冬1两半（去心，焙），木通3分（锉），槟榔3分，诃黎勒皮1两半，郁李仁1两（汤浸，去皮，微炒），川大黄1两（锉碎，微炒），射干半两，川朴消1两，桂心1两，百合1两，紫苏子半两（微炒），陈橘皮3分（汤浸，去白瓤，焙）。', '每服30丸，不拘时候，以热酒送下；夜饭后，取1丸如弹丸大，绵裹，含化咽津。', '《圣惠》卷五十', '五噎，喉咽妨塞，食饮不下。', '-', '-', '柴胡', '[\'昆布\', \'两半\', \'咸味\', \'羚羊角\', \'半两\', \'柴胡\', \'分去\', \'麦门冬\', \'两半\', \'杏仁\', \'半两\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\', \'天门冬\', \'两半\', \'木通\', \'槟榔\', \'分诃黎勒皮\', \'两半\', \'郁李仁\', \'两汤\', \'去皮\', \'炒川\', \'大黄\', \'射干\', \'半两\', \'川朴\', \'消两桂心\', \'百合\', \'紫苏子\', \'半两\', \'橘皮\', \'分汤\', \'浸去\']');
INSERT INTO `prescript` VALUES (57609, '23279', '苓泽芍药汤', '茯苓3钱，泽泻3钱，半夏3钱，杏仁3钱，柴胡3钱，芍药3钱。', '水煎半杯，热服。', '《四圣心源》卷八', '耳流黄水。', '-', '-', '柴胡', '[\'茯苓\', \'泽泻\', \'半夏\', \'杏仁\', \'柴胡\', \'芍药\']');
INSERT INTO `prescript` VALUES (57610, '23277', '柴胡汤', '柴胡（去苗）1两半，五味子1两，桔梗（炒）1两，熟干地黄（焙）1两，白茯苓（去黑皮）1两，麦门冬（去心，焙）1两，紫菀（去苗）1两，人参1两，地骨皮1两，黄耆（锉）1两，白术1两，桂（去粗皮）1两，牡蛎（研粉）1两，半夏（去滑，汤洗7遍）3分，甘草（炙）3分。', '每服3钱匕，水1盏，加生姜半分（拍碎），大枣3枚（擘破），煎至6分，去滓温服，不拘时候。', '《圣济总录》卷三十七', '寒热往来，夜卧盗汗，四肢无力，饮食口苦，上气咳嗽。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两半\', \'五味子\', \'桔梗\', \'两熟\', \'地黄\', \'白茯苓\', \'黑皮\', \'麦门冬\', \'紫菀\', \'人参\', \'地骨皮\', \'两黄耆\', \'白术\', \'两桂去\', \'粗皮\', \'牡蛎\', \'研粉\', \'半夏\', \'洗遍\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (57611, '23274', '柴胡汤', '柴胡（去苗）1两半，鳖甲（去裙澜，醋炙，锉）1两半，郁李仁（汤浸，去皮尖，捣碎）1两半，芍药1两半，大黄（锉，炒）1两半，桃仁21枚（汤浸，去皮尖双仁，炒），诃黎勒皮1两，半桂（去粗皮）1两。', '每服4钱匕，水1盏半，煎至7分，去滓，加朴消少许，空腹温服，如人行4-5里再服。', '《圣济总录》卷五十七', '鼓胀坚块。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两半\', \'鳖甲\', \'两半\', \'郁李仁\', \'汤浸\', \'皮尖\', \'捣碎\', \'两半\', \'芍药\', \'两半\', \'大黄\', \'两半\', \'桃仁\', \'浸去\', \'皮尖\', \'双仁\', \'炒诃黎勒皮\', \'两半\', \'桂去\', \'粗皮\']');
