INSERT INTO `prescript` VALUES (57614, '23238', '柴胡汤', '柴胡（去苗）1两，黄芩（去黑心）1两，陈橘皮（汤浸，去白，焙）1两，栀子仁1两，石膏（碎）1两，羚羊角（镑）1两，生干地黄（焙）1两，芒消半两。', '每服3钱匕，水1盏，煎至7分，去滓温服，日2夜1。', '《圣济总录》卷五十四', '下焦热结，大小便不通。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'黑心\', \'两陈\', \'橘皮\', \'汤浸\', \'栀子\', \'石膏\', \'羚羊角\', \'两生\', \'地黄\', \'两芒\', \'半两\']');
