INSERT INTO `prescript` VALUES (57616, '23167', '补中益气汤', '牛蒡子1钱半，元参3钱，蜜耆3钱，白术1钱半（蜜炒），广陈皮1钱半，当归1钱半，甘草1钱，麦冬3钱半，苦桔梗1钱，红枣1枚，生姜3片，柴胡2钱（酒炒），升麻8分（酒炒）。', '水煎服。', '《喉科种福》卷四', '慢喉风，平素体虚，更兼暴怒，或过食五辛而生，或忧思太过而成。其发缓，其色淡，其肿微，咽干，舌滑而白，大便自利，脉细而微，唇如矾色，午前痛者。', '-', '-', '柴胡', '[\'牛蒡子\', \'钱半元\', \'参钱\', \'蜜耆\', \'白术\', \'钱半蜜\', \'炒广\', \'陈皮\', \'当归\', \'钱半\', \'甘草\', \'麦冬\', \'钱半苦\', \'桔梗\', \'红枣\', \'生姜\', \'柴胡\', \'钱酒\', \'升麻\', \'分酒\']');
INSERT INTO `prescript` VALUES (57617, '23143', '补肝饮', '甘菊2钱，甘草2钱，山药2钱，熟地2钱，防风1钱，柏子仁1钱，茯苓1钱，枸杞子1钱，白芍1钱，柴胡1钱。', '水煎，温服。', '《玉案》卷三', '乌睛陷者。', '-', '-', '柴胡', '[\'甘菊\', \'甘草\', \'山药\', \'熟地\', \'防风\', \'柏子仁\', \'茯苓\', \'枸杞子\', \'白芍\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57618, '23097', '观音救苦丹', '茯苓2两，木瓜2两，麦冬2两，生地2两，当归2两，藿香1两，菖蒲1两，茅术1两，半夏1两，腹皮1两，白矾1两，陈皮1两，防风1两，黄芩1两，杏仁1两，山栀8钱，柴胡8钱，木通8钱，香附8钱，桔梗8钱，泽泻8钱，远志8钱，砂仁5钱，川芎5钱，油朴5钱，羌活5钱，独活5钱，青黛5钱，川连5钱，雄黄5钱，甘草5钱，黄柏5钱，神曲5钱，枳壳5钱，广木香5钱，青木香5钱，嫩苏梗5钱，广郁金5钱。', '每服1钱4分重丸1丸，或3分5厘重丸4丸，鲜姜汤为引。服后取汗。', '《全国中药成药处方集》（吉林方）', '解表和中，化湿解毒。主四时感冒，伤食感冒，头痛体痛，发热畏冷，泛恶呕吐，泄泻。山岚瘴气，霉浊秽气，卒然中人，四肢清冷，呕吐搅闹，神昏吐沫；湿浊中阻，头痛身重，大便溏泄，不欲饮水。', '忌食生冷。', '-', '柴胡', '[\'茯苓\', \'木瓜\', \'麦冬\', \'生地\', \'当归\', \'藿香\', \'菖蒲\', \'两茅术\', \'半夏\', \'两腹\', \'白矾\', \'陈皮\', \'防风\', \'黄芩\', \'杏仁\', \'两山\', \'栀钱\', \'柴胡\', \'木通\', \'香附\', \'桔梗\', \'泽泻\', \'远志\', \'砂仁\', \'川芎\', \'钱油朴\', \'羌活\', \'独活\', \'青黛钱川\', \'雄黄\', \'甘草\', \'黄柏\', \'神曲\', \'枳壳\', \'钱广\', \'木香\', \'青木香\', \'钱嫩\', \'苏梗钱\', \'广郁\', \'金钱\']');
INSERT INTO `prescript` VALUES (57619, '23068', '固真汤', '升麻1钱，羌活1钱，柴胡1钱，炙甘草1钱5分，草龙胆1钱5分，泽泻1钱5分，黄柏2钱，知母2钱。', '《普济方》有苍术；《证治宝鉴》有汉防已。', '《兰室秘藏》卷下', '两丸冷，前阴痿弱，阴汗如水，小便后有余滴，尻臀并前阴冷，恶寒而喜热，膝下亦冷。', '-', '-', '柴胡', '[\'升麻\', \'羌活\', \'柴胡\', \'甘草\', \'分草\', \'龙胆\', \'钱分\', \'泽泻\', \'钱分\', \'黄柏\', \'知母\']');
INSERT INTO `prescript` VALUES (57620, '23052', '苍术芩连汤', '苍术（泔制，炒黄）1钱半，黄芩（酒炒）1钱，黄连（姜汁炒）1钱，木香1钱，枳实1钱，半夏（姜制）1钱，柴胡1钱，升麻1钱，川芎1钱，厚朴（姜制）1钱，桔梗1钱，木通1钱，甘草（炙）7分，生姜3片。', '水煎，温服。', '《张氏医通》卷十五', '瘴疠湿热。', '-', '-', '柴胡', '[\'苍术\', \'泔制\', \'黄钱\', \'黄芩\', \'黄连\', \'姜汁\', \'木香\', \'枳实\', \'半夏\', \'制钱\', \'柴胡\', \'升麻\', \'川芎\', \'厚朴\', \'制钱\', \'桔梗\', \'木通\', \'甘草\', \'炙分\', \'生姜\']');
INSERT INTO `prescript` VALUES (57621, '23030', '补胃汤', '黄耆（蜜炒）2钱，人参5分，甘草（炙）2钱，当归5分，神曲（炒）7分，柴胡3分，升麻2分，苍术（米泔浸）1钱，青皮（去瓤）5分，黄柏（酒炒）3分。', '水煎，食后服。', '《寿世保元》卷二', '脾胃虚弱，元气不足，四肢沉重，食后昏沉，怠于动作，嗜卧无力。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'甘草\', \'当归\', \'神曲\', \'炒分\', \'柴胡\', \'升麻\', \'苍术\', \'泔浸\', \'青皮\', \'黄柏\', \'炒分\']');
INSERT INTO `prescript` VALUES (57622, '23018', '补虚止嗽丸', '党参1两，白术4两，云苓3两，当归3两，广皮2两，半夏3两，炙耆4两，炙升麻5钱，柴胡5钱，麦冬1两，五味子1两，炙百合2两，炙冬花2两，白芥子1两，炙草2两。', '每服3钱，开水送下。', '《全国中药成药处方集》（大同方）', '中气虚弱，日久咳嗽。', '-', '-', '柴胡', '[\'党参\', \'白术\', \'两云苓\', \'当归\', \'两广\', \'半夏\', \'两炙耆\', \'升麻\', \'柴胡\', \'麦冬\', \'五味子\', \'百合\', \'两炙冬花\', \'白芥子\', \'两炙草\']');
INSERT INTO `prescript` VALUES (57623, '22990', '地榆败毒散', '西党参3钱，羌活3钱，独活3钱，柴胡3钱，前胡3钱，甘草3钱，茯苓3钱，枳壳2钱（炒用），抚芎2钱，桔梗2钱，生地榆1两，紫竹根1把，生姜3片。', '水煎服。', '《外科医镜》引沈雨苍方', '中狂犬毒，症见卒病心腹痛，神志不清，痛极无聊，自咬肌肉，甚者嚼瓷器衣服，闻声及风则战栗惊惕不安。', '-', '-', '柴胡', '[\'党参\', \'羌活\', \'独活\', \'柴胡\', \'钱前\', \'胡钱\', \'甘草\', \'茯苓\', \'枳壳\', \'桔梗\', \'钱生\', \'地榆\', \'紫竹\', \'根把\', \'生姜\']');
INSERT INTO `prescript` VALUES (57624, '22974', '和疟饮', '青皮1钱5分，柴胡1钱，制半夏2钱，草蔻仁1钱（研），厚朴1钱5分（姜汁炒），枳壳1钱5分（炒），槟榔1钱5分，甘草5分。', '煨姜3钱、大枣3个为引，河井水煎，露1宿，疟前温服，二煎接服。如恶心呕吐，加藿香3钱；如热重，加炒黄芩1钱5分；如口渴，加知母2钱，川贝母1钱5分；如小便短赤，内热，加青蒿3钱；如腹痛作泻，加赤芍5钱，煨广木香1钱5分；如未病前食荤，加炒山楂3钱；如食鱼腥，加威灵仙1钱；如面食，加炒莱菔子3钱，一服即愈，如不愈，次日加黑豆49粒、炒川山甲1钱。', '《慈航集》卷下', '疟，寒热分清，或一日一发，或间日一发者。', '愈后饮食宜清淡，3-4日再食荤腥调理，庶无疟之反复。面食忌一月。', '-', '柴胡', '[\'青皮\', \'钱分\', \'柴胡\', \'钱制\', \'半夏\', \'草蔻仁\', \'钱研\', \'厚朴\', \'钱分\', \'姜汁\', \'枳壳\', \'钱分\', \'槟榔\', \'钱分\', \'甘草\']');
INSERT INTO `prescript` VALUES (57625, '22925', '和腹汤', '人参1钱，柴胡1钱，甘草1钱，神曲1钱，厚朴1钱，白术2钱，陈皮5分。', '水煎服。', '《辨证录》卷九', '忍饥受饿，腹中空虚，内伤胃气，时遇天气不正，时寒时热，遂至胸膈闷塞，宛如结胸。', '-', '-', '柴胡', '[\'人参\', \'柴胡\', \'甘草\', \'神曲\', \'厚朴\', \'白术\', \'陈皮分\']');
INSERT INTO `prescript` VALUES (57626, '22889', '虎杖饮', '虎杖1两半，柴胡（去苗）1两半，五味子（炒）1两半，熟干地黄（焙）1两半，白茯苓（去黑皮）1两半，陈橘皮（去白，焙）1两半，麦门冬（去心，焙）1两半，黄芩（去黑心）1两半，甘草（炙，锉）1两半，人参1两，桂（去粗皮）2两，黄耆（锉）2两，芍药2两，当归（切，焙）2两。', '每服5钱匕，水1盏半，加生姜7片，大枣3枚（劈），同煎至8分，去滓温服，不拘时候。', '《圣济总录》卷八十七', '男子、妇人冷劳。身体羸瘦，食不化，心腹痞满，呕吐吞酸，面色萎黄，甚则心腹常痛，大肠泄痢，手足逆冷，骨节酸痛。', '-', '-', '柴胡', '[\'虎杖\', \'两半\', \'柴胡\', \'去苗\', \'两半\', \'五味子\', \'两半\', \'熟干\', \'地黄\', \'两半\', \'白茯苓\', \'黑皮\', \'两半\', \'橘皮\', \'两半\', \'麦门冬\', \'两半\', \'黄芩\', \'黑心\', \'两半\', \'甘草\', \'两半\', \'人参\', \'两桂去\', \'粗皮\', \'黄耆\', \'芍药\', \'当归\', \'切焙\']');
INSERT INTO `prescript` VALUES (57627, '22885', '赤芍药散', '赤芍药3分，柴胡1两（去苗），茯苓1两，大麻仁1合，木通半两（锉），槟榔3枚。', '每服4钱，以水一中盏，加生姜半分，豆豉50粒，葱白5寸，煎至6分，去滓，食前温服。', '《圣惠》卷二十九', '虚劳下焦有热，小便不利，骨节疼痛，肌肉急，腹内痞满。', '-', '-', '柴胡', '[\'芍药\', \'柴胡\', \'两去\', \'茯苓\', \'两大\', \'麻仁\', \'木通\', \'半两\', \'槟榔\']');
INSERT INTO `prescript` VALUES (57628, '22818', '和解汤', '柴胡1钱，白芍3钱，甘草1钱，枳壳5分，薄荷1钱，茯神3钱，丹皮2钱，当归3钱。', '水煎服。缓缓服之，3剂则可以开关矣。上关一开，而下格自愈。', '《辨证录》卷五', '少阳之气不通之关格症。忽然上不能食，下不能出，胸中胀急，烦闷不安，大小便窘迫之极。', '-', '-', '柴胡', '[\'柴胡\', \'白芍\', \'甘草\', \'枳壳\', \'薄荷\', \'茯神\', \'丹皮\', \'当归\']');
INSERT INTO `prescript` VALUES (57629, '22753', '虎杖饮', '虎杖1两半，柴胡（去苗）1两半，五味子（炒）1两半，熟干地黄（焙）1两半，白茯苓（去黑皮）1两半，陈橘皮（去白皮）1两半，麦门冬（去心，焙）1两半，黄芩（去黑皮）1两半，甘草（炙，锉）1两半，人参1两，川大黄（锉碎，微炒）1两半，黄连（去须）1两半，当归1两半，赤茯苓1两半，黄耆（锉）3分，生干地黄3分，赤芍药3分，栀子仁半两。', '每服20丸，以温水送下，不拘时候。', '《普济方》卷二三○', '男子妇人冷劳，身体羸瘦。', '-', '-', '柴胡', '[\'虎杖\', \'两半\', \'柴胡\', \'去苗\', \'两半\', \'五味子\', \'两半\', \'熟干\', \'地黄\', \'两半\', \'白茯苓\', \'黑皮\', \'两半\', \'橘皮\', \'白皮\', \'两半\', \'麦门冬\', \'两半\', \'黄芩\', \'黑皮\', \'两半\', \'甘草\', \'两半\', \'人参\', \'两川\', \'大黄\', \'两半\', \'黄连\', \'两半\', \'当归\', \'两半\', \'茯苓\', \'两半\', \'黄耆\', \'分生\', \'地黄\', \'分赤\', \'芍药\', \'栀子\', \'半两\']');
INSERT INTO `prescript` VALUES (57630, '22732', '沉香鳖甲散', '木香1两，沉香3分，鳖甲（九肋者1枚，净去裙襕，醋炙令黄香）1两半，常山1两，当归（去土并苗）1两，柴胡（去苗）1两，人参（去苗）1两，白茯苓（去黑皮）1两，官桂（去粗皮）1两，青橘（去瓤）1两，陈橘（去瓤）1两，生地黄1两，半夏1两（以汤洗7遍去滑止），槟榔3分，甘草3分（炙）。', '沉香鳖甲汤（《普济方》卷三三二）。', '《博济》卷四', '室女荣卫不调，经候凝滞，或时头目昏闷，上膈积涎，肢体不利，五心虚烦，饮食进退，多困少力。', '-', '-', '柴胡', '[\'木香\', \'沉香\', \'鳖甲\', \'九肋者\', \'炙令\', \'黄香\', \'两半\', \'常山\', \'当归\', \'柴胡\', \'人参\', \'白茯苓\', \'黑皮\', \'两官\', \'桂去\', \'粗皮\', \'两青橘\', \'陈橘\', \'生地黄\', \'半夏\', \'两以\', \'洗遍\', \'滑止\', \'槟榔\', \'甘草\']');
INSERT INTO `prescript` VALUES (57631, '22716', '鸡胵汤', '生鸡内金4钱（去净瓦石糟粕，捣碎），于术3钱，生杭芍4钱，柴胡2钱，广陈皮2钱，生姜3钱。', '水煎服。若小便时觉热，且色黄赤者，宜酌加滑石数钱。', '《衷中参西》上册', '气郁成臌胀，兼治脾胃虚而且郁，饮食不能运化。', '-', '-', '柴胡', '[\'鸡内金\', \'净瓦石\', \'糟粕\', \'捣碎\', \'于术\', \'钱生\', \'杭芍\', \'柴胡\', \'钱广\', \'陈皮\', \'生姜\']');
INSERT INTO `prescript` VALUES (57632, '22704', '补真内托散', '柴胡8分，干葛8分，人参5分，黄耆1钱，熟地1钱，当归8分，茯神8分，枣仁6分，麦冬7分。', '如虚火上泛，或吐衄血者，加泽泻6分，茜根8分，丹皮8分；如血不止者，加牛膝、丹参各1钱；如咳嗽痰多，加贝母、阿胶、天冬各7-8分；如脾胃弱，加山药、扁豆1钱。', '《不居集》上集卷十', '房劳过度，耗散真元，外挟客邪者。', '-', '-', '柴胡', '[\'柴胡\', \'分干葛分\', \'人参\', \'分黄耆\', \'熟地\', \'当归\', \'茯神\', \'枣仁\', \'麦冬\']');
INSERT INTO `prescript` VALUES (57633, '22703', '茯苓汤', '茯苓1两，麦门冬1两，黄芩1两，秦艽半两，柴胡半两，五味子1两，杏仁1分。', '每服5钱，水2盏，煎至7分，去滓温服，不拘时候。', '《鸡峰》卷十一', '心咳，恶热脉疾，小便赤涩。', '-', '-', '柴胡', '[\'茯苓\', \'麦门冬\', \'黄芩\', \'秦艽\', \'半两\', \'柴胡\', \'半两\', \'五味子\', \'杏仁\']');
INSERT INTO `prescript` VALUES (57634, '22699', '金莲饮子', '防风半两，甘草（炙）半两，连翘半两，柴胡（去芦）半两，山栀子半两。', '每服2钱，用水6分，煎至3分，食后服。', '《奇效良方》卷六十四', '小儿蕴积壮热，赤眼，口疮，心烦躁闷，咽干多渴，潮热不止。', '-', '-', '柴胡', '[\'防风\', \'半两\', \'甘草\', \'半两\', \'连翘\', \'半两\', \'柴胡\', \'去芦\', \'半两\', \'栀子\', \'半两\']');
