INSERT INTO `prescript` VALUES (57636, '22692', '固真汤', '人参5分，黄柏（炒）1钱，黄芩（炒）1钱，白葵花1钱，干姜3分，甘草（炙）3分，郁李仁8分，柴胡7分，陈皮5分。', '水1钟半，煎7分，空心服。', '《万氏家抄方》卷五', '妇人气滞白带，临行时脐下痛甚。', '-', '-', '柴胡', '[\'人参\', \'黄柏\', \'黄芩\', \'葵花\', \'干姜\', \'甘草\', \'炙分\', \'郁李仁\', \'柴胡\', \'陈皮分\']');
INSERT INTO `prescript` VALUES (57637, '22658', '参胡饮子', '人参1钱2分，柴胡1钱5分，黄芩1钱，甘草1钱，当归1钱，大黄1钱，白芍药1钱，生姜2片，大枣子1枚。', '上(口父)咀。用水煎熟，食远温服。老人、虚人或大便自利者，去大黄。', '《杏苑》卷四', '痰热头疼，手足烦热，身体倦怠，肢节疼痛，嗜卧不安，饮食无味，大便闭结，兼治五饮。', '-', '-', '柴胡', '[\'人参\', \'钱分\', \'柴胡\', \'钱分\', \'黄芩\', \'甘草\', \'当归\', \'大黄\', \'钱白\', \'芍药\', \'生姜\', \'片大\', \'枣子\']');
