INSERT INTO `prescript` VALUES (57640, '22529', '大连翘饮', '连翘1两，瞿麦1两，滑石1两，车前子1两，牛蒡子1两，赤芍1两，山栀子半两，木通半两，当归半两，防风半两，黄芩1两半，柴胡2两，甘草1两半，荆芥穗1两半，蝉蜕2钱半。', '每服2钱，加灯心、薄荷、麦门冬，水煎，温服。疮疹，加紫苏。', '《伤寒图歌活人指掌》卷五', '小儿伤寒，伤风发热，时行发热，痰盛壅，风热丹毒，疮疹，项上生核，腮赤，痈疖，一切发热。', '-', '-', '柴胡', '[\'连翘\', \'瞿麦\', \'滑石\', \'车前子\', \'牛蒡子\', \'赤芍\', \'两山\', \'栀子\', \'半两\', \'木通\', \'半两\', \'当归\', \'半两\', \'防风\', \'半两\', \'黄芩\', \'两半\', \'柴胡\', \'甘草\', \'两半\', \'荆芥\', \'两半\', \'蝉蜕\', \'钱半\']');
INSERT INTO `prescript` VALUES (57641, '22503', '胡黄连散', '胡黄连3分，天灵盖1两（涂醋，炙令黄），鳖甲1两半（涂醋，炙令黄，去裙襕），柴胡1两（去苗），赤芍药3分，生干地黄1两，当归3分，地骨皮1两，黄耆1两（锉），麝香1分（细研），大黄1分，木香1分，青蒿1分，黄芩1分，犀角1分。', '每服4钱，以水1中盏，入生姜半分，桃、柳心各7茎，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十', '妇人热劳体瘦，经脉不通，四肢疼痛，口干烦渴，不得眠卧，饮食全少。', '-', '-', '柴胡', '[\'胡黄连\', \'天灵盖\', \'两涂\', \'炙令\', \'鳖甲\', \'两半\', \'涂醋\', \'炙令\', \'黄去\', \'柴胡\', \'两去\', \'芍药\', \'分生\', \'地黄\', \'当归\', \'地骨皮\', \'两黄耆\', \'麝香\', \'分细研\', \'大黄\', \'木香\', \'青蒿\', \'黄芩\', \'犀角\']');
INSERT INTO `prescript` VALUES (57642, '22459', '和血补气饮', '防风1钱，黄芩1钱，蔓荆子1钱，白芷1钱，柴胡1钱，甘草5分，当归1钱5分，升麻6分。', '水煎服。', '《异授眼科》', '患目之后，多受风寒，而气血不通，九窍闭塞，以致肺气衰弱，心火太旺，故心血欺凌肺金，目有白珠多红，及眵泪少涩难开。', '-', '-', '柴胡', '[\'防风\', \'黄芩\', \'蔓荆子\', \'白芷\', \'柴胡\', \'甘草\', \'当归\', \'钱分\', \'升麻\']');
INSERT INTO `prescript` VALUES (57643, '22456', '八岁汤', '芍药2两，栀子2两，柴胡1两6铢，升麻2两半，黄芩2两半，黄连2两半，竹叶（切）1升半，桔梗1两半，细辛15铢，知母2两，大黄2两（1本有枳实、杏仁各1两半，无桔梗、黄连）。', '上(口父)咀。以水6升，煮取1升8合，去滓，分4服，十岁儿为3服。', '方出《千金》卷五，名见《医部全录》卷四四一', '8岁以上儿，热结痰实，不能食，自下。', '-', '-', '柴胡', '[\'芍药\', \'栀子\', \'柴胡\', \'两铢\', \'升麻\', \'两半\', \'黄芩\', \'两半\', \'黄连\', \'两半\', \'竹叶\', \'切升半\', \'桔梗\', \'两半\', \'细辛\', \'知母\', \'大黄\', \'两本\', \'枳实\', \'杏仁\', \'两半\', \'桔梗\', \'黄连\']');
INSERT INTO `prescript` VALUES (57644, '22432', '补药麝脐丸', '麝脐1枚（烧灰），地黄（洗）1两，地骨皮1两，山药1两，柴胡1两，白术、活鳖1个（2斤者佳）。', '方中白术用量原缺。', '《中藏经》卷下', '劳伤骨蒸，久而瘦弱，肉消毛落，妄血喘咳者。', '-', '-', '柴胡', '[\'麝脐枚\', \'烧灰\', \'地黄\', \'地骨皮\', \'山药\', \'柴胡\', \'白术\', \'活鳖\', \'个斤\']');
INSERT INTO `prescript` VALUES (57645, '22417', '安胃散', '丁香5分，茱萸1钱，草蔻1钱，参1钱，炙甘草5分，耆1钱，柴胡5分，升麻7分，柏3钱，陈皮5分，归1钱5分，苍术1钱。', '水煎，温服。痰饮为患，呕吐痰涎者，加半夏、茯苓、陈皮。', '《脉因证治》卷下', '胃寒所致呕吐哕。', '-', '-', '柴胡', '[\'丁香\', \'茱萸\', \'钱草蔻\', \'参钱\', \'甘草\', \'分耆\', \'柴胡\', \'升麻\', \'分柏钱\', \'陈皮分\', \'钱分\', \'苍术\']');
INSERT INTO `prescript` VALUES (57646, '22393', '附子汤', '附子（炮裂，去皮脐）1两，柴胡（去苗）1两，秦艽（去苗土）1两半。', '每服2钱匕，用猪(月臣)子1两（切令细），酒半盏，水3分，加薤白3寸，同煎令猪(月臣)熟，去滓温服，每日五更初服之。', '《圣济总录》卷八十七', '冷劳肌瘦，盗汗少力，时发寒热，不思饮食。', '-', '-', '柴胡', '[\'附子\', \'炮裂\', \'去皮\', \'柴胡\', \'秦艽\', \'苗土\', \'两半\']');
INSERT INTO `prescript` VALUES (57647, '22385', '举斑汤', '白芍药1钱，当归1钱，升麻5分，柴胡7分，白芷7分，穿山甲2钱，水姜1片。', '水煎，温服。如下后斑毒隐伏，反见循衣撮空，脉微者，加人参3钱，得补发出者不死。', '《温热暑疫全书》卷四', '托里举斑。主疫气留血分，里气壅闭，不下则斑不出，出则毒邪从外解矣。如下后斑渐出，更不可下，设有下证，宜少与承气缓服。倘大下则元气不振，斑毒内陷则危，宜此方。斑出不透而热不退。', '-', '-', '柴胡', '[\'芍药\', \'当归\', \'升麻\', \'柴胡\', \'白芷\', \'穿山甲\', \'钱水\', \'姜片\']');
INSERT INTO `prescript` VALUES (57648, '22330', '补中参附汤', '黄耆6钱，白术6钱，广皮2钱，升麻2钱，柴胡2钱，泡参2两，秦归2钱，炙甘草2钱，肉桂1钱，附片3钱。', '水煎，空腹温服。手足温而兼漏下黑血如衃者，去桂、附，加阿胶珠3钱、乌贼骨1两、姜灰2钱。', '《中医妇科治疗学》', '补正。主瘀积日久，正虚邪实，身体羸弱，饮食不思，头晕目眩，神疲懒言，气短下陷，溲清便溏，甚或四肢不温，舌淡苔少，脉浮虚而涩。', '-', '-', '柴胡', '[\'黄耆\', \'白术\', \'钱广皮\', \'升麻\', \'柴胡\', \'钱泡\', \'参两\', \'秦归钱\', \'甘草\', \'肉桂\', \'附片\']');
INSERT INTO `prescript` VALUES (57649, '22306', '和营双解散', '当归2钱，柴胡1钱，葛根2钱，广皮1钱，半夏1钱5分，贝母2钱，茯苓2钱，防风1钱，薄荷1钱，苏梗1钱，姜皮8分。', '河、井水煎服。', '《医醇剩义》卷三', '疟邪在营，间日一作者。', '-', '-', '柴胡', '[\'当归\', \'柴胡\', \'葛根\', \'钱广皮\', \'半夏\', \'钱分\', \'贝母\', \'茯苓\', \'防风\', \'薄荷\', \'苏梗钱\', \'姜皮分\']');
INSERT INTO `prescript` VALUES (57650, '22276', '参胡芍药汤', '人参1钱，柴胡1钱，芍药1钱，黄芩1钱，知母1钱，麦门冬1钱，生地1钱半，枳壳8分，甘草3分。', '加生姜3片，水煎，温服。胸满腹胀便硬，去参，加厚朴、倍枳壳；小便频数，加茯苓、泽泻；呕，加竹茹；血弱，加当归；虚烦，加竹叶、粳米；二便自利，胸腹不饱，形羸，脉弱，去枳壳，倍人参；不睡，加炒酸枣仁、茯神；宿粪未净，腹满或疼，便硬不通，量加大黄。', '《医学入门》卷四', '伤寒14日外，余热未除，脉息未缓，大便不快，小便黄赤，或渴或烦，不能安睡，不思饮食。', '-', '-', '柴胡', '[\'人参\', \'柴胡\', \'芍药\', \'黄芩\', \'知母\', \'麦门冬\', \'生地\', \'钱半\', \'枳壳\', \'甘草\']');
INSERT INTO `prescript` VALUES (57651, '22241', '金钱鼠粘汤', '鼠粘子1钱，黄连2钱，当归1两，生甘草3钱，天花粉3钱，柴胡1钱5分，连翘2钱，红花1钱，玄参3钱，白芍3钱，金银花1两。', '水煎服。初起之时2剂全消，无令其日久溃败也。', '《洞天奥旨》卷七', '腋痈。发于腋下天池之穴，令人寒热大痛，掌热臂急而赤，俗名夹痈。', '若已溃败，此方不可服。当看阴阳治之。', '-', '柴胡', '[\'粘子\', \'黄连\', \'当归\', \'生甘草\', \'天花粉\', \'柴胡\', \'钱分\', \'连翘\', \'钱红\', \'花钱\', \'玄参\', \'白芍\', \'金银花\']');
INSERT INTO `prescript` VALUES (57652, '22226', '诃黎勒丸', '诃黎勒皮半两，大麦糵1分（炒令微黄），柴胡半两（去苗），芎1两，川大黄半两（锉碎，微炒），赤茯苓1分，鳖甲半两（涂醋炙令黄，去裙襕），枳壳1分（麸炒微黄，去瓤），桂心1分，赤芍药半两，干姜1分（炮裂，锉），厚朴半两（去粗皮，涂生姜汁，炙令香熟）。', '每服5丸，以粥饮送下，日3次。', '《圣惠》卷八十八', '小儿癖气，壮热瘦瘁，不欲乳食。', '-', '-', '柴胡', '[\'黎勒皮\', \'半两\', \'大麦\', \'炒令\', \'微黄\', \'柴胡\', \'半两\', \'苗芎\', \'两川\', \'大黄\', \'半两\', \'茯苓\', \'鳖甲\', \'半两\', \'涂醋\', \'炙令\', \'黄去\', \'枳壳\', \'分麸\', \'微黄\', \'桂心分\', \'芍药\', \'半两\', \'干姜\', \'分炮裂\', \'厚朴\', \'半两\', \'粗皮\', \'涂生\', \'姜汁\', \'炙令\', \'香熟\']');
INSERT INTO `prescript` VALUES (57653, '22223', '经效散', '大黄半两，当归半两，芍药半两，北柴胡1两（去芦），粉草1分，连翘1分，犀角1钱（后入）。', '每用3钱，水1盏煎，食后服，仍用磨翳膏点之。', '《得效》卷十六', '眼因撞刺生翳，疼痛无时，经久不安者，复被物撞之，兼为风热所攻，转加痛楚，昏暗不见。', '-', '-', '柴胡', '[\'大黄\', \'半两\', \'当归\', \'半两\', \'芍药\', \'半两\', \'柴胡\', \'两去\', \'芦粉\', \'草分\', \'连翘\', \'犀角\', \'后入\']');
INSERT INTO `prescript` VALUES (57654, '22194', '败毒散', '羌活1钱（去芦），独活1钱（去芦），柴胡1钱（去毛），前胡1钱（去芦），枳壳（炒）8分，茯苓8分（去皮），川芎7分，甘草5分（炙），桔梗8分（去芦）。', '《医方集解》有薄荷少许；《疫疹一得》以葱为引。', '《明医指掌》卷六', '脏毒协寒便血，伤寒头痛，憎寒壮热，项强睛暗，鼻塞，风痰及时疫，岚瘴鬼疟，或声如蛙鸣，赤眼口疮，湿毒流注，脚肿腮肿，喉痹毒痢，诸疮斑疹。', '-', '-', '柴胡', '[\'羌活\', \'独活\', \'芦柴\', \'胡钱\', \'毛前\', \'胡钱\', \'枳壳\', \'炒分\', \'茯苓\', \'去皮\', \'川芎\', \'甘草\', \'桔梗\', \'分去\']');
