INSERT INTO `prescript` VALUES (57657, '22146', '柴胡汤', '柴胡半两，荆芥穗半两，秦艽半两，知母半两，当归半两，官桂半两，藿香半两，甘松半两，败龟（醋炙）半两，川乌头（炮）半两，地骨皮半两，白胶香半两，芍药半两，京芎1两，苎根（湿秤）2两（切碎）。', '每服2钱，水1盏，加生姜3片，大枣1个，同煎7分，去滓，早、午食后、夜睡各1服。3服滓并煎作1服吃。', '《苏沈良方》卷九', '瘰疬。', '忌一切鱼、面等毒及房事。', '-', '柴胡', '[\'柴胡\', \'半两\', \'荆芥\', \'半两\', \'秦艽\', \'半两\', \'知母\', \'半两\', \'当归\', \'半两\', \'官桂\', \'半两\', \'藿香\', \'半两\', \'甘松\', \'半两\', \'败龟\', \'半两\', \'乌头\', \'半两\', \'地骨皮\', \'半两\', \'白胶\', \'半两\', \'芍药\', \'半两\', \'京芎\', \'根湿\', \'切碎\']');
