INSERT INTO `prescript` VALUES (57673, '21804', '防风散', '防风1两（去芦头），甘菊花1两，赤芍药2两，石膏4两，葛根1两（锉），柴胡2两（去苗），蔓荆子1两，甘草1两（炙微赤，锉），杏仁1两（汤浸，去皮尖双仁，麸炒微黄）。', '每服4钱，以水1中盏，加生姜半分，煎至5分，去滓，加竹沥半合，更煎2-3沸，不拘时候温服。', '《圣惠》卷四十', '上焦风壅头痛，口干烦热。', '忌炙爆、热面、大蒜等。', '-', '柴胡', '[\'防风\', \'两去\', \'芦头\', \'甘菊花\', \'两赤\', \'芍药\', \'石膏\', \'葛根\', \'柴胡\', \'两去\', \'蔓荆子\', \'甘草\', \'两炙微\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (57674, '21803', '表里兼顾汤', '大黄2钱，人参5钱，柴胡3分，甘草1钱，丹皮2钱。', '水煎服。', '《辨证录》卷一', '冬月伤寒，谵语，发潮热，以承气汤下之，不应，脉反微涩者，里虚表邪盛之证。', '-', '-', '柴胡', '[\'大黄\', \'人参\', \'柴胡\', \'甘草\', \'丹皮\']');
INSERT INTO `prescript` VALUES (57675, '21801', '胡黄连散', '胡黄连1两，人参1两（去芦头），赤茯苓1两，柴胡1两（去苗），栀子仁1两，麦门冬1两（去心），犀角屑1两，青橘皮3分（汤浸，去白瓤，焙），桔梗1两（去芦头），槟榔半两，鳖甲2两（涂醋炙令黄，去裙襕）。', '每服4钱以童子小便1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十一', '热劳，心神烦热，食少乏力。', '忌猪肉、苋菜、醋物。', '-', '柴胡', '[\'胡黄连\', \'人参\', \'两去\', \'芦头\', \'茯苓\', \'柴胡\', \'两去\', \'栀子\', \'麦门冬\', \'两去\', \'犀角\', \'两青\', \'橘皮\', \'分汤\', \'浸去\', \'桔梗\', \'两去\', \'芦头\', \'槟榔\', \'半两\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\']');
INSERT INTO `prescript` VALUES (57676, '21743', '茯神丸', '茯神2两（去皮木），柴胡1两半（去苗），黄耆1两半，生干地黄2两，桔梗2两，鳖甲2两（醋炙黄色），人参1两，白前1两，枳壳1两半（炙，去白），赤芍药1两半。', '每服10丸，食后生姜汤送下。临时更加减服。', '《博济》卷二', '心肺壅热，口苦舌干，涕唾稠粘，胸膈烦闷，不思饮食，肢体倦怠，或发烦热，状似骨蒸。', '-', '-', '柴胡', '[\'茯神\', \'去皮\', \'柴胡\', \'两半\', \'去苗\', \'黄耆\', \'半生\', \'地黄\', \'桔梗\', \'鳖甲\', \'两醋\', \'黄色\', \'人参\', \'两白前\', \'枳壳\', \'两半\', \'芍药\', \'两半\']');
INSERT INTO `prescript` VALUES (57677, '21735', '参术柴苓汤', '人参1钱，白术1钱，茯苓1钱，陈皮1钱，柴胡7分，升麻7分，山栀（炒）8分，钩藤钩1钱，甘草（炒）5分。', '每次1-2钱，加生姜、大枣，水煎服。', '《保婴撮要》卷三', '肝经风热，脾土受克，其证善怒，睡中抽搐，遍身作痒，饮食少思；或疮疡脾气虚弱，肝气内动，肢体抽动。', '-', '-', '柴胡', '[\'人参\', \'白术\', \'茯苓\', \'陈皮\', \'柴胡\', \'升麻\', \'分山\', \'炒分\', \'钩藤\', \'甘草\', \'炒分\']');
INSERT INTO `prescript` VALUES (57678, '21732', '久疟饮', '人参1钱，于白术2钱，云苓2钱，制半夏2钱，青皮8分，柴胡5分（炒），草蔻仁3钱（研），龙骨3钱，煨姜2钱，大枣3个。', '疟未来之前，早2时服，2煎接服之。1剂轻，2剂减去其半，3剂全止，4剂精神如常矣。如腹胀气闷，加煨广木香8分；如作泻，加鹿角霜3钱；如夜发者，加炒升麻8分，夜明砂2钱；如口渴，加麦冬8分；如虚热，加青蒿3钱；如大便干结，加鲜首乌8钱，当归5钱；如阴虚咳嗽，加炙鳖甲5钱，川贝母1钱。', '《慈航集》卷下', '久疟不止，面色黄瘦，寒热不清。', '-', '-', '柴胡', '[\'人参\', \'钱于\', \'白术\', \'钱云苓\', \'钱制\', \'半夏\', \'青皮\', \'柴胡\', \'草蔻仁\', \'钱研\', \'龙骨\', \'姜钱\', \'大枣\']');
INSERT INTO `prescript` VALUES (57679, '21677', '保命丸', '苦参10斤，草胡麻5斤，当归5斤，防风5斤，芜荑5斤，白蒺藜5斤，大风肉2斤，薄荷叶2斤，土木鳖2斤，荆芥2斤，胡连12两，银柴胡12两。', '每日服4次，约2合，细茶送下。轻者不过7-8升，重者1斗5升，再重者2-3斗痊愈。如身发多疮，或生湿毒，通身如蛇皮，或破流血水者，是脾经受毒，加白术10两；如手足破伤，麻木遍身，发紫块、白瘢癜，满身肉痛，此胃经受毒，加厚朴10两；如眉发脱落，遍身发疮癣，满头面上蝇虫痕，此肺家受毒，加黄芩10两；如面起紫泡，身如紫云朵，四肢先见者，乃肝属木而有此疮也，春2-3月、秋9-10月各发1次，此肝经受毒，加山栀、连翘各1斤；如脚底先穿，眼泪如珠流者，遍身骨节刺痛，又发血癣如桃花朵者，此心经受毒，加黄连、山栀各1斤；', '《疡医大全》卷二十八', '大麻风。', '-', '-', '柴胡', '[\'苦参\', \'胡麻\', \'当归\', \'防风\', \'芜荑斤\', \'蒺藜\', \'大风\', \'肉斤\', \'薄荷叶\', \'土木\', \'荆芥\', \'胡连\', \'银柴胡\']');
INSERT INTO `prescript` VALUES (57680, '21654', '阿胶汤', '阿胶（炒燥）1两，人参1两，白茯苓（去黑皮）1两，玄参1两，丹参1两，防风（去叉）1两，黄耆1两，生干地黄（焙）1两，葛根1两，柴胡（去南）1两，秦艽（去苗土）1两，黄连（去须）1两，龙胆1两，枳壳（去瓤，麸炒）1两，地骨皮1两，百合1两，鳖甲（去裙襕，醋炙）1两，甘草（炙）1两，桔梗（炒）1两，知母（焙）1两，贝母（去心）1两，款冬花1两，石膏（碎）1两，麻黄（去根节）1两，黄芩（去黑心）1两，栀子仁1两，麦门冬（去心）1两，防己1两，栝楼根1两，马兜铃1两，大黄（炒）1两，桑根白皮（炙）1两，白药子1', '每服5钱匕，水1盏半，煎至8分，去滓，食后、临卧温服。', '《圣济总录》卷九十三', '骨蒸虚劳，热气上熏，咽嗌焦干，津液枯燥，烦渴引饮。', '-', '-', '柴胡', '[\'阿胶\', \'炒燥\', \'人参\', \'白茯苓\', \'黑皮\', \'玄参\', \'丹参\', \'防风\', \'黄耆\', \'两生\', \'地黄\', \'葛根\', \'柴胡\', \'秦艽\', \'苗土\', \'黄连\', \'龙胆\', \'枳壳\', \'地骨皮\', \'百合\', \'鳖甲\', \'甘草\', \'桔梗\', \'知母\', \'贝母\', \'款冬花\', \'石膏\', \'麻黄\', \'根节\', \'黄芩\', \'黑心\', \'栀子\', \'麦门冬\', \'防己\', \'栝楼\', \'根两\', \'马兜铃\', \'大黄\', \'两桑根\', \'白皮\', \'白药子\']');
INSERT INTO `prescript` VALUES (57681, '21569', '参胡温胆汤', '陈皮1钱，半夏1钱，茯苓1钱，枳实1钱，人参1钱，竹茹8分，香附8分，麦门冬8分，柴胡8分，桔梗8分，甘草3分，生姜3片，大枣2个。', '水煎，温服。', '《医学入门》卷四', '心胆虚怯，触事易惊，梦寝不安，气郁生痰，变生诸症，或短气悸乏，或复自汗，四肢浮肿，饮食无味，烦躁不安。痰火。', '-', '-', '柴胡', '[\'陈皮\', \'半夏\', \'茯苓\', \'枳实\', \'人参\', \'竹茹\', \'香附\', \'麦门冬\', \'柴胡\', \'桔梗\', \'甘草\', \'生姜\', \'大枣\']');
INSERT INTO `prescript` VALUES (57682, '21528', '鸡骨丸', '乌鸡骨1具（汤浸，炙令微黄），川大黄1两（锉碎，微炒），枳实半两（麸炒微黄），鳖甲1两（涂醋炙令黄，去裙襕），泽泻1两，柴胡1两（去苗），桔梗1两（去芦头），人参1两（去芦头），赤芍药1两，黄芩1两，防葵3分，（庶虫）虫5枚，（微炒令黄），杏仁3分（汤浸，去皮尖双仁，麸炒微黄）。', '4-5岁儿，每服10丸，以粥饮送下，1日2次。', '《圣惠》卷八十八', '小儿羸瘦，腹内有癖气，胁下坚满，时有腹痛，虽食不成肌肉。', '-', '-', '柴胡', '[\'乌鸡\', \'骨具\', \'浸炙令\', \'微黄\', \'大黄\', \'枳实\', \'半两\', \'微黄\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'泽泻\', \'柴胡\', \'两去\', \'桔梗\', \'两去\', \'芦头\', \'人参\', \'两去\', \'芦头\', \'芍药\', \'黄芩\', \'两防\', \'葵分\', \'虫虫\', \'炒令\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (57683, '21498', '固根汤', '葳蕤1两，当归5钱，白芍5钱，熟地1两，麦冬5钱，甘菊3钱，菖蒲3分，柴胡5分。', '水煎服。连服4剂，即不畏风，再服4剂，见风不流泪矣，再服10剂痊愈。', '《辨证录》卷三', '少年时丧元阳，又加时眼，不守色戒，伤损大眦，眦孔不闭，风寒透入其孔，内气即虚，外邪难杜，以致目痛后迎风流泪，至夜则目暗不明，一见灯光两目干涩。', '-', '-', '柴胡', '[\'葳蕤\', \'当归\', \'白芍\', \'熟地\', \'麦冬\', \'甘菊\', \'菖蒲\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57684, '21481', '净腑汤', '柴胡1钱，白茯苓（去皮）1钱，猪苓1钱，泽泻1钱，三棱（醋炒）1钱，莪术（醋炒）1钱，山楂（去核）1钱，黄芩8分，白术（去芦）8分，半夏（姜制）8分，人参8分，胡黄连3分，甘草3分。', '加生姜、大枣，煎服。', '《回春》卷七', '小儿一切癖块，发热口干，小便赤，或泄泻。', '-', '-', '柴胡', '[\'柴胡\', \'白茯苓\', \'去皮\', \'猪苓\', \'泽泻\', \'三棱\', \'莪术\', \'山楂\', \'去核\', \'黄芩\', \'白术\', \'芦分\', \'半夏\', \'姜制\', \'人参\', \'胡黄连\', \'甘草\']');
INSERT INTO `prescript` VALUES (57685, '21438', '鸡舌香汤', '鸡舌香3分，秦艽（去苗土）3分，胡黄连半两，丁香半两，芎半两，柴胡（去苗）1两。', '每服3钱匕，水1盏，煎至6分，去滓，食前温服。', '《圣济总录》卷六十', '黄疸。', '-', '-', '柴胡', '[\'鸡舌\', \'香分\', \'秦艽\', \'苗土\', \'胡黄连\', \'半两\', \'丁香\', \'半两\', \'半两\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57686, '21434', '济阴至宝丹', '当归（酒洗）1钱，白芍（酒洗）8分，白茯苓（去皮）8分，白术（去芦）1钱，陈皮8分，知母8分（生用），贝母8分（去心），香附（便制）8分，柴胡（酒炒）3分，薄荷3分，地骨皮（去皮）8分，甘草3分，麦门冬（去心）8分。', '用煨生姜3片，水煎，温服。', '《古今医鉴》卷十一', '调经水，滋血脉，补虚劳，扶元气，健脾胃，养心肺，润咽喉，清头目，定心悸，安神魂，退潮热，除骨蒸，止喘嗽，化痰涎，收盗汗，止泄泻，开郁气，利胸膈，疗腹痛，解烦渴，散寒热，祛体疼。主妇人诸虚百损，五劳七伤，经脉不调，肢体羸瘦。', '-', '-', '柴胡', '[\'当归\', \'洗钱\', \'白芍\', \'洗分\', \'白茯苓\', \'去皮\', \'白术\', \'芦钱\', \'陈皮分\', \'知母\', \'分生用\', \'贝母\', \'分去\', \'香附\', \'制分\', \'柴胡\', \'炒分\', \'薄荷\', \'地骨皮\', \'去皮\', \'甘草\', \'麦门冬\', \'心分\']');
INSERT INTO `prescript` VALUES (57687, '21415', '柴胡汤', '柴胡（去苗）2两，射干2两，防葵（去芦）2两，牡丹皮2两，大黄（皂荚水2钟煮干）2两，杏仁（汤浸，去皮尖双仁，炒）2两，紫菀（去土）2两，葶苈子（隔纸炒）2两，紫苏子2两。', '方中防葵，《圣济总录》作“防风”。小便多，减射干。', '《普济方》卷二十七引《护命》', '肺壅热，上膈昏滞，头面及鼻内生疮，精神不爽，胸中烦渴。', '-', '-', '柴胡', '[\'柴胡\', \'射干\', \'两防葵\', \'芦两\', \'牡丹皮\', \'大黄\', \'皂荚\', \'水钟\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\', \'紫菀\', \'葶苈子\', \'隔纸\', \'紫苏子\']');
INSERT INTO `prescript` VALUES (57688, '21411', '和攻散', '柴胡2钱，栀子2钱，丹皮2钱，白芍5钱，茯苓3钱，甘草1钱，陈皮1钱，大黄1钱。', '水煎服。', '《辨证录》卷一', '冬月伤寒，邪在少阳，身热3日，腹满自利。', '-', '-', '柴胡', '[\'柴胡\', \'栀子\', \'丹皮\', \'白芍\', \'茯苓\', \'甘草\', \'陈皮\', \'大黄\']');
INSERT INTO `prescript` VALUES (57689, '21360', '大连翘汤', '连翘1钱，瞿麦1钱，荆芥1钱，木通1钱，车前子1钱，赤芍药1钱，当归1钱，防风1钱，柴胡1钱，滑石1钱，蝉蜕1钱，甘草（炒）1钱，山栀仁半钱，黄芩半钱。', '连翘饮（《得效》卷十一）、连翘汤（《幼科类萃》卷二十八）。', '《直指小儿》卷五', '解里热。主疮疹壮热，小便不通。诸般疮疖，丹毒脐风。', '-', '-', '柴胡', '[\'连翘\', \'瞿麦\', \'荆芥\', \'木通\', \'车前子\', \'芍药\', \'当归\', \'防风\', \'柴胡\', \'滑石\', \'蝉蜕\', \'甘草\', \'炒钱山\', \'栀仁半钱\', \'黄芩\', \'半钱\']');
INSERT INTO `prescript` VALUES (57690, '21345', '虎头骨散', '虎头骨1两（涂酥，炙黄），牡蛎3分（炒转色），地骨皮1两，柴胡1两半（去苗），鳖甲1两（涂酥，炙黄，去裙襕），知母1两，桂心半两，川朴消1两。', '每服5钱，以水1大盏，煎至5分，去滓，食前温服。取利下痰结为度。', '《圣惠》卷五十二', '疟疾。发作无时，寒热不定。', '-', '-', '柴胡', '[\'头骨\', \'两涂\', \'牡蛎\', \'转色\', \'地骨皮\', \'柴胡\', \'两半\', \'去苗\', \'鳖甲\', \'两涂\', \'黄去\', \'知母\', \'两桂心\', \'半两\', \'川朴\', \'消两\']');
INSERT INTO `prescript` VALUES (57691, '21268', '附子猪苓汤', '白芍药1两，甘草1两，羌活1两，附子、猪苓、黄芩、柴胡。', '方中附子、猪苓、黄芩、柴胡用量原缺。', '《银海精微》卷下', '气衰血旺，阳不胜阴，眼痛而憎寒者。', '-', '-', '柴胡', '[\'芍药\', \'甘草\', \'羌活\', \'附子\', \'猪苓\', \'黄芩\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57692, '21253', '参术汤', '人参2两，白术2两，枳壳（去瓤，麸炒）2两，赤茯苓（去黑皮）2两，槟榔（煨，锉）3分，肉豆蔻（去壳）4枚，柴胡（去苗）1两。', '每服3钱匕，水1盏，加生姜3片，大枣1个（擘破），煎至7分，去滓。食前温服。', '《圣济总录》卷一五五', '妊娠心腹痛，胁肋胀满，烦躁。', '-', '-', '柴胡', '[\'人参\', \'白术\', \'枳壳\', \'茯苓\', \'黑皮\', \'槟榔\', \'肉豆蔻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57693, '21176', '扶正散邪汤', '人参1钱，半夏1钱，甘草1钱，白术3钱，茯苓3钱，柴胡3钱。', '水煎服。', '《傅青主男科》卷上', '正气虚而邪气入，头痛发热，右寸脉大于左寸者。', '-', '-', '柴胡', '[\'人参\', \'半夏\', \'甘草\', \'白术\', \'茯苓\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57694, '21086', '补气汤', '柴胡2分，升麻3分，黄耆8分，当归身2钱，炙甘草4钱，红花少许。', '《医学纲目》有细辛少许，麻黄（炒）、苦丁香各半钱，无红花。', '《兰室秘藏》卷中', '年少时气弱，常于气海、三里灸之，节次约50-70壮，至年老添热厥头痛，虽冬天大寒，犹喜寒风，其头痛则愈。微来暖处，或见烟火，其痛复作，5-7年不愈者。服清上泻火汤后用此方。', '-', '-', '柴胡', '[\'柴胡\', \'升麻\', \'分黄\', \'耆分\', \'当归\', \'身钱\', \'甘草\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (57695, '21084', '保生汤', '紫菀（去苗土）1两半，柴胡（去苗）1两半，龙骨1两半，赤石脂1两半，艾叶（炒）3分，白术3分，黄连（去须）1两，厚朴（去粗皮，生姜汁炙）1两，阿胶（炙令燥）1两，枳壳（去瓤，麸炒）1两，地榆1两1分，肉豆蔻（去壳）1枚，益智（去皮）半两，干姜（炮）半两，旋覆花（炒）半两，黄芩（去黑心）半两。', '每服5钱匕，以水1盏半，煎至8分，去滓温服。', '《圣济总录》卷一五五', '祛散寒湿，安和胎气。主妊娠心腹痛。', '-', '-', '柴胡', '[\'紫菀\', \'苗土\', \'两半\', \'柴胡\', \'去苗\', \'两半\', \'龙骨\', \'两半\', \'赤石脂\', \'两半\', \'艾叶\', \'炒分\', \'白术\', \'黄连\', \'厚朴\', \'粗皮生\', \'姜汁\', \'阿胶\', \'炙令燥\', \'枳壳\', \'地榆\', \'两分\', \'肉豆蔻\', \'益智\', \'去皮\', \'半两\', \'干姜\', \'半两\', \'旋覆花\', \'半两\', \'黄芩\', \'黑心\', \'半两\']');
INSERT INTO `prescript` VALUES (57696, '21078', '胡黄连汤', '胡黄连半两，柴胡（去苗）半两，鳖甲（去裙襕，醋炙）半两，甘草（炙，锉）半两，白蒺藜（炒）半两，黄耆半两，附子（炮裂，去皮脐）半两，威灵仙（去土）1两。', '每服3钱匕，水1盏，童子小便、酒共半盏，乌梅1个（拍碎），同煎至1盏，去滓温服，不拘时候。', '《圣济总录》卷八十八', '虚劳，寒热心松，骨节酸疼。', '-', '-', '柴胡', '[\'胡黄连\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'鳖甲\', \'半两\', \'甘草\', \'半两\', \'蒺藜\', \'半两\', \'黄耆\', \'半两\', \'附子\', \'炮裂\', \'去皮\', \'半两\', \'威灵仙\']');
INSERT INTO `prescript` VALUES (57697, '21041', '恒山汤', '柴胡8两，恒山12两，芒消12两，大黄12两，黄芩6两，龙胆6两，茵陈6两，秦艽6两，栀子14个。', '每服2钱，水1盏，煎至6分，去滓，温服，不拘时候。', '《鸡峰》卷九', '急黄，酒黄，心黄，劳黄，气喘欲发。', '-', '-', '柴胡', '[\'柴胡\', \'恒山\', \'两芒\', \'消两\', \'大黄\', \'黄芩\', \'龙胆\', \'茵陈\', \'秦艽\', \'栀子\']');
INSERT INTO `prescript` VALUES (57698, '21012', '柴胡秦艽汤', '柴胡（去苗）1两，秦艽（去苗土）1两，常山1两，贝母（去心）1两，甘草（微炙）1两，乌梅肉（焙干）1两，山栀子仁1两，豉1两，鳖甲（去裙襕，醋炙）1两，黄芩（去黑心）1两，生姜（切）半两，大黄（锉，炒）半两，桃枝（锉）1握，柳枝（锉）1握，葱白（切）1握，薤白（切）1握，糯米半合。', '每服1钱匕，水半盏，酒2分，同煎至4分，去滓温服，早晨、日午、临卧各1。5岁以下分作2服，2岁以下分作3服。', '《圣济总录》卷一七九', '小儿盗汗。', '-', '-', '柴胡', '[\'柴胡\', \'秦艽\', \'苗土\', \'常山\', \'贝母\', \'甘草\', \'微炙两\', \'乌梅\', \'焙干\', \'两山\', \'栀子\', \'仁两豉\', \'鳖甲\', \'黄芩\', \'黑心\', \'生姜\', \'半两\', \'大黄\', \'半两\', \'桃枝\', \'柳枝\', \'锉握\', \'葱白\', \'切握\', \'薤白\', \'切握\', \'糯米\', \'半合\']');
INSERT INTO `prescript` VALUES (57699, '20994', '姜附御寒汤', '干姜（炮）1钱2分，半夏（汤洗）5分，柴胡（去苗）1钱，防风（去芦）半钱，羌活1钱，藁本（去土）8分，人参（去芦）半钱，白葵花5朵（去心萼），甘草（炙）8分，升麻7分，郁李仁（汤浸，去皮尖）半钱，当归身6分（酒制），桃仁半钱（汤浸，去皮尖，与郁李仁研如泥，入正药），黑附子（炮，去皮脐）4钱。', '上（口父）咀，都作1服。水5大盏，煎至3盏，入黄耆1钱，橘皮5分，草豆蔻1钱，再煎至2盏，再入酒制黄柏3分，酒制黄连3分，枳壳3分，酒地黄2分（此4味锉碎，预1日先用新水多半盏浸1宿），蔓荆子2分（亦预先1日用新水各另浸），将前正药去滓，入此3味，再上火同煎至1盏，去滓，空心热服之，待少时以美膳压之。', '《东垣试效方》卷二', '中气不足，遇冬天寒气客于脾胃之间，相引两胁缩急而痛，善嚏，鼻中流浊涕不止，不闻香臭，咳嗽脑痛，上热如火，下寒如冰，头时作阵痛，或暴痛，两目中流火，视物(目荒)(目荒)然，或耳鸣耳聋，喜晴明，恶阴寒，夜不得安卧，胸中痰涎，膈咽不通，饮食失味，口中沃沃，牙齿动摇，不能嚼物，腰脐间及尻肾膝足胻冷，阴汗自出，行步失力，风痹麻木，小便数，气短喘喝，少气不足以息，卒遗失无度。妇人白带，阴户中大痛，上牵心而痛，黧黑失色；男子控睾而痛，牵心腹阴阴而痛，面如赭色，食少，大小便不调，烦心，霍乱，逆气，里急，而腹皮白或黑，下气', '忌肉汤，宜食肉。', '-', '柴胡', '[\'干姜\', \'炮钱分\', \'半夏\', \'洗分\', \'柴胡\', \'苗钱\', \'防风\', \'芦半钱\', \'羌活\', \'藁本\', \'土分\', \'人参\', \'芦半钱\', \'葵花\', \'心萼\', \'甘草\', \'炙分\', \'升麻\', \'郁李仁\', \'汤浸\', \'皮尖\', \'半钱\', \'当归\', \'身分\', \'酒制\', \'桃仁\', \'半钱\', \'浸去\', \'皮尖\', \'郁李仁\', \'研如\', \'泥入\', \'正药\', \'附子\', \'去皮\']');
INSERT INTO `prescript` VALUES (57700, '20973', '金不换神仙膏', '川芎5钱，白芷5钱，生地5钱，熟地5钱，当归5钱，白术5钱，苍术5钱，陈皮5钱，香附5钱，枳壳5钱，乌药5钱，半夏5钱，青皮5钱，细辛5钱，知母5钱，贝母5钱，杏仁5钱，桑白皮5钱，黄连5钱，黄芩5钱，黄柏5钱，栀子5钱，大黄5钱，柴胡5钱，薄荷5钱，赤芍5钱，木通5钱，桃仁5钱，玄参5钱，猪苓5钱，泽泻5钱，桔梗5钱，前胡5钱，升麻5钱，麻黄5钱，牛膝5钱，杜仲5钱，山药5钱，远志5钱，续断5钱，良姜5钱，何首乌5钱，甘草5钱，连翘5钱，藁本5钱，茵陈5钱，地榆5钱，防风5钱，荆芥5钱，羌活5钱，独活5', '临用时加细药：乳香、没药、血竭、轻粉、朝脑（即樟脑）、片脑、麝香、龙骨、海螵蛸、赤石脂，上为细末，瓷器内收贮，临摊膏药时掺上。五劳七伤，遍身筋骨疼痛，腰脚软弱，贴二膏盲穴、两肾俞穴、两足三里穴；痰喘气急，咳嗽，贴肺俞穴、华盖穴、膻中穴；左瘫右痪，手足麻木，贴两肩井穴、两曲池穴；男子遗精白浊，妇人赤白带下，月经不调，血山崩漏，贴两阴交穴、关元穴；赤白痢疾，贴丹田穴；小肠气、疝气，贴膀胱穴；疟疾，男子贴左肩，女子贴右肩；偏正头风。贴风门穴；腰痛，贴命门穴；心气疼痛，贴中脘穴；走气，贴二章门穴；寒湿脚气，贴两三', '《古今医鉴》卷十六', '生肌定痛，调血祛风湿。主劳伤筋骨疼痛，痰喘咳嗽，左瘫右痪，手足麻木，赤白痢疾，疝气，疟疾，偏正头风，心气疼痛，寒湿脚气，男子遗精白浊，女子赤白带下，一切无名肿毒，跌打损伤。', '-', '-', '柴胡', '[\'川芎\', \'白芷\', \'生地\', \'熟地\', \'当归\', \'白术\', \'苍术\', \'陈皮\', \'香附\', \'枳壳\', \'乌药\', \'半夏\', \'青皮\', \'细辛\', \'知母\', \'贝母\', \'杏仁\', \'桑白皮\', \'黄连\', \'黄芩\', \'黄柏\', \'栀子\', \'大黄\', \'柴胡\', \'薄荷\', \'赤芍\', \'木通\', \'桃仁\', \'玄参\', \'猪苓\', \'泽泻\', \'桔梗\', \'钱前\', \'胡钱\', \'升麻\', \'麻黄\', \'牛膝\', \'杜仲\', \'山药\', \'远志\', \'续断\', \'钱良\', \'姜钱\', \'何首乌\', \'甘草\', \'连翘\', \'藁本\', \'茵陈\', \'钱地\', \'榆钱\', \'防风\', \'荆芥\', \'羌活\', \'独活\']');
INSERT INTO `prescript` VALUES (57701, '20964', '连翘汤', '连翘3两，升麻3两，杏仁（去皮尖）3两，射干3两，防己3两，黄芩3两，大黄3两，芒消3两，柴胡3两，芍药4两，甘草（炙）4两。', '以水9升，煮取3升，分服。', '《外台》卷三十四引《集验方》', '妒乳乳痈；附骨疽。', '忌海藻，菘菜。', '-', '柴胡', '[\'连翘\', \'升麻\', \'杏仁\', \'皮尖\', \'射干\', \'防己\', \'黄芩\', \'大黄\', \'两芒\', \'消两\', \'柴胡\', \'芍药\', \'甘草\']');
INSERT INTO `prescript` VALUES (57702, '20956', '虎头骨散', '虎头骨1两（涂酥，炙令黄），白茯苓1两，白术1两，人参3分（去芦头），麦门冬1两半（去心），赤芍药半两，桂心半两，黄耆1两，柴胡1两（去苗），陈橘皮3分（汤浸，去白瓤，焙），当归半两，沉香1两，五味子半两，甘草半两（炙微赤，锉），桃仁1两半（汤浸，去皮尖双仁，麸炒微黄）。', '每服5钱，以水1大盏，加生姜半分，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十八', '热病后，虚劳气发，作寒热，乍进乍退，头痛，眼睛疼，口苦，不思食。', '-', '-', '柴胡', '[\'头骨\', \'两涂\', \'炙令\', \'白茯苓\', \'白术\', \'人参\', \'分去\', \'芦头\', \'麦门冬\', \'两半\', \'芍药\', \'半两\', \'桂心\', \'半两\', \'黄耆\', \'柴胡\', \'两去\', \'橘皮\', \'分汤\', \'浸去\', \'当归\', \'半两\', \'沉香\', \'五味子\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'桃仁\', \'两半\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (57703, '20918', '麦门冬丸', '麦门冬1两（去心，焙），人参半两（去芦头），黄耆半两（锉，微炒），黄连半两（去须），青蒿子半两，桑根白皮半两（锉），柴胡3分（去苗），地骨皮半两，枳壳半两（麸炒微黄，去瓤）。', '每服5丸，以熟水研下，不拘时候。', '《圣惠》卷八十八', '小儿虽食，不着肌肤，羸瘦骨热，小便赤黄。', '-', '-', '柴胡', '[\'麦门冬\', \'两去\', \'人参\', \'半两\', \'芦头\', \'黄耆\', \'半两\', \'黄连\', \'半两\', \'青蒿\', \'半两\', \'桑根\', \'白皮\', \'半两\', \'柴胡\', \'分去\', \'地骨皮\', \'半两\', \'枳壳\', \'半两\', \'微黄\']');
