INSERT INTO `prescript` VALUES (57705, '20780', '茯苓调血汤', '半赤茯苓1两，赤芍药半两，川芎半两，半夏曲半两，前胡2钱半，柴胡2钱半，青皮2钱半，枳壳2钱半，北梗2钱半，桑白皮（炒）2钱半，白茅根2钱半，灯心2钱半，甘草（炙）2钱半。', '每服3钱，加生姜5片，蜜2匙，新水煎服。', '《直指》卷十六', '酒面过度，房劳后，小便下血。', '-', '-', '柴胡', '[\'半赤\', \'茯苓\', \'两赤\', \'芍药\', \'半两\', \'川芎\', \'半两\', \'半夏\', \'半两\', \'胡钱\', \'柴胡\', \'钱半\', \'青皮\', \'钱半\', \'枳壳\', \'钱半北\', \'桑白皮\', \'白茅根\', \'钱半\', \'灯心\', \'钱半\', \'甘草\']');
INSERT INTO `prescript` VALUES (57706, '20720', '还睛补肝丸', '白芍（酒炒）2两，熟地2两，当归（酒洗）2两，天冬2两，五味子2两，炙甘草2两，白术2两，白茯苓2两，官桂2两，车前子（微炒）2两，白菊花2两，青葙子2两，玄参2两，川芎1两，羌活（去芦）1两，防风（去芦）1两，人参1两，骨皮1两，黄芩（酒炒）1两，柴胡1两，细辛1两，决明子1两，苦参1两，黄连（姜汁炒）5钱。', '每服3钱，临睡白汤送下。久久服之，永不再发。', '《惠直堂方》卷二', '眼目羞明多泪，翳膜侵珠，时歇时作，久病不愈。', '-', '-', '柴胡', '[\'白芍\', \'熟地\', \'当归\', \'酒洗\', \'两天\', \'冬两\', \'五味子\', \'甘草\', \'白术\', \'白茯苓\', \'两官桂\', \'车前子\', \'白菊花\', \'青葙子\', \'玄参\', \'川芎\', \'羌活\', \'去芦\', \'防风\', \'去芦\', \'人参\', \'两骨皮\', \'黄芩\', \'柴胡\', \'细辛\', \'决明子\', \'苦参\', \'黄连\', \'姜汁\']');
INSERT INTO `prescript` VALUES (57707, '20718', '诃黎勒散', '诃黎勒1两（煨，用皮），鳖甲1两（涂醋炙徽黄，去裙襕），防葵3分，柴胡1两（去苗），陈橘皮3分（汤浸，去白瓤，焙），木香半两，赤茯苓3分，桔梗半两（去芦头），桂心半两，白术3分，赤芍药3分，槟榔半两。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓，稍热服，不拘时候。', '《圣惠》卷二十八', '气劳羸瘦，四肢疼痛，心腹妨闷，不欲饮食。', '忌苋菜。', '-', '柴胡', '[\'黎勒\', \'鳖甲\', \'两涂\', \'炙徽\', \'黄去\', \'防葵\', \'柴胡\', \'两去\', \'橘皮\', \'分汤\', \'浸去\', \'木香\', \'半两\', \'茯苓\', \'桔梗\', \'半两\', \'芦头\', \'桂心\', \'半两\', \'白术\', \'分赤\', \'芍药\', \'槟榔\', \'半两\']');
INSERT INTO `prescript` VALUES (57708, '20690', '胡黄连丸', '胡黄连1两，犀角（镑）1两，鳖甲（醋炙，去裙襕）1两，诃黎勒皮（半生半熟）1两，桔梗（锉炒）1两1分，升麻（锉）1两1分，地骨皮1两1分，知母（焙）1两1分，黄芩（去黑心）1两1分，甘草（炙、锉）3分，白茯苓（去黑皮）3分，人参3分，栝楼1个（大者），柴胡（去苗）1两半。', '每服20丸，食后以乌梅煎童子小便送下；如腹痛，用糯米饮下。', '《圣济总录》卷八十七', '劳热，骨节烦疼，心膈躁闷。', '-', '-', '柴胡', '[\'胡黄连\', \'犀角\', \'鳖甲\', \'两诃黎勒皮\', \'半生半熟\', \'桔梗\', \'两分\', \'升麻\', \'两分\', \'地骨皮\', \'两分\', \'知母\', \'两分\', \'黄芩\', \'黑心\', \'两分\', \'甘草\', \'白茯苓\', \'黑皮\', \'人参\', \'栝楼\', \'个大者\', \'柴胡\', \'去苗\', \'两半\']');
INSERT INTO `prescript` VALUES (57709, '20685', '肝胆两抒汤', '龙胆草2钱，柴胡1钱，当归5钱，金银花1两，炙甘草2钱，甘菊2钱，半夏1钱5分，白芍5钱，丹皮3钱，黄葵花1钱5分，白蒺藜2钱。', '眉疽一生，宜速治，数剂即消，久则无效。', '《洞天奥旨》卷七', '眉疽。', '-', '-', '柴胡', '[\'龙胆草\', \'柴胡\', \'当归\', \'金银花\', \'甘草\', \'甘菊\', \'半夏\', \'钱分\', \'白芍\', \'丹皮\', \'葵花\', \'分白\', \'蒺藜\']');
INSERT INTO `prescript` VALUES (57710, '20674', '胡黄连丸', '胡黄连3钱2分，银柴胡3钱2分，人参3钱2分，地骨皮3钱2分，犀角3钱2分，知母3钱2分，秦艽3钱2分，鳖甲5钱，牛黄1钱（另研），当归4钱2分，茯神4钱2分，半夏（姜制，作曲用）4钱2分，杏仁4钱2分（另研），紫菀1钱7分。', '每服50丸，食前米汤送服。', '《杏苑》卷五', '一切虚劳，骨蒸潮热，吐咯嗽血，咳嗽声嘶，痰喘不宁，心神恍惚，夜梦遗精。', '-', '-', '柴胡', '[\'胡黄连\', \'钱分\', \'银柴胡\', \'钱分\', \'人参\', \'钱分\', \'地骨皮\', \'钱分\', \'犀角\', \'钱分\', \'知母\', \'钱分\', \'秦艽\', \'钱分\', \'鳖甲\', \'牛黄\', \'另研\', \'当归\', \'钱分\', \'茯神\', \'钱分\', \'半夏\', \'制作\', \'用钱\', \'杏仁\', \'钱分\', \'另研\', \'紫菀\', \'钱分\']');
INSERT INTO `prescript` VALUES (57711, '20640', '固阴和疟饮', '赤鲜首乌5钱（打碎），当归3钱（酒洗），白芍3钱（酒炒），枳壳1钱5分（炒），青皮1钱5分，草蔻仁1钱（研），柴胡5分（微炒），炙甘草3分，煨姜2片。', '上用河水1碗，井水1碗煎，早1时服。如恶心，加藿香梗3钱；如作泻；加车前子3钱、大元枣3个；如热重，加青蒿3钱；如疟来3次未愈，加制半夏2钱、大贝母1钱5分；口渴，加知母1钱5分；心中发嘈，以老姜2钱、大枣5个砂锅煮，去姜吃枣子，2-3日后，再吃饮食，免其病之反复也。', '《慈航集》卷下', '养阴，和少阳之邪，清阳明积滞。主瘟疫表邪未清而转疟，其脉弦滑，多汗，寒轻热重，先伤阴分者。', '-', '-', '柴胡', '[\'赤鲜\', \'首乌\', \'打碎\', \'当归\', \'钱酒\', \'白芍\', \'钱酒\', \'枳壳\', \'钱分\', \'青皮\', \'钱分\', \'草蔻仁\', \'钱研\', \'柴胡\', \'分微\', \'甘草\', \'姜片\']');
