INSERT INTO `prescript` VALUES (57715, '20558', '虎睛丸', '虎睛1对（细研），朱砂半两（细研，水飞），露蜂房半两（微炙），麻黄半两（去根节），子芩半两，钩藤半两，防葵1两，川大黄1两（锉碎，微炒），龙齿1两（细研），栀子仁1两，银箔30片（细研），麝香1分（细研），羌活3分，柴胡3分（去苗），白鲜皮3分，川升麻3分，雷丸3分，沙参3分（去芦头），石膏1两（细研，水飞过），牛黄1分（细研），蚱蝉1两（去翅足，微炒或炙），蛇蜕皮1分（锉，微炒），天麻半两，甘草半两（炙微赤，锉）。', '丹砂丸（《圣济总录》卷一七一）。', '《圣惠》卷八十五', '小儿5岁至7岁，发癫痫，无时发动，口出白沫，遗失大小便不觉。', '-', '-', '柴胡', '[\'虎睛\', \'细研\', \'朱砂\', \'半两\', \'细研\', \'水飞\', \'露蜂房\', \'半两\', \'麻黄\', \'半两\', \'节子\', \'半两\', \'钩藤\', \'半两\', \'防葵\', \'两川\', \'大黄\', \'龙齿\', \'两细研\', \'栀子\', \'仁两银\', \'箔片\', \'细研\', \'麝香\', \'分细研\', \'羌活\', \'柴胡\', \'分去\', \'白鲜皮\', \'分川\', \'升麻\', \'雷丸\', \'沙参\', \'分去\', \'芦头\', \'石膏\', \'两细\', \'研水\', \'飞过\', \'牛黄\', \'分细研\', \'蚱蝉\', \'两去\', \'足微\', \'蜕皮\', \'天麻\', \'半两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57716, '20535', '参归散', '知母（炒）半两，人参（炒）半两，秦艽（去尖芦）半两，北柴胡（同术炒）半两，鳖甲（麦汤浸7次）半两，前胡半两，乌梅3个，地骨皮7钱半，川常山（酒浸3日）7钱半，川归（同柴胡炒）7钱半，甘草7钱半，白茯苓7钱半。', '水煎服。', '《脉因证治》卷上', '骨蒸劳。', '-', '-', '柴胡', '[\'知母\', \'半两\', \'人参\', \'半两\', \'秦艽\', \'尖芦\', \'半两\', \'柴胡\', \'同术\', \'半两\', \'鳖甲\', \'麦汤\', \'浸次\', \'半两\', \'半两\', \'乌梅\', \'地骨皮\', \'钱半川\', \'常山\', \'酒浸\', \'半川\', \'归同\', \'柴胡\', \'甘草\', \'钱半\', \'白茯苓\', \'钱半\']');
