INSERT INTO `prescript` VALUES (57732, '20165', '柴梗半夏汤', '柴胡2钱，黄芩1钱，半夏1钱，枳壳1钱，桔梗1钱，瓜蒌仁1钱，青皮8分，杏仁8分，甘草4分。', '水煎，温服。口燥渴，去半夏；痰在胁下，加白芥子或竹沥、姜汁。', '《医学入门》卷四', '邪热挟痰攻注，发热咳嗽，胸满，两胁锉痛。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'半夏\', \'枳壳\', \'桔梗\', \'钱瓜\', \'青皮\', \'杏仁\', \'甘草\']');
INSERT INTO `prescript` VALUES (57733, '20161', '附子汤', '附子（炮裂，去皮脐）1两，乌头（炮裂，去皮脐）1两，柴胡（去苗土）1两，前胡（去芦头）1两，黄耆1两，芎1两，白术1两，人参1两，木香1两，当归（切，焙）1两，羌活（去芦头）1两，甘草（炙）1两，桔梗（炒）1两，白芷1两，地榆1两，桂（去粗皮）1两。', '每服3钱匕，水1盏，加生姜2片，大枣2枚，葱白1寸，同煎至1分，空心服。', '《圣济总录》卷一八七', '补不足。主身体劳倦，四肢拘急，腹内刺痛，体弱，风痰头疼，肾脏伤惫，胸臆噎塞，久积冷气，妇人血海冷滞。', '-', '-', '柴胡', '[\'附子\', \'炮裂\', \'去皮\', \'乌头\', \'炮裂\', \'去皮\', \'柴胡\', \'苗土\', \'两前\', \'胡去\', \'芦头\', \'黄耆\', \'两芎\', \'白术\', \'人参\', \'木香\', \'当归\', \'切焙\', \'羌活\', \'芦头\', \'甘草\', \'桔梗\', \'白芷\', \'地榆\', \'两桂去\', \'粗皮\']');
INSERT INTO `prescript` VALUES (57734, '20154', '沉香散', '沉香半两，紫苏子3分，赤茯苓1两，木香半两，诃黎勒1两（煨，用皮），柴胡1两（去苗），鳖甲1两（涂醋炙令黄，去裙襕），陈橘皮1两（汤浸，去白瓤，焙），桂心半两，白术半两，槟榔1两。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓，稍热服，不拘时候。', '《圣惠》卷二十八', '气劳，心腹满闷，身体羸瘦，脚膝微肿，不能饮食。', '-', '-', '柴胡', '[\'沉香\', \'半两\', \'紫苏子\', \'分赤\', \'茯苓\', \'木香\', \'半两\', \'黎勒\', \'柴胡\', \'两去\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'橘皮\', \'两汤\', \'浸去\', \'焙桂心\', \'半两\', \'白术\', \'半两\', \'槟榔\']');
INSERT INTO `prescript` VALUES (57735, '20127', '建中加木瓜柴胡汤', '桂枝2两半，芍药3两，甘草1两，胶饴半升，生姜1两半，大枣6个，木瓜5钱，柴胡5钱。', '建中加柴胡木瓜汤（《医碥》卷七）。', '《此事难知》', '吐泻转筋，胁下痛，脉弦者。', '-', '-', '柴胡', '[\'桂枝\', \'两半\', \'芍药\', \'甘草\', \'两胶\', \'饴半升\', \'生姜\', \'两半\', \'大枣\', \'木瓜\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57736, '20126', '柴常汤', '柴胡（酒炒）1钱5分，黄芩（炒）1钱，人参5分，甘草5分，草果（煨）1钱，槟榔1钱，青皮1钱，厚朴（姜汁炒）1钱，常山（酒炒）2钱，何首乌2钱。', '加大枣2枚，生姜3片，水煎服。热痰，加川贝母；湿痰，加半夏；无汗，加羌活、紫苏；汗多，加黄耆、白术；夜发在阴分中，加白芍、鳖甲、红花、酒炒升麻；挟暑，加川黄连、香薷；挟湿，加苍术、茯苓；挟食，加山楂、麦芽、神曲；胸满，加枳壳；渴，加花粉、乌梅、石膏；风热在胃，津液消耗，加梨汁、蔗浆，或生地、生葛、西瓜等汁。', '《医碥》卷六', '寻常之疟，3-4发后。', '-', '-', '柴胡', '[\'柴胡\', \'钱分\', \'黄芩\', \'人参\', \'甘草\', \'草果\', \'槟榔\', \'青皮\', \'厚朴\', \'姜汁\', \'常山\', \'何首乌\']');
INSERT INTO `prescript` VALUES (57737, '20082', '连翘汤', '连翘1两半，玄参1两半，木香1两半，昆布（洗去成，焙）1两半，枳壳（去瓤，麸炒）1两半，犀角（镑）1两半，柴胡（去苗）1两，甘草（炙，锉）1两，木通（锉）1两，芍药1两，黄芩（去黑心）1两，沉香（锉）1两，当归（切，焙）1两，升麻1两。', '每服5钱匕，水2盏，入生姜1枣大（拍碎），柳枝2寸长1握，细锉，煎至1盏，去滓温服，空心、日午、夜卧各1服。', '《圣济总录》卷一二六', '男女长幼瘰疬结核，在项腋下，项强背痛。', '-', '-', '柴胡', '[\'连翘\', \'两半\', \'玄参\', \'两半\', \'木香\', \'两半\', \'昆布\', \'洗去\', \'两半\', \'枳壳\', \'两半\', \'犀角\', \'两半\', \'柴胡\', \'甘草\', \'木通\', \'芍药\', \'黄芩\', \'黑心\', \'沉香\', \'当归\', \'切焙\', \'升麻\']');
INSERT INTO `prescript` VALUES (57738, '20056', '芦荟肥儿丸', '苍术2两，白术2两，胡黄连2两，陈皮2两，厚朴2两，麦芽（乳炒）2两，谷芽2两，使君子肉2两，砂仁1两，三棱1两，甘草1两，芦荟1两，莪术1两，枳壳1两，槟榔1两，神曲1两5钱，银柴胡1两5钱，茯苓1两5钱，山楂1两5钱，川连1两5钱，木香4两，干蟾5只。', '薄荷叶煎水为丸服。', '《成方便读》卷四', '小儿骨蒸发热，面黄肌瘦，肚腹膨胀，积聚作痛，五疳目翳。', '-', '-', '柴胡', '[\'苍术\', \'白术\', \'胡黄连\', \'陈皮\', \'厚朴\', \'麦芽乳\', \'两谷芽\', \'使君子\', \'砂仁\', \'三棱\', \'甘草\', \'芦荟\', \'莪术\', \'枳壳\', \'槟榔\', \'神曲\', \'银柴胡\', \'茯苓\', \'山楂\', \'两钱川\', \'木香\', \'两干\']');
INSERT INTO `prescript` VALUES (57739, '20048', '经验黄鸡煎丸', '柴胡1两，知母1两，秦艽（洗净）1两，川楝肉（炒）1两，宣黄连1两，胡黄连半两，芦荟半两，鹤虱半两，芜荑半两，槟榔半两，丹参半两，川芎半两，神曲（炒）半两，麦糵（炒）半两，青皮半两，五灵脂半两，使君子肉1两半，水银粉1钱，麻子5两，黑豆5升。', '黄鸡煎丸（《医部全录》卷四四四）。', '《婴童百问》卷八', '小儿疳劳，骨蒸潮热，盗汗瘦弱，腹急面黄，食不生肌肉，日夜啼，多渴少食。', '-', '-', '柴胡', '[\'柴胡\', \'知母\', \'秦艽\', \'洗净\', \'川楝\', \'两宣\', \'黄连\', \'胡黄连\', \'半两\', \'芦荟\', \'半两\', \'鹤虱\', \'半两\', \'半两\', \'槟榔\', \'半两\', \'丹参\', \'半两\', \'川芎\', \'半两\', \'神曲\', \'半两\', \'半两\', \'青皮\', \'半两\', \'五灵脂\', \'半两\', \'使君子\', \'两半\', \'水银\', \'粉钱\', \'麻子\', \'黑豆\']');
INSERT INTO `prescript` VALUES (57740, '20004', '柴胡四物汤', '川芎1两半，熟地黄1两半，当归1两半，芍药1两半，柴胡8钱，人参3钱，黄芩3钱，甘草3钱，半夏3钱，曲3钱。', '煎服。', '《保命集》卷下', '妇人虚劳日久，血虚阴亏，微有寒热；经行感冒，热入血室；经枯发热，妊娠吐衄。日久劳虚，脉沉而浮。午后或夜分发热。三阴经温疟或夜发者，脉虚弦数者。妊娠忧虑惊怒伤其脏腑，气干于上，血随溢而心闷，胸满久不已，必致堕胎。', '-', '-', '柴胡', '[\'川芎\', \'两半\', \'熟地黄\', \'两半\', \'当归\', \'两半\', \'芍药\', \'两半\', \'柴胡\', \'人参\', \'黄芩\', \'甘草\', \'半夏\', \'钱曲\']');
INSERT INTO `prescript` VALUES (57741, '19991', '保命丹', '朱砂1两，珍珠2钱，南星1两，麻黄（去根节）半两，白附子（炮）半两，雄黄半两，龙脑半两，琥珀3钱，僵蚕（炒）1两，犀角（镑）1两，麦门冬（去心）1两，枳壳1两，地骨皮1两，神曲1两，茯神1两，远志（去心）1两，人参1两，柴胡1两，金箔1薄片，牛黄3钱，天麻半两，脑子少许，麝香少许，胆矾半两，牙消4钱，毫车1两，天竺黄1两，防风1两，甘草1两，桔梗1两，白术1两，升麻1两，蝉退半两，黄芩2两，荆芥2两。', '每服1丸，薄荷汤化下，不拘时候。更加川乌（炮，去皮脐）、姜制半夏、白芷、川芎各1两；猪牙皂1两，和前药丸服，尤妙。', '《直指附遗》卷三引《千金》', '诸风痪疭，不能语言，心忪健忘，恍惚去来，头目晕眩，胸中烦郁，痰涎壅塞，抑气攻心，精神昏愦；心气不足，神志不定，惊恐怕怖，悲忧惨蹙，虚烦少睡，喜怒不时，或发狂癫，神情昏乱；及小儿惊痫，惊风抽搐不定；及大人暗风，并羊癫、猪癫发叫。', '忌猪、羊、虾、核桃动风引痰之物及猪羊血。', '-', '柴胡', '[\'朱砂\', \'珍珠\', \'南星\', \'麻黄\', \'根节\', \'半两\', \'白附子\', \'半两\', \'雄黄\', \'半两\', \'龙脑\', \'半两\', \'琥珀\', \'僵蚕\', \'犀角\', \'麦门冬\', \'枳壳\', \'地骨皮\', \'神曲\', \'茯神\', \'远志\', \'人参\', \'柴胡\', \'金箔\', \'薄片\', \'牛黄\', \'天麻\', \'半两\', \'脑子\', \'少许\', \'麝香\', \'少许\', \'胆矾\', \'半两\', \'消钱\', \'毫车\', \'天竺黄\', \'防风\', \'甘草\', \'桔梗\', \'白术\', \'升麻\', \'两蝉\', \'半两\', \'黄芩\', \'荆芥\']');
INSERT INTO `prescript` VALUES (57742, '19988', '大和中饮', '熟地2-3钱，白术2-3钱，当归2-3钱，人参随便，山药1钱半，甘草（炙）1钱，柴胡1-2钱，麻黄1-2钱，肉桂1-2钱，白芍（酒炒）1钱，生姜1钱半。', '水煎，温服。如汗甚，去麻黄，加黄耆2钱；如寒甚，加附子1-2钱。', '《会约》卷十二', '痉，因汗下太过，阳虚阴盛，复感邪而畏寒莫解者。', '-', '-', '柴胡', '[\'熟地\', \'白术\', \'当归\', \'人参\', \'随便\', \'山药\', \'钱半\', \'甘草\', \'柴胡\', \'麻黄\', \'肉桂\', \'白芍\', \'生姜\', \'钱半\']');
INSERT INTO `prescript` VALUES (57743, '19982', '复明散', '天麻1两，胡黄连6钱，夜明砂1两（炒），芦荟3钱，银柴胡1钱，木贼1两，青黛1两，蝉退1两，草决明1两（炒），五灵脂1两（酒炒），龙胆草1两，蔓荆子4钱，谷精花3钱。', '用不入水鸡肝连胆一并捣烂，加酒酿少许，包于麻布内，滤去渣滓，将前药调和，每服1钱。', '《幼科金针》卷下', '小儿肝疳。', '-', '-', '柴胡', '[\'天麻\', \'胡黄连\', \'夜明砂\', \'芦荟\', \'银柴胡\', \'木贼\', \'两青\', \'两蝉\', \'草决明\', \'五灵脂\', \'两酒\', \'龙胆草\', \'蔓荆子\', \'钱谷\', \'花钱\']');
INSERT INTO `prescript` VALUES (57744, '19942', '沉香饮', '沉香1两，白蒺藜（炒去角）1两，补骨脂（炒令香）1两，巴戟天（去心）1两，酸枣仁（炒）1两，五味子（炒）1两，泽泻1两，磁石（煅，醋淬7度）1两，桂（去粗皮）1两，人参1两，陈橘皮（去白，焙）1两，枳壳（去瓤，麸炒）1两，牛膝（切，酒浸，焙）1两，芍药1两，石斛（去根）1两，鳖甲（醋炙，去裙襕）1两，槟榔3两，桑螵蛸3两，肉苁蓉（酒浸，切，焙）2两，当归（切，焙）2两，柴胡（去苗）2两，黄耆（锉，炒）2两，芎3两，附子（炮裂，去皮脐）1两半。', '沉香散（《普济方》卷三十）。', '《圣济总录》卷八十六', '五劳七伤，肾气虚乏。', '-', '-', '柴胡', '[\'沉香\', \'两白\', \'蒺藜\', \'补骨脂\', \'炒令香\', \'巴戟天\', \'酸枣仁\', \'五味子\', \'泽泻\', \'磁石\', \'淬度\', \'两桂去\', \'粗皮\', \'人参\', \'两陈\', \'橘皮\', \'枳壳\', \'牛膝\', \'切酒\', \'芍药\', \'石斛\', \'鳖甲\', \'槟榔\', \'桑螵蛸\', \'肉苁蓉\', \'浸切\', \'当归\', \'切焙\', \'柴胡\', \'黄耆\', \'两芎\', \'附子\', \'炮裂\', \'去皮\', \'两半\']');
INSERT INTO `prescript` VALUES (57745, '19901', '芦荟肥儿丸', '五谷虫（炒）2两，芦荟（生）1两，胡黄连（炒）1两，川黄连（姜炒）1两，银柴胡（炒）1两2钱，扁豆（炒）2两，山药（炒）2两，南山楂2两半，虾蟆（煅）4个，肉豆蔻（煨）7钱，槟榔5钱，使君子（炒）2两半，神曲（炒）2两，麦芽（炒）1两6钱，鹤虱（炒）8钱，芜荑（炒）1两，朱砂（飞）2钱，麝香2钱。', '每服1钱，米饮送下。', '《金鉴》卷五十二', '肝疳，面目爪甲皆青，眼失眵泪，隐涩难睁，摇头揉目，合面睡卧，耳疮流脓水，腹大青筋，身体羸瘦，燥湿烦急，粪青如苔。', '-', '-', '柴胡', '[\'五谷\', \'芦荟\', \'生两\', \'胡黄连\', \'两川\', \'黄连\', \'银柴胡\', \'扁豆\', \'山药\', \'两南\', \'山楂\', \'两半\', \'虾蟆\', \'煅个\', \'肉豆蔻\', \'槟榔\', \'使君子\', \'两半\', \'神曲\', \'麦芽\', \'鹤虱\', \'朱砂\', \'飞钱\', \'麝香\']');
INSERT INTO `prescript` VALUES (57746, '19893', '厚朴汤', '厚朴（去粗皮，生姜汁炙，锉）1两，白术1两，鳖甲（去裙襕，醋炙）1两，柴胡（去苗）1两，石斛（去根）1两，肉豆蔻（去壳）1两，地骨皮1两，犀角（镑）1两，白茯苓（去黑皮）1两，人参1两，甘草（炙，锉）1两，青木香半两。', '厚朴散（《普济方》卷二二九）。', '《圣济总录》卷八十七', '气劳。心腹胀满，吃食不得，胸膈烦闷，面色萎黄，身体无力，不能行履。', '-', '-', '柴胡', '[\'厚朴\', \'粗皮生\', \'姜汁\', \'白术\', \'鳖甲\', \'柴胡\', \'石斛\', \'肉豆蔻\', \'地骨皮\', \'犀角\', \'白茯苓\', \'黑皮\', \'人参\', \'甘草\', \'青木香\', \'半两\']');
INSERT INTO `prescript` VALUES (57747, '19865', '草豆蔻丸', '草豆蔻（面包煨去油）1钱，陈皮6钱，泽泻1钱，半夏1钱，桃仁（去皮尖）7粒，麦芽（炒）2钱半，神曲（炒）4钱，柴胡4钱，姜黄4钱。', '白汤送下。', '《片玉心书》卷五', '小儿外感风寒，内伤冷物，胃气当心而痛，啼哭闷绝，手足冷，或吐或不吐，以热手按摩则止者。', '-', '-', '柴胡', '[\'草豆蔻\', \'面包\', \'油钱\', \'陈皮\', \'泽泻\', \'半夏\', \'桃仁\', \'皮尖\', \'麦芽\', \'神曲\', \'柴胡\', \'姜黄\']');
INSERT INTO `prescript` VALUES (57748, '19842', '柴胡清肝散', '柴胡5钱，黄芩1两半（炒），人参1钱，生地5钱，当归1两，赤芍1两，连翘3两，甘草5钱，山栀1两半（炒）。', '每服5钱，水煎，去滓，微温服。', '《医略六书》卷二十一', '调血清火。主肝火伤营，耳衄，脉弦数者。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'两半\', \'人参\', \'生地\', \'当归\', \'赤芍\', \'连翘\', \'甘草\', \'钱山\', \'两半\']');
INSERT INTO `prescript` VALUES (57749, '19816', '柴胡陷胸汤', '柴胡1钱，姜半夏3钱，小川连8分，苦桔梗1钱，黄芩1钱半，栝楼仁（杵）5钱，小枳实1钱半，生姜汁4滴（分冲）。', '-', '《重订通俗伤寒论》卷二', '和解开降达膜。主少阳结胸，症见少阳证具，胸膈痞满，按之痛，用柴胡枳桔汤未效者。现用于慢性胆囊炎急性发作、急性支气管炎。', '-', '-', '柴胡', '[\'柴胡\', \'半夏\', \'小川\', \'连分苦\', \'桔梗\', \'黄芩\', \'钱半\', \'栝楼\', \'仁杵\', \'枳实\', \'半生\', \'姜汁\', \'滴分\']');
INSERT INTO `prescript` VALUES (57750, '19774', '厚朴汤', '厚朴（去粗皮，姜汁炙）1两，柴胡（去苗）1两半，大黄（锉，炒）1两半，朴硝2两，枳实（去瓤，麸炒）3分。', '每服5钱匕，水1盏，加生姜1枣大（拍碎），煎至7分，去滓，空心温服，良久再服，以通为度，未通再服。', '《圣济总录》卷二十六', '伤寒5-6日，大便不通，壮热头疼，谵语，肠中有结燥。', '-', '-', '柴胡', '[\'厚朴\', \'粗皮\', \'姜汁\', \'柴胡\', \'去苗\', \'两半\', \'大黄\', \'两半\', \'朴硝\', \'枳实\', \'炒分\']');
INSERT INTO `prescript` VALUES (57751, '19743', '参耆补气汤', '黄耆1钱，茯苓1钱，人参1钱，石菖蒲5分，黄柏（酒制）5分，甘草（炙）5分，知母8分，白术8分，当归8分，橘皮8分，升麻4分，柴胡4分。', '上(口父)咀。水煎8分，食远热服。精血少，加熟地黄1钱。', '《杏苑》卷六', '气虚耳鸣。', '-', '-', '柴胡', '[\'黄耆\', \'茯苓\', \'人参\', \'石菖蒲\', \'黄柏\', \'酒制\', \'甘草\', \'炙分\', \'知母\', \'白术\', \'当归\', \'橘皮\', \'升麻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57752, '19659', '和肝饮', '当归1钱5分，白芍1钱5分，三棱1钱5分，青皮1钱5分，大茴香8分，木香8分，枳壳8分，柴胡8分，砂仁8分。', '水煎服，不拘时候。', '《玉案》卷五', '胁下杠梗一条作疼。', '-', '-', '柴胡', '[\'当归\', \'钱分\', \'白芍\', \'钱分\', \'三棱\', \'钱分\', \'青皮\', \'钱分\', \'大茴香\', \'木香\', \'枳壳\', \'柴胡\', \'砂仁\']');
INSERT INTO `prescript` VALUES (57753, '19640', '柴胡散', '地骨皮1两半，柴胡1两，鳖甲（去裙，醋炙）1两，知母1两，五味子半两。', '本方方名，《医学纲目》引作“柴胡饮”。', '《卫生宝鉴》卷五', '虚劳羸瘦，面色痿黄，四肢无力，不思饮食，夜多盗汗，咳嗽不止。', '-', '-', '柴胡', '[\'地骨皮\', \'两半\', \'柴胡\', \'鳖甲\', \'知母\', \'五味子\', \'半两\']');
INSERT INTO `prescript` VALUES (57754, '19636', '柴胡四物汤', '柴胡8分，仙半夏1钱，归身1钱，生白芍2钱，条芩8分，清炙草6分，生地1钱半，川芎7分。', '-', '《重订通俗伤寒论》卷二', '疏气和血。主妊妇邪陷入于足厥阴之肝络，寒热如疟，胸胁串痛，至夜尤甚者。', '-', '-', '柴胡', '[\'柴胡\', \'分仙\', \'半夏\', \'钱归身\', \'钱生\', \'白芍\', \'钱条\', \'分清\', \'炙草分\', \'生地\', \'钱半\', \'川芎\']');
INSERT INTO `prescript` VALUES (57755, '19629', '金水两润汤', '熟地1两，麦冬1两，柴胡1钱，甘草1钱，丹皮3钱。', '水煎服。连服2剂而微硬解，再服2剂而潮热除矣。', '《辨证录》卷五', '润肺金之燥。主伤风潮热，大便微硬。', '-', '-', '柴胡', '[\'熟地\', \'麦冬\', \'柴胡\', \'甘草\', \'丹皮\']');
INSERT INTO `prescript` VALUES (57756, '19617', '柴胡汤', '柴胡（去苗）2两，桂（去粗皮）1两，黄芩（去黑心）1两，牡蛎（生用）半两，甘草（炙）半两，栝楼根1两半，木通（锉）1两。', '每服5钱匕，水1盏半，加生姜半分（拍碎，葱白5寸，同煎至7分。去滓，食后温服。', '《圣济总录》卷二十一', '伤寒发汗下之后，过经不解，胸胁满结，渴而不呕，但头汗出，往来寒热，小便不通。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两桂去\', \'粗皮\', \'黄芩\', \'黑心\', \'牡蛎\', \'生用\', \'半两\', \'甘草\', \'半两\', \'栝楼\', \'两半\', \'木通\']');
INSERT INTO `prescript` VALUES (57757, '19604', '洞当饮', '柴胡5分，黄芩5分，黄连5分，茯苓5分，半夏5分，生姜5分，青皮5分，甘草1分，芍药1钱。', '以水2合半，煮取1合半服。', '《产论》', '吐血、衄血，或卒然胸痛。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'黄连\', \'茯苓\', \'半夏\', \'生姜\', \'青皮\', \'甘草\', \'芍药\']');
INSERT INTO `prescript` VALUES (57758, '19600', '厚朴汤', '厚朴（去粗皮，锉）1两半（生姜汁浸1宿，炒令紫），草豆蔻（去皮）半两，桂（去粗皮）半两，高良姜半两，五味子半两，青橘皮（汤浸，去白，焙）1两，陈橘皮（汤浸，去白，焙）1两，甘草（炙）1两，麦糵（炒）1两，柴胡（去苗）1两，人参1两，麻黄（去根节，煮，掠去沫，焙）1两，陈曲（炒）1两，诃黎勒（炮，去核）1两，益智（炒，去皮）2两，乌头（炮裂，去皮脐）2两，干姜（炮）1分。', '每服3钱匕，水1盏，生姜3片，大枣2个（擘破），同煎至7分，去滓，稍热服，不拘时候。', '《圣济总录》卷六十二', '膈气，宿食不消。', '-', '-', '柴胡', '[\'厚朴\', \'粗皮\', \'半生\', \'姜汁\', \'浸宿\', \'炒令\', \'草豆蔻\', \'去皮\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'高良姜\', \'半两\', \'五味子\', \'半两\', \'橘皮\', \'汤浸\', \'橘皮\', \'汤浸\', \'甘草\', \'两麦\', \'柴胡\', \'人参\', \'麻黄\', \'根节\', \'掠去\', \'陈曲\', \'两诃黎勒炮\', \'去核\', \'益智\', \'去皮\', \'乌头\', \'炮裂\', \'去皮\', \'干姜\', \'炮分\']');
INSERT INTO `prescript` VALUES (57759, '19570', '大黄丸', '川大黄半两（锉碎，微炒），柴胡半两（去苗），赤茯苓1分，人参1分（去芦头）木香1分，桂心1分，枳壳1分（麸炒微黄，去瓤），槟榔半两，桃仁2分（汤浸，去皮尖双仁，麸炒微黄）。', '每服5丸，以温水送下，1日3次。', '《圣惠》卷八十四', '小儿憎寒壮热，发歇不定，腹中结实，不能乳食。', '-', '-', '柴胡', '[\'大黄\', \'半两\', \'柴胡\', \'半两\', \'茯苓\', \'人参\', \'分去\', \'芦头\', \'木香\', \'分桂心分\', \'枳壳\', \'分麸\', \'微黄\', \'槟榔\', \'半两\', \'桃仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (57760, '19507', '赤茯苓散', '赤茯苓1两，汉防己1两，川大黄1两半（锉碎，微炒），槟榔3分，柴胡1两（去苗），紫苏茎叶1分，甜葶苈3分（隔纸炒令黄色），桑根白皮1两（锉），陈橘皮1两（汤浸，去白瓤，焙）。', '每服4钱，以水一中盏，煎至6分，去滓，食前温服。', '《圣惠》卷六', '肺气攻注，遍身虚肿，按之没指，心气滞，大小便涩，状如水气。', '-', '-', '柴胡', '[\'茯苓\', \'两汉\', \'防己\', \'两川\', \'大黄\', \'两半\', \'槟榔\', \'柴胡\', \'两去\', \'紫苏\', \'叶分\', \'分隔\', \'炒令\', \'黄色\', \'桑根\', \'白皮\', \'橘皮\', \'两汤\', \'浸去\']');
INSERT INTO `prescript` VALUES (57761, '19487', '保命丹', '川乌2两2钱，草乌2两2钱半（均用泔水浸去皮），大黄1两，五灵脂1两，肉桂5钱，木香5钱，细辛5钱，香附5钱，延胡5钱，三棱5钱，莪术5钱，柴胡5钱，青皮5钱，枳实5钱，桃仁5钱，红花5钱，苏木5钱，大茴5钱，小茴5钱，归尾5钱，甘草5钱，蜂房5钱，蒲黄（生）5钱，鹰骨5钱，土鳖5钱，广黄5钱，三七5钱，川椒2两，广皮1两，乌药7钱半，蚺蛇胆5分，血管鹅毛灰5钱。', '若病重与壮者服1丸，病轻与老弱者服半丸，陈酒和童便化下。', '《伤科汇纂》卷七', '接骨入臼。主跌打损伤。', '-', '-', '柴胡', '[\'川乌\', \'草乌\', \'半均\', \'泔水\', \'去皮\', \'大黄\', \'五灵脂\', \'肉桂\', \'木香\', \'细辛\', \'香附\', \'钱延\', \'胡钱\', \'三棱\', \'莪术\', \'柴胡\', \'青皮\', \'枳实\', \'桃仁\', \'钱红\', \'花钱\', \'苏木\', \'钱大茴钱\', \'小茴钱\', \'归尾\', \'甘草\', \'蜂房\', \'黄生\', \'钱鹰\', \'土鳖\', \'钱广\', \'黄钱\', \'三七\', \'钱川椒\', \'两广\', \'乌药\', \'钱半\', \'蛇胆\', \'血管\', \'鹅毛\', \'灰钱\']');
INSERT INTO `prescript` VALUES (57762, '19459', '柴胡饮', '柴胡1钱5分，广皮1钱半，细辛1钱半，厚朴1钱半，半夏1钱半，甘草8分。', '加生姜，水煎服。阴寒胜，加桂枝。', '《观聚方要补》卷一引《孝慈备览》', '四时感邪，或三阳并病，或时逢寒胜，症无内热，而用凉药致寒邪凝滞。', '-', '-', '柴胡', '[\'柴胡\', \'钱分\', \'广皮\', \'细辛\', \'钱半\', \'厚朴\', \'钱半\', \'半夏\', \'钱半\', \'甘草\']');
INSERT INTO `prescript` VALUES (57763, '19419', '快胃舒肝丸', '片姜黄1两6钱，乌药3两2钱，白芍4两，厚朴（制）16两，橘皮4两，沉香3两，木香2两4钱，香附（炙）4两，砂仁3两2钱，枳壳（炒）4两，柴胡4两，青皮（炒）4两，川芎3两2钱，紫豆蔻仁1两2钱，当归4两，玄胡索（炙）4两。', '每两200丸，袋装，每袋60丸。每服30丸，温开水送下，1日2次。', '《北京市中药成方选集》', '健胃，舒郁，止痛。主胃脘刺痛，痞满嘈杂，两胁膨胀，呕吐吞酸。', '-', '-', '柴胡', '[\'姜黄\', \'乌药\', \'白芍\', \'厚朴\', \'制两\', \'橘皮\', \'沉香\', \'木香\', \'香附\', \'砂仁\', \'枳壳\', \'柴胡\', \'青皮\', \'川芎\', \'豆蔻\', \'仁两钱\', \'当归\', \'两玄\', \'胡索\']');
INSERT INTO `prescript` VALUES (57764, '19355', '保和汤', '厚朴（姜制）8分，大腹皮（黑豆水洗）8分，半夏（制）8分，陈皮（去白）8分，柴胡5分，枳壳5分，甘草5分，生姜3钱（煨）。', '水煎，温服。', '《医统》卷七十六', '中瘴气，发热呕吐，腹满不食。', '-', '-', '柴胡', '[\'厚朴\', \'姜制\', \'大腹皮\', \'黑豆\', \'水洗\', \'半夏\', \'制分\', \'陈皮\', \'白分\', \'柴胡\', \'枳壳\', \'甘草\', \'生姜\']');
INSERT INTO `prescript` VALUES (57765, '19324', '护肝汤', '熟地5钱，鳖甲5钱，山茱萸2钱，何首乌3钱，白芥子3钱，当归1两，柴胡1钱5分。', '水煎服。', '《辨证录》卷八', '补肝祛邪。主肝木亏虚，邪遂乘机突入，致生厥阴肝经之疟，发疟之时，先寒作颤，寒后变热，面色苍白，善起太息之声，甚者状如欲死，或头痛而渴。', '-', '-', '柴胡', '[\'熟地\', \'鳖甲\', \'山茱萸\', \'何首乌\', \'白芥子\', \'当归\', \'柴胡\', \'钱分\']');
INSERT INTO `prescript` VALUES (57766, '19312', '大百劳散', '蛤蚧1对（蜜炙），元卅鳖甲1个（去裙，醋炙），附子1两，人参1两，柴胡1两，川干姜1两，白茯苓（去皮）1两，白术1两，茴香1两，青皮（去白）1两，杏仁（去皮尖）1两，知母1两，贝母1两，陈皮（去白）1两，官桂1两，甘草（炙）1两，半夏（生姜制）1两，苍术（汤浸）1两，苏木半两，龙胆草半两。', '每服2钱，水1盏，加生姜3片，大枣2枚，乌梅2枚，同煎。空心稍热服。有汗加小麦20粒。', '《宣明论》卷九', '一切劳疾，肌劣，喘息不卧，痰涎不食。', '不用铁器。', '-', '柴胡', '[\'蛤蚧\', \'蜜炙元卅\', \'鳖甲\', \'附子\', \'人参\', \'柴胡\', \'两川\', \'干姜\', \'白茯苓\', \'去皮\', \'白术\', \'茴香\', \'青皮\', \'杏仁\', \'皮尖\', \'知母\', \'贝母\', \'陈皮\', \'官桂\', \'甘草\', \'半夏\', \'生姜\', \'制两\', \'苍术\', \'汤浸\', \'苏木\', \'半两\', \'龙胆草\', \'半两\']');
INSERT INTO `prescript` VALUES (57767, '19303', '活血汤', '红花3分，蔓荆子5分，细辛5分，生地黄（夏月加之）1钱，熟地黄1钱，藁本1钱5分，川芎1钱5分，防风2钱，羌活2钱，独活2钱，甘草（炙）2钱，柴胡（去苗）2钱，当归身（酒洗）2钱，葛根2钱，白芍药（炒）3钱，升麻3钱。', '上（口父）咀。每服5钱，清水2盏，煎至1盏，去滓，食前稍热服。', '《中国医学大辞典》引《全生指迷方》', '补血养血，生血益阳。主发热，自汗，盗汗，头晕口干，四肢无力；妇女崩漏太多，昏冒不省。', '-', '-', '柴胡', '[\'红花\', \'蔓荆子\', \'细辛\', \'生地黄\', \'夏月\', \'加之\', \'熟地黄\', \'藁本\', \'钱分\', \'川芎\', \'钱分\', \'防风\', \'羌活\', \'独活\', \'甘草\', \'柴胡\', \'苗钱\', \'当归\', \'身酒\', \'洗钱\', \'葛根\', \'钱白\', \'芍药\', \'升麻\']');
INSERT INTO `prescript` VALUES (57768, '19256', '陈橘皮汤', '陈橘皮（汤浸，去白，焙）1两，柴胡（去苗）1两，半夏（汤洗7遍去滑）3分，枳壳（去瓤，麸炒）3分，诃黎勒皮3分，木香半两，升麻半两，五味子半两。', '每服5钱匕，水1盏半，加生姜5片，煎取7分，去滓温服。', '《圣济总录》卷六十七', '胸胁短气妨闷，不下食。', '-', '-', '柴胡', '[\'橘皮\', \'汤浸\', \'柴胡\', \'半夏\', \'洗遍\', \'滑分\', \'枳壳\', \'分诃黎勒\', \'皮分\', \'木香\', \'半两\', \'升麻\', \'半两\', \'五味子\', \'半两\']');
