INSERT INTO `prescript` VALUES (57770, '19235', '和中养胃汤', '黄耆2钱，人参1钱，茯苓2钱，白术1钱，甘草4分，当归2钱，料豆4钱，柴胡1钱，薄荷1钱，广皮1钱，砂仁1钱，苡仁4钱，大枣2枚，生姜3片。', '-', '《医醇剩义》卷二', '虚火。饥饱劳役，正气受伤，阳陷于阴，发热神疲，饮食减少。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'茯苓\', \'白术\', \'甘草\', \'当归\', \'料豆\', \'柴胡\', \'薄荷\', \'钱广皮\', \'砂仁\', \'大枣\', \'生姜\']');
INSERT INTO `prescript` VALUES (57771, '19223', '宝珍膏', '生地3钱，苍术3钱，枳壳3钱，五加皮3钱，莪术3钱，桃仁3钱，山柰3钱，当归3钱，川乌（生）3钱，橘皮3钱，乌药3钱，三棱3钱，大黄3钱，首乌3钱，草乌（生）3钱，柴胡3钱，防风3钱，刘寄奴3钱，猪牙皂3钱，川芎3钱，官桂3钱，羌活3钱，威灵仙3钱，赤芍3钱，南星（生）3钱，香附（生）3钱，荆芥3钱，白芷3钱，海风藤3钱，藁本3钱，续断3钱，良姜3钱，独活3钱，麻黄3钱，甘松3钱，连翘3钱。', '用时微火化开，贴患处。', '《北京市中药成方选集》', '追风散寒，舒筋活血。主风寒麻木，筋骨疼痛，腰酸腿软，跌打损伤。', '孕妇忌贴。', '-', '柴胡', '[\'生地\', \'苍术\', \'枳壳\', \'五加皮\', \'莪术\', \'桃仁\', \'钱山\', \'当归\', \'川乌\', \'生钱\', \'橘皮\', \'乌药\', \'三棱\', \'大黄\', \'首乌\', \'草乌\', \'生钱\', \'柴胡\', \'防风\', \'刘寄奴\', \'猪牙皂\', \'川芎\', \'官桂钱\', \'羌活\', \'威灵仙\', \'赤芍\', \'南星\', \'生钱\', \'香附\', \'生钱\', \'荆芥\', \'白芷\', \'海风藤\', \'藁本\', \'续断\', \'钱良\', \'姜钱\', \'独活\', \'麻黄\', \'甘松\', \'连翘\']');
INSERT INTO `prescript` VALUES (57772, '19179', '荆防败毒散', '防风3钱（去芦），柴胡（去芦）2钱，僵蚕（姜汁炒）2钱，法夏（姜汁炒）2钱，桔梗2钱，前胡2钱，独活2钱，荆芥1钱5分，羌活1钱5分，银花1钱5分，枳壳1钱，粉草1钱，生姜3片。', '水煎服。', '《白喉全生集》', '白喉初起，白见于关内或关外，色必明润而平，满喉淡红微肿略痛，头痛，恶寒发热，饮食如常，舌苔白，二便和，寒邪尚在表者。', '-', '-', '柴胡', '[\'防风\', \'芦柴\', \'胡去\', \'芦钱\', \'僵蚕\', \'姜汁\', \'炒钱\', \'法夏\', \'姜汁\', \'桔梗\', \'钱前\', \'胡钱\', \'独活\', \'荆芥\', \'钱分\', \'羌活\', \'钱分\', \'花钱\', \'枳壳\', \'钱粉\', \'草钱\', \'生姜\']');
INSERT INTO `prescript` VALUES (57773, '19152', '胡黄连丸', '胡黄连1分，人参1分（去芦头），柴胡半两（去苗），羚羊角屑1分，麦门冬半两（去心，焙），鳖甲半两（涂醋炙令黄），地骨皮1分，秦艽半两（去苗），黄耆1分（锉，微炒），木香1分，犀角屑1分，甘草1分（炙微赤，锉），葳蕤1分。', '每服7丸，以温水下，日3服。', '《圣惠》卷八十八', '小儿骨热，烦躁黄瘦，饮食无味。', '-', '-', '柴胡', '[\'胡黄连\', \'人参\', \'分去\', \'芦头\', \'柴胡\', \'半两\', \'去苗\', \'羚羊角\', \'麦门冬\', \'半两\', \'鳖甲\', \'半两\', \'涂醋\', \'炙令\', \'地骨皮\', \'秦艽\', \'半两\', \'去苗\', \'黄耆\', \'木香\', \'犀角\', \'甘草\', \'分炙微\', \'葳蕤\']');
INSERT INTO `prescript` VALUES (57774, '19060', '柴胡散', '柴胡1两（去苗），黄耆1两（锉），枳壳半两（麸炒微黄，去瓤），麦门冬1两半（去心，焙），鳖甲1两（涂醋炙微黄，去裙襕），地骨皮3分，生干地黄3分，人参1两（去芦头），萎蕤3分，赤茯苓1两，赤芍药3分，甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷二十九', '虚劳烦热，四肢疼痛，不欲饮食。', '忌苋菜。', '-', '柴胡', '[\'柴胡\', \'两去\', \'黄耆\', \'枳壳\', \'半两\', \'微黄\', \'麦门冬\', \'两半\', \'鳖甲\', \'两涂\', \'微黄\', \'地骨皮\', \'分生\', \'地黄\', \'人参\', \'两去\', \'芦头\', \'萎蕤\', \'分赤\', \'茯苓\', \'两赤\', \'芍药\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57775, '19041', '柴胡调经汤', '炙甘草3分，当归身3分，葛根3分，独活5分，藁本5分，升麻5分，柴胡7分，羌活1钱，苍术1钱，红花少许。', '升阳调经汤（《医略六书》卷二十六）。', '《兰室秘藏》卷中', '经水不止，鲜红，项筋急，脑痛，脊骨强痛。', '-', '-', '柴胡', '[\'甘草\', \'当归\', \'身分\', \'葛根\', \'独活\', \'藁本\', \'升麻\', \'柴胡\', \'羌活\', \'苍术\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (57776, '19011', '荜澄茄散', '荜澄茄3分，白术1两，黄耆3分（锉），附子3分（炮裂，去皮脐），草豆蔻3分（去皮），桂心半两，蓬莪荗3分，当归3分，木香半两，芎藭半两，柴胡1两（去苗），牛膝3分（去苗），吴茱萸半两（汤浸7遍，焙干，研碎），甘草半两（炙微赤，锉）。', '每服3钱，以水1中盏，入生姜半分，枣3枚，煎至6分，去滓，稍热服，不拘时候。', '《圣惠》卷二十八', '气劳。心腹冷痛，吃食减少，四肢羸弱。', '-', '-', '柴胡', '[\'荜澄茄\', \'白术\', \'两黄耆\', \'附子\', \'分炮裂\', \'去皮\', \'草豆蔻\', \'去皮\', \'桂心\', \'半两\', \'当归\', \'木香\', \'半两\', \'半两\', \'柴胡\', \'两去\', \'牛膝\', \'分去\', \'吴茱萸\', \'半两\', \'浸遍\', \'焙干\', \'研碎\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57777, '19002', '柴胡汤', '柴胡（去苗）1两，生干地黄（焙）1两，附子（炮裂，去皮脐）1两，当归（切，焙）1两，人参1两，白茯苓（去黑皮）1两，芎藭1两，黄耆1两，芍药1两，肉苁蓉（去皱皮，切，酒浸，焙）1两，石斛（去根）1两。', '每服2钱匕，水1盏，加生姜3片，大枣1枚（擘），同煎至7分，去滓温服，不拘时候。', '《圣济总录》卷一六三', '产后虚热久不解，渐成劳气。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两生\', \'地黄\', \'附子\', \'炮裂\', \'去皮\', \'当归\', \'切焙\', \'人参\', \'白茯苓\', \'黑皮\', \'芍药\', \'肉苁蓉\', \'皮切\', \'石斛\']');
INSERT INTO `prescript` VALUES (57778, '18987', '柴胡散', '柴胡1两半（去苗），贝母1两（煨微黄），杏仁3分（汤浸，去皮尖双仁，麸炒微黄），葛根1两（锉），赤芍药1两，石膏2两，玄参（黄芩）1两，知母3分，栀子仁1两。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十二', '伤寒，咳嗽烦热，四肢骨节头目疼痛。', '-', '-', '柴胡', '[\'柴胡\', \'两半\', \'去苗\', \'贝母\', \'微黄\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'葛根\', \'芍药\', \'石膏\', \'玄参\', \'黄芩\', \'知母\', \'栀子\']');
INSERT INTO `prescript` VALUES (57779, '18960', '拨云散', '羌活1斤，防风1斤，柴胡1斤，甘草（炒）1斤。', '每服2钱，水1盏半，煎至7分，食后、临睡时薄荷汤调下；菊花苗汤下亦得。', '《局方》卷七（绍兴续添方）', '风毒上攻，眼目昏暗，翳膜遮障，怕日羞明，多生热泪，隐涩难开，眶痒赤痛，睑眦红烂，瘀肉侵睛；并治一切风毒眼疾。', '忌腌藏、虾酱，湿面、炙煿，发风毒物。', '-', '柴胡', '[\'羌活\', \'防风\', \'柴胡\', \'甘草\', \'炒斤\']');
INSERT INTO `prescript` VALUES (57780, '18892', '柴胡饮', '柴胡5钱，红花3钱，大桃仁（不去尖）3钱（研末）。', '将柴胡、红花用酒煎好，调桃仁末热服。', '《伤科汇纂》卷七', '大怒及从高坠下，血积肋下，左边疼。', '-', '-', '柴胡', '[\'柴胡\', \'钱红\', \'花钱\', \'桃仁\', \'不去\', \'研末\']');
INSERT INTO `prescript` VALUES (57781, '18889', '活血汤', '当归尾9g，桃仁9g，红花9g，泽兰9g，益母草12g，丹参30g，白芍9g，柴胡6g，香附9g，广陈皮9g，牛膝9g，甘草3g。', '水煎服。', '《临证医案医方》', '活血理气。主闭经（气滞血瘀型）。月经数月不行，小腹硬痛，乳房胀痛，脉沉涩，舌质紫，苔白。', '-', '-', '柴胡', '[\'当归\', \'桃仁\', \'红花\', \'泽兰\', \'益母草\', \'丹参\', \'白芍\', \'柴胡\', \'香附\', \'广陈皮\', \'牛膝\', \'甘草\']');
INSERT INTO `prescript` VALUES (57782, '18857', '柴胡茯苓汤', '柴胡（去苗）2两，白茯苓（去黑皮）1两半，白术1两半，枳壳（去瓤，麸炒）1两半。', '每服3钱匕，水1盏，煎至7分，去滓，食后温服，日2次。', '《圣济总录》卷九十三', '症癖气壮热，咳嗽骨蒸。', '-', '-', '柴胡', '[\'柴胡\', \'白茯苓\', \'黑皮\', \'两半\', \'白术\', \'两半\', \'枳壳\', \'两半\']');
