INSERT INTO `prescript` VALUES (57786, '18800', '柴胡五味汤', '柴胡5两，五味子3两，橘皮3两，紫菀3两，贝母3两，杏仁（去皮尖双仁者，熬）3两，麻黄4两（去节），甘草（炙）3两，黄芩3两。', '每服加麦门冬1两（去心），生姜半两，竹叶1两半，煎服。', '《普济方》卷一八三', '肺痰气，上气、气急及咳。', '-', '-', '柴胡', '[\'柴胡\', \'五味子\', \'橘皮\', \'紫菀\', \'贝母\', \'杏仁\', \'皮尖\', \'仁者\', \'麻黄\', \'两去\', \'甘草\', \'黄芩\']');
INSERT INTO `prescript` VALUES (57787, '18796', '和胎败毒散', '人参1钱，羌活1钱，前胡1钱，柴胡1钱，白苓1钱，甘草1钱，川芎1钱，枳壳1钱，桔梗1钱，黄芩1钱，白术1钱，苏叶1钱，葛根1钱，葱白1钱。', '生姜为引，水煎，热服。得汗而解。', '《会约》卷十四', '妊娠伤寒，天行时气，传染初起，并一切感冒。', '-', '-', '柴胡', '[\'人参\', \'羌活\', \'钱前\', \'胡钱\', \'柴胡\', \'钱白苓\', \'甘草\', \'川芎\', \'枳壳\', \'桔梗\', \'黄芩\', \'白术\', \'苏叶\', \'葛根\', \'葱白\']');
INSERT INTO `prescript` VALUES (57788, '18788', '家传三因冲和丸', '人参1两，石斛1两，白蔻仁1两，广陈皮1两，山楂肉2两（各为末，合研令匀，碗盛碟盖，饭上蒸熟，取起待冷方开），远志（甘草汤泡，去心，取末）1两，山栀（炒焦色，取末）2两，香附（童便浸半日，洗净，醋炒末）2两（3味共研匀，如上法蒸熟，勿令泄气），海石（末）2两，苍术（米泔浸，洗去浮皮，炒黄取末）2两（2味如上法同蒸），川芎（末）2两，北柴胡（末）1两，青黛1两（3味和匀，蒸如上法）。', '冲和丸（《准绳·类方》卷五）。', '《赤水玄珠》卷十三', '养心扶脾，疏肝开胃，畅达三焦，贯通五脏，赞坎离有升降之能，和表里无壅塞之患。主内伤。', '-', '-', '柴胡', '[\'人参\', \'石斛\', \'两白蔻仁\', \'两广\', \'陈皮\', \'山楂\', \'各为\', \'末合\', \'研令匀\', \'盖饭\', \'蒸熟\', \'取起\', \'冷方开\', \'远志\', \'甘草\', \'汤泡\', \'心取\', \'两山\', \'焦色\', \'取末\', \'香附\', \'童便\', \'半日\', \'洗净\', \'两味\', \'共研\', \'匀如\', \'上法\', \'蒸熟\', \'泄气\', \'海石\', \'苍术\', \'浸洗\', \'浮皮\', \'黄取\', \'两味\', \'如上\', \'法同\', \'川芎\', \'两北\', \'柴胡\', \'两青\', \'两味\', \'上法\']');
INSERT INTO `prescript` VALUES (57789, '18720', '保真汤', '当归5分，生地黄5分，熟地黄5分，黄耆（蜜水炙）5分，人参5分，白术5分，甘草5分，白茯苓5分，天门冬（去心）1钱，麦门冬（去心）1钱，白芍药1钱，黄柏（盐水炒）1钱，知母1钱，五味子1钱，软柴胡1钱，地骨皮1钱，陈皮1钱，莲心5分。', '上以水2钟，加生姜3片，大枣1枚，煎8分，食远服。', '《准绳·类方》卷一', '诸虚百损，五劳七伤，骨蒸潮热，咳嗽，诸汗、诸血。', '-', '-', '柴胡', '[\'当归\', \'生地黄\', \'熟地黄\', \'分黄耆\', \'蜜水炙分\', \'人参\', \'白术\', \'甘草\', \'白茯苓\', \'天门冬\', \'心钱\', \'麦门冬\', \'心钱白\', \'芍药\', \'黄柏\', \'盐水\', \'知母\', \'五味子\', \'钱软\', \'柴胡\', \'地骨皮\', \'陈皮\', \'莲心\']');
INSERT INTO `prescript` VALUES (57790, '18678', '鬼箭羽散', '鬼箭羽1分，真珠末1分，桃仁（汤浸，去皮尖双仁，麸炒微黄）、川大黄1两（锉，微炒），羚羊角屑半两，桔梗（去芦头）半两，川朴消半两，川升麻半两，赤芍药半两，柴胡（去苗）半两，黄芩半两。', '鬼箭羽汤（《圣济总录》卷一七七）。《圣济总录》有鬼臼。', '《圣惠》卷八十三', '小儿中恶，心坚强，卒痛欲困。', '-', '-', '柴胡', '[\'鬼箭羽\', \'真珠\', \'末分\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\', \'大黄\', \'羚羊角\', \'半两\', \'桔梗\', \'芦头\', \'半两\', \'川朴消\', \'半两\', \'升麻\', \'半两\', \'芍药\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'黄芩\', \'半两\']');
