INSERT INTO `prescript` VALUES (57792, '18625', '举陷参耆煎', '玄参2钱，黄耆2钱，炒白术1钱，茯苓1钱，陈皮1钱，柴胡1钱，升麻1钱，炙甘草5分，泽泻2钱。', '姜、枣、灶心土为引，水煎服。', '《重订通俗伤寒论》', '妄下阴脱。凡伤寒温热攻下太过，脾胃受伤，心中懊憹，起卧不安，下泻不止者。', '-', '-', '柴胡', '[\'玄参\', \'黄耆\', \'白术\', \'茯苓\', \'陈皮\', \'柴胡\', \'升麻\', \'甘草\', \'泽泻\']');
