INSERT INTO `prescript` VALUES (57795, '18540', '黄耆散', '黄耆1两（锉），酸枣仁3分（微炒），麦门冬3分（去心），枸杞子3分，熟干地黄1两，人参1两（去芦头），柴胡1两（去苗），白茯苓1两，防风半两（去芦头），白术半两，甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，加生姜半分，大枣3枚，煎至6分，去滓温服，不拘时候。', '《圣惠》卷六十', '肠风痔疾，失血后虚损，皮肤干燥，四肢黄瘦，心神虚烦，少得眠卧，不能饮食。', '-', '-', '柴胡', '[\'黄耆\', \'酸枣仁\', \'分微\', \'麦门冬\', \'分去\', \'枸杞子\', \'分熟\', \'地黄\', \'人参\', \'两去\', \'芦头\', \'柴胡\', \'两去\', \'白茯苓\', \'防风\', \'半两\', \'芦头\', \'白术\', \'半两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57796, '18529', '大柴胡鳖甲散', '柴胡1两（去芦），秦艽1两，常山1两，贝母1两，甘草1两，乌梅1两，山栀1两，豉心1两，鳖甲1两（醋炙），黄芩1两，生姜半两，大黄半两，桃枝1握，柳枝1握，葱白1握，薤白1握，糯米半合。', '本方方名，《普济方》引作“柴胡鳖甲散”。', '《博济》卷一', '痨瘦。劳疟及五劳。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'秦艽\', \'常山\', \'贝母\', \'甘草\', \'乌梅\', \'两山\', \'两豉\', \'鳖甲\', \'两醋\', \'黄芩\', \'生姜\', \'半两\', \'大黄\', \'半两\', \'桃枝握\', \'柳枝\', \'葱白\', \'薤白\', \'糯米\', \'半合\']');
INSERT INTO `prescript` VALUES (57797, '18427', '柴胡大黄汤', '柴胡（去苗）1两，大黄（湿纸裹，煨）1两，朴消1两，枳壳（去瓤，麸炒）1两，甘草（炙，锉）半两。', '柴胡汤（原书卷二十八》。', '《圣济总录》卷二十一', '伤寒日数过多，热结在里，心中气闷，或发疼痛，狂言不定，烦躁欲走，不得眠，大小便不通。', '-', '-', '柴胡', '[\'柴胡\', \'大黄\', \'湿纸\', \'两朴\', \'消两\', \'枳壳\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (57798, '18408', '健母固脱丸', '当归1两，杭芍5钱，寸冬5钱，玄参5钱，五味2钱，甘草2钱，仲炭3钱，寄生3钱，川断3钱，蔻仁3钱，山药3钱，胶珠3钱，枳壳3钱，广皮3钱，元芩3钱，远志3钱，京母3钱，川贝3钱，均青3钱，柴胡3钱，艾炭3钱，焦术3钱。', '每日早、晚各服1丸。', '《全国中药成药处方集》（抚顺方）', '妇人流产。', '-', '-', '柴胡', '[\'当归\', \'两杭芍\', \'钱寸\', \'冬钱\', \'玄参\', \'五味\', \'甘草\', \'钱仲炭\', \'寄生\', \'钱川断\', \'钱蔻仁\', \'山药\', \'胶珠\', \'枳壳\', \'钱广皮\', \'钱元芩\', \'远志\', \'钱京\', \'母钱\', \'川贝\', \'钱均\', \'青钱\', \'柴胡\', \'艾炭\', \'焦术\']');
INSERT INTO `prescript` VALUES (57799, '18402', '保赤丹', '柴胡1两，粉葛1两，槟榔2两，紫朴1两，姜夏3钱，广皮5钱，姜连3钱，杭芍1两，木香3钱，车前1两，薏米3两，桔梗5钱，黄芩3钱，苍术5钱，赤苓3钱，神曲5钱，麦芽5钱，焦楂5钱，益元散1两半。', '每服1丸，生姜水送下。', '《全国中药成药处方集》（沈阳方）', '清瘟解热，健胃消食。主小儿四时感冒，头痛发热，伤食伤水，泄泻痢疾，食积痞块，各种疳疾，呕吐腹满，食滞咳嗽，腹痛胀闷，肌肉消瘦。', '-', '-', '柴胡', '[\'柴胡\', \'粉葛\', \'槟榔\', \'两紫朴\', \'姜夏钱\', \'广皮\', \'姜连钱\', \'杭芍\', \'木香\', \'钱车\', \'薏米\', \'桔梗\', \'黄芩\', \'苍术\', \'赤苓\', \'神曲\', \'麦芽\', \'焦楂\', \'钱益\', \'两半\']');
INSERT INTO `prescript` VALUES (57800, '18376', '柴胡饮子', '柴胡1两半（去苗），木通1两半（锉），赤芍药1两半，豉1合，石膏2两半（捣），甘草半两（炙微赤，锉）。', '每服半两，用水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十七', '热病3日，头痛口苦，寒热往来。', '-', '-', '柴胡', '[\'柴胡\', \'两半\', \'苗木\', \'两半\', \'芍药\', \'两半\', \'豉合\', \'石膏\', \'两半\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57801, '18336', '柴胡汤', '柴胡（去苗）1两，黄芩（去黑心）1两，犀角（镑）3分，芍药3分，山栀子仁半两，大黄（细锉，炒）1两半，木通（锉）3分，朴消1两1分。', '本方方名，《普济方》引作“柴胡散”。', '《圣济总录》卷二十一', '伤寒4日以后，烦热不解，大小肠涩。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'黑心\', \'犀角\', \'芍药\', \'分山\', \'栀子\', \'半两\', \'大黄\', \'两半\', \'木通\', \'锉分\', \'朴消\', \'两分\']');
INSERT INTO `prescript` VALUES (57802, '18334', '柴胡散', '柴胡1两半（去苗），赤茯苓1两，麦门冬1两（去心），人参半两（去芦头），枇杷叶半两（拭去毛，炙微黄），陈橘皮半两（汤浸，去白瓤，焙），甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服。', '《圣惠》卷七十四', '妊娠心烦，头昏躁闷，不思饮食，或时呕吐。', '-', '-', '柴胡', '[\'柴胡\', \'两半\', \'茯苓\', \'麦门冬\', \'两去\', \'人参\', \'半两\', \'芦头\', \'枇杷叶\', \'半两\', \'拭去\', \'微黄\', \'橘皮\', \'半两\', \'汤浸\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57803, '18327', '柴胡汤', '柴胡（去苗）1两，前胡（去芦头）1两，桔梗（炒）1两，贝母（去心）1两，牡丹皮1两，黄芩（去黑心）1两，麻黄（去根节）1两，甘草（炙，锉）1钱，枳壳（去瓤，麸炒）1钱，升麻半两。', '每服3钱匕，水1盏，煎2-3沸，去滓，食后、临卧服。', '《圣济总录》卷十三', '因于露风成寒热，精神昏困，肢节烦疼。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两前\', \'胡去\', \'芦头\', \'桔梗\', \'贝母\', \'牡丹皮\', \'黄芩\', \'黑心\', \'麻黄\', \'根节\', \'甘草\', \'枳壳\', \'升麻\', \'半两\']');
INSERT INTO `prescript` VALUES (57804, '18325', '柴胡汤', '柴胡（去苗）1两，胡黄连半两，紫菀（去土）3分，知母3分，鳖甲（小者）半斤（小便浸1宿，醋炙），天灵盖（醋炙）1两，秦艽（去苗土）1两，甘草（炙黄）半两，杏仁（去皮尖双仁，炒）1两，地骨皮3分。', '每服2钱，水1盏，加青蒿少许，同煎至7分，露天处安1夜，去滓，平旦空腹温服。', '《普济方》卷二三六', '骨蒸劳热。', '-', '-', '柴胡', '[\'柴胡\', \'胡黄连\', \'半两\', \'紫菀\', \'土分\', \'知母\', \'鳖甲\', \'半斤\', \'小便\', \'浸宿\', \'天灵盖\', \'秦艽\', \'苗土\', \'甘草\', \'半两\', \'杏仁\', \'皮尖\', \'双仁\', \'地骨皮\']');
INSERT INTO `prescript` VALUES (57805, '18316', '柴胡散', '柴胡1两（去苗），赤芍药1两，枳实3分（麸炒微黄），杏仁3分（汤浸，去皮尖双仁，麸炒微黄），黄芩3分，川大黄2两（锉碎，微炒），槟榔1两，汉防己3分，甘草半两（炙微赤，锉）。', '每服3钱，以水1中盏，煎至6分，去滓，食前温服。', '《圣惠》卷六', '大肠实热，气壅不通，心腹胀满，发歇寒热。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'芍药\', \'枳实\', \'分麸\', \'微黄\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'黄芩\', \'分川\', \'大黄\', \'槟榔\', \'两汉\', \'防己\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57806, '18308', '凉血解毒汤', '紫草1钱，生地8分，柴胡8分，牡丹皮7分，赤芍3分，苏木3分，防风3分，荆芥3分，黄连3分，木通3分，牛子4分，天麻2分，红花2分，甘草2分。', '凉血化毒汤（《种痘新书》卷十二）。方中柴胡，《种痘新书》作“前胡”。', '《赤水玄珠》卷二十八', '痘出热不退，红不分地；或痘苗干枯黑陷。', '-', '-', '柴胡', '[\'紫草\', \'生地\', \'柴胡\', \'牡丹皮\', \'赤芍\', \'苏木\', \'防风\', \'荆芥\', \'黄连\', \'木通\', \'分牛子\', \'天麻\', \'红花\', \'甘草\']');
INSERT INTO `prescript` VALUES (57807, '18257', '纯阳救苦丹', '藿香1两，菖蒲1两，砂仁5钱（粒），苍术1两，栀子8钱（炒），远志8钱，半夏1两（京），木香5钱，青木香5钱，腹皮1两，紫苏5钱，神曲5钱，柴胡8钱，白矾1两，玉金5钱，茯神2两，陈皮1两，当归2两（全），川芎5钱，木通8钱，木瓜2两，厚朴5钱，香附8钱，黄芩1两，麦冬2两，羌活5钱，独活5钱，青黛5钱，枳壳5钱，杏仁1两（去皮尖），川连5钱，雄黄5钱，生地2两，防风1两，桔梗8钱，苦梗8钱，泽泻8钱，甘草5钱，黄柏5钱。', '大人病重者，每服不过4丸，病轻者2丸，小儿10岁以外者1丸，10岁以内者半丸，周岁内外者，用1丸，烧黄土水泡开，灌饮十分之三四。妇女胎前，用当归汤送下；产后，用红花汤送下，或桃仁为引亦可；催生，佛手3钱煎汤送下；妇女临产不下，用酥龟板汤送下；便血，用阿胶汤送下；胎漏，用阿胶汤送下；妇人不能生育，用当归汤送下；红白崩症，红症用白狗尾花汤送下，白症用红狗尾花汤送下；妇女行经腹痛，用艾叶汤送下；症瘕，用红花茨菇根汤送下；妇女干血痨症，用真红花汤送下；血虚，用当归红花汤送下；幼童幼女，风续天花，痘疹等症，用姜葱汤', '《春脚集》卷三', '妇女临产不下，便血，胎漏，不孕，红白崩症，行经腹痛，症瘕，干血痨；小儿风续天花，痘疹，小儿急慢惊风，食积胃热，脾虚等症；疯癫因痰，邪魔，疯疾，瘟疫，咳嗽，伤寒内热，心口闷，头痛，腰疼，腿痛，遗尿，结尿，结粪，膈症，吐血，痢疾，疮疾，瘰疬，疥癣，无名肿毒，疟疾，劳伤黄病，蛊症，各种胃气疼痛，小肠疝气攻心疼痛，以及夏令受暑，山岚瘴气，自汗盗汗，翻胃呕吐，单双乳蛾喉闭，食积，水积，酒积，怔忡，中湿，肿胀，腹痛，脱肛，牙疼耳聋，暴发火眼，寸白虫，破伤风，溺河轻生，手足冷痛，疯狗咬伤。', '-', '-', '柴胡', '[\'藿香\', \'菖蒲\', \'砂仁\', \'钱粒\', \'苍术\', \'栀子\', \'远志\', \'半夏\', \'两京\', \'木香\', \'青木香\', \'腹皮\', \'紫苏\', \'神曲\', \'柴胡\', \'白矾\', \'两玉\', \'金钱\', \'茯神\', \'陈皮\', \'当归\', \'两全\', \'川芎\', \'木通\', \'木瓜\', \'厚朴\', \'香附\', \'黄芩\', \'麦冬\', \'羌活\', \'独活\', \'青黛钱\', \'枳壳\', \'杏仁\', \'两去\', \'皮尖\', \'川连钱\', \'雄黄\', \'生地\', \'防风\', \'桔梗\', \'钱苦\', \'泽泻\', \'甘草\', \'黄柏\']');
INSERT INTO `prescript` VALUES (57808, '18218', '大黑虎膏', '白芷1两，大黄1两，黄连1两，白及1两，白蔹1两，黄芩1两，木鳖子1两，黄柏1两，羌活1两，独活1两，金毛狗脊1两，杏仁1两，当归1两，芍药1两，川芎1两，肉苁蓉1两，生地1两，前胡1两，肉桂1两，柴胡1两，荆芥1两，黄耆1两，连翘1两，防风1两，蓖麻子1两，乳香1两，没药1两，血竭1两，樟脑4两，血余4两，香油3斤，飞丹1斤，麝香5钱，槐枝2两，柳枝2两。', '外贴。', '《万氏家抄方》卷四', '痈疽发背，跌扑损伤，折骨，疔疮。', '-', '-', '柴胡', '[\'白芷\', \'大黄\', \'黄连\', \'两白及\', \'白蔹\', \'黄芩\', \'木鳖子\', \'黄柏\', \'羌活\', \'独活\', \'金毛狗\', \'杏仁\', \'当归\', \'芍药\', \'川芎\', \'肉苁蓉\', \'生地\', \'两前\', \'胡两\', \'肉桂\', \'柴胡\', \'荆芥\', \'两黄耆\', \'连翘\', \'防风\', \'蓖麻子\', \'乳香\', \'没药\', \'血竭\', \'樟脑\', \'两血余\', \'香油\', \'飞丹斤\', \'麝香\', \'槐枝\', \'柳枝\']');
INSERT INTO `prescript` VALUES (57809, '18205', '柴胡散', '柴胡（去苗）半两，桔梗（去芦头）半两，枳壳（麸炒微黄，去瓤）半两，麦门冬（去心）半两，鳖甲（涂醋炙令黄，去裙襕）半两，地骨皮半两，生干地黄半两，人参（去芦头）半两，葳蕤半两，赤茯苓半两，木通半两，赤芍药半两，甘草（炙微赤，锉）半两。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷六', '肺脏壅热，胸膈烦闷，四肢疼痛。', '忌热面、炙煿、苋菜。', '-', '柴胡', '[\'柴胡\', \'去苗\', \'半两\', \'桔梗\', \'芦头\', \'半两\', \'枳壳\', \'微黄\', \'半两\', \'麦门冬\', \'去心\', \'半两\', \'鳖甲\', \'涂醋\', \'炙令\', \'黄去\', \'半两\', \'地骨皮\', \'半两\', \'生干\', \'地黄\', \'半两\', \'人参\', \'芦头\', \'半两\', \'葳蕤\', \'半两\', \'茯苓\', \'半两\', \'木通\', \'半两\', \'芍药\', \'半两\', \'甘草\', \'炙微\', \'半两\']');
INSERT INTO `prescript` VALUES (57810, '18196', '柴胡汤', '柴胡（去苗）1两，麻黄（去根节）1两，石膏1两，甘草半两（炙）。', '每服3钱匕，水1盏，加盐豉30粒，葱白2寸，煎至6分，去滓，并3服。汗出为度。', '《圣济总录》卷二十一', '伤寒3日以前，表证不除；或时行1日至3日，头痛壮热，心神烦闷。', '-', '-', '柴胡', '[\'柴胡\', \'麻黄\', \'根节\', \'石膏\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (57811, '18162', '肝炎冲剂', '柴胡9g，当归9g，赤芍9g，白芍9g，陈皮9g，枳壳9g，郁金9g，香附12g，丹参15g，玄参15g，茵陈30g，板蓝根30g，败酱草30g。', '-', '《常见病的中医治疗研究》', '疏肝解郁，清热解毒。主传染性无黄疸型肝炎。', '-', '-', '柴胡', '[\'柴胡\', \'当归\', \'赤芍\', \'白芍\', \'陈皮\', \'枳壳\', \'郁金香\', \'丹参\', \'玄参\', \'茵陈\', \'板蓝根\', \'败酱草\']');
INSERT INTO `prescript` VALUES (57812, '18159', '柴桔汤', '柴胡2钱，黄芩1钱，枳壳1钱，半夏1钱，桔梗1钱，人参7分，甘草5分，生姜、大枣。', '-', '《杂病源流犀烛》卷二十七', '支结症，即伤寒未曾下，而心下妨闷，不满不硬，非痞亦非结胸者。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'枳壳\', \'半夏\', \'桔梗\', \'人参\', \'甘草\', \'生姜\', \'大枣\']');
INSERT INTO `prescript` VALUES (57813, '18156', '鬼哭饮子', '天灵盖（酥炙黄色）、鳖甲（醋炙黄色）、柴胡2钱半，木香1钱2分，桃仁22枚（另研），豉心（醋炙黄）、青蒿半握，阿魏1钱，贯众2钱半，安息香1钱，甘草1钱。', '散子方：槟榔二钱半，辰砂一钱二分，麝香一钱（另研），蜈蚣一条（姜汁浸），乌鸡屎（先将鸡五日前用火麻子喂之，五日后则用其粪）。为细末，作三帖，各入前煎汤内服。方中天灵盖、鳖甲用量原缺。', '《医统》卷七十八引《青囊》', '取传尸虫。主', '-', '-', '柴胡', '[\'天灵盖\', \'黄色\', \'鳖甲\', \'黄色\', \'柴胡\', \'钱半\', \'木香\', \'钱分\', \'桃仁\', \'研豉\', \'心醋\', \'青蒿\', \'半握\', \'阿魏\', \'贯众\', \'钱半\', \'安息香\', \'甘草\']');
INSERT INTO `prescript` VALUES (57814, '18154', '常山饮', '常山1两，豉1合，葛根（锉）3分，柴胡（去苗）3分，升麻3分，牡蛎（熬）3分，大黄（锉，炒）3分，前胡（去头芦）3分，知母（焙）3分，山栀子仁3分，枳壳（去瓤，麸炒）3分，半夏（汤洗7遍去滑，炒）半两，麦门冬（去心，焙）半两，桃仁（去皮尖双仁，炒）半两，甘草（炙）半两，犀角（镑）半两。', '每用5钱匕，水1盏半，入葱、薤白各3寸，切碎，煎取1盏，去滓，分2服，发前及发时热服之。', '《圣济总录》卷三十四', '疟疾。先寒后热，四肢黄瘦，头痛。', '-', '-', '柴胡', '[\'常山\', \'两豉\', \'葛根\', \'锉分\', \'柴胡\', \'苗分\', \'升麻\', \'牡蛎\', \'熬分\', \'大黄\', \'分前\', \'胡去\', \'芦分\', \'知母\', \'分山\', \'栀子\', \'仁分\', \'枳壳\', \'炒分\', \'半夏\', \'洗遍\', \'半两\', \'麦门冬\', \'半两\', \'桃仁\', \'皮尖\', \'双仁\', \'半两\', \'甘草\', \'半两\', \'犀角\', \'半两\']');
