INSERT INTO `prescript` VALUES (57816, '18117', '范志神曲', '香附2两，槟榔2两，乌药2两，白芷2两，茯苓2两，桔梗2两，玄胡2两，枳壳2两，五灵脂2两，苏子2两，山楂2两，郁金2两，车前2两，黄芩2两，甘菊花2两，木通2两，莱菔子2两，赤苓2两，泽泻2两，陈皮2两，柴胡2两，白扁豆2两，砂仁2两，枳实2两，大麦芽2两，防风2两，干葛2两，苍术（米泔浸）2两，木香2两，薄荷2两，白术2两，栀子2两，赤小豆2两，丁香2钱，肉豆蔻4钱，羌活6钱，沉香6钱，小麦1斤（盦芽，晒干）。', '大人每服3钱，小儿钱半。外感发热，头眩，咳嗽、疟，痢，呕吐，俱加生姜同煎；泄泻加乌梅同煎；痢疾加倍将陈武夷茶同煎。', '《卫生鸿宝》卷一', '搜风解表，开胸快膈，调胃健脾，消积进食和中，解酒止泻，利水，痘疹初发用托邪毒外出。主四时不正之气，感冒发热，头眩，咳嗽，及伤食腹痛，痞满，呕吐，泄泻，不服水土，瘴气，疟，痢，痘疹初发。', '-', '-', '柴胡', '[\'香附\', \'槟榔\', \'乌药\', \'白芷\', \'茯苓\', \'桔梗\', \'两玄\', \'胡两\', \'枳壳\', \'五灵脂\', \'苏子\', \'山楂\', \'郁金\', \'两车\', \'黄芩\', \'甘菊花\', \'木通\', \'莱菔子\', \'两赤苓\', \'泽泻\', \'陈皮\', \'柴胡\', \'白扁豆\', \'砂仁\', \'枳实\', \'大麦芽\', \'防风\', \'两干葛\', \'苍术\', \'泔浸\', \'木香\', \'薄荷\', \'白术\', \'栀子\', \'赤小豆\', \'丁香\', \'肉豆蔻\', \'羌活\', \'沉香\', \'小麦\', \'晒干\']');
INSERT INTO `prescript` VALUES (57817, '18088', '荆芥柴胡散', '鳖甲1两，柴胡1两，荆芥穗1两，人参1两，白术1两，绵黄耆1两，延胡索1两，赤芍药半两，当归半两，熟干地黄半两，木香半两，青橘皮半两，黄橘皮半两，桑白皮半两，地骨皮半两，甘草半两。', '每服1钱，水1盏，生姜3片，煎至7分，去滓温服，日午、临卧各1服。', '《鸡峰》卷十五', '处女气虚，血海不调，时发寒热，目涩舌干，身体困倦，心忪气短，不思饮食，小便赤涩，大便或秘，迤逦瘦弱，面色萎黄，变为劳疾。', '-', '-', '柴胡', '[\'鳖甲\', \'柴胡\', \'荆芥\', \'人参\', \'白术\', \'两绵\', \'黄耆\', \'延胡索\', \'两赤\', \'芍药\', \'半两\', \'当归\', \'半两\', \'熟干\', \'地黄\', \'半两\', \'木香\', \'半两\', \'橘皮\', \'半两\', \'橘皮\', \'半两\', \'桑白皮\', \'半两\', \'地骨皮\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (57818, '18086', '大半夏汤', '半夏5两，大黄5两，吴茱萸1两，朴消1两，桂1两，牡丹3两，柴胡3两，干姜3两，细辛3两，白术3两。', '每服3钱，水2盏，煎至1盏，去滓温服，不拘时候。', '《鸡峰》卷五', '天行病，7日以上，热势弥固，大便秘涩，心腹痞满，饮食不下，精神昏乱恍惚，狂言异语，其脉沉细。', '-', '-', '柴胡', '[\'半夏\', \'大黄\', \'吴茱萸\', \'两朴\', \'消两桂\', \'牡丹\', \'柴胡\', \'干姜\', \'细辛\', \'白术\']');
