INSERT INTO `prescript` VALUES (57823, '18014', '和肝饮', '鳖甲2钱，柴胡2钱，当归2钱，川芎2钱，半夏2钱，白芍药2钱，枳壳2钱。', '水煎服。左胁痛者，怒伤血滞也，加青皮、桃仁；右胁痛者，气逆夹痰也，加桔梗、白芥子；左右胁俱痛者，肝火盛而痰气结也，加龙胆草、香附、贝母、白芥子；两胁走注痛而有声者，是痰饮也，加苍术、白芥子、胆星、瓜蒌仁。', '《本草汇言》卷十九', '胁痛。', '-', '-', '柴胡', '[\'鳖甲\', \'柴胡\', \'当归\', \'川芎\', \'半夏\', \'钱白\', \'芍药\', \'枳壳\']');
INSERT INTO `prescript` VALUES (57824, '17954', '凉明饮', '黄连1钱，柴胡1钱，黄芩1钱，防风1钱，芍药1钱，生地1钱，当归5分，羌活5分，升麻5分，白芷5分，山栀子5分，川芎5分，甘草2分。', '水煎服。有赤脉，加菊花、木贼；白睛赤肿痛甚，加桑白皮；多泪不止，加荆芥、薄荷、菊花。', '《续名家方选》', '风热上冲，眼目赤肿，多泪不止。', '-', '-', '柴胡', '[\'黄连\', \'柴胡\', \'黄芩\', \'防风\', \'芍药\', \'生地\', \'当归\', \'羌活\', \'升麻\', \'白芷\', \'分山\', \'栀子\', \'川芎\', \'甘草\']');
INSERT INTO `prescript` VALUES (57825, '17924', '姜柴汤', '柴胡1钱，桂枝3分，花粉5分，牡蛎2分，炮姜2分，炙草2分。', '水煎服。汗出愈。', '《嵩崖尊生》卷十二', '妇人热病经来，寒热如疟，狂妄。', '-', '-', '柴胡', '[\'柴胡\', \'桂枝\', \'花粉\', \'牡蛎\', \'分炮\', \'姜分\', \'草分\']');
INSERT INTO `prescript` VALUES (57826, '17914', '柴胡散', '柴胡半两（去苗），赤芍药1分，麻黄半两（去根节），黄芩1分，石膏1两，葛根1分（锉），甘草1分（炙微赤，锉）。', '每分服1钱，以水1小盏，加生姜少许，葱白3寸，豉20粒，煎至5分，去滓温服，不拘时候。以汗为效。', '《圣惠》卷八十四', '小儿伤寒，表里俱热，壮热头痛，口干烦渴，无汗；小儿囟填，有表热症者。面黄颊赤，身体痛，小便赤涩，大便焦黄，夹惊腮肿。', '-', '-', '柴胡', '[\'柴胡\', \'半两\', \'芍药\', \'麻黄\', \'半两\', \'根节\', \'黄芩\', \'石膏\', \'葛根\', \'甘草\', \'分炙微\']');
