INSERT INTO `prescript` VALUES (57831, '17783', '和解至圣丹', '郁金3钱，柴胡1钱，白芍3钱，白芥子1钱，天花粉1钱，苏子1钱，荆芥1钱，甘草5分，茯苓1钱。', '水煎服。', '《石室秘录》卷二', '开郁。主关隔症。', '-', '-', '柴胡', '[\'金钱\', \'柴胡\', \'白芍\', \'白芥子\', \'天花粉\', \'苏子\', \'荆芥\', \'甘草\', \'茯苓\']');
INSERT INTO `prescript` VALUES (57832, '17755', '草豆蔻散', '草豆蔻1两（去皮），人参1两（去芦头），柴胡1两（去苗），白术1两，陈橘皮1两半（汤浸，去白瓤，焙），甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，加生姜半分，大枣3个，煎至6分，去滓，稍热服，不拘时候。', '《圣惠》卷七十五', '妊娠心腹胀满，脾胃气虚，不下食饮。', '-', '-', '柴胡', '[\'草豆蔻\', \'去皮\', \'人参\', \'两去\', \'芦头\', \'柴胡\', \'两去\', \'白术\', \'两陈\', \'橘皮\', \'两半\', \'汤浸\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57833, '17750', '菖蒲饮子', '川芎半两，猪苓（去黑皮）半两，藁本（净）半两，柴胡（去芦头）半两，菖蒲半两，干葛半两，甘草半两，桑白皮半两。', '每服5钱，水盏半，生姜3片，同煎8分，去滓温服，滓再煎，如要出汗者，加连须葱白1根，同煎。7日内外皆可服，病在上则吐，在表则汗，下则泄，其病即解。', '《普济方》卷一四一引《德生堂方》', '四时伤寒，不问两感，头疼，发热憎寒，咳嗽。', '-', '-', '柴胡', '[\'川芎\', \'半两\', \'猪苓\', \'黑皮\', \'半两\', \'藁本\', \'半两\', \'柴胡\', \'芦头\', \'半两\', \'菖蒲\', \'半两\', \'干葛\', \'半两\', \'甘草\', \'半两\', \'桑白皮\', \'半两\']');
INSERT INTO `prescript` VALUES (57834, '17744', '桂心散', '桂心1两，麻黄3分（去根节），荆芥3分，石膏2两，赤芍药3分，柴胡1分（去苗），葛根2分，芎藭半两，人参半两（去芦头），细辛半两（去苗土），甘草1分（炙微赤，锉）。', '每服4钱，以水1中盏，加生姜半分，大枣3枚，煎至6分，去滓温服，如人行5-7里再服，以得汗出为效。', '《圣惠》卷七十八', '产后伤寒，头目四肢俱疼，心胸烦热。', '-', '-', '柴胡', '[\'桂心\', \'麻黄\', \'分去\', \'根节\', \'荆芥\', \'石膏\', \'两赤\', \'芍药\', \'柴胡\', \'分去\', \'葛根\', \'半两\', \'人参\', \'半两\', \'芦头\', \'细辛\', \'半两\', \'苗土\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (57835, '17683', '柴苓扶元汤', '柴胡1钱5分，黄芩1钱5分，人参1钱5分，猪苓1钱，泽泻1钱，白术1钱，青皮1钱，何首乌2钱，茯苓8分，肉桂8分，', '水2钟，加大枣2枚，食远服。', '《玉案》卷三', '疟疾。阴阳不和，元气虚弱，寒热渐盛。', '-', '-', '柴胡', '[\'柴胡\', \'钱分\', \'黄芩\', \'钱分\', \'人参\', \'钱分\', \'猪苓\', \'泽泻\', \'白术\', \'青皮\', \'何首乌\', \'茯苓\', \'肉桂\']');
INSERT INTO `prescript` VALUES (57836, '17667', '和血散痛汤', '羌活身1钱半，升麻1钱半，麻黄（去节）1钱半，桃仁10个，柴胡2钱，红花1分，归身1分，防风1钱，甘草（炙）2分，独活5分，猪苓5分，黄柏1钱，防己6分，知母（酒）1钱，黄连（酒）6分。', '和血散瘀汤（《证治宝鉴》卷十二）；方中黄连用量原缺，据《赤水玄珠》补。', '《医学纲目》卷十二引李东垣方', '和血止痛。主两手十指，一指疼了一指疼，疼后又肿，骨头里痛，膝痛，左膝痛了右膝痛，发时多则5日，少则3日，昼轻夜重，痛时觉热，行则痛轻，肿却重。', '-', '-', '柴胡', '[\'羌活\', \'身钱\', \'升麻\', \'钱半\', \'麻黄\', \'去节\', \'桃仁\', \'柴胡\', \'红花\', \'分归\', \'身分\', \'防风\', \'甘草\', \'炙分\', \'独活\', \'猪苓\', \'黄柏\', \'防己\', \'知母\', \'酒钱\', \'黄连\', \'酒分\']');
INSERT INTO `prescript` VALUES (57837, '17663', '柴胡汤', '柴胡1钱，芍药1钱，龙胆草1钱，当归1钱，青皮1钱，山栀1钱，连翘1钱，甘草5分。', '水煎，食后服。', '《回春》卷二', '肝火盛，水气实，或胁痛，或气从左边起者，或目红肿痛。', '-', '-', '柴胡', '[\'柴胡\', \'芍药\', \'龙胆草\', \'当归\', \'青皮\', \'钱山\', \'栀钱\', \'连翘\', \'甘草\']');
INSERT INTO `prescript` VALUES (57838, '17662', '柴胡汤', '柴胡1钱半，赤茯苓1钱，麦冬1钱，条芩1钱，人参5分，橘皮5分，甘草（炙）5分。', '水1盏半，加生姜3片，煎8分，温服。', '《广嗣纪要》卷九', '妊娠子烦，烦闷不安，呕吐恶阻。', '-', '-', '柴胡', '[\'柴胡\', \'钱半赤\', \'茯苓\', \'麦冬\', \'钱条\', \'人参\', \'橘皮\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (57839, '17597', '黄耆汤', '黄耆（薄切）1两，犀角（镑）1两，白茯苓（去黑皮）1两半，人参1两半，柴胡（去苗）1两，升麻1两，秦艽（去苗）1两，芎1两，木香1两，桑根白皮（锉）1两，枳壳（去瓤，麸炒）1两，防风（去叉）1两，芍药1两，黄芩（去黑心）1两，肉豆蔻（去壳，炒）1两，天麻1两，鳖甲（醋浸，炙，去裙襕）1两，地骨皮1两，甘草（炙，锉）1两，羌活（去芦头）3分，当归（切，焙）3分，青橘皮（去白，切，炒）3分，槟榔（锉）半两，桔梗（去芦头，炒）半两。', '每服3钱匕，以水1盏，煎至7分，去滓，空腹温服。', '《圣济总录》卷十三', '风消，气血虚弱。', '-', '-', '柴胡', '[\'黄耆\', \'薄切\', \'犀角\', \'白茯苓\', \'黑皮\', \'两半\', \'人参\', \'两半\', \'柴胡\', \'升麻\', \'秦艽\', \'去苗\', \'两芎\', \'木香\', \'两桑根\', \'白皮\', \'枳壳\', \'防风\', \'芍药\', \'黄芩\', \'黑心\', \'肉豆蔻\', \'天麻\', \'鳖甲\', \'浸炙去\', \'地骨皮\', \'甘草\', \'羌活\', \'芦头分\', \'当归\', \'切焙\', \'分青\', \'橘皮\', \'白切\', \'炒分\', \'槟榔\', \'半两\', \'桔梗\', \'芦头\', \'半两\']');
INSERT INTO `prescript` VALUES (57840, '17585', '大功散', '苍术5钱（米泔浸1宿），陈皮5钱（去白），防风（去芦）3钱，紫草3钱，荆芥穗3钱，赤芍药5钱，厚朴（姜制，炒）2钱，柴胡（去芦）2钱，缩砂仁2钱，川芎5钱，当归（酒浸）2钱，干姜2钱，牛蒡子（炒）2钱，甘草（炙）1钱，黄芩2钱。', '每用田螺厣7个，桃枝7寸、柳枝7寸，绿豆汤浸透，取皮半盏，净洗锅，入井水煎熟，去滓，取清汁；再研雄黄末1钱，同入锅内搅匀，取4盏，将药与已出及方受热与未受证者服。', '《普济方》卷四○三', '痘疮未发正发，及未受证者。', '-', '-', '柴胡', '[\'苍术\', \'钱米\', \'浸宿\', \'陈皮\', \'防风\', \'芦钱\', \'紫草\', \'荆芥\', \'芍药\', \'厚朴\', \'姜制\', \'柴胡\', \'芦钱缩\', \'砂仁\', \'川芎\', \'当归\', \'酒浸\', \'干姜\', \'牛蒡子\', \'甘草\', \'黄芩\']');
INSERT INTO `prescript` VALUES (57841, '17580', '柴胡丸', '柴胡（去苗）1两，紫菀（去土）1两，白茯苓（去黑皮）1分，雄黄（研）1分，人参1分，黄芩（去黑心）1分，牛膝（生）半两，丹砂（研）半两，马兜铃半两。', '每服1丸，烧绵灰，温酒送下，不拘时候，每日3次。', '《圣济总录》卷八十七', '宁心志，止咳嗽，除肌热。主热劳。', '-', '-', '柴胡', '[\'柴胡\', \'紫菀\', \'白茯苓\', \'黑皮\', \'雄黄\', \'研分\', \'人参\', \'黄芩\', \'黑心\', \'牛膝\', \'半两\', \'丹砂\', \'半两\', \'马兜铃\', \'半两\']');
INSERT INTO `prescript` VALUES (57842, '17505', '栝楼汤', '栝楼实1枚，黄芩3两，甘草3两，生姜4两，大枣12枚，柴胡半斤。', '瓜蒌汤（《普济方》卷一三一）。本方方名，《外台》引作“栝楼实汤”。', '《千金》卷十', '伤寒、中风5-6日以上，但胸中烦，干呕。', '-', '-', '柴胡', '[\'栝楼\', \'实枚\', \'黄芩\', \'甘草\', \'生姜\', \'大枣\', \'柴胡\', \'半斤\']');
INSERT INTO `prescript` VALUES (57843, '17472', '金沸草散', '荆芥穗1两，麻黄（去节）5钱，北柴胡5钱，旋覆花5钱，半夏（汤泡）2钱半，赤芍药2钱半，甘草2钱半。', '加生姜、桑白皮煎，食后服。', '《普济方》卷三八七', '伤风，鼻塞流涕，痰壅热嗽。', '-', '-', '柴胡', '[\'荆芥\', \'麻黄\', \'节钱北\', \'柴胡\', \'旋覆花\', \'半夏\', \'汤泡\', \'芍药\', \'钱半\', \'甘草\', \'钱半\']');
INSERT INTO `prescript` VALUES (57844, '17447', '胡黄连丸', '胡黄连1两，黄连1两，柴胡1两，乌犀半两，赤茯苓半两，使君子半两，黄芩半两，鳖甲（炙）半两。', '本方方名，原书（人卫本）作“胡连丸”。', '《幼幼新书》（古籍本）卷二十引《庄氏家传》', '生肌，消疳黄。主骨蒸潮热羸瘦。', '-', '-', '柴胡', '[\'胡黄连\', \'黄连\', \'柴胡\', \'两乌犀\', \'半两\', \'茯苓\', \'半两\', \'使君子\', \'半两\', \'黄芩\', \'半两\', \'鳖甲\', \'半两\']');
INSERT INTO `prescript` VALUES (57845, '17435', '柴胡汤', '前胡（去芦头）1两，柴胡（去苗）1两，桔梗（炒）1两，羌活（去芦头）1两，独活（去芦头）1两，人参1两，枳壳（去瓤，麸炒）1两，鳖甲（去裙襕，醋炙）1两，旋覆花1两半，甘草（炙，锉）半两，石膏（碎）1分。', '每服2钱，水1盏，煎至7分，去滓温服。', '《普济方》卷二三○', '虚劳荣卫不调，寒热羸瘦，肢体烦倦，头目昏疼，饮食无味，多困少力。', '-', '-', '柴胡', '[\'胡去\', \'芦头\', \'柴胡\', \'桔梗\', \'羌活\', \'芦头\', \'独活\', \'芦头\', \'人参\', \'枳壳\', \'鳖甲\', \'旋覆花\', \'两半\', \'甘草\', \'半两\', \'石膏\', \'碎分\']');
INSERT INTO `prescript` VALUES (57846, '17425', '大青散', '大青半两，知母半两，柴胡半两（去苗），葛根半两（锉），甘草半两（炙微赤，锉），川升麻半两，石膏1两，黄芩半两，川芒消1分，赤芍药半两，栀子仁半两。', '每服1钱，以水1小盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷八十四', '小儿伤寒，头痛壮热，烦渴。', '-', '-', '柴胡', '[\'大青\', \'半两\', \'知母\', \'半两\', \'柴胡\', \'半两\', \'去苗\', \'葛根\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'锉川\', \'升麻\', \'半两\', \'石膏\', \'黄芩\', \'半两\', \'川芒\', \'消分\', \'芍药\', \'半两\', \'栀子\', \'半两\']');
INSERT INTO `prescript` VALUES (57847, '17415', '复元通气散', '柴胡（去白）3分，桂（去粗皮）3分，桃仁（去皮尖双仁，麸炒黄）3分，木香3分，吴茱萸3分，干姜（炮）3分，细辛（去毛叶）3分，桔梗（锉）3分，赤茯苓（去黑皮）3分，芎3分，大黄（锉，炒）2两。', '本方方名，糖剂型，当作“复元通气丸”。', '《普济方》卷一八二', '和血。内消疮肿。主诸气涩闭，耳聋头痛，腹皮痈疮无头，一切刺痛、痈肿。', '-', '-', '柴胡', '[\'柴胡\', \'分桂去\', \'粗皮分\', \'桃仁\', \'皮尖\', \'双仁麸\', \'黄分\', \'木香\', \'吴茱萸\', \'干姜\', \'炮分\', \'细辛\', \'毛叶分\', \'桔梗\', \'锉分\', \'茯苓\', \'黑皮\', \'分芎\', \'大黄\']');
INSERT INTO `prescript` VALUES (57848, '17398', '酒煮当归丸', '茴香5钱，黑附子（炮制，去皮脐）7钱，良姜7钱，当归1两（上4味锉如麻豆大，以上等好酒1升半同煎至酒尽，焙干），炙甘草5钱，苦楝（生用）5钱，丁香5钱，木香1钱，升麻1钱，柴胡2钱，炒黄盐3钱，全蝎3钱，延胡索4钱。', '每服50-70丸，空心淡醋汤送下。', '《兰室秘藏》卷中', '升阳胜湿。主(疒颓)疝，白带下注，脚气。腰以下如在冰雪中，以火焙炕，重重厚绵衣盖其上，犹寒冷不任，面白如枯鱼之象，肌肉如刀割削瘦。小便不止，与白带长流而不禁固，自不知不觉，面白目青，蓝如菜色，目（目巟）（目巟）无所见，身重如山，行步倚侧，不能安地，腿膝枯细，大便难秘，口不能言，无力之极，食不下，心下痞，烦心懊憹，不任其苦，面停垢，背恶寒，小便遗而不知，此上中下三阳真气俱虚竭。胃虚之极，哕呕不止，脉沉厥紧而涩，按之空虚。小腹下痛。', '-', '-', '柴胡', '[\'茴香\', \'附子\', \'炮制\', \'去皮\', \'脐钱良\', \'姜钱\', \'当归\', \'两上\', \'麻豆\', \'以上\', \'升半同\', \'焙干\', \'甘草\', \'苦楝\', \'用钱\', \'丁香\', \'木香\', \'升麻\', \'柴胡\', \'黄盐\', \'全蝎\', \'延胡索\']');
INSERT INTO `prescript` VALUES (57849, '17388', '柴胡汤', '柴胡（去苗）1两，秦艽（去苗土）1两，麦门冬（去心，焙）1两，芦根1两，淡竹叶1两。', '每服5钱匕，水1盏半，煎至1盏，去滓，加青蒿自然汁半合，再煎沸，未发前五更初温服。', '《圣济总录》卷三十六', '足少阳疟，热多汗出。', '-', '-', '柴胡', '[\'柴胡\', \'秦艽\', \'苗土\', \'麦门冬\', \'芦根\', \'淡竹叶\']');
INSERT INTO `prescript` VALUES (57850, '17381', '虎骨散', '虎头骨1两（涂酥，炙令黄），鳖甲1两（涂醋，炙微黄，去裙襕），川升麻1两，柴胡1分（去苗），桃仁1两（汤浸，去皮尖双仁，麸炒微黄），川大黄1两（锉碎，微炒），甘草1两（炙微赤，锉）。', '每服3钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十一', '传尸羸瘦，肢节烦疼，不思饮食。', '忌苋莱。', '-', '柴胡', '[\'头骨\', \'两涂\', \'炙令\', \'鳖甲\', \'两涂\', \'微黄\', \'升麻\', \'柴胡\', \'分去\', \'桃仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'大黄\', \'甘草\', \'两炙微\']');
INSERT INTO `prescript` VALUES (57851, '17369', '柴胡芍药丹皮汤', '黄芩3钱（酒炒），柴胡1钱，白芍药3钱，甘草2钱，丹皮3钱。', '煎半杯，热服。', '《四圣心源》卷八', '眼病疼痛。', '-', '-', '柴胡', '[\'黄芩\', \'钱酒\', \'柴胡\', \'钱白\', \'芍药\', \'甘草\', \'丹皮\']');
INSERT INTO `prescript` VALUES (57852, '17366', '经验加减解毒活血汤', '连翘3钱，柴胡2钱，葛根2钱，生地5钱，当归1钱半，赤芍3钱，桃仁8钱（去皮尖，杵碎之），红花5钱，川朴1钱，甘草2钱。', '一、二、三日病在上焦、药味取其轻清，煎宜六七沸；四、五、六日病在中焦，药味取其稍重，煎宜十沸；七日以后病在下焦，药味取其浓重，煎十余沸。此方药已大剂，水用1碗半，先用大罐煎合沸数，倾入小罐，后入水大罐，再煎再倾，煎汤大半碗服。', '《鼠疫约编》', '鼠疫。', '-', '-', '柴胡', '[\'连翘\', \'柴胡\', \'葛根\', \'生地\', \'当归\', \'钱半\', \'赤芍\', \'桃仁\', \'皮尖\', \'花钱\', \'川朴\', \'甘草\']');
INSERT INTO `prescript` VALUES (57853, '17344', '败瘀止痛汤', '大黄3钱，桃仁14粒，当归3钱，白芍1两，柴胡1钱，黄连1钱，厚朴2钱，甘草1钱。', '水煎服。1剂而瘀血下，2剂而痛除，肿亦尽消。', '《辨证录》卷二', '通脾中伏热而下其瘀血。主脾火内伏，瘀血存注，右胁大痛，肿起如覆杯，手不可按，按之痛益甚。', '-', '-', '柴胡', '[\'大黄\', \'桃仁\', \'当归\', \'白芍\', \'柴胡\', \'黄连\', \'厚朴\', \'甘草\']');
INSERT INTO `prescript` VALUES (57854, '17336', '柴胡生化汤', '柴胡（酒炒）1钱，川芎2钱，当归5钱，桃仁13粒，炙甘草5分，炮姜5分，荆芥1钱，酒炒黄芩1钱。', '水煎，加酒半盏冲服。', '《医方简义》卷六', '产后郁冒寒多，复汗，身热。', '-', '-', '柴胡', '[\'柴胡\', \'川芎\', \'当归\', \'桃仁\', \'甘草\', \'分炮\', \'姜分\', \'荆芥\', \'钱酒\', \'黄芩\']');
INSERT INTO `prescript` VALUES (57855, '17324', '柴芩双解汤', '柴胡1钱半，生葛根1钱，羌活8分，知母2钱，炙草6分，青子芩1钱半，生石膏4钱（研），防风1钱，猪苓1钱半，白蔻末6分（冲）。', '-', '《重订通俗伤寒论》卷二', '和解表里，调剂阴阳。主少阳相火，郁子腠理而不达，表邪未罢，里邪已盛，寒热，身疼无汗，口渴，恶热。', '-', '-', '柴胡', '[\'柴胡\', \'半生\', \'葛根\', \'羌活\', \'知母\', \'草分\', \'青子\', \'半生\', \'石膏\', \'钱研\', \'防风\', \'猪苓\', \'半白\', \'分冲\']');
INSERT INTO `prescript` VALUES (57856, '17323', '柴芍地黄丸', '六味地黄丸加柴胡3两，白芍3两。', '柴芍六味丸（《全国中药成药处方集》福州方）。', '《饲鹤亭集方》', '滋肾平肝，益阴养血。主血虚肝燥，骨蒸内热。', '-', '-', '柴胡', '[\'六味地黄\', \'丸加\', \'柴胡\', \'白芍\']');
INSERT INTO `prescript` VALUES (57857, '17322', '柴胡栝楼汤', '柴胡（去苗，洗）8钱，芍药2钱，人参2钱，半夏（汤洗7遍）2钱半，甘草（炙）2钱，栝楼2钱。', '上(口父)咀。水2升，加生姜10片，大枣2个（擘破），同煎至1升，分3服，去滓服。', '《全生指迷方》卷二', '肺素有热，气盛于身，厥逆上冲，中气实而不外泄，其气内藏于心，外舍于分肉之间，而致瘅疟，间日发热，发必数欠，头痛拘倦，消烁脱肉，其脉弦大而数。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'洗钱\', \'芍药\', \'人参\', \'半夏\', \'洗遍\', \'甘草\', \'栝楼\']');
INSERT INTO `prescript` VALUES (57858, '17307', '大补延龄膏', '党参1两，丹参1两，玄参1两，黄耆1两，于术1两，木通1两，生地1两，熟地1两，酒川芎1两，酒当归1两，酒白芍1两，川乌1两，萸肉1两，香白芷1两，淮山药1两，羌活1两，防风1两，柴胡1两，秦艽1两，苍术1两，厚朴1两，青皮1两，陈皮1两，乌药1两，杏仁1两，香附子1两，苏子1两，贝母1两，生半夏1两，生南星1两，枳实1两，丹皮1两，地骨皮1两，桑白皮1两，菟丝子1两，蛇床子1两，杜仲1两，牛膝1两，续断1两，炙甘草1两，破故纸1两，黄柏1两，知母1两，锁阳1两，巴戟天1两，胡桃仁1两，五味子1两，天冬1', '共享油20斤，分熬丹收。再入：铅粉（炒）1斤、陀僧4两，净松香4两，赤石脂1两，木香1两，砂仁1两，官桂1两，丁香1两，檀香1两，雄黄1两，明矾1两，轻粉1两，降香1两，乳香（制）1两，没药（制）1两，另用龟胶、鹿胶（酒蒸化），俟丹收后，搅至温温，以1滴试之不爆，方下，再搅千余遍令匀。或加桂、麝以为引更妙。', '《理瀹》', '调和五脏，配合阴阳。主气血两衰证。', '-', '-', '柴胡', '[\'党参\', \'丹参\', \'玄参\', \'两黄耆\', \'两于术\', \'木通\', \'生地\', \'熟地\', \'两酒\', \'川芎\', \'两酒\', \'当归\', \'两酒\', \'白芍\', \'川乌\', \'两萸\', \'两香\', \'白芷\', \'两淮\', \'山药\', \'羌活\', \'防风\', \'柴胡\', \'秦艽\', \'苍术\', \'厚朴\', \'青皮\', \'陈皮\', \'乌药\', \'杏仁\', \'香附子\', \'苏子\', \'贝母\', \'两生\', \'半夏\', \'两生\', \'南星\', \'枳实\', \'丹皮\', \'地骨皮\', \'桑白皮\', \'菟丝子\', \'蛇床子\', \'杜仲\', \'牛膝\', \'续断\', \'甘草\', \'两破\', \'故纸\', \'黄柏\', \'知母\', \'锁阳\', \'巴戟天\', \'胡桃\', \'五味子\', \'两天\']');
INSERT INTO `prescript` VALUES (57859, '17244', '金仙膏', '苍术5两，上白术4两，羌活3两，川乌3两，姜黄3两，生半夏（姜制）3两，乌药3两，川芎3两，青皮3两，生大黄3两，生香附2两，炒香附2两，生灵脂2两，炒灵脂2两，生延胡2两，炒延胡2两，枳实2两，黄连2两，姜制厚朴2两，当归2两，灵仙2两，黑丑头（半生半炒）2两，巴仁2两，枯黄芩1两，黄柏1两，生蒲黄1两，黑山栀1两，川郁金1两，莪术1两，三棱1两，槟榔1两，陈皮1两，山楂1两，麦芽1两，神曲1两，南星1两，白丑头1两，苦葶苈1两，苏梗1两，藿梗1两，南薄荷1两，草乌1两，独活1两，柴胡1两，前胡1两，细', '外感风寒暑湿，头疼发热，贴胸口，先用生姜擦后再贴；内伤饮食、胸膈饱满，贴胸口痛处并脐上，用莱菔子、枳实，麸皮、食盐炒熨；咳嗽，贴胸口，用苍术、枳壳、陈皮、半夏、白术、干姜、皂角炒熨（若肺咳用清肺膏，胃咳用清胃膏，肾咳用滋阴膏，此膏勿用）；痰喘、痰哮，贴胸背；痰饮，贴心口；嘈杂、噫气、吞酸、吐酸，贴心口，或用苍术、陈皮、半夏、黄连、黄芩、吴萸、神曲煎抹；恶心、干呕，贴心口，或用芦根煎汤抹胃脘，掺黄连末贴；噎嗝、反胃，用生姜汁、韭菜汁、牛乳抹胸口，膏内掺真郁金末，凤仙子末贴，再用陈米同黄土合上平肝顺气保中方药', '《理瀹》', '开胸膈，进饮食，化痰消痞；升降阴阳，流通气血。主风寒暑湿，气血痰食，六郁五积诸病，中州脾胃之病，四时外感内伤，表里不分，寒热相杂，非一偏所能治者；夏时暑湿、湿温之症偏于阴湿者；一切腹痛，妇人痛经，小儿虫痛、疟疾、痢疾。', '-', '-', '柴胡', '[\'苍术\', \'两上\', \'白术\', \'羌活\', \'川乌\', \'姜黄\', \'两生\', \'半夏\', \'姜制\', \'乌药\', \'川芎\', \'青皮\', \'两生\', \'大黄\', \'两生\', \'香附\', \'香附\', \'生灵\', \'灵脂\', \'两生\', \'延胡\', \'延胡\', \'枳实\', \'黄连\', \'两姜制\', \'厚朴\', \'当归\', \'两灵仙\', \'两黑\', \'丑头\', \'半生\', \'两巴仁\', \'黄芩\', \'黄柏\', \'两生\', \'蒲黄\', \'黑山\', \'栀两川\', \'郁金\', \'莪术\', \'三棱\', \'槟榔\', \'陈皮\', \'山楂\', \'麦芽\', \'神曲\', \'南星\', \'两白\', \'丑头\', \'两苦\', \'两苏\', \'两南\', \'薄荷\', \'草乌\', \'独活\', \'柴胡\', \'两前\', \'胡两细\']');
INSERT INTO `prescript` VALUES (57860, '17238', '拨云散', '木贼1两，防风6钱，柴胡6钱，青葙子8钱，蝉蜕1两，黄芩6钱，菊花8钱，车前子8钱。', '早晨空心开水调服2钱，晚服1钱。或用猪肝1块割开，放药末2钱填内，用湿棉纸包好，置灰中很熟食之，亦可煎服之。外点消炉散。', '《程松崖眼科》', '眼睛黑珠云翳围满，有瞳仁者。', '-', '-', '柴胡', '[\'木贼\', \'防风\', \'柴胡\', \'青葙子\', \'蝉蜕\', \'黄芩\', \'菊花\', \'车前子\']');
INSERT INTO `prescript` VALUES (57861, '17195', '柴芩七物汤', '柴胡1钱，黄芩1钱半，法夏1钱半，厚朴1钱半，茯苓2钱，紫苏1钱。', '水煎，食前服。胃脘胀痛，呕吐吞酸，加左金丸1钱。', '《中医妇科治疗学》', '调气行滞。主妊娠气滞腹痛证。妊娠数月，胸腹及两胁胀痛，性情暴躁易怒，口苦，头晕，兼有咳嗽，苔白腻或薄黄，脉弦而滑。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'钱半法\', \'夏钱\', \'厚朴\', \'钱半\', \'茯苓\', \'紫苏\']');
INSERT INTO `prescript` VALUES (57862, '17189', '柴胡汤', '柴胡（去苗）1分，枳壳（去瓤，麸炒）2枚，诃黎勒大者1颗（煨，取皮用），黄芩（去黑心）半两，甘草（炙，锉）半两，芍药半两，知母（焙）半两。', '柴胡饮（《医部全录》卷四四二）。', '《圣济总录》卷一七四', '小儿疟发寒热，腹胀下痢，呕逆黄瘦。', '-', '-', '柴胡', '[\'柴胡\', \'苗分\', \'枳壳\', \'黎勒大者\', \'取皮用\', \'黄芩\', \'黑心\', \'半两\', \'甘草\', \'半两\', \'芍药\', \'半两\', \'知母\', \'半两\']');
INSERT INTO `prescript` VALUES (57863, '17188', '柴胡木香汤', '柴胡（去苗）10两，木香1两，半夏（汤选7遍，去滑）2两，人参2两，黄芩（去黑心）3两，甘草（炙）3两。', '每服2钱匕，水1盏，加生姜2片，大枣1枚（擘破），煎至6分，去滓温服，不拘时候。', '《圣济总录》卷一六八', '小儿风热潮作，不思饮食，肌体消瘦。', '-', '-', '柴胡', '[\'柴胡\', \'木香\', \'半夏\', \'汤选遍\', \'人参\', \'黄芩\', \'黑心\', \'甘草\']');
INSERT INTO `prescript` VALUES (57864, '17164', '柴胡汤', '柴胡（去苗）4两，葛根（锉）4两，枣肉（焙）4两，甘草（炙）4两，槟榔（锉）4两，常山4两，乌梅（去核，焙）4两，草豆蔻（去皮）4两，厚朴（去粗皮，姜汁炙）4两。', '每服5钱匕，酒半盏，水1盏，煎至1盏，去滓，未发前温服。', '《圣济总录》卷三十六', '脾疟，寒多热少，有汗，头目昏暗，背胛拘急，或胸膈痞闷，呕逆咳嗽，心腹胀痛，面黄肌瘦，肢节疼倦。', '-', '-', '柴胡', '[\'柴胡\', \'葛根\', \'两枣\', \'甘草\', \'槟榔\', \'常山\', \'乌梅\', \'去核\', \'草豆蔻\', \'去皮\', \'厚朴\', \'粗皮\', \'姜汁\']');
INSERT INTO `prescript` VALUES (57865, '17155', '桂附汤', '桂枝1两，附子3钱，青皮4钱，甘草4钱，柴胡4钱。', '每服加生姜3片，作3次服。', '《玉案》卷二', '厥阴症，口吐涎沫，小腹痛，不渴者。', '-', '-', '柴胡', '[\'桂枝\', \'附子\', \'青皮\', \'甘草\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57866, '17152', '柴胡半夏汤', '柴胡1钱半，半夏1钱半，黄芩1钱，白术1钱，陈皮1钱，麦门冬1钱，甘草5分。', '加生姜3片，大枣2枚，水煎，温服。小便不利，加茯苓；冬月无汗，加麻黄；三时无汗，加苏叶；冬月有汗，加桂枝；三时有汗，加防风；咽痛，加桔梗；喘嗽，去白术，加杏仁、桑白皮；酒热，加黄连；食积，加山楂、神曲；痰伏胁下作痛，加白芥子；痰盛，喉中如牵锯，加竹沥、姜汁；痰稠如胶，加金沸草、前胡；胸膈痞闷，加枳壳。', '《医学入门》卷四', '伤风发热恶寒，头痛无汗而咳嗽，或协热自利，及一切痰证，状似伤寒。', '-', '-', '柴胡', '[\'柴胡\', \'钱半\', \'半夏\', \'钱半\', \'黄芩\', \'白术\', \'陈皮\', \'麦门冬\', \'甘草\']');
INSERT INTO `prescript` VALUES (57867, '17124', '柴胡散', '北柴胡3钱，地骨皮1钱，黄芩3钱，桂2钱，甘草3钱，京芥2钱。', '上(口父)咀。水煎服，仍服小柴胡汤3-5帖。', '《医方类聚》卷二一二引《仙传济阴方》', '妇人受胎杂病，阴阳相胜，寒热往来。', '-', '-', '柴胡', '[\'柴胡\', \'地骨皮\', \'黄芩\', \'钱桂钱\', \'甘草\', \'钱京\']');
INSERT INTO `prescript` VALUES (57868, '17117', '柴胡汤', '柴胡（去苗，锉）1两，甘草（炙，锉）3分，人参3分，白茯苓（去黑皮）3分，当归（切，炒）3分，赤芍药（锉）3分，枳壳（去瓤，麸炒）3分，厚朴（去粗皮，涂生姜汁炙）3分，黄耆（锉）3分。', '每服3钱匕，水1盏半，先煮猪肾1只，取汁1盏，去肾入药，加生姜3片，葱白3寸，同煎7分，去滓温服，不拘时候。', '《圣济总录》卷一六四', '产后蓐劳。寒热，日渐瘦损。', '-', '-', '柴胡', '[\'柴胡\', \'甘草\', \'人参\', \'白茯苓\', \'黑皮\', \'当归\', \'炒分\', \'芍药\', \'锉分\', \'枳壳\', \'炒分\', \'厚朴\', \'粗皮\', \'涂生\', \'姜汁\', \'炙分\', \'黄耆\']');
INSERT INTO `prescript` VALUES (57869, '17100', '荆芥散', '荆芥3分，芎藭半两，人参3分（去芦头），当归半两（锉碎，微炒），白术3分，桂心3分（去苗），防风半两（去芦头），生干地黄3分，柴胡3分（去苗），鳖甲3分（涂醋，炙令黄，去裙襕），牡丹半两，赤芍药半两，枳壳3分（麸炒微黄，去瓤），羚羊角屑3分，酸枣仁3分（微炒），甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，入生姜半分，煎至6分，去滓，食前温服。', '《圣惠》卷七十', '妇人血风劳气，经脉涩滞，四肢拘急烦疼，不能饮食，渐加羸弱。', '-', '-', '柴胡', '[\'荆芥\', \'半两\', \'人参\', \'分去\', \'芦头\', \'当归\', \'半两\', \'白术\', \'分桂心\', \'分去\', \'防风\', \'半两\', \'去芦\', \'头生\', \'地黄\', \'柴胡\', \'分去\', \'鳖甲\', \'分涂\', \'炙令\', \'黄去\', \'牡丹\', \'半两\', \'芍药\', \'半两\', \'枳壳\', \'分麸\', \'微黄\', \'羚羊角\', \'酸枣仁\', \'分微\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57870, '17095', '金液汤', '软前胡1钱，白桔梗8分，直防风1钱，川独活3分，京芍药1钱，肥知母5分，荆芥穗5分，苏薄荷6分，蔓荆子7分（炒），北柴胡1钱（炒），片黄芩5分（炒）。', '上咀片，水煎，热服。如初发赤眼，服药6-7剂可愈，且无后患。如屡发者，风邪积热，入在经络、遇寒即发，服金液汤10余剂后，或作散，或作丸服，调理34日，外用玉华丹日点1次，即愈。如受风寒重者，初2剂加羌活5分，小川芎2分，白芷梢2分，后服仍去；如泪多者，加北细辛2分，家园菊5分；如肿胀者，加葶苈子3分；如痛甚者，加厚黄柏3分；如红甚者，加连翘3分，桑白皮4分，牡丹皮6分、红花3分；如翳膜者，加木贼4分，白蒺藜8分；如翳障胬肉者，加石决明1钱（煅）；如昏蒙者，加密蒙花8分，家白菊5分；如大眦红者，加栀仁7分（', '《一草亭》', '外障。', '如体虚者，须用加减地黄丸，空心服。饭后用金液散，此法最妙，如服金液汤，须饭后热服。每日只服1剂，不可骤进，恐伤胃气；服至42日，自愈。如外障等症，多是有余，不可妄投补剂，恐助邪，为害不浅。', '-', '柴胡', '[\'软前\', \'胡钱白\', \'桔梗\', \'分直\', \'防风\', \'钱川\', \'独活\', \'分京\', \'芍药\', \'钱肥\', \'知母\', \'荆芥\', \'分苏\', \'薄荷\', \'蔓荆子\', \'炒北\', \'柴胡\', \'炒片\', \'黄芩\']');
INSERT INTO `prescript` VALUES (57871, '17089', '豉心粥', '豆豉心2合（以百沸汤泡，细研），桃仁（汤浸，去皮尖，研）30枚，柴胡（去苗，末）3钱。', '上先将前2味，以白米3合水，似例程煮粥，后欲熟时，次入柴胡末3钱，搅匀食之。', '《圣济总录》卷一九○', '诸种疟疾，往来寒热。', '-', '-', '柴胡', '[\'豆豉\', \'心合\', \'以百\', \'沸汤\', \'泡细研\', \'桃仁\', \'汤浸\', \'皮尖\', \'研枚\', \'柴胡\', \'苗末\']');
INSERT INTO `prescript` VALUES (57872, '17087', '柴胡猪苓汤', '柴胡（去苗）1两半，猪苓（去黑皮）1两半，紫苏茎叶（细锉）1两半，陈橘皮（汤去白，焙），防己，大麻仁（炒，研），郁李仁（汤去皮尖，炒，研），桑根白皮（锉碎，炒令黄）2两半。', '每服3钱匕，以水1盏，煎至7分，去滓，空腹服，日午晡时再服。', '《圣济总录》卷八十一', '风寒湿毒脚气，疼痛，皮肤不仁，缓弱（疒帬）痹，足胫肿满，心下急，大便涩。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两半\', \'猪苓\', \'黑皮\', \'两半\', \'紫苏\', \'叶细\', \'两半\', \'橘皮\', \'汤去\', \'防己\', \'麻仁\', \'炒研\', \'郁李仁\', \'汤去\', \'皮尖\', \'研桑根\', \'白皮\', \'炒令\', \'两半\']');
INSERT INTO `prescript` VALUES (57873, '17068', '补气升阳和中汤', '生甘草1钱，酒黄柏1钱，白茯苓1钱，泽泻1钱，升麻1钱，柴胡1钱，苍术1钱5分，草豆蔻仁1钱5分，橘皮2钱，当归身2钱，白术2钱，白芍药3钱，人参3钱，佛耳草4钱，炙甘草4钱，黄耆5钱。', '上(口父)咀。每服5钱，水2盏，煎至1盏，去滓，空腹服。', '《兰室秘藏》卷中', '风热下陷，阳气不行，闭目则浑身麻木，昼减而夜甚，觉而开目，则麻木渐退，久则绝止，常开其目，此证不作，惧其麻木不敢合眼，致不得眠，身体昏重，时有痰嗽，觉胸中常似有痰而不利时，烦躁，气短促而喘，诊得六脉俱中得弦洪缓相合，按之无力。', '-', '-', '柴胡', '[\'生甘草\', \'钱酒\', \'黄柏\', \'白茯苓\', \'泽泻\', \'升麻\', \'柴胡\', \'苍术\', \'钱分\', \'草豆蔻\', \'仁钱分\', \'橘皮\', \'当归\', \'身钱\', \'白术\', \'钱白\', \'芍药\', \'人参\', \'佛耳草\', \'甘草\', \'黄耆\']');
