INSERT INTO `prescript` VALUES (57884, '16841', '大腹皮汤', '大腹皮（锉，炒）3分，柴胡（去苗）2两，白茯苓（去黑皮）1两，桂（去粗皮）1两，半夏（汤浸去滑，生姜汁同炒干）1两，青蒿（童便浸1日，晒干）1两，白术1两，桔梗（炒）1两，黄芩（去黑心）1两，山栀子（去皮）1两。', '每服5钱匕，水1盏，童便半盏，煎至1盏，去滓温服。如妇人服，加虎杖、当归各少许。', '《圣济总录》卷八十七', '热劳，肌瘦盗汗，潮热咳嗽。', '-', '-', '柴胡', '[\'大腹皮\', \'炒分\', \'柴胡\', \'白茯苓\', \'黑皮\', \'两桂去\', \'粗皮\', \'半夏\', \'汤浸\', \'滑生\', \'姜汁\', \'青蒿\', \'童便\', \'日晒\', \'白术\', \'桔梗\', \'黄芩\', \'黑心\', \'两山\', \'栀子\', \'去皮\']');
INSERT INTO `prescript` VALUES (57885, '16837', '柴胡六合汤', '四物汤4两，柴胡7钱，黄芩7钱。', '柴胡四物汤（《金鉴》卷七十四）。', '《元戎》', '妊娠伤寒，胸胁满痛，烦躁，蓄血呕血；妇人经行身热，及产妇头昏项强，脉弦或数。少阳。胁痛。', '-', '-', '柴胡', '[\'四物汤\', \'柴胡\', \'黄芩\']');
INSERT INTO `prescript` VALUES (57886, '16832', '柴胡茵陈五苓散', '五苓散1两，茵陈半两，车前子1钱，木通1钱半，柴胡1钱半。', '上分2服。用水1钟半，灯草50茎煎服，连进数服。小便清利而愈。酒后，加干葛2钱。', '《景岳全书》卷五十四', '伤寒温湿热病，汗下太早，湿热未除，以致遍身发黄，小便赤黑，烦渴发热。', '-', '-', '柴胡', '[\'五苓散\', \'茵陈\', \'半两\', \'车前子\', \'木通\', \'钱半\', \'柴胡\', \'钱半\']');
INSERT INTO `prescript` VALUES (57887, '16827', '柴青泻肝汤', '柴胡1钱半，黄芩1钱半，人参1钱，半夏1钱，黄连1钱，青皮1钱，甘草5分。', '水煎服。', '《济阳纲目》卷二十五', '男子肝火旺极，阴茎肿裂，健硬不休。', '-', '-', '柴胡', '[\'柴胡\', \'钱半\', \'黄芩\', \'钱半\', \'人参\', \'半夏\', \'黄连\', \'青皮\', \'甘草\']');
