INSERT INTO `prescript` VALUES (57889, '16785', '黄芩散', '黄芩1两，川升麻1两，赤芍药半两，麦门冬半两（去心），石膏2两，柴胡1两（去苗），甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十一', '阳毒伤寒。气盛，昏昏如醉，热躁烦渴，口苦舌干。', '-', '-', '柴胡', '[\'黄芩\', \'两川\', \'升麻\', \'两赤\', \'芍药\', \'半两\', \'麦门冬\', \'半两\', \'去心\', \'石膏\', \'柴胡\', \'两去\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57890, '16783', '和肝醒脾化湿丸', '醋柴胡3钱，青皮4钱（炒），炙香附6钱，白芍4钱（炒焦），藿香梗4钱，厚朴4钱（紫老），新会皮4钱，苍术4钱（炒），落水沉3钱，于术3钱（炒焦），白茯苓6钱，广砂3钱（仁），炒谷芽6钱，木香3钱，东山楂8钱（肉），枳实4钱（炒）。', '每服2钱5分，白开水送下。', '《慈禧光绪医方选议》', '和肝醒脾化湿。主脾胃病。', '-', '-', '柴胡', '[\'柴胡\', \'青皮\', \'香附\', \'白芍\', \'藿香\', \'厚朴\', \'老新会\', \'苍术\', \'落水\', \'沉钱\', \'于术\', \'白茯苓\', \'钱广砂\', \'钱仁\', \'谷芽\', \'木香\', \'钱东\', \'山楂\', \'钱肉\', \'枳实\']');
INSERT INTO `prescript` VALUES (57891, '16780', '黄耆散', '黄耆1两（锉），人参1两（去芦头），白茯苓2两，柴胡半两（去苗），当归半两，白术1两，桂心半两，甘草半两（炙微赤，锉），枳壳半两（麸炒微黄，去瓤），桔梗半两（去芦头），桃仁半两（汤浸，去皮尖双仁，麸炒微黄）。', '每服4钱，以水1中盏，加生姜半分，大枣3枚，煎至6分，去滓温服，不拘时候。', '《圣惠》卷二十九', '虚劳寒热，不能饮食，四肢羸瘦少力。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'两去\', \'芦头\', \'白茯苓\', \'柴胡\', \'半两\', \'去苗\', \'当归\', \'半两\', \'白术\', \'两桂心\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'枳壳\', \'半两\', \'微黄\', \'桔梗\', \'半两\', \'芦头\', \'桃仁\', \'半两\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (57892, '16773', '绝疟药枣', '白术1钱，黄芩1钱，槟榔1钱，常山（醋酒煮）1钱，半夏1钱，柴胡8分，茯苓7分，橘红5分，炙甘草3分，白姜蚕（炙黄）3条，乌梅1枚，生姜5钱，红枣49枚。', '水、酒各1大碗，用武火煮3-5滚后，再用文火煮1枝香，去滓，露1宿，黎明空心热服，即以枣子过药一齐吃完，毋剩。', '《活人方》卷三', '和中健脾，清痰止疟。主疟。', '-', '-', '柴胡', '[\'白术\', \'黄芩\', \'槟榔\', \'常山\', \'半夏\', \'柴胡\', \'茯苓\', \'橘红\', \'甘草\', \'分白\', \'姜蚕\', \'黄条\', \'乌梅\', \'生姜\', \'红枣\']');
