INSERT INTO `prescript` VALUES (57894, '16751', '金银平怒散', '金银花2两，白芍5钱，当归1两，柴胡1钱，白芥子3钱，生甘草3钱，炒栀子3钱，丹皮3钱。', '水煎服。1剂即消，2剂痊愈。', '《洞天奥旨》卷六', '胁痛生痈。', '-', '-', '柴胡', '[\'金银花\', \'白芍\', \'当归\', \'柴胡\', \'白芥子\', \'生甘草\', \'栀子\', \'丹皮\']');
