INSERT INTO `prescript` VALUES (57896, '16670', '柴术参苓汤', '白术（炒）1钱，人参1钱，茯苓1钱，柴胡5分，川芎5分，山栀5分，芍药（炒）5分，甘草（炒）5分，熟地（自制）8分，当归8分。', '水煎服。', '《校注妇人良方》卷二十四', '妇人肝火血热，遍身瘙痒，或起赤晕，或筋挛结核。', '-', '-', '柴胡', '[\'白术\', \'人参\', \'茯苓\', \'柴胡\', \'川芎\', \'分山\', \'栀分\', \'芍药\', \'炒分\', \'甘草\', \'炒分\', \'熟地\', \'自制\', \'当归\']');
INSERT INTO `prescript` VALUES (57897, '16668', '柴胡片姜散', '归尖2钱5分，赤芍1钱5分，柴胡1钱，片姜3钱，桃仁11粒，红花1钱，花粉1钱5分，山林1钱，甲珠1钱5分，石菖1钱。', '-', '《青囊全集》卷上', '两胁坚硬，兼腹痛瘀凝。', '-', '-', '柴胡', '[\'归尖\', \'钱分\', \'赤芍\', \'钱分\', \'柴胡\', \'钱片\', \'姜钱\', \'桃仁\', \'粒红\', \'花钱\', \'花粉\', \'钱分\', \'山林\', \'钱甲珠\', \'钱分\', \'石菖钱\']');
INSERT INTO `prescript` VALUES (57898, '16629', '茯神丸', '茯神（去木）2两，生干地黄（洗，切，焙）2两，鳖甲（九肋者，醋炙，去裙襕）1两，桔梗（去芦头，切，炒）1两，人参1两，升麻1两，大腹（炮）1两，防风（去叉）1两，黄芩（去黑心）1两，白前1两，枳壳（去瓤，麸炒）1两半，赤芍药1两半，柴胡（去苗）1两半，黄耆（薄切）1两半。', '每服10丸，食后生姜汤送下。', '《圣济总录》卷四十三', '心实壅热，口苦舌干，涕唾稠粘，胸膈烦闷，不思饮食，肢体倦怠，或发烦热，状似骨蒸。', '-', '-', '柴胡', '[\'茯神\', \'两生\', \'地黄\', \'洗切\', \'鳖甲\', \'九肋者\', \'桔梗\', \'芦头切\', \'人参\', \'升麻\', \'两大\', \'腹炮\', \'防风\', \'黄芩\', \'黑心\', \'两白前\', \'枳壳\', \'两半\', \'芍药\', \'两半\', \'柴胡\', \'去苗\', \'两半\', \'黄耆\', \'薄切\', \'两半\']');
INSERT INTO `prescript` VALUES (57899, '16595', '复明散', '青皮3分，橘皮5分，川芎5分，苍术5分，炙甘草1钱，生地黄1钱，连翘1钱，柴胡1钱，黄耆1钱5分，当归身2钱。', '以水2大盏，去滓，食后稍热服。', '《兰室秘藏》卷上', '内障。', '忌酒、醋、湿面、辛热，大料物之类。', '-', '柴胡', '[\'青皮\', \'橘皮\', \'川芎\', \'苍术\', \'甘草\', \'生地黄\', \'连翘\', \'柴胡\', \'黄耆\', \'钱分\', \'当归\', \'身钱\']');
INSERT INTO `prescript` VALUES (57900, '16533', '茯苓丸', '白茯苓（去黑皮）1两半，赤芍药1两，柴胡（去苗）1两，百合1两，诃黎勒皮1两，羚羊角（镑）1两，陈橘皮（汤浸，去白，焙）1两，防风（去叉）1两，菊花1两，郁李仁（去皮，炒）1两半，大麻仁（研）4两，生干地黄（焙）3两。', '每服30丸，煎麦门冬汤送下。', '《圣济总录》卷十二', '风热攻头面虚浮，心下满闷，烦躁热渴，腰胯酸疼，咳逆咽干，小便赤涩。', '-', '-', '柴胡', '[\'白茯苓\', \'黑皮\', \'两半\', \'芍药\', \'柴胡\', \'百合\', \'两诃黎勒皮\', \'羚羊角\', \'橘皮\', \'汤浸\', \'防风\', \'菊花\', \'郁李仁\', \'去皮\', \'两半\', \'麻仁\', \'研两生\', \'地黄\']');
INSERT INTO `prescript` VALUES (57901, '16523', '柴胡解毒汤', '柴胡5-20g，黄芩5-10g，白芍5-10g，芒消5-10g，黄连3-5g，郁金3-5g，广木香3-5g，姜半夏3-5g，大黄3-5g，栀子3-5g，甘草3-5g，夏枯草10g，茵陈（后下）10g。', '热重，重用柴胡、黄芩，选加紫地丁、野菊花、黄连；大热、大渴、脉洪大者，加石膏、知母、天花粉、鲜芦根；湿重，重用茵陈、郁金、金钱草、栀子、大黄；痛重，加玄胡、川楝子；呕吐，加半夏、竹茹、生姜；驱蛔，加苦楝根皮、槟榔、使君子；瘀血，加丹参、川芎、红花；气阴欲脱，加独参汤、生脉散。', '《古今名方》', '疏肝清热，通里攻下。主胆道感染湿热型或实火型。症见身热，口干，百苔黄腻，脉洪大。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'白芍\', \'芒消\', \'黄连\', \'郁金\', \'木香\', \'半夏\', \'大黄\', \'栀子\', \'甘草\', \'夏枯草\', \'茵陈\']');
INSERT INTO `prescript` VALUES (57902, '16509', '假补中益气汤', '上党参1两，炙黄耆5钱，当归2钱，甜白术3钱，陈皮1钱5分，炙甘草5分，柴胡（炒）5分，升麻（炒）8分，生姜2钱，大枣5枚。', '水煎服。或8剂，或10剂，正气自复。脾虚饮食难克者，加炒枳壳、白蔻仁、神曲。', '《慈航集》卷上', '病后气弱之证。', '-', '-', '柴胡', '[\'党参\', \'黄耆\', \'当归\', \'白术\', \'陈皮\', \'钱分\', \'甘草\', \'柴胡\', \'炒分\', \'升麻\', \'炒分\', \'生姜\', \'大枣\']');
