INSERT INTO `prescript` VALUES (57924, '16258', '麦门冬汤', '麦门冬（去心）1两半，升麻1两，知母（锉，焙）1两，甘草（炙，锉）1两，鳖甲（醋炙，去裙襕）1两，柴胡（去苗）1两，前胡（去芦头）1两，桃仁（去皮尖双仁，炒研）1两，枳壳（去瓤，麸炒）1两，栀子（去皮）半两，芦根（锉）半两，乌梅肉（炒）半两，人参3分。', '每服3钱匕，水1盏半，加桃枝5寸，柳枝5寸（锉），生姜3片，煎至8分，去滓，入石膏末半钱匕，更煎沸，未发前1-2服。', '《圣济总录》卷三十六', '兼补心气。主肺疟。', '-', '-', '柴胡', '[\'麦门冬\', \'去心\', \'两半\', \'升麻\', \'知母\', \'甘草\', \'鳖甲\', \'柴胡\', \'去苗\', \'两前\', \'胡去\', \'芦头\', \'桃仁\', \'皮尖\', \'双仁\', \'研两\', \'枳壳\', \'栀子\', \'去皮\', \'半两\', \'芦根\', \'半两\', \'乌梅\', \'半两\', \'人参\']');
INSERT INTO `prescript` VALUES (57925, '16213', '肝痿汤', '生地2钱，当归1钱，白芍1钱，柴胡8分，玄参8分，栀子8分，花粉1钱，丹皮8分。', '水煎服。', '《脉症正宗》卷一', '肝痿。', '-', '-', '柴胡', '[\'生地\', \'当归\', \'白芍\', \'柴胡\', \'玄参\', \'栀子\', \'花粉\', \'丹皮\']');
INSERT INTO `prescript` VALUES (57926, '16209', '除疯丸', '枫子1斤（酒浸，秋冬5日，夏3日，研碎入药），人参5钱，防己1两，没药1两，官桂5钱，白芍1两，荆芥1两，防风1两5钱，当归1两，独活2两，羌活1两，玄胡索1两，大黄1两，蝉退7钱，苍术1两，半夏1两5钱，姜黄1两5钱，麻黄1两5钱，干姜1两5钱，甘草1两5钱，柴胡1两，枳壳1两，石楠叶1两，黑丑1两5钱，杏仁（去皮尖）1两，乌药1两，地骨皮1两，乳香1两，陈皮1两，黄芩2两，胡麻（炒）2两，香蛇1两，黄柏2两，苦参2两，穿甲7钱5分，黄连6钱5分，甘菊2钱2分，灵仙5钱，蔓荆子1两（炒）。', '每服50丸，空心以酒送下，日3次。', '《秘传大麻疯方》', '大麻疯。', '-', '-', '柴胡', '[\'枫子斤\', \'秋冬\', \'夏日\', \'研碎\', \'入药\', \'人参\', \'防己\', \'没药\', \'两官\', \'桂钱\', \'白芍\', \'荆芥\', \'防风\', \'当归\', \'独活\', \'羌活\', \'两玄\', \'胡索\', \'大黄\', \'两蝉\', \'退钱\', \'苍术\', \'半夏\', \'姜黄\', \'麻黄\', \'干姜\', \'甘草\', \'柴胡\', \'枳壳\', \'石楠叶\', \'两黑\', \'杏仁\', \'皮尖\', \'乌药\', \'地骨皮\', \'乳香\', \'陈皮\', \'黄芩\', \'胡麻\', \'两香蛇\', \'黄柏\', \'苦参\', \'穿甲\', \'钱分\', \'黄连\', \'钱分\', \'甘菊\', \'钱分\', \'灵仙\', \'蔓荆子\']');
INSERT INTO `prescript` VALUES (57927, '16186', '柴防煎', '柴胡2钱，防风2钱，桔梗2钱，甘草1钱。', '水煎服。', '《不知医必要》卷二', '温热时疟。', '-', '-', '柴胡', '[\'柴胡\', \'防风\', \'桔梗\', \'甘草\']');
INSERT INTO `prescript` VALUES (57928, '16179', '橘皮散', '陈橘皮2两（汤浸，去白瓤，焙），紫苏子1两（微炒），人参1两（去芦头），赤茯苓1两，柴胡1两（去苗），杏仁1两（汤浸，去皮尖双仁，麸炒微黄）。', '每服3钱，以水1中盏，加生姜半分，大枣3枚，煎至6分，去滓温服，不拘时候。', '《圣惠》卷四十二', '上气呕吐，不能下食。', '-', '-', '柴胡', '[\'橘皮\', \'两汤\', \'浸去\', \'紫苏子\', \'两微\', \'人参\', \'两去\', \'芦头\', \'茯苓\', \'柴胡\', \'两去\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (57929, '16178', '柴胡散', '柴胡（去苗）1两，大黄半两，秦艽半两，甘草半两，常山半两，干漆7钱（炒），鳖甲1两半（炙黄）。', '每服2钱，水1盏，加小麦2钱，同煎6分，去滓温服。加人参、石膏各半两妙。', '《永乐大典》卷八○二○引《经验普济本事方》', '童男室女，骨蒸热劳；或因伤寒时疾后，余热不解，瘦弱，减食，困倦，寒热嗽喘，面赤口干，眼涩多睡。', '-', '-', '柴胡', '[\'柴胡\', \'大黄\', \'半两\', \'秦艽\', \'半两\', \'甘草\', \'半两\', \'常山\', \'半两\', \'干漆\', \'鳖甲\', \'两半\']');
INSERT INTO `prescript` VALUES (57930, '16133', '金露丸', '厚朴2分（去皮，姜制），柴胡（去芦）1分，桔梗（去芦）1分，附子1个（炮），大黄3分，紫花术（炒）3分，干姜（炮）半两，川椒（去合目者）半两，吴茱萸半两，白茯苓2钱，人参（去芦）2钱，川乌（炮）2钱，官桂（去皮）2钱，猪牙皂角（去皮）2钱，菖蒲2钱。', '空心服。', '《医方大成》卷十引《汤氏方》', '小儿劳瘵，尸虫作痛，面目羸瘦，五心烦热。心腹胀满，症癖气块，大如鹅卵者，及黄疸朝起呕逆，四肢沉重，上气盘结，血气冲心，膀胱两胁膨胀，背连腰痛，十种水气，五淋五痔，骨蒸顽麻，半身不遂，眉毛脱落，皮肤燥痒，常如虫行，手足烦热，夜卧惊悸，梦与鬼交，年月深远，行成劳病，尸虫鬼疰，久不能疗；及治室女经闭阻滞，血脉不通，羸瘦憔悴，不思饮食。', '-', '-', '柴胡', '[\'厚朴\', \'去皮\', \'姜制\', \'柴胡\', \'芦分\', \'桔梗\', \'芦分\', \'附子\', \'个炮\', \'大黄\', \'紫花\', \'炒分\', \'干姜\', \'半两\', \'川椒\', \'合目者\', \'半两\', \'吴茱萸\', \'半两\', \'白茯苓\', \'人参\', \'芦钱\', \'川乌\', \'炮钱\', \'官桂\', \'去皮\', \'猪牙皂\', \'去皮\', \'菖蒲\']');
INSERT INTO `prescript` VALUES (57931, '16132', '大香甲散', '沉香半两，鳖甲（汤浸，去裙襕，炙令黄香用）1两，柴胡（去芦）半两，人参半两，桔梗半两，茯苓（去皮）半两，川芎半两，藿香叶半两，羌活半两，木香半两，陈橘皮（去白）半两，牡丹皮半两，安息香半两，当归半两，厚朴半两（姜汁炙令香），京三棱半两（炮），官桂（去皮）半两，附子（炮，去皮脐）半两，牛膝（去苗）半两，桃仁（汤浸，去皮尖）半两，和皮大腹子1分。', '大香甲丸散（《妇人良方》卷五）。', '《博济》卷四', '补血海，调气。主妇人血脏风虚冷气，肌肉黄瘦，饮食进退，经候不匀，心腹多胀，渐变如劳。', '-', '-', '柴胡', '[\'沉香\', \'半两\', \'鳖甲\', \'汤浸\', \'炙令\', \'黄香用\', \'柴胡\', \'去芦\', \'半两\', \'人参\', \'半两\', \'桔梗\', \'半两\', \'茯苓\', \'去皮\', \'半两\', \'川芎\', \'半两\', \'藿香\', \'半两\', \'羌活\', \'半两\', \'木香\', \'半两\', \'橘皮\', \'半两\', \'牡丹皮\', \'半两\', \'安息香\', \'半两\', \'当归\', \'半两\', \'厚朴\', \'半两\', \'姜汁\', \'炙令\', \'香京\', \'三棱\', \'半两\', \'炮官桂\', \'去皮\', \'半两\', \'附子\', \'去皮\', \'半两\', \'牛膝\', \'去苗\', \'半两\', \'桃仁\', \'汤浸\', \'皮尖\', \'半两\', \'皮大\', \'腹子分\']');
INSERT INTO `prescript` VALUES (57932, '16075', '柴胡汤', '柴胡（去苗）半两，酸枣仁（微炒）2两，远志（去心）1分，当归（切，焙）半两，防风（去叉）半两，甘草（炙，锉）半两，茯神（去木）半两，猪苓（去黑皮）半两，桂（去粗皮）半两，黄耆（锉）半两，人参半两，生干地黄半两，芎藭半两，麦门冬（去心，焙）半两。', '每服3钱匕，水1大盏，加生姜3片，煎至7分，去滓，空心温服，1日2次。', '《圣济总录》卷三十一', '伤寒后虚劳烦热，惊悸，不得眠睡。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'半两\', \'酸枣仁\', \'远志\', \'心分\', \'当归\', \'切焙\', \'半两\', \'防风\', \'半两\', \'甘草\', \'半两\', \'茯神\', \'半两\', \'猪苓\', \'黑皮\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'黄耆\', \'半两\', \'人参\', \'半两\', \'生干\', \'地黄\', \'半两\', \'半两\', \'麦门冬\', \'半两\']');
INSERT INTO `prescript` VALUES (57933, '16073', '定志紫葳丸', '紫葳6分，远志15分（去心），白龙骨7分，牛黄1两，甘草10分（炙），虎头皮12分（炙令焦），人参8分，桂心8分，白术8分，防风7分，麦门冬（去心，熬）5分，雷丸5分，柴胡6分。', '每服10丸，食前服，1日3次。', '《外台》卷十五引《古今录验》', '五惊，喜怒不安。', '忌食海藻、菘菜、桃李，生葱。', '-', '柴胡', '[\'紫葳\', \'远志\', \'分去\', \'心白\', \'龙骨\', \'牛黄\', \'甘草\', \'分炙虎\', \'头皮\', \'分炙令\', \'人参\', \'分桂心分\', \'白术\', \'防风\', \'麦门冬\', \'雷丸\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57934, '16070', '姜椒丸', '蜀椒（去目及合口，炒出汗）1两1分，干姜（炮）1两，厚朴（去粗皮，涂生姜汁炙）1两，黄芩（去黑心）1两，细辛（去苗叶）1两，芍药1两，桂（去皮）1两，桔梗（炒）半两，乌喙（炮裂，去皮脐）1分，柴胡（去苗）1分，白茯苓（去黑皮）1分，牡丹皮1分。', '每服20丸，温酒或米饮送下，1日3次。', '《普济方》卷二四七', '七疝诸寒，在脐旁痛，上及胸，中满少气。', '-', '-', '柴胡', '[\'蜀椒\', \'目及\', \'合口\', \'出汗\', \'两分\', \'干姜\', \'厚朴\', \'粗皮\', \'涂生\', \'姜汁\', \'黄芩\', \'黑心\', \'细辛\', \'苗叶\', \'芍药\', \'两桂\', \'去皮\', \'桔梗\', \'半两\', \'炮裂\', \'去皮\', \'脐分\', \'柴胡\', \'苗分\', \'白茯苓\', \'黑皮\', \'牡丹皮\']');
INSERT INTO `prescript` VALUES (57935, '16057', '柴胡散', '柴胡1两（去苗），生干地黄1两，黄连1两（去须），地骨皮1两，枳壳1分（麸炒微黄，去瓤），赤茯苓1两，甘草半两（炙微赤，锉），知母半两，鳖甲3分（涂醋炙令微黄，去裙襕）。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十八', '热病后虚劳烦热，四肢疼痛，小便赤黄，不欲饮食。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'苗生\', \'地黄\', \'黄连\', \'两去\', \'地骨皮\', \'枳壳\', \'分麸\', \'微黄\', \'茯苓\', \'甘草\', \'半两\', \'炙微\', \'知母\', \'半两\', \'鳖甲\', \'分涂\', \'炙令\', \'微黄\']');
INSERT INTO `prescript` VALUES (57936, '16053', '沉香四宝丹', '川芎1两，柴胡1两，藿香1两，片姜黄1两，甘草1两，木香1两，公丁香1两，香附（炙）4两，橘皮4两，厚朴（炙）2两，黑郁金2两，砂仁1两5钱，豆蔻仁1两5钱，青皮（炒）8两，山楂6两，二丑（焦）5两，玄胡（炙）2两，佛手2两，沉香2两，冰片2两，枳壳（炒）4两，白芍4两。', '每服2钱，以温开水送下，1日2次。', '《北京市中药成方选集》', '健胃宽胸，舒郁化滞。主气郁不舒，痞满腹胀，吞酸倒饱，呃逆嘈杂。', '孕妇忌服。', '-', '柴胡', '[\'川芎\', \'柴胡\', \'藿香\', \'两片\', \'姜黄\', \'甘草\', \'木香\', \'两公\', \'丁香\', \'香附\', \'橘皮\', \'厚朴\', \'郁金\', \'砂仁\', \'豆蔻\', \'仁两钱\', \'青皮\', \'山楂\', \'两二丑\', \'焦两玄\', \'胡炙两\', \'佛手\', \'沉香\', \'冰片\', \'枳壳\', \'白芍\']');
