INSERT INTO `prescript` VALUES (57958, '15747', '柴胡汤', '柴胡（去苗）1两，枳实（麸炒令黄色）1两，知母1两，黄芩1两，栀子仁1两，麦门冬（去心，焙）1两，龙胆（去芦头）1两，川大黄（锉碎，微炒）1两，甘草（炙微赤，锉）1两。', '本方方名，《医方类聚》引作“柴胡散”。', '《圣惠》卷十七', '热病5日，壮热，骨节烦疼，目眩，胁下胀痛，不能饮食，欲变成黄。', '-', '-', '柴胡', '[\'柴胡\', \'枳实\', \'炒令\', \'黄色\', \'知母\', \'黄芩\', \'栀子\', \'麦门冬\', \'龙胆\', \'芦头\', \'两川\', \'大黄\', \'甘草\', \'炙微\']');
INSERT INTO `prescript` VALUES (57959, '15743', '柴胡知母汤', '柴胡（去苗）1两，知母（焙）3分，鳖甲（去裙襕，醋炙）1两，石膏（捣碎）1两半，雄鼠粪（炒）3-7粒，秦艽（去苗土）半两', '每服5钱匕，水1盏半，入鼓100粒，同煎至8分，去滓，食后温服。', '《圣济总录》卷三十一', '伤寒愈后，因食劳复如初，壮热头疼。', '-', '-', '柴胡', '[\'柴胡\', \'知母\', \'焙分\', \'鳖甲\', \'石膏\', \'捣碎\', \'两半\', \'雄鼠\', \'秦艽\', \'苗土\', \'半两\']');
INSERT INTO `prescript` VALUES (57960, '15708', '柴胡汤', '柴胡（去苗）1两，常山（锉）1两，鳖甲（去裙襕，醋炙令黄色）1两，知母（焙）1两，青蒿（干者）1两，甘草（炙，锉）1两，枳壳（去瓢，麸炒）1两，桂（去粗皮）1两。', '每服3钱匕。水1盏，加柳枝心7枚，葱白2寸（细切），同煎至7分，去滓，空腹温服，发前再服。', '《圣济总录》卷三十六', '足太阳疟，寒热。', '-', '-', '柴胡', '[\'柴胡\', \'常山\', \'鳖甲\', \'炙令\', \'黄色\', \'知母\', \'青蒿\', \'干者\', \'甘草\', \'枳壳\', \'两桂去\', \'粗皮\']');
INSERT INTO `prescript` VALUES (57961, '15660', '大连翘饮', '连翘4分，瞿麦4分，滑石4分，车前子4分，牛蒡子4分，赤芍4分，栀子4分，木通4分，当归4分，防风4分，柴胡1钱2分，黄芩1钱2分，荆芥1钱2分，蝉蜕5分，甘草1钱6分。', '加竹叶10个，灯心10茎，水煎，温服，不拘时候。风痰热、变蒸，加麦冬；实热、丹热，加大黄；胎热、疮疹余毒，加薄荷叶；痈疖、热毒，加大黄、芒消。', '《回春》卷七', '小儿伤风感冒，发热痰壅，风热丹毒肿痛，颈项有核，腮赤痈疖，眼目赤肿，口舌生疮，咽喉疼痛，小便淋沥，胎毒，痘疹余毒，一切热毒；三焦积热，二便不利，重舌、木舌，咽痛疮疡。', '-', '-', '柴胡', '[\'连翘\', \'瞿麦分\', \'滑石\', \'车前子\', \'牛蒡子\', \'赤芍\', \'栀子\', \'木通\', \'当归\', \'防风\', \'柴胡\', \'钱分\', \'黄芩\', \'钱分\', \'荆芥\', \'钱分\', \'蝉蜕\', \'甘草\', \'钱分\']');
INSERT INTO `prescript` VALUES (57962, '15632', '除风湿羌活汤', '羌活1两，防风（去芦）1钱，苍术（酒浸，去皮）1钱，黄耆1钱，升麻7分，炙甘草5分，独活5分，柴胡5分，川芎3分，黄柏3分，橘皮3分，藁本3分，泽泻（去须）3分，猪苓（去黑皮）3分，茯苓3分，黄连（去须）1分。', '本方方名，《玉机微义》引作“羌活汤”。', '《脾胃论》卷中', '风湿热痹，痿证，眩晕麻木。湿气胜，风证不退，眩晕麻木不已者。着痹。热痹，或痛风挟热者，肌肉热极，体上如鼠走，唇裂。', '-', '-', '柴胡', '[\'羌活\', \'防风\', \'芦钱\', \'苍术\', \'酒浸\', \'去皮\', \'黄耆\', \'升麻\', \'甘草\', \'独活\', \'柴胡\', \'川芎\', \'黄柏\', \'橘皮\', \'藁本\', \'泽泻\', \'猪苓\', \'黑皮\', \'茯苓\', \'黄连\']');
