INSERT INTO `prescript` VALUES (57964, '15608', '钩藤饮', '钩藤3分，蚱蝉（去头足翅，炙）2枚，犀角屑（微炒）半两，麦门冬（去心，焙）半两，升麻半两，石膏（捣碎）3分，柴胡（去苗）半两，甘草（微炙）1分。', '每服2钱匕，水1小盏，煎至6分，去滓，下竹沥半合，重煎3-5沸，分温3服。空心、午后、夜卧各1次。', '《圣济总录》卷一六九', '小儿惊热，睡眠不稳。', '-', '-', '柴胡', '[\'钩藤\', \'蚱蝉\', \'头足\', \'犀角\', \'半两\', \'麦门冬\', \'半两\', \'升麻\', \'半两\', \'石膏\', \'捣碎\', \'柴胡\', \'去苗\', \'半两\', \'甘草\', \'微炙分\']');
