INSERT INTO `prescript` VALUES (57975, '15457', '柴胡散', '柴胡1两（去苗），半夏半两（汤洗7遍去滑），川大黄3分（锉碎，微炒），枳壳3分（麸炒微黄，去瓤），百合3分，桑根白皮1两（锉），麦门冬2两（去心），赤茯苓1两，秦艽3分（去苗），紫菀3分（洗，去苗土），黄芩3分，赤芍药3分，甘草半两（炙微赤，锉），鳖甲2两（涂醋炙令黄，去裙襕），知母3分，木通3分（锉）。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十', '妇人骨蒸劳热，咳嗽，胸膈痰壅，腹胁妨闷，不欲饮食。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'半夏\', \'半两\', \'洗遍\', \'滑川\', \'大黄\', \'枳壳\', \'分麸\', \'微黄\', \'百合\', \'分桑根\', \'白皮\', \'麦门冬\', \'两去\', \'茯苓\', \'秦艽\', \'分去\', \'紫菀\', \'分洗\', \'苗土\', \'黄芩\', \'分赤\', \'芍药\', \'甘草\', \'半两\', \'炙微\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'知母\', \'木通\']');
INSERT INTO `prescript` VALUES (57976, '15449', '独圣青金丸', '川巴豆3两（净肉），硫黄2两（不研。2味同用生绢袋盛，悬于瓷罐中，不得着底，以水煮3日3夜，如水竭即旋添，熟汤取出，弃硫黄，只用巴豆，去皮心），独活1两，柴胡1两，桔梗1两，干姜（炮）1两，防风1两（生），青黛3两。', '每服1-2丸，食伤虚肿，橘皮汤送下；霍乱吐泻及赤痢，甘草汤送下；白痢，干姜汤送下；赤白痢，干姜、甘草汤送下；水泻，冷水送下；小便不通，灯心汤送下；大便不通，米饮送下；妇人血气，当归酒送下；元气虚，炒茴香酒送下；腰痛，茱萸汤送下；小儿疳积，米饮送下；气疾，橘皮汤送下；气块症癖，热酒送下；伤寒头痛，甘草汤送下，临卧时服。如脏腑实热，临时更加减丸数。', '《幼幼新书》卷二十六引《灵苑方》', '小儿疳积。', '-', '-', '柴胡', '[\'巴豆\', \'两净肉\', \'硫黄\', \'研味\', \'同用\', \'生绢袋\', \'悬于\', \'瓷罐\', \'不得\', \'以水\', \'日夜\', \'如水竭\', \'即旋\', \'添熟\', \'取出\', \'硫黄\', \'只用\', \'巴豆\', \'去皮\', \'独活\', \'柴胡\', \'桔梗\', \'干姜\', \'防风\', \'两生\', \'青黛两\']');
INSERT INTO `prescript` VALUES (57977, '15408', '菊花散', '甘菊花1两，防风1两（去芦头），决明子1两，栀子仁1两，黄芩1两，车前子1两，川升麻1两，玄参1两，地骨皮1两，柴胡1两（去苗），麦门冬1两（去心），生干地黄1两，甘草1两（炙微赤，锉），羚羊角屑1两。', '每服3钱，以水1中盏，加淡竹叶2-7片，煎至6分，去滓，食后温服。', '《圣惠》卷三十二', '肝心壅热，眼涩痛。', '忌炙煿、油腻、热面、生果。', '-', '柴胡', '[\'甘菊花\', \'防风\', \'两去\', \'芦头\', \'决明子\', \'栀子\', \'黄芩\', \'车前子\', \'两川\', \'升麻\', \'玄参\', \'地骨皮\', \'柴胡\', \'两去\', \'麦门冬\', \'两去\', \'心生\', \'地黄\', \'甘草\', \'两炙微\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (57978, '15338', '柴苓汤', '柴胡1钱6分，半夏（汤泡7次）7分，黄芩6分，人参6分，甘草6分，白术7分半，猪苓7分半，茯苓7分半，泽泻1钱2分半，桂5分。', '水2盏，生姜3片，煎至1盏，温服。', '《丹溪心法附余》卷一', '分利阴阳，和解表里。主伤寒、温热病、伤暑、疟疾、痢疾等，邪在半表半里，症见发热，或寒热往来，或泻泄，小便不利者，以及小儿麻疹、痘疮、疝气见有上述症状者。', '-', '-', '柴胡', '[\'柴胡\', \'钱分\', \'半夏\', \'汤泡次\', \'黄芩\', \'人参\', \'甘草\', \'白术\', \'分半\', \'猪苓\', \'分半\', \'茯苓\', \'分半\', \'泽泻\', \'钱分\', \'半桂分\']');
INSERT INTO `prescript` VALUES (57979, '15323', '柴胡散', '柴胡1两（去苗），葵根3分，甘草1分（炙微赤，锉），当归3分（锉，微炒），白茅根3分（锉），石韦3分（去毛），木香3分，榆白皮3分（锉），木通3分（锉）。', '每服4钱，以水1中盏，煎至6分，去滓，食前温服。', '《圣惠》卷五十八', '劳淋。每小便茎中痛，卒不能出，引小腹急胀，淋沥，痛不止。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'苗葵\', \'根分\', \'甘草\', \'分炙微\', \'当归\', \'白茅根\', \'韦分\', \'木香\', \'分榆\', \'白皮\', \'木通\']');
INSERT INTO `prescript` VALUES (57980, '15322', '柴胡散', '柴胡1两半（去苗），石膏3两，人参1两（去芦头），茯神1两，枳实1两（麸炒微黄），桑根白皮1两（锉），麦门冬1两（去心），桂心3分，白术3分，防风3分（去芦头），地骨皮3分，甘草2分（炙微赤，锉）。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，日3服，夜1服。', '《圣惠》卷十', '伤寒中风，汗后虚燥，头痛，四肢乏力。', '-', '-', '柴胡', '[\'柴胡\', \'两半\', \'去苗\', \'石膏\', \'人参\', \'两去\', \'芦头\', \'茯神\', \'枳实\', \'两麸\', \'微黄\', \'桑根\', \'白皮\', \'麦门冬\', \'两去\', \'心桂心分\', \'白术\', \'防风\', \'分去\', \'芦头\', \'地骨皮\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (57981, '15307', '大黄丸', '川大黄1两（锉碎，微炒），川椒半两（去目及闭口者，微炒去汗），人参3分（去芦头），半夏3分（汤洗7遍，去滑），桔梗3分（去芦头），菖蒲3分，柴胡3分（去苗），赤茯苓3分，芎3分，桂心3分，桃仁3分（汤浸，去皮尖双仁，麸炒微黄），木香3分，吴茱萸3分（汤浸7遍，焙干，微炒），干姜3分（炮裂，锉），细辛3分。', '每服10丸，食前以温酒送下。渐加至20丸。', '《圣惠》卷四十二', '七气，积聚坚牢，心腹胀痛。', '-', '-', '柴胡', '[\'大黄\', \'碎微\', \'炒川椒\', \'半两\', \'目及\', \'闭口\', \'人参\', \'分去\', \'芦头\', \'半夏\', \'分汤\', \'洗遍\', \'桔梗\', \'分去\', \'芦头\', \'菖蒲\', \'柴胡\', \'分去\', \'茯苓\', \'分芎\', \'分桂心分\', \'桃仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'木香\', \'吴茱萸\', \'分汤\', \'浸遍\', \'焙干\', \'干姜\', \'分炮裂\', \'细辛\']');
