INSERT INTO `prescript` VALUES (57983, '15294', '大青散', '大青3分，远志3分（去心），川升麻1两半，柴胡1两（去苗），黄芩1两，犀角屑3分，人参3分（去芦头），甘草半两（炙微赤，锉），芦根半两（锉）。', '每服4钱，以水1中盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒邪热在胃，谵言妄语，身体壮热。', '-', '-', '柴胡', '[\'大青分\', \'远志\', \'分去\', \'心川\', \'升麻\', \'两半\', \'柴胡\', \'两去\', \'黄芩\', \'犀角\', \'人参\', \'分去\', \'芦头\', \'甘草\', \'半两\', \'炙微\', \'芦根\', \'半两\']');
INSERT INTO `prescript` VALUES (57984, '15286', '拌肝散', '茵陈1两，犀角屑半两，石斛半两（去根，锉），白术3分，赤芍药半两，柴胡3分（去苗），缩砂半两（去皮），人参3分（去芦头），桔梗3分（去芦头），防风半两（去芦头），肉桂3分（去皱皮），白芜荑仁半两，肉豆蔻半两（去壳）。', '用猪肝1叶，净去筋膜，不洗，薄切作片子，葱白3茎细切，入散5钱，重重掺在肝上，用湿纸5-7重裹，以慢火煨令熟，空心食，食后吃暖酒半盏。', '《圣惠》卷二十六', '脾劳虚冷，大肠滑泄，不思饮食，口舌生疮，四肢无力，日渐羸弱。', '-', '-', '柴胡', '[\'茵陈\', \'犀角\', \'半两\', \'石斛\', \'半两\', \'白术\', \'分赤\', \'芍药\', \'半两\', \'柴胡\', \'分去\', \'苗缩\', \'半两\', \'去皮\', \'人参\', \'分去\', \'芦头\', \'桔梗\', \'分去\', \'芦头\', \'防风\', \'半两\', \'芦头\', \'肉桂\', \'分去\', \'皱皮\', \'白芜\', \'半两\', \'肉豆蔻\', \'半两\']');
INSERT INTO `prescript` VALUES (57985, '15285', '活血饮', '滑石1钱5分，桃仁1钱，红花5分，桔梗5分，粉草4分，瓜蒌2钱，丹皮8分，茜草8分，贝母8分，柴胡5分，香附曲5分。', '水煎服；或作末以韭菜汁拌为丸服。', '《赤水玄珠》卷七', '怒气积血在胸胁，咳嗽年久不愈，每咳则隐隐而痛。', '-', '-', '柴胡', '[\'滑石\', \'钱分\', \'桃仁\', \'红花\', \'桔梗\', \'分粉\', \'草分瓜\', \'丹皮\', \'茜草\', \'贝母\', \'柴胡\', \'香附\', \'曲分\']');
