INSERT INTO `prescript` VALUES (57987, '15169', '茯神散', '茯神2两，柴胡2两（去苗），黄耆2两（锉），远志1两（去心），天门冬1两（去心），人参1两（去芦头），泽泻2两，生干地黄2两，甘草1两（炙微赤，锉）。', '每服3钱，以水1中盏，加淡竹叶2-7片，煎至6分，去滓温服，不拘时候。', '《圣惠》卷二十六', '精极。实热勇悍，多惊壮热。', '-', '-', '柴胡', '[\'茯神\', \'柴胡\', \'两去\', \'黄耆\', \'远志\', \'两去\', \'天门冬\', \'两去\', \'人参\', \'两去\', \'芦头\', \'泽泻\', \'两生\', \'地黄\', \'甘草\', \'两炙微\']');
INSERT INTO `prescript` VALUES (57988, '15150', '胡黄连丸', '胡黄连半两，柴胡1两（去苗），赤芍药3分，鳖甲2两（涂醋炙令黄，去裙襕），知母半两，犀角屑3分，川升麻半两，玄参半两，人参半两（去芦头），地骨皮3分，当归半两，杏仁3分（汤浸，去皮尖双仁，麸炒微黄），茯神3分，枳壳3分（麸炒微黄，去瓤），麦门冬1两半（去心，焙），紫菀3分（洗去苗土），川大黄3分（锉碎，微炒），甘草半两（炙微赤，锉），秦艽3分（去苗），槟榔半两，桔梗半两（去芦头）。', '每服30丸，以粥饮下，不拘时候。', '《圣惠》卷七十', '妇人热劳烦闷，四肢黄瘦疼痛，时有咳嗽，不欲饮食。', '-', '-', '柴胡', '[\'胡黄连\', \'半两\', \'柴胡\', \'两去\', \'芍药\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'知母\', \'半两\', \'犀角\', \'分川\', \'升麻\', \'半两\', \'玄参\', \'半两\', \'人参\', \'半两\', \'芦头\', \'地骨皮\', \'当归\', \'半两\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'茯神\', \'枳壳\', \'分麸\', \'微黄\', \'麦门冬\', \'两半\', \'紫菀\', \'分洗\', \'苗土\', \'大黄\', \'甘草\', \'半两\', \'炙微\', \'秦艽\', \'分去\', \'槟榔\', \'半两\', \'桔梗\', \'半两\', \'芦头\']');
INSERT INTO `prescript` VALUES (57989, '15140', '琥珀丸', '琥珀1两（细研），当归1两（锉碎，微炒），芎半两，木香半两，桂心半两，羌活3分，槟榔3分，没药半两，牛膝1两（去苗），朱砂3分（细研，水飞过），延胡索3分，桃仁3分（汤浸，去皮尖双仁，麸炒微黄），熟干地黄半两，硼砂3分（不夹石者，细研），鳖甲1两（涂醋，炙令黄，去裙襕），姜黄半两，苏合香半两，柴胡1两（去苗），赤芍药半两，牡丹半两，川大黄1两（锉碎，微炒），麝香1分（细研）。', '每服30丸，食前以温酒送下。', '《圣惠》卷七十', '妇人血风劳气，四肢羸瘦，骨节酸痛，口干心烦，经脉不利，或时腹痛，干呕，不思饮食，日渐困乏。', '-', '-', '柴胡', '[\'琥珀\', \'两细研\', \'当归\', \'半两\', \'木香\', \'半两\', \'桂心\', \'半两\', \'羌活\', \'槟榔\', \'没药\', \'半两\', \'牛膝\', \'两去\', \'朱砂\', \'分细\', \'研水\', \'飞过\', \'延胡索\', \'桃仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'熟干\', \'地黄\', \'半两\', \'硼砂\', \'石者\', \'细研\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'姜黄\', \'半两\', \'苏合香\', \'半两\', \'柴胡\', \'两去\', \'芍药\', \'半两\', \'牡丹\', \'半两\', \'大黄\', \'麝香\', \'分细研\']');
INSERT INTO `prescript` VALUES (57990, '15139', '黄芩汤', '黄芩1两，人参1两，甘草（炙）1两，半夏（洗）1两，干姜1两，柴胡3两，大枣10个（去核）。', '上切。以水3升，煮1升，为3服。烦，去半夏、人参，加栝楼子半个，当归2两，龙骨2两，栝楼根2两；腹中痛，去黄芩，加芍药1两，茯苓2两；表证不解，去人参，加桂心2两微发汗；得病7-8日不解，结热在内，往来寒热，加黄连2两，芒消半两。', '《幼幼新书》卷十三引《婴孺方》', '少小中风，往来寒热，胸胁满，嘿嘿烦心，喜呕，不欲食。', '-', '-', '柴胡', '[\'黄芩\', \'人参\', \'甘草\', \'半夏\', \'干姜\', \'柴胡\', \'大枣\', \'去核\']');
