INSERT INTO `prescript` VALUES (58012, '14802', '桂枝汤', '桂枝1两，赤芍药1两，甘草1两（炙微赤，锉），麻黄1两（去根节），芎藭1两，柴胡1两（去苗），厚朴2两（去粗皮，涂生姜汁，炙令香熟）。', '每服4钱，以水1大盏，加生姜半分，大枣3枚，煎至6分，去滓热服，不拘时候。衣覆取汗，如人行10里未汗，再服。', '《圣惠》卷九', '伤寒7日不解，头痛，小便清者。', '-', '-', '柴胡', '[\'桂枝\', \'两赤\', \'芍药\', \'甘草\', \'两炙微\', \'麻黄\', \'两去\', \'根节\', \'柴胡\', \'两去\', \'厚朴\', \'两去\', \'粗皮\', \'涂生\', \'姜汁\', \'炙令\', \'香熟\']');
INSERT INTO `prescript` VALUES (58013, '14797', '柴胡人参汤', '柴胡（去苗）1两，人参1两，知母（焙）1两，石膏（碎）1两，葛根（锉）1两，赤茯苓（去黑皮）1两，甘草（炙）半两。', '每服5钱匕，水1盏半，加生姜半分（拍碎），煎至7分，去滓温服，不拘时候。', '《圣济总录》卷二十三', '伤寒汗下后，潮热不退，口干烦躁。', '-', '-', '柴胡', '[\'柴胡\', \'人参\', \'知母\', \'石膏\', \'葛根\', \'茯苓\', \'黑皮\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (58014, '14772', '柴芩汤', '柴胡1钱5分，黄芩1钱5分，猪苓1钱5分，泽泻1钱5分，茯苓1钱5分，白术1钱5分。', '柴苓汤（《景岳全书》卷五十四）、柴苓散（《治疹全书》卷上）、柴胡汤（《不知医必要》卷三）。', '《保婴撮要》卷十八', '痘疹，小便不利。身热烦渴，泄泻。', '-', '-', '柴胡', '[\'柴胡\', \'钱分\', \'黄芩\', \'钱分\', \'猪苓\', \'钱分\', \'泽泻\', \'钱分\', \'茯苓\', \'钱分\', \'白术\', \'钱分\']');
INSERT INTO `prescript` VALUES (58015, '14771', '柴胡加细辛汤', '柴胡（去头芦，削去黄薄皮少许，拭净，锉碎）1两，黄芩（取中空者，用流水润透，蒸半炷香，晒干，锉碎）7钱，人参（饭上蒸熟）3钱，甘草（去头尾，酒润，炙令黄色，锉碎）4钱，生姜（切）6钱，半夏1合（用白芥子末半合、酽醋2合搅浊令匀，投半夏洗数次，再以水漂晒干，锉碎），大枣（劈）4枚，细辛（取北地1根，只1叶，茎柔根细，端直而长，色紫味辛，嚼之细细如椒者始真，修事拣去双叶者，切去头上子，以瓜水浸1宿，晒干，锉碎）3钱。', '以水3升，煮取2升，去滓；纳细辛，再煎取1升半，去滓，分温3服，未发、已发、发后各1服。', '《痎疟论疏》', '少阴痎疟。', '-', '-', '柴胡', '[\'柴胡\', \'头芦\', \'削去\', \'黄薄皮\', \'少许\', \'拭净\', \'黄芩\', \'中空\', \'流水\', \'润透\', \'蒸半\', \'晒干\', \'人参\', \'饭上\', \'蒸熟\', \'甘草\', \'头尾\', \'酒润炙令\', \'黄色\', \'生姜\', \'切钱\', \'半夏\', \'合用\', \'白芥子\', \'末半合\', \'醋合\', \'搅浊\', \'令匀\', \'半夏\', \'数次\', \'水漂\', \'晒干\', \'大枣\', \'劈枚\', \'细辛\', \'取北\', \'地根\', \'叶茎\', \'柔根细\', \'端直\', \'长色\', \'紫味\', \'辛嚼\', \'细细\', \'如椒者\', \'始真\', \'修事\', \'拣去\', \'双叶\', \'头上\', \'以瓜\', \'水浸\', \'晒干\']');
INSERT INTO `prescript` VALUES (58016, '14736', '活鳖煎', '附子（去皮脐）半两，白术半两，当归半两，人参半两，枳壳半两，桃仁半两，杏仁半两，三棱半两，陈橘皮半两，白芷半两，茯苓半两，甘草半两，秦艽半两，柴胡半两，桑白皮半两，阿胶半两，麻黄半两，官桂半两，槟榔1个，豆蔻1个。', '每服30丸，米饮送下，空心服。', '《普济方》卷二二八引《指迷方》', '虚劳咳嗽喘急，唾如浓涕，渐渐瘦弱。', '忌雀、鸽、苋菜、鱼腥、海味等。', '-', '柴胡', '[\'附子\', \'去皮\', \'半两\', \'白术\', \'半两\', \'当归\', \'半两\', \'人参\', \'半两\', \'枳壳\', \'半两\', \'桃仁\', \'半两\', \'杏仁\', \'半两\', \'三棱\', \'半两\', \'橘皮\', \'半两\', \'白芷\', \'半两\', \'茯苓\', \'半两\', \'甘草\', \'半两\', \'秦艽\', \'半两\', \'柴胡\', \'半两\', \'桑白皮\', \'半两\', \'阿胶\', \'半两\', \'麻黄\', \'半两\', \'官桂\', \'半两\', \'槟榔\', \'豆蔻\']');
INSERT INTO `prescript` VALUES (58017, '14727', '茯苓佐经汤', '茯苓1钱，陈皮1钱，半夏1钱，白术1钱，苍术1钱，藿香5分，泽泻5分，甘草5分，葛根5分，柴胡5分，厚朴5分，木瓜5分。', '水2钟，加生姜2片，煎8分，食前服。', '《外科正宗》卷三', '足少阳经为四气所乘，以致腰腿发热疼痛，头目昏眩，呕吐不食，胸膈不利，心烦热闷。', '-', '-', '柴胡', '[\'茯苓\', \'陈皮\', \'半夏\', \'白术\', \'苍术\', \'藿香\', \'泽泻\', \'甘草\', \'葛根\', \'柴胡\', \'厚朴\', \'木瓜\']');
INSERT INTO `prescript` VALUES (58018, '14717', '活络流气饮', '白通草7分，白芷7分，桔梗7分，薄荷7分，川芎7分，猪牙皂7分，红兰花1钱，连翘1钱，当归1钱，羌活1钱，柴胡1钱，土木鳖肉1钱，威灵仙8分，升麻5分，生甘草4分。', '水煎，加酒半盏，食远服。素禀虚弱而脉微细者，加人参、黄耆；脉洪大者，加玄参、天花粉。', '《观聚方要补》卷八引《会元方》', '流注块，或痛或不痛者；或发乍寒乍热之流注风。', '-', '-', '柴胡', '[\'通草\', \'白芷\', \'桔梗\', \'薄荷\', \'川芎\', \'猪牙皂\', \'分红\', \'兰花\', \'连翘\', \'当归\', \'羌活\', \'柴胡\', \'土木\', \'鳖肉\', \'威灵仙\', \'升麻\', \'生甘草\']');
INSERT INTO `prescript` VALUES (58019, '14716', '黄耆丸', '黄耆1两（锉），麦门冬1两（去心，焙），人参3分（去芦头），黄芩3分，枸杞子3分，茯神1两，百合半两，枳壳半两（麸炒微黄，去瓤），秦艽半两（去苗），酸枣仁3分（微炒），柴胡1两（去苗），赤芍药半两，知母半两，鳖甲3两（涂醋，炙令黄，去裙襕），杏仁3分（汤浸，去皮尖双仁，麸炒微黄），甘草半两（炙微赤，锉），生干地黄1两，郁李仁3分（汤浸，去皮，微炒）。', '每服30丸，不拘时候，以清粥饮送下。', '《圣惠》卷七十', '补气益精，养血安神，清肺热，解劳热，宽胸膈。主妇人骨蒸烦热，四肢羸瘦疼痛，口干心躁，不得眠卧。', '郁李仁、杏仁虽能润燥，然大便滑者不宜。', '-', '柴胡', '[\'黄耆\', \'麦门冬\', \'两去\', \'人参\', \'分去\', \'芦头\', \'黄芩\', \'枸杞子\', \'茯神\', \'百合\', \'半两\', \'枳壳\', \'半两\', \'微黄\', \'秦艽\', \'半两\', \'去苗\', \'酸枣仁\', \'分微\', \'柴胡\', \'两去\', \'芍药\', \'半两\', \'知母\', \'半两\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'甘草\', \'半两\', \'炙微\', \'生干\', \'地黄\', \'郁李仁\', \'分汤\', \'去皮\']');
INSERT INTO `prescript` VALUES (58020, '14673', '麻黄汤', '麻黄（去根节）1分，荆芥穗1分，杏仁（去皮尖及双仁，麸炒）1分，木香1分，当归（切，焙）1分，黄芩（去黑心）1分，羌活（去芦头）1分，芍药1分，柴胡（去苗）1分，大黄（炮熟）1分，半夏（汤洗去滑7遍）1钱，牵牛子半两。', '每服2钱匕，以水1盏，加生姜1片，同煎取8分，去滓，食后温服。', '《圣济总录》卷十三', '劳风。', '-', '-', '柴胡', '[\'麻黄\', \'根节\', \'荆芥\', \'穗分\', \'杏仁\', \'皮尖\', \'双仁麸\', \'炒分\', \'木香\', \'当归\', \'切焙分\', \'黄芩\', \'黑心\', \'羌活\', \'芦头分\', \'芍药\', \'柴胡\', \'苗分\', \'大黄\', \'炮熟分\', \'半夏\', \'汤洗\', \'滑遍\', \'牵牛子\', \'半两\']');
INSERT INTO `prescript` VALUES (58021, '14632', '金不换膏', '栀子1两，防风1两，良姜1两，海风藤1两，灵仙1两，牛膝1两，熟地1两，桃仁1两，柴胡1两，白鲜皮1两，全虫1两，枳壳1两，白芷1两，甘草1两，黄连1两，细辛1两，白芍1两，玄参1两，猪苓1两，前胡1两，麻黄1两，桔梗1两，僵蚕1两，升麻1两，地丁1两，大黄1两，木通1两，橘皮1两，川乌1两，生地1两，香附1两，双花1两，知母1两，薄荷1两，当归1两，杜仲1两，白术1两，泽泻1两，青皮1两，黄柏1两，杏仁1两，黄芩1两，穿山甲1两，蒺藜1两，天麻1两，苦参1两，乌药1两，羌活1两，半夏1两，茵陈1两，浙贝', '随证按穴摊贴之。', '《全国中药成药处方集》（沈阳方）', '舒筋通络，驱风散寒，调经止痛。主腰痛瘫痿，关节疼痛，麻痹不仁，心腹诸痛，男子遗精，女子带下，虚冷泄泻，月经崩漏，疟疾，疝气，偏正头痛，寒湿脚气。', '-', '-', '柴胡', '[\'栀子\', \'防风\', \'两良\', \'海风藤\', \'两灵仙\', \'牛膝\', \'熟地\', \'桃仁\', \'柴胡\', \'白鲜皮\', \'两全\', \'枳壳\', \'白芷\', \'甘草\', \'黄连\', \'细辛\', \'白芍\', \'玄参\', \'猪苓\', \'两前\', \'胡两\', \'麻黄\', \'桔梗\', \'僵蚕\', \'升麻\', \'两地\', \'大黄\', \'木通\', \'橘皮\', \'川乌\', \'生地\', \'香附\', \'两双\', \'知母\', \'薄荷\', \'当归\', \'杜仲\', \'白术\', \'泽泻\', \'青皮\', \'黄柏\', \'杏仁\', \'黄芩\', \'穿山甲\', \'蒺藜\', \'天麻\', \'苦参\', \'乌药\', \'羌活\', \'半夏\', \'茵陈\', \'两浙\']');
INSERT INTO `prescript` VALUES (58022, '14625', '柴芩清膈煎', '川柴胡8分，生锦纹（酒浸）1钱半，生枳壳1钱半，焦山栀3钱，青子芩1钱半，苏薄荷1钱半，苦桔梗1钱，青连翘2钱，生甘草6分，鲜淡竹叶36片。', '-', '《重订通俗伤寒论》卷二', '攻里清膈。主少阳表邪，内结膈中，膈上如焚，寒热如疟，心烦懊憹，大便不通。', '-', '-', '柴胡', '[\'柴胡\', \'分生\', \'锦纹\', \'浸钱\', \'半生\', \'枳壳\', \'钱半\', \'焦山\', \'栀钱\', \'青子\', \'半苏\', \'薄荷\', \'钱半苦\', \'桔梗\', \'钱青\', \'连翘\', \'生甘草\', \'分鲜\', \'淡竹叶\']');
INSERT INTO `prescript` VALUES (58023, '14611', '除风湿羌活汤', '羌活7分，防风5分，升麻5分，柴胡5分，藁本1钱，苍术1钱。', '除湿羌活汤（《回春》卷二）、除湿汤（《金鉴》卷三十九）', '《内外伤辨》卷中', '湿从外受，或一身尽痛，或头重如蒙，甚而昏冒。风湿相搏，湿因外中，得之于天阴淫雨，晴后湿蒸，早晨雾露，及久卧湿地，远行涉水，瘴气山岚，其证头身重痛，甚而昏冒，大便溏薄，皮肤浮肿。湿似疸（熏黄黑晦一症，若兼一身尽痛，乃是湿病，非疸，当从湿治），霍乱因湿，及因干湿四肢重着，骨节烦病，胸膈滞闷。风湿上冲，头重如裹，似有物蒙之，兼有热者。', '-', '-', '柴胡', '[\'羌活\', \'防风\', \'升麻\', \'柴胡\', \'藁本\', \'苍术\']');
INSERT INTO `prescript` VALUES (58024, '14596', '柴胡散', '柴胡1两（去苗），白术3分，木香半两，紫菀2分（洗，去苗土），赤茯苓3分，赤芍药1两，鳖甲1两半（涂醋炙令黄，去裙襕），半夏3分（汤洗7遍去滑），杏仁3分（汤浸，去皮尖双仁，麸炒微黄），人参3分（去芦头），前胡3分（去芦头），诃黎勒1两半（煨，用皮），枳壳3分（麸炒微黄，去瓤），枇杷叶半两（拭去毛，炙微黄），甘草1分（炙微赤，锉）。', '本方方名，《普济方》引作“柴胡汤”。', '《圣惠》卷二十六', '肺劳，胸膈壅滞，痰嗽不止，四肢无力，少思饮食。', '忌饴糖、羊肉、苋菜。', '-', '柴胡', '[\'柴胡\', \'两去\', \'白术\', \'木香\', \'半两\', \'紫菀\', \'分洗\', \'苗土\', \'茯苓\', \'分赤\', \'芍药\', \'鳖甲\', \'两半\', \'涂醋\', \'炙令\', \'黄去\', \'半夏\', \'分汤\', \'洗遍\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'人参\', \'分去\', \'头前\', \'胡分\', \'芦头\', \'黎勒\', \'两半\', \'枳壳\', \'分麸\', \'微黄\', \'枇杷叶\', \'半两\', \'拭去\', \'微黄\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (58025, '14595', '理脾化滞清疟饮', '甜白术1钱5分（土炒），云苓1钱5分，制半夏2钱，炒枳壳1钱，青皮8分，甘草3分，柴胡5分，草蔻仁1钱（研）。', '煨姜2片，大枣3枚为引，河井水煎，露1宿，疟前2时温服。1服寒热轻，2服疟止，3服痊愈。如恶心呕吐，加藿香1钱5分，灶心土2钱；如作泻，加炒白芍3钱，车前子2钱；如口渴发烧不退，加葛根2钱；如有汗不退，加青蒿3钱；如肚口不宽，加槟榔1钱；荤腥停滞，加炒山楂2钱；大便结燥，加当归3钱，炒麦芽2钱；如缠绵寒热不退，加川贝母1钱5分，炒知母1钱5分，黑料豆49粒。', '《慈航集》卷下', '小儿五岁至十岁以外而患疟者，初由饮食伤脾，脾虚食积不化，外感风寒暑湿而成胎疟矣。', '凡疟初愈，饮食不可猛进鱼腥面食，必须俟脾胃强健，食则无凝。早食则成肥气疟母，而成痼疾矣。俟风寒退尽方可进食。', '-', '柴胡', '[\'白术\', \'分土\', \'炒云苓\', \'分制\', \'半夏\', \'枳壳\', \'青皮\', \'甘草\', \'柴胡\', \'分草蔻仁\', \'钱研\']');
INSERT INTO `prescript` VALUES (58026, '14592', '羚羊角散', '羚羊角屑1两，葛根半两（锉），黄芩半两，赤芍药半两，石膏2两，麦门冬3分（去心），甘草半两（炙微赤，锉），柴胡1两（去苗），赤茯苓1两，栀子仁半两。', '每服3钱，以水1中盏，加竹叶7片、豉30粒，煎至6分，去滓温服，不拘时候。', '《圣惠》卷四', '心胸烦热，渴逆头痛。', '-', '-', '柴胡', '[\'羚羊角\', \'葛根\', \'半两\', \'黄芩\', \'半两\', \'芍药\', \'半两\', \'石膏\', \'麦门冬\', \'分去\', \'甘草\', \'半两\', \'炙微\', \'柴胡\', \'两去\', \'茯苓\', \'栀子\', \'半两\']');
INSERT INTO `prescript` VALUES (58027, '14580', '柴胡通塞汤', '柴胡（去苗）3分，黄芩（去黑心）3分，陈橘皮（汤浸去白，微炒）3分，泽泻3分，羚羊角（镑）3分，栀子仁1两，石膏1两，大黄（锉，炒）1两。', '每服4钱匕，水1盏，入生地黄1分（拍破），豉半分（微炒），同煎至7分，去滓，食前服。', '《圣济总录》卷一五七', '妊娠大小便不通，下焦热结。', '-', '-', '柴胡', '[\'柴胡\', \'苗分\', \'黄芩\', \'黑心\', \'分陈\', \'橘皮\', \'汤浸\', \'白微\', \'炒分\', \'泽泻\', \'羚羊角\', \'栀子\', \'石膏\', \'大黄\']');
INSERT INTO `prescript` VALUES (58028, '14560', '柴胡加大黄芒消桑螵蛸汤', '柴胡2两16钱，黄芩1两，人参1两，甘草（炙）1两，生姜1两（切），半夏1合（洗），大枣4枚（擘），芒消2两。', '前7味，以水7升，下芒消3合，大黄4分，桑螵蛸5枚，煮取1升半，去滓，温服5合，微下即愈。', '《千金翼》卷九', '伤寒不解，胸胁满而呕，日晡所发潮热而微利，服柴胡加芒消汤不解者。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'人参\', \'甘草\', \'生姜\', \'两切\', \'半夏\', \'合洗\', \'大枣\', \'消两\']');
INSERT INTO `prescript` VALUES (58029, '14522', '柴胡汤', '柴胡（去苗）1两，升麻1两半，车前子1两，决明子（微炒）1两，栀子仁1两，黄芩（去黑心）1两，黄连（去须）1两，甘草（炙，锉）1两，防风（去叉）1两，羚羊角（镑）1两，马牙消1两。', '每服5钱匕，水1盏半，煎至8分，去滓，食后、临卧服。', '《圣济总录》卷一○四', '肝虚风热上冲，目暗赤痛。', '-', '-', '柴胡', '[\'柴胡\', \'升麻\', \'两半\', \'车前子\', \'决明子\', \'栀子\', \'黄芩\', \'黑心\', \'黄连\', \'甘草\', \'防风\', \'羚羊角\', \'两马牙\', \'消两\']');
INSERT INTO `prescript` VALUES (58030, '14505', '柴胡人参汤', '柴胡（去苗）3分，人参3分，生干地黄（焙）3分，桔梗（锉，炒）半两，知母半两，紫菀（去苗土）半两，桑根白皮半两，枳壳（去瓤，麸炒令黄）半两，赤芍药半两，桂（去粗皮）半两，当归（微炙）半两，附子大者1枚（炮裂，去皮脐）。', '每服3钱匕，水1盏，加生姜3片，大枣1枚（擘破），同煎7分，去滓温服，不拘时候。', '《圣济总录》卷一六四', '产后失于将理，血气虚损，日渐困瘁，少寒多热，烦渴嗽逆，痰壅减食。', '-', '-', '柴胡', '[\'柴胡\', \'苗分\', \'人参\', \'分生\', \'地黄\', \'焙分\', \'桔梗\', \'半两\', \'知母\', \'半两\', \'紫菀\', \'苗土\', \'半两\', \'桑根\', \'白皮\', \'半两\', \'枳壳\', \'炒令\', \'半两\', \'芍药\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'当归\', \'半两\', \'附子\', \'大者\', \'炮裂\', \'去皮\']');
INSERT INTO `prescript` VALUES (58031, '14501', '柴胡枳壳汤', '柴胡（去苗）半两，枳壳（去瓤，麸炒）半两，黄芩（去黑心）半两，栀子仁半两，茵陈蒿半两，龙胆半两，大黄（锉，炒）半两，甘草（炙）半两。', '每服5钱匕，用水1盏半，煎至1盏，去滓，早、晚食后温服。', '《圣济总录》卷二十八', '伤寒发黄，壮热，骨节烦疼，两胸下气胀急硬痛，不能食。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'半两\', \'枳壳\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'栀子\', \'半两\', \'茵陈蒿\', \'半两\', \'龙胆\', \'半两\', \'大黄\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (58032, '14472', '大柴胡散', '柴胡1两，川大黄1两（锉碎，微炒），黄芩1两，赤芍药1两，枳实1两（麸炒微黄），半夏半两（汤洗7遍，去滑），人参1两（去芦头），甘草半两（炙微赤，锉），黄耆1两（锉）。', '每服5钱，以水1大盏，加竹茹1分，生姜半分，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十七', '热病己得汗，热犹不解，腹胀烦躁，狂言不定。', '-', '-', '柴胡', '[\'柴胡\', \'两川\', \'大黄\', \'黄芩\', \'两赤\', \'芍药\', \'枳实\', \'两麸\', \'微黄\', \'半夏\', \'半两\', \'洗遍\', \'人参\', \'两去\', \'芦头\', \'甘草\', \'半两\', \'炙微\', \'黄耆\']');
INSERT INTO `prescript` VALUES (58033, '14456', '黄耆汤', '黄耆（细锉）半两，人参1两，白术1两，白茯苓（去黑皮）1两，京三棱（锉）1两，芎半两，陈橘皮（汤浸去白，焙）半两，麦门冬（去心，焙）半两，诃黎勒皮半两，前胡（去芦头）半两，桔梗（炒）半两，柴胡（去苗）半两，牡丹皮3分，甘草（炙，锉）3分，芍药3分。', '每服3钱匕，水1盏，加生姜2片，煎至7分，去滓温服，不拘时候。', '《圣济总录》卷四十七', '胃热肠寒，食已复饥，小腹胀痛。', '-', '-', '柴胡', '[\'黄耆\', \'半两\', \'人参\', \'白术\', \'白茯苓\', \'黑皮\', \'两京\', \'三棱\', \'两芎\', \'半两\', \'橘皮\', \'汤浸\', \'半两\', \'麦门冬\', \'半两\', \'黎勒皮\', \'半两\', \'胡去\', \'芦头\', \'半两\', \'桔梗\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'牡丹皮\', \'甘草\', \'芍药\']');
INSERT INTO `prescript` VALUES (58034, '14453', '柴胡汤', '柴胡（去苗）1两半，鳖甲（去裙襕，醋炙）半两，秦艽（去苗土）半两，知母（焙）半两，桂（去粗皮）半两，人参半两，白茯苓（去黑皮）半两，附子（炮裂，去皮脐）半两，黄耆半两，五味子半两，羌活（去芦头）半两，木香半两，沉香半两，枳壳（去瓤，炒）1分，枸杞子1分，槟榔（炮，锉）2枚。', '每服3钱匕，水1盏，煎至6分，去滓温服，不拘时候。', '《圣济总录》卷九十', '五劳七伤，四肢少力，肌瘦盗汗，遗精心忪，不思饮食，咳嗽唾脓血。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两半\', \'鳖甲\', \'半两\', \'秦艽\', \'苗土\', \'半两\', \'知母\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'人参\', \'半两\', \'白茯苓\', \'黑皮\', \'半两\', \'附子\', \'炮裂\', \'去皮\', \'半两\', \'黄耆\', \'半两\', \'五味子\', \'半两\', \'羌活\', \'芦头\', \'半两\', \'木香\', \'半两\', \'沉香\', \'半两\', \'枳壳\', \'炒分\', \'枸杞子\', \'槟榔\']');
INSERT INTO `prescript` VALUES (58035, '14431', '活血散', '当归4两，川芎4两，白芍药4两，柴胡4两，肉桂（去粗皮）2两。', '方中柴胡，《御药院方》卷十作“延胡”。', '《产乳备要》', '冲任气虚，经事不调，或多或少，或前或后。', '-', '-', '柴胡', '[\'当归\', \'川芎\', \'两白\', \'芍药\', \'柴胡\', \'肉桂\', \'粗皮\']');
INSERT INTO `prescript` VALUES (58036, '14422', '凉荣泻火汤', '川芎1钱，当归1钱，白芍1钱，生地1钱，黄芩1钱，黄连1钱，山栀1钱，木通1钱，柴胡1钱，茵陈1钱，胆草1钱，知母1钱，麦门冬1钱，甘草5分，大黄（酒炒）2钱。', '水2钟，煎8分，空心服。便利，去大黄。', '《外科正宗》卷四', '妇人怀抱忧郁，致生内热，小水涩滞，大便秘结；及阴中火郁作痛，亦如涩淋。', '-', '-', '柴胡', '[\'川芎\', \'当归\', \'白芍\', \'生地\', \'黄芩\', \'黄连\', \'钱山\', \'栀钱\', \'木通\', \'柴胡\', \'茵陈\', \'钱胆\', \'草钱\', \'知母\', \'麦门冬\', \'甘草\', \'分大\', \'黄酒\']');
