INSERT INTO `prescript` VALUES (58038, '14386', '柴胡散', '柴胡（去苗）1两，黄芩1两，桔梗（去芦头）1两，玄参1两，地骨皮1两，麦门冬1两半（去心，焙），赤茯苓半两，人参半两（去芦头），紫菀半两（去苗土），甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十五', '时气热势未退，壅滞，虚羸，咳嗽。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'桔梗\', \'芦头\', \'玄参\', \'地骨皮\', \'麦门冬\', \'两半\', \'茯苓\', \'半两\', \'人参\', \'半两\', \'芦头\', \'紫菀\', \'半两\', \'苗土\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58039, '14378', '固经膏', '全当归3两，丹皮（酒炒）2两，柴胡2两，酒芍2两，生地2两，黄芩2两，知母2两，麦冬2两，地骨皮2两，川芎2两，贝母2两，黄连2两，羌活1两，防风1两，连翘1两，薄荷1两，蔓荆子1两，紫苏1两，独活1两，藁本1两，细辛1两，丹参1两，党参1两，黄耆1两，熟地1两，元参1两，白术1两，天冬1两，赤芍1两，白薇1两，苍术1两，萸肉1两，淮山药1两，枳壳1两，桔梗1两，麦芽1两，郁金1两，贯众1两，青皮1两，陈皮1两，半夏1两，胆南星1两，白芷1两，升麻1两，葛根1两，黄柏1两，黑山栀1两，生甘草1两，熟牛膝1', '外用，上贴心口，中贴脐眼，下贴丹田，或兼贴对脐两腰。', '《理瀹》', '举经固经，补阴清火。主妇人血虚有热，月经先期，或经行过多，先后不定，或经行不止，或崩中，或漏下，或湿热带下，或五旬后经行者。', '-', '-', '柴胡', '[\'当归\', \'丹皮\', \'柴胡\', \'两酒\', \'生地\', \'黄芩\', \'知母\', \'麦冬\', \'地骨皮\', \'川芎\', \'贝母\', \'黄连\', \'羌活\', \'防风\', \'连翘\', \'薄荷\', \'蔓荆子\', \'紫苏\', \'独活\', \'藁本\', \'细辛\', \'丹参\', \'党参\', \'两黄耆\', \'熟地\', \'两元\', \'参两\', \'白术\', \'两天\', \'冬两\', \'赤芍\', \'两白薇\', \'苍术\', \'两萸\', \'两淮\', \'山药\', \'枳壳\', \'桔梗\', \'麦芽\', \'郁金\', \'贯众\', \'青皮\', \'陈皮\', \'半夏\', \'胆南星\', \'白芷\', \'升麻\', \'葛根\', \'黄柏\', \'黑山\', \'栀两\', \'生甘草\', \'两熟\', \'牛膝\']');
