INSERT INTO `prescript` VALUES (58041, '14361', '柴胡姜桂汤', '柴胡1钱5分，半夏2钱，人参1钱，黄芩1钱，桂枝2钱，甘草5分。', '加煨姜3钱，大枣3枚。2服全愈。恶心，加藿香梗3钱；饱闷，加炒枳壳1钱2分。', '《慈航集》卷下', '疟痢寒多热少，或但寒不热。', '-', '-', '柴胡', '[\'柴胡\', \'钱分\', \'半夏\', \'人参\', \'黄芩\', \'桂枝\', \'甘草\']');
