INSERT INTO `prescript` VALUES (58045, '14214', '大茴香丸', '山楂（炒）4两，橘核（炒）2两，茴香（炒）2两，山栀仁2两，柴胡1两，牡丹皮1两，桃仁（炒）1两，八角茴香（炒）1两，吴茱萸（炒）半两。', '每服50丸，空心盐汤送下。', '《杏苑》卷六', '结核偏坠，头肿胀；或一核缩入小腹，痛不可忍，用手捺按，方得还旧。', '-', '-', '柴胡', '[\'山楂\', \'橘核\', \'茴香\', \'两山\', \'栀仁\', \'柴胡\', \'牡丹皮\', \'桃仁\', \'八角茴香\', \'吴茱萸\', \'半两\']');
INSERT INTO `prescript` VALUES (58046, '14190', '草灵宝丹', '川芎4两，天麻（酒浸，去苗）4两，当归（洗）4两，白芍药4两，细辛（去土叶）4两，荆芥穗4两，川楝子肉（炒）4两，麻黄（去根节）4两，五加皮4两，白鲜皮4两，何首乌（酒浸）4两，自然铜（火烧7遍，醋浸7遍）4两，菊花4两，枳壳（炒，去瓤）4两，白术（炒）4两，薄荷叶4两，石斛（去根，炒）4两，威灵仙（去土）4两，枸杞子4两，木香4两，川乌头（炮，去皮脐尖）4两，甘草（炒）4两，附子（炮，去皮脐）4两，草乌头（炮，去皮脐尖）4两，香附子（炒）4两，车前子（酒浸）4两，金毛狗脊（去毛）4两，没药（别研）4两，', '每服半丸或1丸，热酒化下，不拘时候。', '《杨氏家藏方》卷一', '中风，及八风五痹，瘫痪亸曳，口眼斜，眉角牵引，项背拘强，牙关紧急，心中悸闷，神情如醉，遍身发热，骨节烦疼，肌肉麻木，腰膝沉重，皮肤瞤动，状若虫行；阳虚头痛，风寒入脑，目旋晕转，似在舟船，耳内蝉鸣，有如风雨之声应；风寒湿痹，脚气缓急，及打扑伤于筋骨，或遇天明，一身尽痛，不得睡卧。', '-', '-', '柴胡', '[\'川芎\', \'天麻\', \'酒浸\', \'当归\', \'两白\', \'芍药\', \'细辛\', \'土叶\', \'荆芥\', \'川楝子\', \'麻黄\', \'根节\', \'五加皮\', \'白鲜皮\', \'何首乌\', \'酒浸\', \'自然铜\', \'火烧\', \'遍醋\', \'浸遍\', \'菊花\', \'枳壳\', \'白术\', \'薄荷叶\', \'石斛\', \'威灵仙\', \'枸杞子\', \'木香\', \'两川\', \'乌头\', \'去皮\', \'脐尖\', \'甘草\', \'附子\', \'去皮\', \'两草\', \'乌头\', \'去皮\', \'脐尖\', \'香附子\', \'车前子\', \'酒浸\', \'金毛狗\', \'脊去\', \'没药\', \'别研\']');
INSERT INTO `prescript` VALUES (58047, '14187', '调经清郁丸', '生地3两，当归3两，续断3两，杜仲3两，川芎2两，阿胶2两，香附2两，知母2两，黄芩2两，川连2两，柴胡2两，干葛2两，白芍3两。', '每服3钱，早、晚空心白汤吞服。', '《活人方》卷二', '滋阴散郁，调和冲任之气血，清散经脉之郁火。主热证初发，阴虚内蒸，月经不调。', '-', '-', '柴胡', '[\'生地\', \'当归\', \'续断\', \'杜仲\', \'川芎\', \'阿胶\', \'香附\', \'知母\', \'黄芩\', \'两川\', \'柴胡\', \'两干葛\', \'白芍\']');
INSERT INTO `prescript` VALUES (58048, '14179', '柴胡散', '柴胡（去苗土）1斤，人参5两，甘草（炙）4两，白术3两，半夏（汤浸，煮软，切作片子，焙干）5两，黄芩（去黑心）5两，防风（去叉）3两。', '每服3钱，水1盏，加生姜5片，同煎至7分，去滓温服，不拘时候。', '《圣济总录》卷十三', '阴阳不和，寒热往来，头目昏重，身体烦疼，咳嗽咽干，鼻塞清涕。', '-', '-', '柴胡', '[\'柴胡\', \'苗土\', \'人参\', \'甘草\', \'白术\', \'半夏\', \'汤浸\', \'软切作\', \'片子\', \'焙干\', \'黄芩\', \'黑心\', \'防风\']');
INSERT INTO `prescript` VALUES (58049, '14172', '凉解汤', '茯神3钱，麦冬5钱，玄参1两，柴胡1钱，甘草3分，炒枣仁2钱。', '水煎服。', '《辨证录》卷一', '心虚之人，冬月伤寒，身热5-6日不解，谵语口渴，小便自利，欲卧。', '-', '-', '柴胡', '[\'茯神\', \'麦冬\', \'玄参\', \'柴胡\', \'甘草\', \'枣仁\']');
INSERT INTO `prescript` VALUES (58050, '14164', '酒煎饮', '常山1两，鳖甲（去裙襕，醋炙，令黄色）1两，知母3分，白头翁3分，桂（去粗皮）半两，青蒿1握，甘草（生）3分，桃枝头心7枚，李枝头心7枚，葱白7茎，薤白7茎，柴胡（去苗）3分。', '每服5钱匕，以酒1盏半，浸1宿，平旦煎取1盏，去滓，空腹顿服，当吐痰出，再煎滓服。', '《圣济总录》卷三十六', '足太阳疟，腰痛头重，寒热互作。', '-', '-', '柴胡', '[\'常山\', \'鳖甲\', \'炙令\', \'黄色\', \'知母\', \'白头翁\', \'分桂去\', \'粗皮\', \'半两\', \'青蒿\', \'甘草\', \'生分\', \'枝头\', \'心枚\', \'枝头\', \'心枚\', \'葱白\', \'薤白\', \'柴胡\', \'苗分\']');
INSERT INTO `prescript` VALUES (58051, '14153', '和气平胃散', '厚朴5钱，黄连5钱，猪苓5钱，泽泻5钱，地榆5钱，苍术3钱，升麻1钱5分，豆蔻1钱5分，白芍药3钱，陈皮4钱，柴胡1钱5分，甘草1钱。', '《女科旨要》有白术。', '《女科万金方》', '安胎和气。主妇人胎前8-9个月，胎儿长发以致胎母脾胃虚弱而不调和，湿热相攻，五脏六腑不和，或变痢疾、杂患之病。', '-', '-', '柴胡', '[\'厚朴\', \'黄连\', \'猪苓\', \'泽泻\', \'钱地\', \'榆钱\', \'苍术\', \'升麻\', \'钱分\', \'豆蔻\', \'分白\', \'芍药\', \'陈皮\', \'柴胡\', \'钱分\', \'甘草\']');
INSERT INTO `prescript` VALUES (58052, '14143', '茴香鳖甲丸', '茴香子（舶上者，炒）1两，鳖甲（去裙襕，醋炙）1两，附子（大者）1枚（炮裂，去皮脐），胡芦巴1分，柴胡（去苗）半两，黄连（去须）半两，楝实10枚（炮）。', '每服10-20丸，食前温酒送下。', '《圣济总录》卷八十九', '虚劳腰膝痛。', '-', '-', '柴胡', '[\'茴香\', \'子舶\', \'上者\', \'鳖甲\', \'附子\', \'大者\', \'炮裂\', \'去皮\', \'胡芦巴\', \'柴胡\', \'去苗\', \'半两\', \'黄连\', \'半两\', \'实枚\']');
INSERT INTO `prescript` VALUES (58053, '14142', '栝楼根散', '栝楼根1两，柴胡1分（去苗），地骨皮1两，枳壳半两（麸炒微黄），赤茯苓1两，鳖甲1两（涂醋炙令黄，去裙襕）。', '每服4钱，以水1中盏，煎至5分，去滓，加生地黄汁3合，更煎1-2沸，温服，不拘时候。', '《圣惠》卷十六', '时气余热木退，发作有时。', '-', '-', '柴胡', '[\'栝楼\', \'根两\', \'柴胡\', \'分去\', \'地骨皮\', \'枳壳\', \'半两\', \'微黄\', \'茯苓\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\']');
