INSERT INTO `prescript` VALUES (58056, '14025', '麻仁龙胆丸', '大麻仁1两，大黄1两，柴胡2分，黄芩2分，白鲜皮2分，秦艽2分，赤芍药2分，龙胆草2分，黄连1分，栀子40个。', '食后煎淡竹叶汤下30丸，每日3次。以大利为度。10日小愈，1月平复。', '《伤寒总病论》卷三', '一切时行，不知身上疼痛，不寒亦不热，沉沉似有所思，顺事多语。', '-', '-', '柴胡', '[\'麻仁\', \'大黄\', \'柴胡\', \'黄芩\', \'白鲜皮\', \'秦艽\', \'分赤\', \'芍药\', \'龙胆草\', \'黄连\', \'栀子\']');
INSERT INTO `prescript` VALUES (58057, '13981', '胡黄连散', '胡黄连2两，黄连（去须）2两，龙胆2两，桑螵蛸1两，知母1两，秦艽（去苗土）1两，柴胡1两，枳壳（去瓤）1两，人参1两，桔梗1两，射干1两，白术1两。', '每服3钱匕，以葱、薤白、槐柳心、乌梅肉浸童子小便调下，或只用槐技、小麦煎亦得，空心、日午、夜卧各1次。', '《圣济总录》卷八十七', '急劳发热，羸瘦颊赤，口干，心神烦躁。', '-', '-', '柴胡', '[\'胡黄连\', \'黄连\', \'龙胆\', \'桑螵蛸\', \'知母\', \'秦艽\', \'苗土\', \'柴胡\', \'枳壳\', \'人参\', \'桔梗\', \'射干\', \'白术\']');
INSERT INTO `prescript` VALUES (58058, '13974', '羚羊角汤', '羚羊角1两（镑），黄芩1两（去黑心），芎1两，石膏1两（碎），大黄1两（锉，炒），芒消（研）1两，芍药1两半，柴胡（去苗）3两。', '每服3钱匕，以水1盏，加淡竹叶10片，煎至7分，去滓，食后、临卧温服。', '《圣济总录》卷一○六', '目睛痛，上连头并颊骨俱痛不可忍，生障泪出。', '-', '-', '柴胡', '[\'羚羊角\', \'两镑\', \'黄芩\', \'两去\', \'黑心\', \'石膏\', \'两碎\', \'大黄\', \'炒芒\', \'消研\', \'芍药\', \'两半\', \'柴胡\']');
INSERT INTO `prescript` VALUES (58059, '13970', '大黄丸', '川大黄3两（锉，微炒，别研为末），鳖甲1两（涂醋，炙令黄，去裙襕），柴胡1两（去苗），吴茱萸半两（汤浸7遍，焙干，微炒），当归半两（锉，微炒），京三棱半两（微煨，锉），赤芍药半两，牛膝半两（去苗），槟榔半两，桂心半两，干漆3分（捣碎，炒令烟出）。', '每服30丸，食前以生姜、橘皮汤送下。', '《圣惠》卷七十二', '妇人月水不通，积聚成块，或歇寒热，时复刺痛。', '-', '-', '柴胡', '[\'大黄\', \'炒别\', \'研为\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'柴胡\', \'两去\', \'吴茱萸\', \'半两\', \'浸遍\', \'焙干\', \'当归\', \'半两\', \'锉微\', \'炒京\', \'三棱\', \'半两\', \'芍药\', \'半两\', \'牛膝\', \'半两\', \'去苗\', \'槟榔\', \'半两\', \'桂心\', \'半两\', \'干漆\', \'捣碎\', \'炒令\', \'烟出\']');
INSERT INTO `prescript` VALUES (58060, '13969', '调中汤', '白芍药4两，续断1两，芎藭1两，甘草1两，白术3两，柴胡3两，当归1两半，乌梅1升，生姜4两，厚朴3两，枳实3两，生李根3两，白皮3两。', '上（口父）咀。以水1斗，煮取3升，分4服，日3夜1，8日后复服1剂。', '《千金》卷二', '曾伤四月胎者，当预服。', '海藻、菘菜、桃李、雀肉等。', '-', '柴胡', '[\'芍药\', \'续断\', \'甘草\', \'白术\', \'柴胡\', \'当归\', \'两半\', \'乌梅\', \'生姜\', \'厚朴\', \'枳实\', \'两生\', \'李根\', \'白皮\']');
INSERT INTO `prescript` VALUES (58061, '13943', '柴胡参归汤', '党参（去芦）1钱5分，半夏（制）1钱5分，归身1钱5分，紫苏1钱5分，柴胡2钱，陈皮1钱，炙草1钱。', '加生姜3片，红枣2枚，水煎服。有汗，去紫苏；粪溏，去归身；口渴，加葛根、麦冬各1钱5分；无汗腹痛，胁胀或痛，去红枣，加煅牡蛎粉1钱5分。', '《不知医必要》卷一', '虚弱人疟疾初起。', '-', '-', '柴胡', '[\'党参\', \'芦钱分\', \'半夏\', \'制钱\', \'分归身\', \'钱分\', \'紫苏\', \'钱分\', \'柴胡\', \'陈皮\']');
INSERT INTO `prescript` VALUES (58062, '13928', '柴胡散', '柴胡1两（去苗），赤芍药半两，知母半两，人参1两（去芦头），川大黄1两（锉碎，微炒），甘草半两（炙微赤，锉），半夏半两（汤浸7遍，去滑），葳蕤半两，黄芩半两。', '每服5钱，以水1大盏，加生姜半分，煎至5分，去滓温服，不拘时候，以微利为度。', '《圣惠》卷九', '伤寒8日不解，默默烦闷，腹中干燥，大肠结涩，狂言。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'芍药\', \'半两\', \'知母\', \'半两\', \'人参\', \'两去\', \'芦头川\', \'大黄\', \'甘草\', \'半两\', \'炙微\', \'半夏\', \'半两\', \'浸遍\', \'葳蕤\', \'半两\', \'黄芩\', \'半两\']');
INSERT INTO `prescript` VALUES (58063, '13924', '柴胡汤', '柴胡（去苗）3两，枳壳（去瓤，麸炒）2两，白茯苓（去黑皮）3分，白术2两，人参1两，麦门冬（去心，焙）1两半。', '每服3钱匕，水1盏，加生姜半分（拍碎），煎至7分，去滓，空腹温服，日午、夜卧各1次。', '《圣济总录》卷九十', '虚劳气逆，心腹痞满，四肢羸瘦，腹胀不下食。', '-', '-', '柴胡', '[\'柴胡\', \'枳壳\', \'白茯苓\', \'黑皮\', \'白术\', \'人参\', \'麦门冬\', \'两半\']');
