INSERT INTO `prescript` VALUES (58066, '13853', '凉血固真汤', '当归身7分，川芎5分，白芍药7分，生地黄1钱，熟地黄1钱，条实黄芩（生用，细切）1钱2分，香附子（童便浸透）7分，续断5分，柴胡8分，丹参5分，白术7分，荆芥穗5分，黄柏（微炒）5分。', '用水1钟半，煎至7分，去滓，食前温服。', '《摄生众妙方》卷十', '血分有热，月水频并，劳倦腰疼，手足烦热，头目昏眩，渐成崩漏。', '-', '-', '柴胡', '[\'当归\', \'身分\', \'川芎\', \'分白\', \'芍药\', \'生地黄\', \'熟地黄\', \'条实\', \'黄芩\', \'生用\', \'细切\', \'钱分\', \'香附子\', \'童便\', \'浸透\', \'续断\', \'柴胡\', \'丹参\', \'白术\', \'荆芥\', \'穗分\', \'黄柏\', \'炒分\']');
INSERT INTO `prescript` VALUES (58067, '13804', '附子丸', '附子（炮裂，去皮脐）1两，人参1两，枳壳（去瓤，麸炒）1两，干姜（炮）1两，甘草（炙，锉）1两，当归（切，焙）1两，陈橘皮（汤浸，去白，焙）1两，厚朴（去粗皮，姜汁炙熟）1两，荜茇半两，杏仁（汤浸，去皮尖双仁，炒）半两，桂（去粗皮）半两，吴茱萸（汤洗7遍，焙干，炒）半两，诃黎勒（微炒，去核）半两，柴胡（去苗）半两。', '每服20丸，空心煎诃黎勒汤送下。服尽觉手脚冷麻，是劳气散也。', '《圣济总录》卷九十一', '冷劳下痢，脐腹疼痛。', '-', '-', '柴胡', '[\'附子\', \'炮裂\', \'去皮\', \'人参\', \'枳壳\', \'干姜\', \'甘草\', \'当归\', \'切焙\', \'橘皮\', \'汤浸\', \'厚朴\', \'粗皮\', \'姜汁\', \'炙熟\', \'两荜\', \'半两\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'吴茱萸\', \'洗遍\', \'焙干\', \'半两\', \'黎勒微\', \'去核\', \'半两\', \'柴胡\', \'去苗\', \'半两\']');
INSERT INTO `prescript` VALUES (58068, '13781', '大黄汤', '大黄（锉，炒）半两，黄连（去须）半两，甘草（炙，锉）半两，麦门冬（去心，焙）1两，柴胡（去苗）1两。', '每服3钱匕，水1盏，加竹叶10片，生姜3片，煎至6分，去滓温服，不拘时候。', '《圣济总录》卷二十三', '伤寒，心神烦躁，壮热狂言，毒气伏留于胸膈。', '-', '-', '柴胡', '[\'大黄\', \'半两\', \'黄连\', \'半两\', \'甘草\', \'半两\', \'麦门冬\', \'柴胡\']');
INSERT INTO `prescript` VALUES (58069, '13779', '琥珀散', '琥珀1两，虎杖3分，牛膝1两（去苗），木香半两，鳖甲1两（涂醋，炙令微黄，去裙襕），赤芍药1两，柴胡1两（去苗），赤茯苓3分，桂心半两，桃仁3分（汤浸，去皮尖双仁，麸炒微黄），当归3分（锉，微炒），枳壳3分（麸炒微黄，去瓤）。', '《普济方》引本方有川大黄一两（锉碎，微炒）。', '《圣惠》卷七十九', '产后月水不通，胁腹妨闷，四体烦疼，吃食减少，渐觉虚困。', '忌生冷、油腻、苋菜。', '-', '柴胡', '[\'琥珀\', \'虎杖\', \'牛膝\', \'两去\', \'苗木\', \'半两\', \'鳖甲\', \'两涂\', \'炙令\', \'微黄\', \'芍药\', \'柴胡\', \'两去\', \'茯苓\', \'分桂心\', \'半两\', \'桃仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'当归\', \'枳壳\', \'分麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (58070, '13772', '柴胡茯苓汤', '柴胡1钱2分，茯苓1钱2分，枳实（炙）6分，白术6分，人参6分，麦门冬（去心）6分，生姜（合皮，切）6分。', '以水6升，煮取1升八合，绞去滓，分温2服。服别相去7-8里，吃1服。', '《普济方》卷二三三', '腹胀瘦病，不下食。', '忌生冷、油腻、小豆、粘食、桃、李、醋物、雀肉。', '-', '柴胡', '[\'柴胡\', \'钱分\', \'茯苓\', \'钱分\', \'枳实\', \'炙分\', \'白术\', \'人参\', \'麦门冬\', \'心分\', \'生姜\', \'合皮\', \'切分\']');
INSERT INTO `prescript` VALUES (58071, '13770', '钩藤皮汤', '钩藤皮2分，麻黄（去节）2分，龙齿6分（研，绵裹），银1斤，寒水石12分，栀子（劈）12分，知母12分，石膏（碎，绵裹）12分，杏仁（去两仁皮尖，研）12分，升麻10分，子芩14分，蛇蜕皮7寸（炙），蚱蝉4枚（去足翅，炙），柴胡10分，芍药8分，沙参8分，生葛汁5分，蜜7合，牡牛黄（如大豆粒）10枚（煎成研下之）。', '钩藤饮（原书卷三七七）若大便涩者，加大黄10分。', '《普济方》卷一○○', '救急疗痫。主小儿癫痫。', '慎热面、炙肉、鱼、蒜、粘食、油腻、冷水。', '-', '柴胡', '[\'钩藤\', \'皮分\', \'麻黄\', \'节分\', \'龙齿\', \'分研\', \'绵裹\', \'银斤\', \'寒水石\', \'栀子\', \'劈分\', \'知母\', \'石膏\', \'碎绵裹\', \'杏仁\', \'两仁\', \'皮尖\', \'研分\', \'升麻\', \'分子\', \'分蛇\', \'蜕皮\', \'蚱蝉\', \'柴胡\', \'芍药\', \'沙参\', \'分生\', \'葛汁\', \'分蜜合\', \'牡牛\', \'黄如大\', \'豆粒\', \'煎成\', \'研下\']');
INSERT INTO `prescript` VALUES (58072, '13766', '柴胡清肝饮', '柴胡1钱5分，白芍（酒炒）1钱5分，黑栀1钱，连翘1钱，黄芩1钱，甘草7分。', '-', '《不知医必要》', '怒动肝火而耳闭。', '-', '-', '柴胡', '[\'柴胡\', \'钱分\', \'白芍\', \'分黑\', \'栀钱\', \'连翘\', \'黄芩\', \'甘草\']');
INSERT INTO `prescript` VALUES (58073, '13750', '柴芩解托汤', '柴胡1钱，黄芩1钱，干葛1钱，陈皮8分，山楂1钱，泽泻1钱，甘草5分，赤苓。', '方中赤苓用量原缺。内热甚，加连翘7分；外邪甚，加防风1钱；痰甚，加贝母、橘红6分；兼风热，加玉竹1钱；小便不利者，加车前子1钱。', '《不居集》上集卷十', '外感之症，寒热往来，热重寒轻，有似虚劳寒热者。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'干葛钱\', \'陈皮分\', \'山楂\', \'泽泻\', \'甘草\', \'分赤苓\']');
INSERT INTO `prescript` VALUES (58074, '13734', '柴胡散', '柴胡2两（去芦头），半夏1两（汤洗7遍，去滑），赤芍药1两，甘草半两（炙微赤，锉），知母1两，黄芩1两，川大黄2两（锉碎，微炒），陈橘皮1两（汤浸，去白瓤，焙），人参1两（去芦头）。', '每服4钱，以水1中盏，加生姜半分，煎至5分，去滓，温温频服，不拘时候，稍利为度。', '《圣惠》卷九', '伤寒7日不解，心烦，肠中有结燥，谵语。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'芦头\', \'半夏\', \'两汤\', \'洗遍\', \'芍药\', \'甘草\', \'半两\', \'炙微\', \'知母\', \'黄芩\', \'两川\', \'大黄\', \'橘皮\', \'两汤\', \'浸去\', \'人参\', \'两去\', \'芦头\']');
INSERT INTO `prescript` VALUES (58075, '13716', '大秦艽汤', '防风1钱，知母1钱，生地1钱，柴胡5分，前胡5分，秦艽5分，甘草5分，人参5分。', '-', '《嵩崖尊生》卷十四', '妇人血病，寒热往来。', '-', '-', '柴胡', '[\'防风\', \'知母\', \'生地\', \'柴胡\', \'分前\', \'胡分\', \'秦艽\', \'甘草\', \'人参\']');
INSERT INTO `prescript` VALUES (58076, '13705', '柴平散', '柴胡3钱，白术3钱，人参3钱，半夏3钱，甘草1钱，苍术3钱。', '方中人参用量原缺。', '《扶寿精方》', '疟疾。', '-', '-', '柴胡', '[\'柴胡\', \'白术\', \'人参\', \'半夏\', \'甘草\', \'苍术\']');
INSERT INTO `prescript` VALUES (58077, '13700', '活鳖煎', '活鳖1个（重半斤者，以河水养5日，用童便5升，法酒5升，乌梅5个，捶碎，桃、柳枝各锉1合，共享新绵包子慢火同煮令鳖死，便减耗一半，取出绵包子，将鳖去甲肠肚，细切研烂，再熬约得至半，碗[土自]盖盛，将甲骨炙令焦黄色，入后药），人参、琥珀、木香、柴胡、枳实、杏仁、黄耆、恒山、安息香、附子、当归、桂、羌活、知母、茯苓、乌梅肉各等分。', '每服30丸，食前、临卧用温酒送下。', '《鸡峰》卷十', '先因吐血，止而后嗽中出血如线，引胁下时痛，日渐羸瘦。', '-', '-', '柴胡', '[\'活鳖\', \'半斤\', \'河水\', \'日用\', \'童便\', \'升法\', \'酒升\', \'乌梅\', \'碎桃\', \'柳枝\', \'锉合\', \'共享\', \'新绵\', \'包子\', \'慢火\', \'同煮\', \'减耗\', \'一半\', \'取出\', \'包子\', \'去甲\', \'肠肚\', \'细切\', \'研烂\', \'半碗\', \'土自盖\', \'甲骨\', \'炙令\', \'黄色\', \'入后\', \'人参\', \'琥珀\', \'木香\', \'柴胡\', \'枳实\', \'杏仁\', \'黄耆\', \'恒山\', \'安息\', \'香附子\', \'当归\', \'羌活\', \'知母\', \'茯苓\', \'乌梅\']');
INSERT INTO `prescript` VALUES (58078, '13695', '茯神散', '茯神（去木）1两，柴胡（去苗）1两，陈橘皮（去白，炒）1两，甘草（炙）1两。', '每服5钱匕，水1盏半，煎取8分，去滓温服，不拘时候。', '《圣济总录》卷三十一', '伤寒后虚烦，心腹不快。', '-', '-', '柴胡', '[\'茯神\', \'柴胡\', \'橘皮\', \'甘草\']');
INSERT INTO `prescript` VALUES (58079, '13691', '柴胡汤', '柴胡（去苗）1两，枳壳（去瓤，麸炒）1两，升麻1两，黄连（去须）1两，麻黄（去根节）1两半，甘草（炙，锉）3分，知母（切，焙）3分，栀子仁3分。', '此证先烙上脘穴，次烙关元，及灸十八壮，若不愈，再灸七壮，次烙背心下廉及天聪，如不愈者宜服本方。', '《圣济总录》卷六十一', '心黄。面赤口张，气喘多惊，饶睡，手脚烦疼，舌上疮生，心下急闷，不欲饮食，舌缩口干，7-8日内必发狂走。', '-', '-', '柴胡', '[\'柴胡\', \'枳壳\', \'升麻\', \'黄连\', \'麻黄\', \'根节\', \'两半\', \'甘草\', \'知母\', \'切焙分\', \'栀子\', \'仁分\']');
INSERT INTO `prescript` VALUES (58080, '13670', '柴胡清肝散', '柴胡1钱，黄芩（炒）1钱，黄连（炒）7分，山栀（炒）7分，当归1钱，川芎6分，生地黄1钱，牡丹皮1钱，升麻8分，甘草3分。', '柴胡清肝饮（《证治汇补》卷四）。脾胃弱，去芩、连，加芩、术。', '《明医杂着》卷六', '肝气郁结，风热外侵，颈项结核，肿痛不消，或有寒热；肝热犯脾，口唇肿裂；妇人肝火内盛，月经光期而量多者。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'黄连\', \'分山\', \'炒分\', \'当归\', \'川芎\', \'生地黄\', \'牡丹皮\', \'升麻\', \'甘草\']');
INSERT INTO `prescript` VALUES (58081, '13634', '凉血明目汤', '熟地黄1钱5分，羌活6分，防风6分，甘菊花5分，山栀子（炒）5分，谷精草5分，柴胡5分，木贼4分，甘草3分，川芎1钱，当归1钱，白芍8分。', '上（口父）咀。水煎熟，食前温服。有泪，加川椒（炒）10粒；红热，加黄连（酒浸）5分，龙胆草（酒浸）3分。', '《杏苑》卷六', '凉血明目。主眼久昏疼，或视物不清，恶日羞明。', '-', '-', '柴胡', '[\'熟地黄\', \'钱分\', \'羌活\', \'防风\', \'甘菊花\', \'分山\', \'栀子\', \'炒分\', \'谷精草\', \'柴胡\', \'木贼\', \'甘草\', \'川芎\', \'当归\', \'白芍\']');
INSERT INTO `prescript` VALUES (58082, '13605', '黄耆散', '黄耆1两（锉），地骨皮1两，赤茯苓1两，麦门冬1两（去心），人参3分（去芦头），赤芍药1两，生干地黄1两，柴胡1两半（去苗），黄芩3分，当归3分，甘草1分（炙微赤，锉）。', '每服4钱，用水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十', '妇人热劳羸瘦，四肢烦疼，口干心躁，不欲饮食。', '-', '-', '柴胡', '[\'黄耆\', \'地骨皮\', \'两赤\', \'茯苓\', \'麦门冬\', \'两去\', \'人参\', \'分去\', \'芦头\', \'芍药\', \'两生\', \'地黄\', \'柴胡\', \'两半\', \'去苗\', \'黄芩\', \'当归\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (58083, '13603', '柴胡清肝汤', '北柴胡7分，小生地1钱5分，炒白芍1钱5分，西当归1钱5分，川贝母1钱，牡蛎粉3钱，北连翘1钱，玄参1钱，炒山甲1片，金银花1钱5分，甘草7分。', '-', '《外科真铨》卷上', '谋虑不决，郁火凝结少阳胆经而成夭疽、锐毒，生于耳后1寸3分高骨之后，左名夭疽，右为锐毒。', '-', '-', '柴胡', '[\'柴胡\', \'小生\', \'地钱分\', \'白芍\', \'分西\', \'当归\', \'钱分\', \'川贝母\', \'牡蛎\', \'粉钱北\', \'连翘\', \'玄参\', \'炒山\', \'甲片\', \'金银\', \'花钱\', \'甘草\']');
INSERT INTO `prescript` VALUES (58084, '13583', '海蛤丸', '海蛤（研如粉）1两半，诃黎勒皮（焙）1两半，柴胡（去苗）1两半，赤茯苓（去黑皮）1两半，杏仁（汤浸，去皮尖双仁，麸炒）1两半，赤芍药1两半，牵牛子（炒令熟）1两半，桑根白皮（炙，锉）1两，陈橘皮（汤浸，去白，焙）1两，贝母（去心，焙）1两，白槟榔（锉）1两，郁李仁（汤浸，去皮，别研）1两，大黄（炒，锉）1两，枳壳（去瓤，麸炒）1两。', '每服20丸，空腹煎枣汤送下，日2次。', '《圣济总录》卷八十二', '脚气上冲，头目面浮肿，咳嗽喘乏，气促胸胀，两胁硬，腰重，小便涩，胯冷，腿膝疼肿乏力。', '-', '-', '柴胡', '[\'海蛤研\', \'如粉\', \'两半\', \'黎勒皮\', \'两半\', \'柴胡\', \'去苗\', \'两半\', \'茯苓\', \'黑皮\', \'两半\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁麸\', \'两半\', \'芍药\', \'两半\', \'牵牛子\', \'炒令熟\', \'两半\', \'桑根\', \'白皮\', \'橘皮\', \'汤浸\', \'贝母\', \'两白\', \'槟榔\', \'郁李仁\', \'汤浸\', \'去皮\', \'别研\', \'大黄\', \'枳壳\']');
INSERT INTO `prescript` VALUES (58085, '13573', '柴胡散', '柴胡1两（去苗），甘草半两（炙微赤，锉），桑根白皮1两（锉），鳖甲1两（涂醋炙令黄，去裙襕），槟榔1两，旋覆花半两，川黄2两（锉碎，微炒），桔梗1两（去芦头）。', '柴胡饮（《圣济总录》卷四十九）、柴胡汤（《普济方》卷二十七）。', '《圣惠》卷四十六', '肺气暴热，大便不通，时时咳嗽，喘息促急。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'甘草\', \'半两\', \'炙微\', \'桑根\', \'白皮\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'槟榔\', \'旋覆花\', \'半两\', \'桔梗\', \'两去\', \'芦头\']');
INSERT INTO `prescript` VALUES (58086, '13571', '大紫菀丸', '紫菀茸半两，吴茱萸（汤洗7次，焙）半两，菖蒲半两，厚朴（姜制）半两，柴胡茸半两，桔梗半两，皂角（去皮子，炒黄）半两，茯苓（去皮）半两，官桂半两，干姜（炮）半两，黄连（净）半两，槟榔半两，蜀椒（去目并闭口者，炒）半两，巴豆霜半两，人参半两，羌活半两，苁蓉（酒浸，焙）半两，川大黄半两，当归（切，焙）半两，陈皮（去白）半两，防风半两，麦门冬（去心，焙）半两，熟地黄（焙）半两，汉防己半两，车前子（炒）半两，白术半两，鳖甲（去裙襕，醋炙）半两，川乌头（炮，去皮）2两半。', '每服3丸，温酒送下；米饮亦得。', '《医方类聚》卷二十四引《施圆端效方》', '诸风偏枯，风痫暗风；五癞大风，眉发退落，肢体顽痹；五噎五膈，九种心痛，八种痞闷；五邪失心，或歌或哭，如鬼所使；积癖气块，黄疸水肿；妇人经病，脐腹痛；疟疾连年不愈，一切风痹，不知痛痒。', '-', '-', '柴胡', '[\'紫菀\', \'半两\', \'吴茱萸\', \'洗次\', \'半两\', \'菖蒲\', \'半两\', \'厚朴\', \'姜制\', \'半两\', \'柴胡\', \'半两\', \'桔梗\', \'半两\', \'皂角\', \'皮子\', \'半两\', \'茯苓\', \'去皮\', \'半两\', \'官桂\', \'半两\', \'干姜\', \'半两\', \'黄连\', \'半两\', \'槟榔\', \'半两\', \'蜀椒\', \'闭口\', \'半两\', \'巴豆霜\', \'半两\', \'人参\', \'半两\', \'羌活\', \'半两\', \'苁蓉\', \'酒浸\', \'半两\', \'大黄\', \'半两\', \'当归\', \'切焙\', \'半两\', \'陈皮\', \'半两\', \'防风\', \'半两\', \'麦门冬\', \'半两\', \'熟地黄\', \'焙半\', \'两汉\', \'防己\', \'半两\', \'车前子\', \'半两\', \'白术\', \'半两\', \'鳖甲\', \'半两\', \'乌头\', \'去皮\', \'两半\']');
INSERT INTO `prescript` VALUES (58087, '13570', '柴胡汤', '柴胡（去苗）1两半，当归（切，焙）1两，食茱萸（去枝茎）1两，芍药1两半，厚朴（去粗皮，涂生姜汁炙熟，锉）1两，槟榔（炮，锉）3枚，郁李仁（汤浸，去皮）3分。', '每服3钱匕，水1盏，煎至6分，去滓，食前温服，日3次。', '《圣济总录》卷九十九', '虫心痛。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两半\', \'当归\', \'切焙\', \'食茱萸\', \'芍药\', \'两半\', \'厚朴\', \'粗皮\', \'涂生\', \'姜汁\', \'炙熟\', \'槟榔\', \'郁李仁\', \'汤浸\', \'去皮\']');
INSERT INTO `prescript` VALUES (58088, '13560', '柴胡散', '柴胡1两半（去苗），川大黄1两（锉碎，微炒），常山1两半，茵陈1两，知母1两，赤芍药1两，甘草1两（炙微赤，锉），鳖甲1两半（涂醋炙令微黄，去裙襕）。', '每服5钱，以淡浆水1大盏，煎至6分，去滓温服，不拘时候。以微吐为度，未吐再服。', '《圣惠》卷九', '伤寒4日，头痛背膊急疼，心腹壅滞。', '-', '-', '柴胡', '[\'柴胡\', \'两半\', \'苗川\', \'大黄\', \'常山\', \'两半\', \'茵陈\', \'知母\', \'两赤\', \'芍药\', \'甘草\', \'两炙微\', \'鳖甲\', \'两半\', \'涂醋\', \'炙令\', \'微黄\']');
INSERT INTO `prescript` VALUES (58089, '13546', '柴胡汤', '柴胡（去苗）1两，茵陈蒿1两，甘草（炙，锉）1两，人参1两，大黄（锉，炒）半两。', '每服3钱匕，水1盏，小麦50粒，同煎至7分，去滓温服，不拘时候。', '《圣济总录》卷三十一', '伤寒后余毒不解，颊赤口干，四肢烦热。', '-', '-', '柴胡', '[\'柴胡\', \'茵陈蒿\', \'甘草\', \'人参\', \'大黄\', \'半两\']');
