INSERT INTO `prescript` VALUES (58101, '13386', '柴胡芒消汤', '柴胡4两（洗，去苗），黄芩1两半，甘草（炙）1两半，赤茯苓1两半，半夏（汤洗7遍）1两1分。', '每服5钱，水2盏，加生姜5片，大枣2个（擘破），同煎至1盏，去滓，入芒消1钱，搅和温服。以大便利为度。', '《全生指迷方》卷二', '由三阳气盛，蕴于经络，内属脏腑，或因他病而致热证，但热不歇，日晡尤甚，口中勃勃气出，耳无所闻，昼多昏睡，睡即浪言，喜冷，小便赤涩，大便不通，脉短疾而数。', '-', '-', '柴胡', '[\'柴胡\', \'两洗\', \'黄芩\', \'两半\', \'甘草\', \'两半\', \'茯苓\', \'两半\', \'半夏\', \'洗遍\', \'两分\']');
INSERT INTO `prescript` VALUES (58102, '13369', '柴胡达原饮', '柴胡1钱半，生枳壳1钱半，川朴1钱半，青皮1钱半，炙草7分，黄芩1钱半，苦桔梗1钱，草果6分，槟榔2钱，荷叶梗5寸。', '-', '《重订通俗伤寒论》卷二', '和解三焦。主湿重于热，阻滞膜原。', '若湿已开，热已透，相火炽盛，再投此剂，反助相火愈炽，适劫胆汁而烁肝阴，酿成火旺生风，痉厥兼臻之变矣。用此方者宜慎之。', '-', '柴胡', '[\'柴胡\', \'半生\', \'枳壳\', \'钱半川朴\', \'青皮\', \'钱半炙草分\', \'黄芩\', \'钱半苦\', \'桔梗\', \'草果\', \'槟榔\', \'荷叶\', \'梗寸\']');
