INSERT INTO `prescript` VALUES (58109, '13249', '柴胡石膏汤', '柴胡4两，甘草2两，石膏8两。', '上(口父)咀。每服8钱，水1盏半，加生姜5片，煎8分，去滓温服，不拘时候。气虚体冷，加人参4两。', '《袖珍》卷四引《圣惠》', '妊妇伤暑，头痛恶寒，身热躁闷，四肢疼痛，项背拘急，口干燥。少阳阳明，外感挟火，头痛口干，身热恶寒拘急。', '-', '-', '柴胡', '[\'柴胡\', \'甘草\', \'石膏\']');
INSERT INTO `prescript` VALUES (58110, '13239', '荆芥散', '荆芥3两，雀脑芎3两，当归半两，人参半两，桂心3分，牡丹皮3分，羌活3分，防风3分，苦梗3分，大腹子3分，甘草3分，蒲黄3分，白茯苓3分，枳壳3分，厚朴3分，半夏3分，杏仁3分，款冬花3分，附子（炮）1两，干地黄1两，鳖甲1两，白芍药1两，北柴胡1两，黄耆1两，干姜半分，木香半分，沉香1分。', '每服2钱，加生姜3片，大枣1个，水1盏，煎至7分，温服。', '《妇人良方》卷五', '血风诸般疾，产后诸疾。', '-', '-', '柴胡', '[\'荆芥\', \'两雀脑\', \'当归\', \'半两\', \'人参\', \'半两\', \'桂心分\', \'牡丹皮\', \'羌活\', \'防风\', \'分苦\', \'分大\', \'腹子分\', \'甘草\', \'分蒲\', \'黄分\', \'白茯苓\', \'枳壳\', \'厚朴\', \'半夏\', \'杏仁\', \'款冬花\', \'附子\', \'两干\', \'地黄\', \'鳖甲\', \'两白\', \'芍药\', \'两北\', \'柴胡\', \'两黄耆\', \'干姜\', \'半分\', \'木香\', \'半分\', \'沉香\']');
INSERT INTO `prescript` VALUES (58111, '13234', '茯神汤', '茯神（去木）1两半，赤芍药1两半，地榆1两半，熟干地黄（焙）1两半，地骨皮1两，白术1两，甘菊花1两，柴胡（去苗）1两。', '每服3钱匕，水1盏，煎至6分，去滓温服。', '《圣济总录》卷一五一', '妇人月水不调，头目昏眩，心腹气痛，四肢麻痹，脐下胀闷。', '-', '-', '柴胡', '[\'茯神\', \'两半\', \'芍药\', \'两半\', \'地榆\', \'两半\', \'熟干\', \'地黄\', \'两半\', \'地骨皮\', \'白术\', \'甘菊花\', \'柴胡\']');
INSERT INTO `prescript` VALUES (58112, '13217', '除疟汤', '柴胡1钱，茯苓1钱，白术1钱，陈皮5分，半夏（泡）5分，黄芩5分，甘草5分，干葛5分，苍术（米泔浸，炒）5分，川芎5分。', '加生姜3片，水煎服。饮食不思，加麦芽、青皮、山楂各5分；阳分汗多，加人参、黄耆各7分，去干葛；阴分，加酒炒芍药1钱，归身、生地黄各8分，或用升麻4分；寒痰停饮，加草果仁；痰盛，加贝母、知母（炒）；截疟，加青皮、常山、槟榔、贝母各1钱；久疟邪微、潮热，加四君、四物，去干葛、苍术、川芎。', '《济阳纲目》卷二十三', '初疟。', '-', '-', '柴胡', '[\'柴胡\', \'茯苓\', \'白术\', \'陈皮分\', \'半夏\', \'泡分\', \'黄芩\', \'甘草\', \'分干葛分\', \'苍术\', \'泔浸\', \'炒分\', \'川芎\']');
INSERT INTO `prescript` VALUES (58113, '13210', '柴胡栀子饮', '柴胡1两，栀子1两，桔梗1两，生地黄1两半，地骨皮1两半，人参1两半，茯苓1两半，白术1两半，甘草1两半，当归1两半，薄荷5钱，滑石1两半，草参2钱半。', '上(口父)咀。每服1两，水2盏，加生姜3片，煎至8分，去滓，食后温服。', '《袖珍》卷二引《圣惠》', '劳热。', '-', '-', '柴胡', '[\'柴胡\', \'栀子\', \'桔梗\', \'生地黄\', \'两半\', \'地骨皮\', \'两半\', \'人参\', \'两半\', \'茯苓\', \'两半\', \'白术\', \'两半\', \'甘草\', \'两半\', \'当归\', \'两半\', \'薄荷\', \'滑石\', \'两半\', \'参钱\']');
