INSERT INTO `prescript` VALUES (58117, '13157', '柴胡汤', '柴胡（去苗）1两，麻黄（去根节）1两半，升麻1两，桂（去粗皮）3分，大黄（锉，炒）1两，甘草（炙）3分，鳖甲（醋炙，去裙襕）1两1分，枳实（去瓤，麸炒）3分，知母（焙）3分，栀子仁1分。', '每服5钱匕，水1盏半，加生姜3片，煎至7分，去滓温服。', '《圣济总录》卷二十二', '时行疫疠，数日未得汗，浑身壮热，呕逆不下食。', '-', '-', '柴胡', '[\'柴胡\', \'麻黄\', \'根节\', \'两半\', \'升麻\', \'两桂去\', \'粗皮分\', \'大黄\', \'甘草\', \'炙分\', \'鳖甲\', \'两分\', \'枳实\', \'炒分\', \'知母\', \'焙分\', \'栀子\', \'仁分\']');
