INSERT INTO `prescript` VALUES (58128, '12880', '柴胡饮', '柴胡（去苗）1两半，芎藭1两半，桑根白皮（炙，锉）1两半，白槟榔1两，羚羊角（镑）1两，人参1两，黄连（去须）1两，天雄（炮裂，去皮脐）1两，旋覆花（炒）半两，桂（去粗皮）半两，枳壳（去瓤，麸炒）半两。', '每服3钱匕，以水1盏，加生姜3片，煎取7分，去滓温服。', '《圣济总录》卷十五', '首风，头重呕哕。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两半\', \'两半\', \'桑根\', \'白皮\', \'两半\', \'槟榔\', \'羚羊角\', \'人参\', \'黄连\', \'两天\', \'雄炮裂\', \'去皮\', \'旋覆花\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'枳壳\', \'半两\']');
INSERT INTO `prescript` VALUES (58129, '12856', '黄耆丸', '黄耆1两（锉），蒺藜子1两（炒，去角），独活1两（去芦头），柴胡1两（去苗），生干地黄1两（焙），甘草1两（炙），栀子仁1两，苦参1两，白术1两，白花蛇（酒浸，去皮骨，炙）1两，防风3分（去叉），菊花3分，茯神3分（去木），山芋3分，秦艽（去苗土）3分，天门冬1两半（去心，焙），枳壳1两半（去瓤，麸炒），白槟榔（锉）1两半。', '每服30丸，空心以温酒送下。', '《圣济总录》卷一一○', '血气不足，肤睑下复睛轮，垂缓难开，又名睢目。', '-', '-', '柴胡', '[\'黄耆\', \'蒺藜\', \'独活\', \'两去\', \'芦头\', \'柴胡\', \'两去\', \'苗生\', \'地黄\', \'两焙\', \'甘草\', \'栀子\', \'苦参\', \'白术\', \'白花蛇\', \'酒浸\', \'去皮\', \'防风\', \'分去\', \'菊花\', \'茯神\', \'分去\', \'山芋\', \'秦艽\', \'苗土\', \'天门冬\', \'两半\', \'枳壳\', \'两半\', \'槟榔\', \'两半\']');
INSERT INTO `prescript` VALUES (58130, '12838', '柴胡散', '柴胡3分（去苗），木香半两，当归半两（微炒），木通3分（锉），紫苏茎叶半两，竹茹半两，黄耆3分（锉）。', '每服5钱，用水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十四', '伤寒后阴阳易，小腹急胀，卵缩，小便不通。', '-', '-', '柴胡', '[\'柴胡\', \'苗木\', \'半两\', \'当归\', \'半两\', \'木通\', \'紫苏\', \'半两\', \'竹茹\', \'半两\', \'黄耆\']');
INSERT INTO `prescript` VALUES (58131, '12816', '柴胡汤', '柴胡（去苗）半两，芍药半两，知母（焙）半两，桃仁（汤浸，去皮尖双仁，炒黄）半两，木香半两，山栀子仁半两，升麻半两，大青半两，白芷半两，黄芩（去黑心）半两，细辛（去苗叶）1分，甘草（炙，锉）1分，石膏1两。', '每服5钱匕，水1盏半，加生姜半分（拍碎），同煎至7分，去滓，食后温服，日3次。', '《圣济总录》卷三十一', '伤寒后百节疼痛，壮热头疼。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'半两\', \'芍药\', \'半两\', \'知母\', \'半两\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁\', \'半两\', \'木香\', \'半两\', \'栀子\', \'半两\', \'升麻\', \'半两\', \'大青\', \'半两\', \'白芷\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'细辛\', \'苗叶分\', \'甘草\', \'石膏\']');
INSERT INTO `prescript` VALUES (58132, '12812', '柴胡加大黄汤', '柴胡1两，黄芩1钱，人参1钱，半夏1钱，甘草1钱，生姜3钱半，大黄（量虚实加之）。', '柴胡饮子（《伤寒大白》卷二）。', '《袖珍小儿》卷二', '小儿惊风，痰热。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'人参\', \'半夏\', \'甘草\', \'生姜\', \'钱半\', \'大黄\', \'虚实\', \'加之\']');
INSERT INTO `prescript` VALUES (58133, '12801', '草果饮', '常山1两，柴胡1两，甘草半两，槟榔半两，白姜7钱，乌梅（随意加减）。', '本方名草果饮，但方中无草果，疑脱。', '《朱氏集验方》卷二', '实疟。', '-', '-', '柴胡', '[\'常山\', \'柴胡\', \'甘草\', \'半两\', \'槟榔\', \'半两\', \'白姜钱\', \'乌梅\', \'随意\', \'加减\']');
INSERT INTO `prescript` VALUES (58134, '12800', '济阳逐火汤', '胆草2钱，升麻2钱，柴胡1钱，甘草2钱，生石膏2钱（引）。', '此方服后，头痛甚者，切不可畏，火燥则炀，随服必平，须记之。', '《点点经》卷一', '头脑作痛，不拘左右；胃火牙痛。', '-', '-', '柴胡', '[\'胆草\', \'升麻\', \'柴胡\', \'甘草\', \'生石膏\', \'钱引\']');
INSERT INTO `prescript` VALUES (58135, '12793', '羚羊饮', '羚羊角（镑）1钱5分，知母1钱，黄芩1钱，黑参1钱，桔梗1钱，柴胡1钱，栀子（炒）1钱，茺蔚子2钱。', '以水2盏，煎至1盏，去滓，食后温服。', '《金鉴》卷七十八', '肝肺之热，冲于眼内，致生赤膜下垂。初患之时，气轮上边起赤膜一片，垂至风轮，下覆瞳仁，泪流痛痒。', '-', '-', '柴胡', '[\'羚羊角\', \'钱分\', \'知母\', \'黄芩\', \'参钱\', \'桔梗\', \'柴胡\', \'栀子\', \'茺蔚子\']');
INSERT INTO `prescript` VALUES (58136, '12792', '柴胡散', '柴胡1两（去苗），旋覆花3分，青橘皮3分（汤浸，去白瓤），大腹子3分，半夏3分（汤洗7遍去滑），桑根白皮1两半（锉），紫苏茎叶3分，马牙消2两，甘草半两（炙微赤，锉）。', '每服4钱，以水1大盏，加生姜半分，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十四', '伤寒后脚气上攻，心胸烦闷，喘促食少。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'旋覆花\', \'分青\', \'橘皮\', \'分汤\', \'浸去\', \'白瓤大\', \'腹子分\', \'半夏\', \'分汤\', \'洗遍\', \'滑桑根\', \'白皮\', \'两半\', \'紫苏\', \'叶分\', \'马牙消\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58137, '12767', '鬼箭羽汤', '鬼箭羽1两，桃仁（汤浸，去皮尖双仁）60枚（炒），芍药2两，鬼臼（削去皮，微炒）1两，陈橘皮（汤浸，去白，焙）2两，当归（切，焙）1两，桂（去粗皮）1两，柴胡（去苗）1两，大黄（锉碎，醋炒）1两半。', '每服5钱匕，以水1盏半，加生姜1分（拍破），同煎至1盏，去滓，加麝香末1字匕，丹砂末、朴消末各半钱匕，再煎1沸，温服，每日2次，以快利为度。', '《圣济总录》卷五十六', '心腹(疒丂) 痛，或暴得恶注。', '-', '-', '柴胡', '[\'鬼箭羽\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁枚\', \'芍药\', \'鬼臼\', \'去皮\', \'橘皮\', \'汤浸\', \'当归\', \'切焙\', \'两桂去\', \'粗皮\', \'柴胡\', \'大黄\', \'两半\']');
INSERT INTO `prescript` VALUES (58138, '12763', '黑虎汤', '玄参1斤，柴胡3钱，生甘草1两。', '煎汤10碗，再煎汁取3碗，分2日服完。未破者即消，已破者生肌自愈。生头面者，加川芎2两，附子2钱；生身左右前后者，加当归2两，甘菊花1两，附子3分；生手足四肢者，加白术2两，茯苓1两，附子5分。', '《疡医大全》卷二十二', '无名肿毒。', '-', '-', '柴胡', '[\'玄参\', \'柴胡\', \'生甘草\']');
INSERT INTO `prescript` VALUES (58139, '12745', '柴胡汤', '柴胡（去苗）1两，升麻3分，芍药3分，麦门冬（去心，焙）3分，甘草3分，黄芩（去黑心）3分，知母（焙）5钱，黄连5钱。', '方中知母、黄连用量原缺，据《普济方》补。', '《圣济总录》卷三十', '伤寒热病后，乍寒乍热，骨节痛，口舌生疮。', '-', '-', '柴胡', '[\'柴胡\', \'升麻\', \'芍药\', \'麦门冬\', \'心焙分\', \'甘草\', \'黄芩\', \'黑心\', \'知母\', \'黄连\']');
INSERT INTO `prescript` VALUES (58140, '12731', '柴胡散', '柴胡（去苗）2两，赤芍药2两，知母2两，栀子仁2两，川升麻3分，黄芩3分，大青3分，杏仁（汤浸，去皮尖双仁，麸炒微黄）3分，甘草（炙微赤，锉）3分，石膏4两，川大黄3分（锉碎，微炒）。', '每服4钱，以水1中盏，加豉50粒，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒谵语，头痛壮热，百骨节疼痛。', '-', '-', '柴胡', '[\'柴胡\', \'芍药\', \'知母\', \'栀子\', \'仁两川\', \'升麻\', \'黄芩\', \'分大青分\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\', \'甘草\', \'炙微\', \'石膏\', \'两川\', \'大黄\']');
INSERT INTO `prescript` VALUES (58141, '12730', '柴胡散', '柴胡1两（去苗），赤茯苓3分，甘草半两（炙微赤，锉），白术3分，枳壳1两（麸炒），川大黄1两（锉碎，微炒），芎藭半两，桂心半两，京三棱1两（炮，锉）。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓，食前温服。', '《圣惠》卷三十一', '骨蒸痃癖，体瘦食少。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'茯苓\', \'甘草\', \'半两\', \'炙微\', \'白术\', \'枳壳\', \'两麸\', \'炒川\', \'大黄\', \'半两\', \'桂心\', \'半两\', \'三棱\', \'两炮\']');
INSERT INTO `prescript` VALUES (58142, '12718', '柴胡饮', '柴胡（去苗）2两，牛膝（去苗）2两，枳壳（去瓤，麸炒）2两，庵（艹闾）子2两半，大黄（锉，炒）2两半，土瓜根2两半，牡丹皮1两半，桂（去粗皮）1两半，桃仁（汤浸，去皮尖双仁，麸炒）50枚，朴消2两。', '上(口父)咀。每服3钱匕，水1盏，加生姜1枣大（拍碎），煎至6分，去滓温服，有顷再服。', '《圣济总录》卷一五一', '室女月水不通，心腹胀满。', '-', '-', '柴胡', '[\'柴胡\', \'牛膝\', \'枳壳\', \'两庵\', \'闾子\', \'两半\', \'大黄\', \'两半\', \'土瓜根\', \'两半\', \'牡丹皮\', \'两半\', \'桂去\', \'粗皮\', \'两半\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁麸\', \'朴消\']');
INSERT INTO `prescript` VALUES (58143, '12704', '黄耆散', '黄耆（去芦，蒸出，擘破，于槐砧上碎锉）1两，甘草半两（炙），柴胡1两（去芦，以布拭去土净，锉，勿犯铁器），人参1两，秦艽1两（须是于脚下左交裂者，以布拭却毛），川升麻半两，山栀子1两（如雀脑者，去皮，以甘草水浸1宿，焙用），黄芩1两，地骨皮半两，茯苓（赤者，以水中澄去浮者，炒用）1两。', '每服3钱，水1盏，同煎至6分，去滓，食后温服。', '《博济》卷二', '肺脏壅塞咳嗽，涕唾稠粘，咽喉不利。', '-', '-', '柴胡', '[\'黄耆\', \'芦蒸\', \'出擘\', \'破于\', \'甘草\', \'半两\', \'柴胡\', \'两去\', \'芦以布\', \'拭去\', \'土净\', \'铁器\', \'人参\', \'秦艽\', \'两须\', \'脚下\', \'左交裂者\', \'以布\', \'毛川\', \'升麻\', \'半两\', \'栀子\', \'两如雀\', \'脑者\', \'去皮\', \'甘草\', \'水浸\', \'宿焙用\', \'黄芩\', \'地骨皮\', \'半两\', \'茯苓\', \'赤者\', \'水中\', \'澄去\', \'浮者\', \'炒用\']');
INSERT INTO `prescript` VALUES (58144, '12688', '麻黄汤', '前胡5钱，柴胡（去毛）5钱，石膏5钱，苍术（锉，炒）5钱，藁本5钱，赤芍药5钱，白芷5钱，土芎5钱，干葛5钱，升麻5钱，麻黄3钱。', '每服4钱，加生姜3片，连须葱2根，水煎服，不拘时候。春加黄芩，夏用正方，秋加麻黄，冬加豆豉。', '《得效》卷十五', '发散四时伤寒。主四时伤寒，潮热头痛，及时疫。', '-', '-', '柴胡', '[\'胡钱\', \'柴胡\', \'毛钱\', \'石膏\', \'苍术\', \'藁本\', \'芍药\', \'白芷\', \'钱土\', \'干葛钱\', \'升麻\', \'麻黄\']');
INSERT INTO `prescript` VALUES (58145, '12670', '葛根解托汤', '干葛8分，柴胡8分，前胡8分，防风6分，陈皮1钱，半夏1钱，泽泻1钱，生甘草3分，生姜、大枣。', '方中生姜、大枣用量原缺。如寒气胜者，加当归7分，肉桂5分；阴气不足者，加熟地1钱；若元气大虚，正不胜邪，兼用补托之法；如头痛者，加川芎、白芷各7分；气逆多嗽者，加杏仁1钱；痞满气滞者，加白芥子5-7分。', '《不居集》（上集》卷十', '正气内虚，客邪外逼，有似虚劳各症。', '-', '-', '柴胡', '[\'干葛分\', \'柴胡\', \'分前\', \'胡分\', \'防风\', \'陈皮\', \'半夏\', \'泽泻\', \'生甘草\', \'生姜\', \'大枣\']');
INSERT INTO `prescript` VALUES (58146, '12657', '栝楼散', '栝楼1两半，柴胡（去苗）1两半，知母1两半，黄芩1两半，甘草（炙微赤，锉）1两半。', '本方方名，《普济方》引作“瓜蒌散”。', '《圣惠》卷十一', '阳毒伤寒5-6日以上，但胸中烦热，干呕。', '-', '-', '柴胡', '[\'栝楼\', \'两半\', \'柴胡\', \'去苗\', \'两半\', \'知母\', \'两半\', \'黄芩\', \'两半\', \'甘草\', \'炙微\', \'两半\']');
INSERT INTO `prescript` VALUES (58147, '12656', '春泽汤', '泽泻3钱，猪苓3钱，赤茯苓2钱，白术2钱，官桂1钱，人参2钱，柴胡2钱，麦门冬2钱。', '每服5钱，水1盏半，灯草20根，同煎8分，去滓，空心服。', '《普济方》卷一三三引《御药院方》', '伤寒表里不解，发渴饮水，小便赤涩，阴阳不分，疑贰之间。', '-', '-', '柴胡', '[\'泽泻\', \'猪苓\', \'茯苓\', \'白术\', \'官桂钱\', \'人参\', \'柴胡\', \'麦门冬\']');
