INSERT INTO `prescript` VALUES (58168, '12421', '桔梗散', '桔梗1两（去芦头），柴胡1两（去苗），赤芍药3分，赤茯苓3分，旋覆花半两，五味子3分，人参1两（去芦头），鳖甲1两（涂醋炙微黄，去裙襕），陈橘皮1两（汤浸，去白瓤，焙），白术3分，槟榔3分，甘草1分（炙微赤，锉）。', '每服3钱，以水1中盏，加生姜半分，大枣3枚，煎至6分，去滓稍热服，不拘时候。', '《圣惠》卷二十八', '虚劳痰饮，胸胁气不利。', '忌苋菜。', '-', '柴胡', '[\'桔梗\', \'两去\', \'芦头\', \'柴胡\', \'两去\', \'芍药\', \'分赤\', \'茯苓\', \'旋覆花\', \'半两\', \'五味子\', \'人参\', \'两去\', \'芦头\', \'鳖甲\', \'两涂\', \'微黄\', \'橘皮\', \'两汤\', \'浸去\', \'白术\', \'槟榔\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (58169, '12413', '柴胡饮', '柴胡（去苗）2两，青蒿2两，嫩桃枝2两，嫩柳枝（各阴干取）2两，地骨皮2两，甘草（炙）2两。', '每服2钱，加乌梅1个（拍破），小麦49粒，水1盏，煎7分，食后、临卧温服。', '《幼幼新书》卷二十引《庄氏家传》', '小儿肌热盗汗，不思饮食。', '-', '-', '柴胡', '[\'柴胡\', \'青蒿\', \'两嫩\', \'桃枝\', \'两嫩\', \'柳枝\', \'阴干\', \'地骨皮\', \'甘草\']');
INSERT INTO `prescript` VALUES (58170, '12406', '柴胡饮', '柴胡（去芦）2钱，羌活2钱，法夏（姜汁炒）2钱，僵蚕2钱（姜汁炒），桔梗5分，银花5分，蝉退7只（去头足翅），厚朴5分（姜汁炒），陈皮1钱，粉草1钱，生姜3片。', '水煎服。', '《白喉全生集》', '白喉寒证初起，满喉淡红，微肿略痛，头痛，恶寒发热，饮食如常，舌苔白，二便和。', '-', '-', '柴胡', '[\'柴胡\', \'芦钱\', \'羌活\', \'钱法\', \'姜汁\', \'僵蚕\', \'姜汁\', \'桔梗\', \'银花\', \'分蝉\', \'头足\', \'厚朴\', \'姜汁\', \'陈皮\', \'粉草\', \'生姜\']');
INSERT INTO `prescript` VALUES (58171, '12405', '桂心散', '桂心2两，麻黄2两（去根节），附子2两（炮裂，去皮脐），甘草半两（炙微赤，锉），赤芍药半两，干姜半两（炮裂），枳壳半两（麸炒微黄，去瓤），柴胡1两（去苗），杏仁半两（汤浸，去皮尖双仁，麸炒微黄）。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓，稍热频服，不拘时候。汗出为度。', '《圣惠》卷十一', '阴毒伤寒，项直脚冷，百节疼痛。', '-', '-', '柴胡', '[\'桂心\', \'麻黄\', \'两去\', \'根节\', \'附子\', \'两炮裂\', \'去皮\', \'甘草\', \'半两\', \'炙微\', \'芍药\', \'半两\', \'干姜\', \'半两\', \'炮裂\', \'枳壳\', \'半两\', \'微黄\', \'柴胡\', \'两去\', \'杏仁\', \'半两\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (58172, '12390', '柴胡散', '柴胡2分（去苗），黄芩1两，人参半两（去芦头），甘草半两（炙微赤，锉），赤茯苓半两，麦门冬半两（去心，焙）。', '柴胡汤（《圣济总录》卷一六八）、柴苓散（《南北经验方》卷十引汤氏方）、柴苓汤（《袖珍小儿》卷四）、柴芩竹叶饮（《不知医必要》）。', '《圣惠》卷八十二', '小儿腹中有伏热，温壮来去。', '-', '-', '柴胡', '[\'柴胡\', \'分去\', \'黄芩\', \'人参\', \'半两\', \'芦头\', \'甘草\', \'半两\', \'炙微\', \'茯苓\', \'半两\', \'麦门冬\', \'半两\']');
INSERT INTO `prescript` VALUES (58173, '12384', '茯苓汤', '赤茯苓（去黑皮）2两，五味子（炒）2两，麦门冬（去心，焙）2两，柴胡（去苗）1两半，桂（去粗皮）1两，槟榔（锉）半两，细辛（去苗叶）半两。', '每服4钱匕，水1大盏，煎至6分，去滓温服。', '《圣济总录》卷二十八', '伤寒柔痓，病经三日不愈，恐阴气攻五脏致损。', '-', '-', '柴胡', '[\'茯苓\', \'黑皮\', \'五味子\', \'麦门冬\', \'柴胡\', \'去苗\', \'两半\', \'桂去\', \'粗皮\', \'槟榔\', \'半两\', \'细辛\', \'苗叶\', \'半两\']');
INSERT INTO `prescript` VALUES (58174, '12379', '柴胡当归汤', '柴胡（去苗）4两，当归（切，焙）1两，细辛（去苗叶）半两，防风（去叉）1两，麻黄（去根节）1两，桂（去粗皮）1两，半夏（汤洗去滑，生姜汁制，焙干）2两半，人参半两，黄耆（挫）1两，黄芩（去黑心）半两，杏仁（汤浸，去皮尖双仁，炒）25枚。', '每服3钱匕，加生姜1枣大（拍碎），大枣2枚（擘），水1盏，煎至7分，去滓温服，日3次。', '《圣济总录》卷六十七', '五脏虚乏不足，上气；五脏闭塞，不得饮食，胸胁支胀，乍来乍去，虚气在心，滞气在胃，唇干口燥，肢体动摇，手足冷疼，梦寐恐怖。', '-', '-', '柴胡', '[\'柴胡\', \'当归\', \'切焙\', \'细辛\', \'苗叶\', \'半两\', \'防风\', \'麻黄\', \'根节\', \'两桂去\', \'粗皮\', \'半夏\', \'汤洗\', \'滑生\', \'姜汁\', \'焙干\', \'两半\', \'人参\', \'半两\', \'黄耆\', \'黄芩\', \'黑心\', \'半两\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\']');
INSERT INTO `prescript` VALUES (58175, '12372', '柴胡汤', '柴胡（去苗）1两，桑根白皮1两，羌活（去芦头）1两，百合1两，当归（切，焙）1两，半石膏（碎），麻黄（去根节，先煎，掠去沫，焙）、天雄（炮裂，去皮脐）2两，黄连（去须）1两，贝母（煨，去心）1两，五味子1两，桂（去粗皮）1两，枳壳（去瓤，麸炒）1两，白石脂1两，款冬花1两，黄芩（去黑心）半两，杏仁10枚（去皮尖双仁，炒）。', '每服5钱匕，水1盏半，加生姜1枣大（切），煎至8分，去滓温服。', '《圣济总录》卷二十四', '伤寒后咳嗽经旬未愈。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两桑根\', \'白皮\', \'羌活\', \'芦头\', \'百合\', \'当归\', \'切焙\', \'两半\', \'石膏\', \'麻黄\', \'根节\', \'掠去\', \'天雄\', \'炮裂\', \'去皮\', \'黄连\', \'贝母\', \'五味子\', \'两桂去\', \'粗皮\', \'枳壳\', \'白石\', \'脂两\', \'款冬花\', \'黄芩\', \'黑心\', \'半两\', \'杏仁\', \'皮尖\', \'双仁\']');
INSERT INTO `prescript` VALUES (58176, '12367', '柴胡厚朴汤', '柴胡（去苗）1两，厚朴（去粗皮，姜汁炙）1两，朴消（研）1两，大黄（锉，炒）1两半，枳壳（去瓤，麸炒）3分。', '每服5钱匕，水1盏半，煎至7分，去滓温服。以利为度。', '《圣济总录》卷二十三', '伤寒后，腹中有结燥，潮热不退，或时头痛目眩。', '-', '-', '柴胡', '[\'柴胡\', \'厚朴\', \'粗皮\', \'姜汁\', \'两朴\', \'消研\', \'大黄\', \'两半\', \'枳壳\', \'炒分\']');
INSERT INTO `prescript` VALUES (58177, '12360', '荆芥汤', '荆芥穗1两，人参半两，木香半两，芍药半两，生干地黄（焙）半两，秦艽（去苗土）半两，柴胡（去苗）半两，当归（切，焙）半两，半夏（生姜自然汁制，焙干）半两，乌药半两，芎藭半两，甘草（炙）半两。', '每服3钱匕，水1盏，加生姜3片，同煎至7分，去滓，空心、日午、临卧服。', '《圣济总录》卷一五○', '妇人血风劳气，肢体羸瘦，饮食减少，疼痛寒热。', '-', '-', '柴胡', '[\'荆芥\', \'人参\', \'半两\', \'木香\', \'半两\', \'芍药\', \'半两\', \'生干\', \'地黄\', \'半两\', \'秦艽\', \'苗土\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'当归\', \'切焙\', \'半两\', \'半夏\', \'生姜\', \'自然\', \'汁制\', \'焙干\', \'半两\', \'乌药\', \'半两\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (58178, '12350', '柴胡散', '柴胡1两（去苗），龙胆半两（去芦头），麦门冬1两半（去心，焙），玄参1两，甘草1两（炙微赤，锉），人参1两（去芦头）。', '柴胡汤（《圣济总录》卷一六七）、柴胡麦门冬散（《小儿痘疹方论》）、柴胡麦门冬汤（《普济方》卷四○四）、二参饮（《普济方》卷四○四）、六味柴胡散（《疮疡经验全书》卷八）、二参汤（《医学入门》卷八）、柴胡麦冬汤（《治痘全书》卷十四）、六味柴胡麦冬散（《痘麻绀珠》卷六）。', '《圣惠》卷八十二', '小儿变蒸，经时不止，挟热心烦，啼叫无歇，骨热面黄。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'龙胆\', \'半两\', \'芦头\', \'麦门冬\', \'两半\', \'玄参\', \'甘草\', \'两炙微\', \'人参\', \'两去\', \'芦头\']');
INSERT INTO `prescript` VALUES (58179, '12346', '柴胡散', '柴胡1两（去苗），鳖甲1两（涂醋炙令黄，去裙襕），白术3分，人参3分（去芦头），天门冬1两（去心），桑根白皮半两（锉），枳壳半两（麸炒微黄，去瓤），桂心半两，当归半两（锉，微炒），百合3分，紫菀3分（洗，去苗土），桔梗半两（去芦头），款冬花半两，麦门冬半两（去心），陈橘皮半两（汤浸，去白瓤，焙），黄芩半两，枇杷叶3分（拭去毛，炙令黄），甘草半两（炙微赤，锉）。', '每服5钱，以水1大盏，加生姜半分，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十四', '伤寒后夹劳，体瘦少力，四肢疼痛，心膈痰壅，时有咳嗽，不思饮食。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'白术\', \'人参\', \'分去\', \'芦头\', \'天门冬\', \'两去\', \'心桑根\', \'白皮\', \'半两\', \'枳壳\', \'半两\', \'微黄\', \'桂心\', \'半两\', \'当归\', \'半两\', \'百合\', \'紫菀\', \'分洗\', \'苗土\', \'桔梗\', \'半两\', \'芦头\', \'款冬花\', \'半两\', \'麦门冬\', \'半两\', \'橘皮\', \'半两\', \'汤浸\', \'黄芩\', \'半两\', \'枇杷叶\', \'拭去\', \'毛炙令\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58180, '12339', '柴胡白虎汤', '官拣参1钱，熟石膏2钱，净知母1钱，北柴胡1钱，炙甘草1钱。', '合1剂，用早粳米1握为引，水煎。热服。', '《幼幼集成》卷二', '表里皆热，大热大渴，自汗。', '-', '-', '柴胡', '[\'参钱\', \'熟石膏\', \'钱净\', \'知母\', \'钱北\', \'柴胡\', \'甘草\']');
INSERT INTO `prescript` VALUES (58181, '12321', '茯神散', '茯神1两，黄耆3分（锉），赤芍药3分，麦门冬3分（去心），石膏1两半，蔓荆子3分，人参1两（去芦头），防风半两（去芦头），酸枣仁3分（微炒），羚羊角屑3分，柴胡1两（去苗），甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷六十九', '妇人风眩头疼，心神烦热，恍惚不得睡卧，少思饮食。', '-', '-', '柴胡', '[\'茯神\', \'两黄耆\', \'芍药\', \'麦门冬\', \'分去\', \'石膏\', \'两半\', \'蔓荆子\', \'人参\', \'两去\', \'芦头\', \'防风\', \'半两\', \'芦头\', \'酸枣仁\', \'分微\', \'羚羊角\', \'柴胡\', \'两去\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58182, '12297', '大黄汤', '大黄（锉，炒）1两，柴胡（去苗）3分，升麻3分，黄芩（去黑心）3分，知母（焙）3分，芍药3分，栀子仁3分，枳实（去瓤，麸炒黄）半两，细辛（去苗叶）1分。', '每服2钱匕，水1盏，加竹叶10片，同煎至6分，去滓，分温3服，早、晚各1次。', '《圣济总录》卷一七七', '小儿8岁以上热结痰实，不能食。', '-', '-', '柴胡', '[\'大黄\', \'柴胡\', \'苗分\', \'升麻\', \'黄芩\', \'黑心\', \'知母\', \'焙分\', \'芍药\', \'栀子\', \'仁分\', \'枳实\', \'半两\', \'细辛\', \'苗叶分\']');
INSERT INTO `prescript` VALUES (58183, '12294', '淋洗秦皮汤', '秦皮2两（去粗皮），柴胡2两（去苗），黄柏2两（去粗皮），黄连2两（去须），蛇衔2两，苦竹叶2握，细辛（去苗叶）1两。', '本方方名，《普济方》引作“秦皮汤”。', '《圣济总录》卷一一三', '目生眵（目蔑）。', '-', '-', '柴胡', '[\'秦皮\', \'两去\', \'粗皮\', \'柴胡\', \'两去\', \'黄柏\', \'两去\', \'粗皮\', \'黄连\', \'两去\', \'苦竹叶\', \'细辛\', \'苗叶\']');
INSERT INTO `prescript` VALUES (58184, '12286', '柴胡膏', '柴胡半两（去芦），红芍药1两1分，蒺藜根1两1分，青橘皮半两，川附子（炮）半两，吴茱萸半两，陈橘皮半两，青木香1分，乌鸡1只（净，去骨皮毛肠肚，唯择肉）。', '每用膏1匙头，食前用盐酒1盏调服。常令患人有酒容，只服2日，便见效验。', '《博济》卷一', '五劳七伤，肢体烦倦，日渐消瘦，行步稍难，饮食不进。', '-', '-', '柴胡', '[\'柴胡\', \'半两\', \'芦红\', \'芍药\', \'两分\', \'蒺藜\', \'两分\', \'橘皮\', \'半两\', \'附子\', \'半两\', \'吴茱萸\', \'半两\', \'橘皮\', \'半两\', \'青木香\', \'乌鸡\', \'只净\', \'皮毛\', \'肠肚\', \'唯择\']');
INSERT INTO `prescript` VALUES (58185, '12269', '常山甘草饮', '常山1两，甘草（炙，锉）1两，大黄（锉，炒）1两，桑根白皮（锉）1两半，乌梅5枚（去核），杏仁（汤浸，去皮尖双仁，炒）10枚，黄连（去须）1两，羌活（去芦头）1两半，黄芩（去黑心）半两，芎藭1两，枳壳（去瓤，麸炒）1两，赤茯苓（去黑皮）1两，旋复花（熬）半两，柴胡（去苗）1两半，附子（炮裂，去皮脐）1两，牵牛子（炒）1两半，桂（去粗皮）1两，虎头骨牙齿（酥炙黄）1两半。', '每服5钱匕，水1盏半，煎至8分，去滓温服。', '《圣济总录》卷三十五', '痰疟寒热。', '-', '-', '柴胡', '[\'常山\', \'甘草\', \'大黄\', \'两桑根\', \'白皮\', \'两半\', \'乌梅\', \'去核\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\', \'黄连\', \'羌活\', \'芦头\', \'两半\', \'黄芩\', \'黑心\', \'半两\', \'枳壳\', \'茯苓\', \'黑皮\', \'旋复花\', \'半两\', \'柴胡\', \'去苗\', \'两半\', \'附子\', \'炮裂\', \'去皮\', \'牵牛子\', \'两半\', \'桂去\', \'粗皮\', \'两虎\', \'头骨\', \'牙齿\', \'两半\']');
INSERT INTO `prescript` VALUES (58186, '12259', '柴胡桂姜半夏汤', '柴胡3钱，黄芩2钱，干姜3钱，桂枝2钱，甘草2钱，牡蛎2钱，瓜蒌3钱，半夏3钱，生姜3钱。', '流水煎大半杯，温服。', '《医学摘粹》', '寒疫。少阳经传太阴脏，胸胁痞满，呕吐。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'干姜\', \'桂枝\', \'甘草\', \'牡蛎\', \'钱瓜\', \'半夏\', \'生姜\']');
INSERT INTO `prescript` VALUES (58187, '12254', '黄芩散', '黄芩1两，黄连（去须）1两，决明子1两，玄参1两，柴胡（去苗）1两。', '每服5钱，以水1大盏，入竹叶3-7片，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒热毒气攻眼，翳膜赤痛。', '-', '-', '柴胡', '[\'黄芩\', \'黄连\', \'决明子\', \'玄参\', \'柴胡\']');
INSERT INTO `prescript` VALUES (58188, '12249', '柴胡鳖甲汤', '柴胡（去苗）1两，鳖甲（小便浸3日，逐日换小便，炙黄，去裙襕脊骨）1两半，秦艽（去苗土）1两，桔梗（炒）1两，人参1两，芎藭1两，当归（切，焙）1两，白茯苓（去黑皮）1两，桂（去粗皮）1两，槟榔（锉）1两，紫菀（去苗土）1两，桑根白皮（锉）1两，地骨皮1两，生干地黄（焙）1两，白术1两，知母（焙）1两，芍药1两，甘草（炙，锉）3分。', '每服3钱匕，水、童便各半盏，同煎至7分，去滓，通口服，空心、日午、临卧各1次。', '《圣济总录》卷八十八', '虚劳潮热，心神烦躁，咳嗽盗汗，肢节酸痛，夜卧不安。', '-', '-', '柴胡', '[\'柴胡\', \'鳖甲\', \'小便\', \'浸日\', \'逐日\', \'小便\', \'黄去\', \'脊骨\', \'两半\', \'秦艽\', \'苗土\', \'桔梗\', \'人参\', \'当归\', \'切焙\', \'白茯苓\', \'黑皮\', \'两桂去\', \'粗皮\', \'槟榔\', \'紫菀\', \'苗土\', \'两桑根\', \'白皮\', \'地骨皮\', \'两生\', \'地黄\', \'白术\', \'知母\', \'芍药\', \'甘草\']');
