INSERT INTO `prescript` VALUES (58191, '12198', '柴胡散', '柴胡（去芦）3钱，人参1钱，茯芩（去皮）1钱，桔梗2钱，芍药（酒浸，炒）2钱，川归（酒浸）2钱，青皮（去白）3分，麦门冬3分（去心），甘草1分。', '水1盏，煎7分，温服。', '《医学正传》卷三', '虚劳。', '-', '-', '柴胡', '[\'柴胡\', \'芦钱\', \'人参\', \'茯芩\', \'去皮\', \'桔梗\', \'芍药\', \'酒浸\', \'钱川\', \'归酒\', \'浸钱\', \'青皮\', \'白分\', \'麦门冬\', \'分去\', \'甘草\']');
INSERT INTO `prescript` VALUES (58192, '12187', '鹿胎丸', '益母草32两，当归8两，白芍8两，柴胡2两，木香2两，川芎1两，鹿胎1具（约16两）。', '每服1丸，黄酒送下，温开水亦可。', '《全国中药成药处方集》（北京方）', '理血温经。主经血不调，少腹冷痛，肢体酸软。', '孕妇忌服。', '-', '柴胡', '[\'益母草\', \'当归\', \'白芍\', \'柴胡\', \'木香\', \'川芎\', \'两鹿\', \'胎具\']');
INSERT INTO `prescript` VALUES (58193, '12176', '柴胡散', '柴胡1两（去苗），麻黄1两（去根节），葛根3分（锉），桂心3分，甘草1分（炙微赤，锉）。', '每服4钱，以水1中盏，加豉50粒，薄荷2-7叶，煎至6分，去滓，不拘时候，先吃粥少许，后以热水淋浴，然后服此药。衣覆取汗。', '《圣惠》卷十五', '时气2日，壮热憎寒，头痛，腰脊强重。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'麻黄\', \'两去\', \'根节\', \'葛根\', \'桂心分\', \'甘草\', \'分炙微\']');
