INSERT INTO `prescript` VALUES (58195, '12156', '柴胡散', '柴胡1两（去苗），贝母1两（煨令微黄），知母1两，人参1两（去芦头），赤芍药1两，石膏1两，黄芩3分，白术半两，杏仁1两（汤浸，去皮尖双仁，麸炒微黄），栀子仁半两，鳖甲1两（涂醋炙微黄，去裙澜）。', '每服5钱，以水1大盏，加生姜半分，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十四', '伤寒后夹劳，骨节烦疼，时有寒热，咳嗽，头目疼痛。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'贝母\', \'微黄\', \'知母\', \'人参\', \'两去\', \'芦头\', \'芍药\', \'石膏\', \'黄芩\', \'白术\', \'半两\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'栀子\', \'半两\', \'鳖甲\', \'两涂\', \'微黄\']');
INSERT INTO `prescript` VALUES (58196, '12143', '柴胡散', '柴胡1两（去苗），川大黄3分（锉碎，微炒），黄芩3分，赤芍药3分，枳壳半两（麸炒微黄，去瓤），半夏3分（汤浸7遍去滑）。', '每服5钱，以水1大盏，加生姜半分，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十七', '热病已得汗，热不解，腹满胀痛，烦躁发狂。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'苗川\', \'大黄\', \'黄芩\', \'分赤\', \'芍药\', \'枳壳\', \'半两\', \'微黄\', \'半夏\', \'分汤\', \'浸遍\']');
INSERT INTO `prescript` VALUES (58197, '12098', '凉血地黄汤', '黄芩1分，荆芥穗1分，蔓荆子1分，黄柏2分，知母2分，藁本2分，细辛2分，川芎2分，黄连3分，羌活3分，柴胡3分，升麻3分，防风3分，生地黄5分，当归5分，甘草1钱，红花少许。', '生地黄汤（《杏苑》卷八）、地黄汤（《女科秘要》卷四）。', '《兰室秘藏》卷中', '妇人肾水阴虚，不能镇守包络相火，而致血崩。', '-', '-', '柴胡', '[\'黄芩\', \'荆芥\', \'穗分\', \'蔓荆子\', \'黄柏\', \'知母\', \'藁本\', \'细辛\', \'川芎\', \'黄连\', \'羌活\', \'柴胡\', \'升麻\', \'防风\', \'生地黄\', \'当归\', \'甘草\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (58198, '12081', '黄耆散', '黄耆1两（锉），牛膝1两（去苗），附子1两（炮裂，去皮脐），甘草半两（炙微赤，锉），人参1两（去芦头），白茯苓3分，五味子3分，木香半两，白芍药3分，熟干地黄1两，桂心3分，柴胡1两（去苗），当归半两（锉，微炒），半夏3分（汤浸7遍，去滑），陈橘皮3分（汤浸，去白瓤，焙）。', '每服5钱，以水1大盏，加生姜半分，大枣3枚，煎至5分，去滓，食前稍热服。', '《圣惠》卷十四', '伤寒后，体气虚羸，四肢黄瘦，不思饮食。', '-', '-', '柴胡', '[\'黄耆\', \'牛膝\', \'两去\', \'附子\', \'两炮裂\', \'去皮\', \'甘草\', \'半两\', \'炙微\', \'人参\', \'两去\', \'芦头\', \'白茯苓\', \'五味子\', \'木香\', \'半两\', \'芍药\', \'分熟\', \'地黄\', \'两桂心分\', \'柴胡\', \'两去\', \'当归\', \'半两\', \'半夏\', \'分汤\', \'浸遍\', \'橘皮\', \'分汤\', \'浸去\']');
INSERT INTO `prescript` VALUES (58199, '12040', '茯苓饮', '茯苓3两，紫苏叶3两，杏仁3两，橘皮3两，升麻3两，柴胡3两，生姜4两，犀角2两（屑），槟榔12枚（并皮子，碎）。', '茯苓饮子（《鸡峰》卷四）。', '《外台》卷十八引《延年秘录》', '脚气肿，气急上气，心闷热烦，呕逆不下食。', '忌醋物。', '-', '柴胡', '[\'茯苓\', \'紫苏叶\', \'杏仁\', \'橘皮\', \'升麻\', \'柴胡\', \'生姜\', \'犀角\', \'两屑\', \'槟榔\', \'皮子\']');
INSERT INTO `prescript` VALUES (58200, '12033', '柴胡散', '柴胡1两（去苗），续断1两，芎藭3分，当归半两（锉，微炒），白术1两，赤芍药1两，厚朴1两（去粗皮，涂生姜汁炙令香熟），枳壳3两（麸炒微黄，去瓤），甘草半两（炙微赤，锉）。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十四', '妊娠7-8月伤寒，头痛壮热，心腹虚胀，四肢少力。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'续断\', \'当归\', \'半两\', \'白术\', \'两赤\', \'芍药\', \'厚朴\', \'两去\', \'粗皮\', \'涂生\', \'姜汁\', \'炙令\', \'香熟\', \'枳壳\', \'两麸\', \'微黄\', \'甘草\', \'半两\', \'炙微\']');
