INSERT INTO `prescript` VALUES (58202, '11995', '常山汤', '常山1两，鳖甲（去裙襕，醋炙黄）1两，知母（焙）1两，柴胡（去苗）1两，青蒿（焙）1两，甘草（炙锉）1两，枳壳（去瓤，麸炒）1两，桂（去粗皮）1两，桃枝1握，柳枝1握。', '每服5钱匕，水1盏半，入葱白、薤白各3寸，切，同煎至8分，去滓温服，不拘时候。', '《圣济总录》卷三十五', '劳疟经年不愈。', '-', '-', '柴胡', '[\'常山\', \'鳖甲\', \'知母\', \'柴胡\', \'青蒿\', \'甘草\', \'枳壳\', \'两桂去\', \'粗皮\', \'两桃枝握\', \'柳枝\']');
