INSERT INTO `prescript` VALUES (58204, '11962', '柴胡汤', '柴胡（去苗）1两，白术1两（米泔浸半日，炒），芎藭1分，当归（焙干）1分，芍药1分，防风（去叉）1分，赤茯苓（去黑皮）1分，黄耆（细锉）半两，生干地黄（焙）半两。', '每服3钱匕，水1盏，加大枣2枚（擘破），生姜3片，煎6分，去滓温服，不拘时候。', '《圣济总录》卷一五六', '妊娠伤寒，憎寒壮热，头痛体疼。', '-', '-', '柴胡', '[\'柴胡\', \'白术\', \'两米\', \'泔浸\', \'半日\', \'当归\', \'焙干\', \'芍药\', \'防风\', \'去叉分\', \'茯苓\', \'黑皮\', \'分黄耆\', \'半两\', \'生干\', \'地黄\', \'半两\']');
INSERT INTO `prescript` VALUES (58205, '11931', '大黄丸', '川大黄2两（锉碎，微炒），柴胡（去苗）1两，黄芩1两，黄连（去须）1两，白鲜皮1两，栀子仁1两，秦艽（去苗）1两，龙胆（去芦头）1两，赤芍药1两，大麻仁（别杵如膏）1两。', '每服30丸，以竹叶汤送下，不拘时候。以利为度。', '《圣惠》卷十六', '时气余热不退，发歇不定，大便秘涩。', '-', '-', '柴胡', '[\'大黄\', \'柴胡\', \'黄芩\', \'黄连\', \'白鲜皮\', \'栀子\', \'秦艽\', \'龙胆\', \'芦头\', \'芍药\', \'两大\', \'麻仁\', \'如膏\']');
INSERT INTO `prescript` VALUES (58206, '11921', '柴胡散', '柴胡（去苗）1两，茵陈1两，木通1两，土瓜根1两，白鲜皮1两，栀子仁1两，川芒消2两，川大黄2两（锉碎，微炒）。', '每服3钱，以温水调下，不拘时候。少时当利1-2行，利后煮葱豉稀粥食之；如热未歇，再服。', '《圣惠》卷十六', '时气恶寒，头痛，壮热，大便不通。', '-', '-', '柴胡', '[\'柴胡\', \'茵陈\', \'木通\', \'两土\', \'瓜根\', \'白鲜皮\', \'栀子\', \'两川芒\', \'消两川\', \'大黄\']');
INSERT INTO `prescript` VALUES (58207, '11898', '柴胡桑白皮汤', '柴胡（去苗）1两半，桑根白皮1两半，天雄（炮裂，去皮脐）1两半，羌活（去芦头）1两半，枳壳（去瓤，麸炒）1两半，大腹（连皮，锉）1两半，黄连（去须）1两，当归（切，焙）1两，麻黄（去根节）1两，桂（去粗皮）1两，甘草（炙，锉）1两，白梅（拍碎）4枚，黄芩（去黑心）半两，旋覆花（微炒）半两。', '每服5钱匕，水1盏半，加生姜3片，同煎至8分，去滓温服。', '《圣济总录》卷六十六', '咳嗽，上气促急，心躁寒热，四肢烦疼，夜间甚者。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两半\', \'桑根\', \'白皮\', \'两半\', \'天雄\', \'炮裂\', \'去皮\', \'两半\', \'羌活\', \'芦头\', \'两半\', \'枳壳\', \'两半\', \'大腹\', \'连皮\', \'两半\', \'黄连\', \'当归\', \'切焙\', \'麻黄\', \'根节\', \'两桂去\', \'粗皮\', \'甘草\', \'两白梅\', \'拍碎\', \'黄芩\', \'黑心\', \'半两\', \'旋覆花\', \'半两\']');
INSERT INTO `prescript` VALUES (58208, '11872', '柴胡汤', '柴胡（去苗）1两半，黄芩（去黑心）1两半，泽泻1两半，葛根（炙，锉）1两半，升麻1两半，玄参3两，生干地黄（切，焙）2两。', '每用5钱匕，水1盏半，加竹叶7片，煎至1盏，去滓，下芒消1钱匕，分为2服，空心、食后各1次。', '《圣济总录》卷八十六', '调气下热。主肝劳。关格不通，精神不守，气逆上冲，胸中烦闷。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两半\', \'黄芩\', \'黑心\', \'两半\', \'泽泻\', \'两半\', \'葛根\', \'两半\', \'升麻\', \'两半\', \'玄参\', \'两生\', \'地黄\', \'切焙\']');
INSERT INTO `prescript` VALUES (58209, '11808', '黄耆散', '黄耆半两（锉），麦门冬半两（去心），黄芩半两，葛根半两（锉），枇杷叶半两（拭去毛，炙微黄），栀子仁半两，人参半两（去芦头），赤茯苓半两，柴胡半两（去苗），赤芍药半两，甘草（炙微赤，锉）半两。', '每服5钱，以水1大盏。加生姜半分，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒烦渴不止。', '-', '-', '柴胡', '[\'黄耆\', \'半两\', \'麦门冬\', \'半两\', \'去心\', \'黄芩\', \'半两\', \'葛根\', \'半两\', \'枇杷叶\', \'半两\', \'拭去\', \'微黄\', \'栀子\', \'半两\', \'人参\', \'半两\', \'芦头\', \'茯苓\', \'半两\', \'柴胡\', \'半两\', \'芍药\', \'半两\', \'甘草\', \'炙微\', \'半两\']');
INSERT INTO `prescript` VALUES (58210, '11776', '保孺丸', '鳖甲1枚（醋煮，锉），柴胡（去土净称，锉细）2两，青橘皮（去白称）2两，使君子（去壳）1两，杏仁6两（汤浸，去皮尖，生用）。', '每服30丸，乳食前温米饮送下。', '《杨氏家藏方》卷十八', '小儿五疳羸瘦，潮热盗汗，面色萎黄，腹大股细，虽能饮食，不生肌肉。', '-', '-', '柴胡', '[\'鳖甲\', \'柴胡\', \'土净称\', \'两青\', \'橘皮\', \'白称\', \'使君子\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'生用\']');
INSERT INTO `prescript` VALUES (58211, '11772', '柴胡鳖甲饮', '鳖甲3钱（醋炙），柴胡3分（盐水炒），青蒿1钱半，地骨皮1钱半，丹皮1钱半，生地5钱，知母1钱半（盐水炒），麦冬3钱（去心），茯神2钱（去木），乌梅3枚。', '水煎，去滓温服。', '《医略六书》卷十九', '滋阴疏热。主虚劳，骨蒸烦渴，脉弦数。', '-', '-', '柴胡', '[\'鳖甲\', \'柴胡\', \'盐水\', \'青蒿\', \'钱半\', \'地骨皮\', \'钱半\', \'丹皮\', \'半生\', \'知母\', \'钱半\', \'盐水\', \'麦冬\', \'茯神\', \'乌梅\']');
INSERT INTO `prescript` VALUES (58212, '11767', '大柴胡加玄参地黄汤', '柴胡3钱，黄芩2钱，半夏3钱，芍药1钱，枳实1钱，大黄2钱，生姜2钱，大枣2枚，玄参2钱，生地3钱。', '流水煎大半杯，分2次温服。', '《四圣悬枢》卷四', '少阳疹病，半入阳明胃腑，呕吐泄利。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'半夏\', \'芍药\', \'枳实\', \'大黄\', \'生姜\', \'大枣\', \'玄参\', \'生地\']');
INSERT INTO `prescript` VALUES (58213, '11765', '柴胡牡蛎汤', '柴胡3钱，牡蛎1钱（粉），甘草2钱，瓜蒌3钱，半夏3钱，芍药3钱，生姜3钱。', '水煎大半杯，温服。', '《医学摘粹》', '心腹疼痛，痛在心胸，热多者。', '-', '-', '柴胡', '[\'柴胡\', \'牡蛎\', \'钱粉\', \'甘草\', \'钱瓜\', \'半夏\', \'芍药\', \'生姜\']');
INSERT INTO `prescript` VALUES (58214, '11754', '调元渗湿汤', '羌活8分，白术8分，防风8分，独活8分，升麻4分，苍术1钱，猪苓1钱，柴胡1钱，茯苓1钱，泽泻6分，干葛6分，甘草6分，人参6分，黄柏6分，神曲6分。', '水煎，空心服。', '《玉案》卷三', '肾疸。目与浑身金色，小便赤涩。', '-', '-', '柴胡', '[\'羌活\', \'白术\', \'防风\', \'独活\', \'升麻\', \'苍术\', \'猪苓\', \'柴胡\', \'茯苓\', \'泽泻\', \'分干葛分\', \'甘草\', \'人参\', \'黄柏\', \'神曲\']');
INSERT INTO `prescript` VALUES (58215, '11737', '大柴胡汤', '柴胡2两，半夏3分，黄芩3分，甘草3分，枳实半两，大黄1两。', '上(口父)咀。每服5钱，水1盏半，加生姜7片，大枣1个，煎6分，去滓，食前服。', '《易简方》', '伤寒十余日，热结在里，往来寒热；或心下急，郁郁微烦；或口生白苔，大便不通；或发热汗出；或腹中满痛；或日晡发热如疟；或6-7日目中不了了，睛不和，无表里证，大便难，身微热者。', '热除，不宜遽服补药，仍忌羊肉、腰子、酒，并难化之物。避房室。', '-', '柴胡', '[\'柴胡\', \'半夏\', \'黄芩\', \'甘草\', \'枳实\', \'半两\', \'大黄\']');
INSERT INTO `prescript` VALUES (58216, '11677', '柴荆饮', '柴胡1钱，薄荷1钱，荆芥1钱，甘菊1钱，甘草3分，茯苓3钱，白芍4钱，白蒺藜2钱，草决明2钱，炒栀子2钱，密蒙花5分，半夏5分。', '水煎服。', '《辨证录》卷三', '因肝木风火作祟，脾胃之气不能升腾，而致目痛如刺触，两角多眵，羞明畏日，两胞浮肿，泪湿不已。', '-', '-', '柴胡', '[\'柴胡\', \'薄荷\', \'荆芥\', \'甘菊\', \'甘草\', \'茯苓\', \'白芍\', \'钱白\', \'蒺藜\', \'草决明\', \'栀子\', \'密蒙花\', \'半夏\']');
INSERT INTO `prescript` VALUES (58217, '11675', '柴胡散', '柴胡3分（去苗），枳壳半两（麸炒微黄，去瓤），黄芩3分，赤芍药3分，半夏半两（汤洗7遍去滑），大腹皮半两（锉），槟榔3分，木香半两。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十二', '伤寒发汗后气壅不散，攻心腹胀痛。', '-', '-', '柴胡', '[\'柴胡\', \'分去\', \'枳壳\', \'半两\', \'微黄\', \'黄芩\', \'分赤\', \'芍药\', \'半夏\', \'半两\', \'洗遍\', \'大腹皮\', \'半两\', \'槟榔\', \'木香\', \'半两\']');
INSERT INTO `prescript` VALUES (58218, '11638', '大黄丸', '大黄（锉，炒）1两，丹砂（研）1分半，人参1分半，枳壳（去瓤，麸炒）1分半，白茯苓（去黑皮）1分半，柴胡（去苗），桂（去粗皮）半分，木香1分。', '每服5丸，乳汁送下，1日3次。大者加至20丸。', '《圣济总录》卷一七六', '宣下胎中宿物。主小儿初生至百日周晬，腹内有气，冲心喉；及壮热头疼，呕逆腹痛，寒热乳癖。', '-', '-', '柴胡', '[\'大黄\', \'丹砂\', \'研分\', \'人参\', \'分半\', \'枳壳\', \'分半\', \'白茯苓\', \'黑皮\', \'分半\', \'柴胡\', \'苗桂去\', \'粗皮\', \'半分\', \'木香\']');
INSERT INTO `prescript` VALUES (58219, '11630', '柴胡散', '柴胡（去苗）1两，枳实（麸炒微黄）1两，栝楼根1两，黄芩1两，栀子仁1两，茵陈1两，白鲜皮1两，川大黄（锉碎，微炒）1两，甘草（炙微赤，锉）1两。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十五', '时气6日，壮热，骨节烦疼，头痛，目眩，心胁气胀急硬，不能饮食，恐变为黄。', '-', '-', '柴胡', '[\'柴胡\', \'枳实\', \'微黄\', \'栝楼\', \'根两\', \'黄芩\', \'栀子\', \'茵陈\', \'白鲜皮\', \'两川\', \'大黄\', \'甘草\', \'炙微\']');
INSERT INTO `prescript` VALUES (58220, '11584', '菊花清燥汤', '甘菊花2钱，当归1钱，生地1钱，白芍1钱（酒炒），川芎1钱，知母1钱，贝母1钱（去心，研），地骨皮1钱，麦冬（去心）1钱，柴胡5分，黄芩5分，升麻5分，犀角5分（镑），甘草（生）5分。', '加竹叶20片，灯心20寸，水2钟，煎8分，食后温服。', '《金鉴》卷六十八', '石榴疽，焮肿。', '-', '-', '柴胡', '[\'甘菊花\', \'当归\', \'生地\', \'白芍\', \'钱酒\', \'川芎\', \'知母\', \'贝母\', \'心研\', \'地骨皮\', \'麦冬\', \'心钱\', \'柴胡\', \'黄芩\', \'升麻\', \'犀角\', \'分镑\', \'甘草\', \'生分\']');
INSERT INTO `prescript` VALUES (58221, '11568', '健步丸', '防己（酒洗）1两，羌活5钱，柴胡5钱，滑石（炒）5钱，炙甘草5钱，瓜蒌根（酒洗）5钱，泽泻3钱，防风3钱，苦参（酒洗）1钱，川乌1钱，肉桂5分。', '本方改为膏剂，名“健步膏”（见《理瀹》）。', '《兰室秘藏》卷下', '膝中无力，伸而不得屈，屈而不能伸，腰背腿膝沉重，行步艰难。', '-', '-', '柴胡', '[\'防己\', \'酒洗\', \'羌活\', \'柴胡\', \'滑石\', \'甘草\', \'钱瓜\', \'根酒\', \'洗钱\', \'泽泻\', \'防风\', \'苦参\', \'洗钱\', \'川乌\', \'肉桂\']');
