INSERT INTO `prescript` VALUES (58223, '11557', '麻黄散', '麻黄3分（去根节），葛根3分（锉），柴胡1两（去苗），知母3分，赤芍药1两，栀子仁3分，石膏1两半，陈橘皮半两（汤浸，去白瓤，焙），生干地黄1两。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷九', '伤寒4日吐后，或壮热头痛，身体酸疼，口苦心烦。', '-', '-', '柴胡', '[\'麻黄\', \'分去\', \'根节\', \'葛根\', \'柴胡\', \'两去\', \'知母\', \'分赤\', \'芍药\', \'栀子\', \'仁分\', \'石膏\', \'两半\', \'橘皮\', \'半两\', \'汤浸\', \'生干\', \'地黄\']');
INSERT INTO `prescript` VALUES (58224, '11551', '柴胡汤', '柴胡3两，枳实3两（炙），栝楼3两，黄芩3两，栀子仁3两，茵陈3两，龙胆2两，大黄3两（切）。', '以水9升，煮取2升7合，去滓，分温3服。', '《外台》卷三引《延年秘录》', '天行5-6日，壮热，骨烦疼，兼两胁连心助下气胀急硬痛，不能食，恐变发黄者。', '忌热面、蒜。', '-', '柴胡', '[\'柴胡\', \'枳实\', \'栝楼\', \'黄芩\', \'栀子\', \'茵陈\', \'龙胆\', \'大黄\', \'两切\']');
INSERT INTO `prescript` VALUES (58225, '11547', '黄耆散', '黄耆3分（锉），鳖甲3分（涂醋，炙令黄，去裙襕），人参3分（去芦头），柴胡3分（去苗），赤茯苓3分，桑根白皮3分（锉），木通3分（锉），羚羊角屑3分，知母3分，麦门冬3分（去心），地骨皮3分，甘草（炙微赤，锉）3分，赤芍药1两，白术1两，枳壳（麸炒微黄，去瓤）1两。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒得汗后，热不除，发歇身热，肢节烦疼。', '-', '-', '柴胡', '[\'黄耆\', \'鳖甲\', \'分涂\', \'炙令\', \'黄去\', \'人参\', \'分去\', \'芦头\', \'柴胡\', \'分去\', \'茯苓\', \'分桑根\', \'白皮\', \'木通\', \'羚羊角\', \'知母\', \'麦门冬\', \'分去\', \'地骨皮\', \'甘草\', \'炙微\', \'芍药\', \'白术\', \'枳壳\', \'微黄\']');
INSERT INTO `prescript` VALUES (58226, '11540', '柴胡汤', '柴胡（去苗）1两1分，葛根（锉）2两，白茯苓（去黑皮）1两半，麦门冬（去心，焙）1两半，麻黄（去根节）1两半，桂（去粗皮）3分，萎蕤（切，焙）2两，黄芩（去黑心）1两。', '每服5钱匕，水1盏半，煎取8分，去滓，食后温服。衣被覆取微汗，汗不可太过。', '《圣济总录》卷八十四', '服乳石脚气发动，或憎寒壮热，头痛心闷，眼目疼，恶心欲吐，头旋脑痛。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两分\', \'葛根\', \'白茯苓\', \'黑皮\', \'两半\', \'麦门冬\', \'两半\', \'麻黄\', \'根节\', \'两半\', \'桂去\', \'粗皮分\', \'萎蕤\', \'黄芩\', \'黑心\']');
