INSERT INTO `prescript` VALUES (58228, '11525', '柴胡散', '柴胡（去苗）1两，茵陈1两，川大黄（锉碎，微炒）1两，黄芩1两，木通（锉）1两，川升麻1两，栀子仁1两，川芒消1两，茅根（锉）1两。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。以得通利为度。', '《圣惠》卷十六', '时气恶寒壮热，头痛，小便不通。', '-', '-', '柴胡', '[\'柴胡\', \'茵陈\', \'两川\', \'大黄\', \'黄芩\', \'木通\', \'两川\', \'升麻\', \'栀子\', \'两川芒\', \'消两茅根\']');
INSERT INTO `prescript` VALUES (58229, '11511', '羚羊解毒汤', '紫草1钱，黑参1钱，柴胡8分，荆芥6分，蝉蜕4分，川芎5分，红花3分，山楂1钱，连翘8分，木通7分，羚羊角尖（镑细）1钱。', '水煎，去滓，入羚羊角末，搅匀服之。', '《张氏医通》卷十五', '小儿痘初起，根窠不分，颧颊一片如朱涂。', '-', '-', '柴胡', '[\'紫草\', \'参钱\', \'柴胡\', \'荆芥\', \'蝉蜕\', \'川芎\', \'红花\', \'山楂\', \'连翘\', \'木通\', \'羚羊角\', \'尖镑\', \'细钱\']');
INSERT INTO `prescript` VALUES (58230, '11492', '大泽泻汤', '柴胡1两，茯神（《外台》作茯苓）1两，黄芩1两，泽泻1两，升麻1两，杏仁1两，磁石4两（碎），羚羊角1两，地黄3两，大青3两，芒消3两，淡竹叶（切）1升。', '上(口父)咀。以水1斗，煮取3升，去滓，下芒消，分3服。', '方出《千金》卷十九，名见《圣济总录》卷五十一', '肾热。好怒好忘，耳听无闻，四肢满急，腰背转动强直。', '-', '-', '柴胡', '[\'柴胡\', \'茯神\', \'外台作\', \'茯苓\', \'黄芩\', \'泽泻\', \'升麻\', \'杏仁\', \'磁石\', \'两碎\', \'羚羊角\', \'两地\', \'黄两大青\', \'两芒\', \'消两\', \'淡竹叶\', \'切升\']');
